import React, { useState, useEffect, useRef } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, AreaChart, Area } from 'recharts';

const App = () => {
  const [currentView, setCurrentView] = useState('dashboard');
  const [modules, setModules] = useState([]);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [chatInput, setChatInput] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [userName, setUserName] = useState(null);
  const [analytics, setAnalytics] = useState(null);
  const [clientId] = useState('default'); // In production, this would be dynamic
  
  const chatInputRef = useRef(null);
  const chatHistoryRef = useRef(null);

  // Load client data on startup
  useEffect(() => {
    loadClientData();
    loadAnalytics();
  }, []);

  const loadClientData = async () => {
    try {
      const response = await fetch(`/api/client/load/${clientId}`);
      const data = await response.json();
      
      setModules(data.modules || []);
      setUserName(data.userName);
      setChatHistory(data.conversationHistory || []);
      
      console.log('✅ Client data loaded:', data);
    } catch (error) {
      console.error('Failed to load client data:', error);
    }
  };

  const loadAnalytics = async () => {
    try {
      const response = await fetch(`/api/analytics/${clientId}`);
      const data = await response.json();
      setAnalytics(data);
    } catch (error) {
      console.error('Failed to load analytics:', error);
    }
  };

  const saveClientData = async (updatedData) => {
    try {
      await fetch(`/api/client/save/${clientId}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updatedData)
      });
    } catch (error) {
      console.error('Failed to save client data:', error);
    }
  };

  const sendMessage = async () => {
    if (!chatInput.trim() || isLoading) return;
    
    const userMessage = chatInput.trim();
    setChatInput('');
    setIsLoading(true);
    
    // Add user message to chat
    const newUserMessage = {
      message: userMessage,
      type: 'user',
      timestamp: new Date().toISOString()
    };
    
    setChatHistory(prev => [...prev, newUserMessage]);
    
    try {
      const response = await fetch('/api/aetheris/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          message: userMessage,
          clientId
        })
      });
      
      const data = await response.json();
      
      // Add AI response to chat
      const aiMessage = {
        message: data.response,
        type: 'ai',
        timestamp: new Date().toISOString()
      };
      
      setChatHistory(prev => [...prev, aiMessage]);
      
      // Update modules if new ones were created
      if (data.modules) {
        setModules(data.modules);
      }
      
      // Update user name if learned
      if (data.userName && data.userName !== userName) {
        setUserName(data.userName);
      }
      
      // Refresh analytics
      loadAnalytics();
      
    } catch (error) {
      console.error('Chat error:', error);
      setChatHistory(prev => [...prev, {
        message: "Sorry, I'm having trouble connecting. Please try again.",
        type: 'ai',
        timestamp: new Date().toISOString()
      }]);
    }
    
    setIsLoading(false);
  };

  const deleteModule = async (moduleId) => {
    try {
      const response = await fetch(`/api/modules/${clientId}/${moduleId}`, {
        method: 'DELETE'
      });
      
      const data = await response.json();
      if (data.success) {
        setModules(data.modules);
        loadAnalytics();
        console.log('✅ Module deleted successfully');
      }
    } catch (error) {
      console.error('Failed to delete module:', error);
    }
  };

  // Auto-scroll chat to bottom
  useEffect(() => {
    if (chatHistoryRef.current) {
      chatHistoryRef.current.scrollTop = chatHistoryRef.current.scrollHeight;
    }
  }, [chatHistory]);

  // Handle Enter key in chat
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // Mock data for charts
  const performanceData = [
    { month: 'Jan', revenue: 45000, efficiency: 88 },
    { month: 'Feb', revenue: 52000, efficiency: 92 },
    { month: 'Mar', revenue: 48000, efficiency: 85 },
    { month: 'Apr', revenue: 61000, efficiency: 94 },
    { month: 'May', revenue: 55000, efficiency: 89 },
    { month: 'Jun', revenue: 67000, efficiency: 96 }
  ];

  const moduleDistribution = modules.length > 0 ? 
    Object.entries(modules.reduce((acc, module) => {
      acc[module.type] = (acc[module.type] || 0) + 1;
      return acc;
    }, {})).map(([name, value]) => ({ name, value })) :
    [{ name: 'No Modules', value: 1 }];

  const COLORS = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#06B6D4'];

  const renderDashboard = () => (
    <div className="p-8 max-w-7xl mx-auto">
      <div className="mb-8">
        <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
          {userName ? `Welcome back, ${userName}!` : 'Sentari OS Dashboard'}
        </h1>
        <p className="text-gray-600 mt-2">Your AI-powered business intelligence platform</p>
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <div className="bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl p-6 text-white">
          <h3 className="text-sm font-medium opacity-90">Active Modules</h3>
          <p className="text-3xl font-bold mt-1">{modules.length}</p>
          <p className="text-sm mt-2 opacity-75">
            {modules.length === 0 ? 'Build your first module!' : 'Platform modules running'}
          </p>
        </div>
        
        <div className="bg-gradient-to-r from-green-500 to-green-600 rounded-xl p-6 text-white">
          <h3 className="text-sm font-medium opacity-90">Monthly Revenue</h3>
          <p className="text-3xl font-bold mt-1">
            ${analytics?.monthlyRevenue?.toLocaleString() || '0'}
          </p>
          <p className="text-sm mt-2 opacity-75">+12% from last month</p>
        </div>
        
        <div className="bg-gradient-to-r from-purple-500 to-purple-600 rounded-xl p-6 text-white">
          <h3 className="text-sm font-medium opacity-90">Platform Efficiency</h3>
          <p className="text-3xl font-bold mt-1">{analytics?.efficiency || '0'}%</p>
          <p className="text-sm mt-2 opacity-75">AI-optimized operations</p>
        </div>
        
        <div className="bg-gradient-to-r from-orange-500 to-orange-600 rounded-xl p-6 text-white">
          <h3 className="text-sm font-medium opacity-90">Client Satisfaction</h3>
          <p className="text-3xl font-bold mt-1">{analytics?.clientSatisfaction || '0'}%</p>
          <p className="text-sm mt-2 opacity-75">Exceeding expectations</p>
        </div>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
        <div className="bg-white rounded-xl shadow-lg p-6">
          <h3 className="text-xl font-semibold mb-4">Performance Trends</h3>
          <ResponsiveContainer width="100%" height={300}>
            <AreaChart data={performanceData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              <Area type="monotone" dataKey="revenue" stroke="#3B82F6" fill="#3B82F6" fillOpacity={0.3} />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-white rounded-xl shadow-lg p-6">
          <h3 className="text-xl font-semibold mb-4">Module Distribution</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={moduleDistribution}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
              >
                {moduleDistribution.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-xl shadow-lg p-6">
        <h3 className="text-xl font-semibold mb-4">Quick Actions</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <button 
            onClick={() => setIsChatOpen(true)}
            className="flex items-center space-x-3 p-4 bg-blue-50 hover:bg-blue-100 rounded-lg transition-colors"
          >
            <div className="w-10 h-10 bg-blue-500 rounded-lg flex items-center justify-center">
              <span className="text-white font-bold">🧠</span>
            </div>
            <div className="text-left">
              <p className="font-medium">Chat with AI</p>
              <p className="text-sm text-gray-600">Build modules, ask questions</p>
            </div>
          </button>
          
          <button 
            onClick={loadAnalytics}
            className="flex items-center space-x-3 p-4 bg-green-50 hover:bg-green-100 rounded-lg transition-colors"
          >
            <div className="w-10 h-10 bg-green-500 rounded-lg flex items-center justify-center">
              <span className="text-white font-bold">📊</span>
            </div>
            <div className="text-left">
              <p className="font-medium">Refresh Data</p>
              <p className="text-sm text-gray-600">Update analytics</p>
            </div>
          </button>
          
          <button 
            onClick={() => setCurrentView('modules')}
            className="flex items-center space-x-3 p-4 bg-purple-50 hover:bg-purple-100 rounded-lg transition-colors"
          >
            <div className="w-10 h-10 bg-purple-500 rounded-lg flex items-center justify-center">
              <span className="text-white font-bold">⚙️</span>
            </div>
            <div className="text-left">
              <p className="font-medium">Manage Modules</p>
              <p className="text-sm text-gray-600">View and customize</p>
            </div>
          </button>
        </div>
      </div>
    </div>
  );

  const renderModules = () => (
    <div className="p-8 max-w-7xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-800">Module Library</h1>
        <p className="text-gray-600 mt-2">Manage your business modules</p>
      </div>

      {modules.length === 0 ? (
        <div className="text-center py-12">
          <div className="text-6xl mb-4">🤖</div>
          <h2 className="text-2xl font-semibold text-gray-700 mb-2">No modules built yet</h2>
          <p className="text-gray-600 mb-6">Chat with Aetheris AI to build your first business module!</p>
          <button
            onClick={() => setIsChatOpen(true)}
            className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-lg font-medium transition-colors"
          >
            🧠 Start Building with AI
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {modules.map((module) => (
            <div key={module.id} className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <span className="text-2xl">{module.icon}</span>
                  <h3 className="text-xl font-semibold text-gray-800">{module.name}</h3>
                </div>
                <button
                  onClick={() => deleteModule(module.id)}
                  className="text-gray-400 hover:text-red-500 transition-colors"
                  title="Delete module"
                >
                  🗑️
                </button>
              </div>
              
              <div className="space-y-2 mb-4">
                <p className="text-sm text-gray-600 font-medium">Key Features:</p>
                <ul className="text-sm text-gray-700 space-y-1">
                  {module.features.slice(0, 3).map((feature, index) => (
                    <li key={index} className="flex items-start space-x-2">
                      <span className="text-green-500 mt-0.5">•</span>
                      <span>{feature}</span>
                    </li>
                  ))}
                  {module.features.length > 3 && (
                    <li className="text-gray-500 text-xs">
                      +{module.features.length - 3} more features
                    </li>
                  )}
                </ul>
              </div>
              
              <div className="flex space-x-2">
                <button 
                  onClick={() => setCurrentView(module.type)}
                  className="flex-1 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg text-sm font-medium transition-colors"
                >
                  Open Module
                </button>
                <button 
                  onClick={() => {
                    setIsChatOpen(true);
                    setChatInput(`Enhance my ${module.name} module`);
                  }}
                  className="bg-gray-100 hover:bg-gray-200 text-gray-700 px-4 py-2 rounded-lg text-sm font-medium transition-colors"
                >
                  Enhance
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  const renderModuleView = (moduleType) => {
    const module = modules.find(m => m.type === moduleType);
    if (!module) {
      return (
        <div className="p-8 text-center">
          <h2 className="text-2xl font-bold text-gray-700 mb-4">Module Not Found</h2>
          <button
            onClick={() => setCurrentView('dashboard')}
            className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-lg"
          >
            Back to Dashboard
          </button>
        </div>
      );
    }

    return (
      <div className="p-8 max-w-7xl mx-auto">
        <div className="mb-8 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <span className="text-4xl">{module.icon}</span>
            <div>
              <h1 className="text-3xl font-bold text-gray-800">{module.name}</h1>
              <p className="text-gray-600">Professional module interface</p>
            </div>
          </div>
          <button
            onClick={() => setCurrentView('dashboard')}
            className="bg-gray-100 hover:bg-gray-200 text-gray-700 px-4 py-2 rounded-lg font-medium"
          >
            ← Back
          </button>
        </div>

        <div className="bg-white rounded-xl shadow-lg p-8">
          <h2 className="text-xl font-semibold mb-6">Module Features</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {module.features.map((feature, index) => (
              <div key={index} className="flex items-start space-x-3 p-4 bg-gray-50 rounded-lg">
                <span className="text-green-500 mt-1">✅</span>
                <div>
                  <p className="font-medium text-gray-800">{feature}</p>
                  <p className="text-sm text-gray-600 mt-1">Ready to use</p>
                </div>
              </div>
            ))}
          </div>
          
          <div className="mt-8 p-6 bg-blue-50 rounded-lg">
            <h3 className="font-semibold text-blue-800 mb-2">🚀 Module Active</h3>
            <p className="text-blue-700">
              This {module.name} module is fully operational and integrated with your platform. 
              All features are active and ready for your business use.
            </p>
            <button
              onClick={() => {
                setIsChatOpen(true);
                setChatInput(`How do I use my ${module.name} module effectively?`);
              }}
              className="mt-4 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg font-medium"
            >
              Get Usage Help
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className="fixed left-0 top-0 h-full w-64 bg-white shadow-lg z-40">
        <div className="p-6 border-b border-gray-200">
          <h1 className="text-xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
            Sentari OS
          </h1>
          {userName && (
            <p className="text-sm text-gray-600 mt-1">Welcome, {userName}</p>
          )}
        </div>
        
        <nav className="p-4 space-y-2">
          <button
            onClick={() => setCurrentView('dashboard')}
            className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg text-left transition-colors ${
              currentView === 'dashboard' 
                ? 'bg-blue-100 text-blue-700 border border-blue-200' 
                : 'hover:bg-gray-100 text-gray-700'
            }`}
          >
            <span>📊</span>
            <span className="font-medium">Dashboard</span>
          </button>
          
          <button
            onClick={() => setCurrentView('modules')}
            className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg text-left transition-colors ${
              currentView === 'modules' 
                ? 'bg-blue-100 text-blue-700 border border-blue-200' 
                : 'hover:bg-gray-100 text-gray-700'
            }`}
          >
            <span>⚙️</span>
            <span className="font-medium">Module Library</span>
          </button>
          
          {modules.map((module) => (
            <button
              key={module.id}
              onClick={() => setCurrentView(module.type)}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg text-left transition-colors ${
                currentView === module.type 
                  ? 'bg-blue-100 text-blue-700 border border-blue-200' 
                  : 'hover:bg-gray-100 text-gray-700'
              }`}
            >
              <span>{module.icon}</span>
              <span className="font-medium truncate">{module.name}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Main Content */}
      <div className="ml-64">
        {currentView === 'dashboard' && renderDashboard()}
        {currentView === 'modules' && renderModules()}
        {modules.some(m => m.type === currentView) && renderModuleView(currentView)}
      </div>

      {/* Floating AI Chat Button */}
      <button
        onClick={() => setIsChatOpen(true)}
        className="fixed bottom-6 right-6 w-14 h-14 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-full shadow-lg hover:shadow-xl transition-all duration-300 flex items-center justify-center text-2xl z-50"
      >
        🧠
      </button>

      {/* AI Chat Interface */}
      {isChatOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black bg-opacity-50">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-2xl h-[600px] flex flex-col">
            {/* Chat Header */}
            <div className="flex items-center justify-between p-6 border-b border-gray-200">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white font-bold">
                  🧠
                </div>
                <div>
                  <h3 className="text-lg font-semibold">Chat with Aetheris</h3>
                  <p className="text-sm text-gray-600">Your AI business architect</p>
                </div>
              </div>
              <button
                onClick={() => setIsChatOpen(false)}
                className="text-gray-400 hover:text-gray-600 text-2xl"
              >
                ×
              </button>
            </div>

            {/* Chat History */}
            <div 
              ref={chatHistoryRef}
              className="flex-1 overflow-y-auto p-6 space-y-4"
            >
              {chatHistory.length === 0 && (
                <div className="text-center py-8">
                  <div className="text-4xl mb-4">👋</div>
                  <h4 className="text-lg font-semibold text-gray-700 mb-2">
                    {userName ? `Hello ${userName}!` : 'Hello!'}
                  </h4>
                  <p className="text-gray-600">
                    I'm Aetheris, your AI business architect. I can help you build modules,
                    answer questions, and optimize your platform. What can I help you with?
                  </p>
                </div>
              )}
              
              {chatHistory.map((msg, index) => (
                <div key={index} className={`flex ${msg.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                  <div className={`max-w-[80%] p-3 rounded-lg ${
                    msg.type === 'user'
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-100 text-gray-800'
                  }`}>
                    <p className="whitespace-pre-wrap">{msg.message}</p>
                    <p className={`text-xs mt-1 ${
                      msg.type === 'user' ? 'text-blue-100' : 'text-gray-500'
                    }`}>
                      {new Date(msg.timestamp).toLocaleTimeString()}
                    </p>
                  </div>
                </div>
              ))}
              
              {isLoading && (
                <div className="flex justify-start">
                  <div className="bg-gray-100 p-3 rounded-lg">
                    <p className="text-gray-600">Aetheris is thinking...</p>
                  </div>
                </div>
              )}
            </div>

            {/* Chat Input */}
            <div className="p-6 border-t border-gray-200">
              <div className="flex space-x-3">
                <input
                  ref={chatInputRef}
                  type="text"
                  value={chatInput}
                  onChange={(e) => setChatInput(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder={`Type your message${userName ? `, ${userName}` : ''}...`}
                  className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  disabled={isLoading}
                />
                <button
                  onClick={sendMessage}
                  disabled={isLoading || !chatInput.trim()}
                  className="bg-gradient-to-r from-blue-500 to-purple-600 text-white px-6 py-3 rounded-lg font-medium hover:opacity-90 disabled:opacity-50 disabled:cursor-not-allowed transition-all"
                >
                  Send
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
