import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import { Calendar, Users, MapPin, BarChart3, Clock, Shield, Zap, DollarSign, TrendingUp, Activity, X, MessageSquare, Wrench, Code, Settings, Loader, RefreshCw, Upload, FileText, AlertCircle } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  // Core Platform State
  const [activeModule, setActiveModule] = useState('dashboard');
  const [platformModules, setPlatformModules] = useState([]);
  const [aiChatOpen, setAiChatOpen] = useState(false);
  const [buildHistory, setBuildHistory] = useState([]);
  
  // Real Dashboard Data State
  const [realDashboardData, setRealDashboardData] = useState(null);
  const [dashboardLoading, setDashboardLoading] = useState(true);
  const [loadingError, setLoadingError] = useState(null);
  
  // Delete confirmation state
  const [deleteConfirmation, setDeleteConfirmation] = useState(null);
  
  // AI Chat State
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [userName, setUserName] = useState(null);
  const [companyName, setCompanyName] = useState('');
  const chatInputRef = useRef(null);
  const chatMessagesRef = useRef(null);

  // Chat UI State
  const [chatWidth, setChatWidth] = useState(450);
  const [chatHeight, setChatHeight] = useState(600);
  const [isResizing, setIsResizing] = useState(false);

  // File Upload State
  const [fileAnalysis, setFileAnalysis] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [dragActive, setDragActive] = useState(false);

  // Connection status
  const [backendConnected, setBackendConnected] = useState(false);

  // Auto-scroll effect for chat
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [chatMessages, isProcessing]);

  // Initialize platform on startup
  useEffect(() => {
    initializePlatform();
  }, []);

  // Comprehensive platform initialization
  const initializePlatform = async () => {
    console.log('ðŸš€ Initializing Sentari OS Platform...');
    
    // Initialize default modules first
    initializeDefaultModules();
    
    // Test backend connection
    await testBackendConnection();
    
    // Load real data and modules
    await Promise.all([
      loadRealDashboardData(),
      loadExistingModules()
    ]);
    
    console.log('âœ… Platform initialization complete');
  };

  // Test backend connection
  const testBackendConnection = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/health');
      const data = await response.json();
      
      if (data.status) {
        setBackendConnected(true);
        console.log('âœ… Backend connected successfully');
      } else {
        setBackendConnected(false);
        console.warn('âš ï¸ Backend health check failed');
      }
    } catch (error) {
      setBackendConnected(false);
      console.error('âŒ Backend connection failed:', error);
    }
  };

  // Enhanced dashboard data loading with error handling
  const loadRealDashboardData = useCallback(async () => {
    try {
      setDashboardLoading(true);
      setLoadingError(null);
      
      console.log('ðŸ“Š Loading real dashboard data...');
      
      const response = await fetch('http://localhost:3000/api/business-metrics');
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
      
      const data = await response.json();
      
      if (data.success) {
        setRealDashboardData(data.metrics);
        if (data.companyName) setCompanyName(data.companyName);
        console.log('âœ… Real dashboard data loaded:', data.metrics);
      } else {
        throw new Error(data.error || 'Failed to load dashboard data');
      }
    } catch (error) {
      console.error('âŒ Dashboard data fetch error:', error);
      setLoadingError(error.message);
      // Set fallback data
      setRealDashboardData({
        monthlyRevenue: 0,
        activeGuards: 0,
        totalSites: 0,
        totalClients: 0,
        guardsOnLeave: 0,
        averageHourlyRate: 0,
        lastUpdated: null
      });
    } finally {
      setDashboardLoading(false);
    }
  }, []);

  // Enhanced module loading with better error handling
  const loadExistingModules = useCallback(async () => {
    try {
      console.log('ðŸ“¦ Loading existing modules...');
      
      const response = await fetch('http://localhost:3000/api/modules');
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
      
      const data = await response.json();
      
      if (data.success) {
        const builtModules = data.modules || [];
        const serverUserName = data.userName;
        
        console.log(`ðŸ“¦ Loaded ${builtModules.length} persistent modules from backend`);
        
        const convertedModules = builtModules.map(module => ({
          id: module.id,
          name: module.name,
          type: 'ai-generated',
          status: 'active',
          description: module.description,
          features: module.features || [],
          created: module.created,
          hasGPS: module.hasGPS || false,
          originalType: module.type
        }));
        
        setPlatformModules(prev => {
          const coreModules = prev.filter(m => m.type === 'core' || m.type === 'ai' || m.type === 'system');
          const newTotal = [...coreModules, ...convertedModules];
          console.log(`ðŸ“Š Total modules after reload: ${newTotal.length} (${coreModules.length} core + ${convertedModules.length} AI)`);
          return newTotal;
        });
        
        if (serverUserName && serverUserName !== userName) {
          setUserName(serverUserName);
        }
      } else {
        throw new Error(data.error || 'Failed to load modules');
      }
    } catch (error) {
      console.error('âŒ Failed to load existing modules:', error);
    }
  }, [userName]);

  const initializeDefaultModules = () => {
    setPlatformModules(prev => {
      const hasDefaults = prev.some(m => m.id === 'dashboard');
      if (hasDefaults) return prev;
      
      return [
        { 
          id: 'dashboard', 
          name: 'Executive Dashboard', 
          type: 'core', 
          status: 'active',
          description: 'Real-time business metrics and AI insights'
        },
        { 
          id: 'business-setup', 
          name: 'AI Business Setup', 
          type: 'ai', 
          status: 'active',
          description: 'Aetheris-powered business configuration wizard'
        },
        { 
          id: 'module-library', 
          name: 'Module Library', 
          type: 'system', 
          status: 'active',
          description: 'Manage and deploy AI-built business modules'
        }
      ];
    });
  };

  // File Upload Functions
  const handleFileUpload = async (file) => {
    if (!file) return;
    
    console.log('ðŸ“ Processing file upload:', file.name);
    setIsAnalyzing(true);
    setFileAnalysis(null);
    
    const formData = new FormData();
    formData.append('file', file);
    
    try {
      const response = await fetch('http://localhost:3000/api/analyze-file', {
        method: 'POST',
        body: formData
      });
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
      
      const result = await response.json();
      console.log('ðŸ§  File analysis result:', result);
      
      if (result.success) {
        setFileAnalysis(result);
        
        // Add AI message about file analysis
        setChatMessages(prev => [...prev, {
          id: Date.now(),
          sender: 'aetheris',
          message: `ðŸ“ **File Analysis Complete!**

**File:** ${result.fileName}
**Type:** ${result.analysis.dataType}
**Records:** ${result.recordCount}
**Suggested Module:** ${result.analysis.suggestedModuleName}

${result.requiresUserDecision ? 'âš ï¸ **Conflicts Found** - I found existing similar modules.' : 'âœ… **Ready to Create** - No conflicts detected.'}

What would you like me to do with this data?`,
          timestamp: new Date().toISOString()
        }]);
        
        setAiChatOpen(true); // Open chat to show results
      } else {
        throw new Error(result.error || 'File analysis failed');
      }
    } catch (error) {
      console.error('File upload error:', error);
      setChatMessages(prev => [...prev, {
        id: Date.now(),
        sender: 'aetheris',
        message: `âŒ **File Analysis Error**: ${error.message}

Supported formats: CSV, Excel (.xlsx, .xls), JSON, TXT`,
        timestamp: new Date().toISOString()
      }]);
      setAiChatOpen(true);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    
    const files = Array.from(e.dataTransfer.files);
    const file = files[0];
    
    if (file) {
      handleFileUpload(file);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragActive(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setDragActive(false);
  };

  // Enhanced chat message processing
  const processChatMessage = useCallback(async () => {
    if (!currentMessage.trim() || isProcessing) return;
    
    const userMessage = currentMessage;
    setCurrentMessage('');
    setIsProcessing(true);
    
    // Add user message immediately
    const newUserMessage = {
      id: Date.now(),
      sender: 'user',
      message: userMessage,
      timestamp: new Date().toISOString()
    };
    setChatMessages(prev => [...prev, newUserMessage]);
    
    try {
      console.log('ðŸŽ¯ Sending message to Aetheris:', userMessage);
      
      const response = await fetch('http://localhost:3000/api/aetheris/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          message: userMessage,
          userId: userName || 'anonymous',
          companyId: companyName || 'unknown',
          fileAnalysisContext: fileAnalysis
        })
      });
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
      
      const result = await response.json();
      console.log('âœ… Aetheris response received:', result);
      
      // Add AI response
      const aiMessage = {
        id: Date.now() + 1,
        sender: 'aetheris',
        message: result.response || 'No response received',
        timestamp: new Date().toISOString()
      };
      setChatMessages(prev => [...prev, aiMessage]);
      
      // Handle data changes
      if (result.dataChanges && result.dataChanges.length > 0) {
        console.log('ðŸ“Š Business data changed, reloading dashboard...');
        setTimeout(() => loadRealDashboardData(), 500);
      }
      
      // Handle new modules
      if (result.newModule) {
        const newPlatformModule = {
          id: result.newModule.id,
          name: result.newModule.name,
          type: 'ai-generated',
          status: 'active',
          description: result.newModule.description,
          features: result.newModule.features || [],
          created: result.newModule.created,
          hasGPS: result.newModule.hasGPS || false,
          originalType: result.newModule.type
        };
        
        setPlatformModules(prev => {
          const exists = prev.some(m => m.id === newPlatformModule.id);
          if (exists) return prev;
          
          console.log('ðŸ“¦ Adding new module to frontend:', newPlatformModule.name);
          return [...prev, newPlatformModule];
        });
        
        setBuildHistory(prev => [...prev, {
          request: userMessage,
          module: newPlatformModule,
          timestamp: new Date().toISOString()
        }]);
      }
      
      // Handle user info updates
      if (result.userName && result.userName !== userName) {
        setUserName(result.userName);
        console.log('ðŸ‘¤ User name updated:', result.userName);
      }
      
      if (result.companyName && result.companyName !== companyName) {
        setCompanyName(result.companyName);
        console.log('ðŸ¢ Company name updated:', result.companyName);
      }
      
      // Clear file analysis if it was processed
      if (fileAnalysis && userMessage.includes('create') || userMessage.includes('module')) {
        setFileAnalysis(null);
      }
      
    } catch (error) {
      console.error('âŒ Chat error:', error);
      const errorMessage = {
        id: Date.now() + 1,
        sender: 'aetheris',
        message: `**Connection Error**: ${error.message}\n\nMake sure your backend server is running on port 3000!`,
        timestamp: new Date().toISOString()
      };
      setChatMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsProcessing(false);
      
      // Return focus to input
      setTimeout(() => {
        if (chatInputRef.current) {
          chatInputRef.current.focus();
        }
      }, 100);
    }
  }, [currentMessage, isProcessing, userName, companyName, loadRealDashboardData, fileAnalysis]);

  // Enhanced module deletion with proper error handling
  const deleteModule = useCallback(async (moduleId) => {
    const moduleToDelete = platformModules.find(m => m.id === moduleId);
    if (!moduleToDelete) {
      console.error('âŒ Module not found for deletion:', moduleId);
      return;
    }
    
    console.log('ðŸ—‘ï¸ Preparing to delete module:', moduleToDelete.name);
    
    setDeleteConfirmation({
      moduleId,
      moduleName: moduleToDelete.name,
      moduleType: moduleToDelete.type
    });
  }, [platformModules]);

  // Confirm and execute module deletion
  const confirmDeleteModule = useCallback(async () => {
    if (!deleteConfirmation) {
      console.error('âŒ No delete confirmation found');
      return;
    }
    
    console.log('ðŸ”¥ Executing module deletion:', deleteConfirmation.moduleId);
    
    try {
      const response = await fetch(`http://localhost:3000/api/modules/${deleteConfirmation.moduleId}`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' }
      });
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
      
      const data = await response.json();
      
      if (data.success) {
        // Remove from frontend state immediately
        setPlatformModules(prev => {
          const newModules = prev.filter(m => m.id !== deleteConfirmation.moduleId);
          console.log(`ðŸ“Š Modules updated: ${prev.length} â†’ ${newModules.length}`);
          return newModules;
        });
        
        // Switch to dashboard if we deleted the active module
        if (activeModule === deleteConfirmation.moduleId) {
          setActiveModule('dashboard');
        }
        
        // Show success message
        setChatMessages(prev => [...prev, {
          id: Date.now(),
          sender: 'aetheris',
          message: `âœ… Module "${deleteConfirmation.moduleName}" deleted successfully${data.backupCreated ? ' (backup created)' : ''}`,
          timestamp: new Date().toISOString()
        }]);
        
        console.log('âœ… Module deletion completed successfully');
      } else {
        throw new Error(data.error || 'Failed to delete module');
      }
    } catch (error) {
      console.error('âŒ Delete request failed:', error);
      
      setChatMessages(prev => [...prev, {
        id: Date.now(),
        sender: 'aetheris',
        message: `âŒ Failed to delete module: ${error.message}`,
        timestamp: new Date().toISOString()
      }]);
    } finally {
      setDeleteConfirmation(null);
    }
  }, [deleteConfirmation, activeModule]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Ctrl/Cmd + K to open AI chat
      if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
        e.preventDefault();
        setAiChatOpen(prev => !prev);
      }
      
      // Escape to close chat or dialogs
      if (e.key === 'Escape') {
        if (aiChatOpen) {
          setAiChatOpen(false);
        } else if (deleteConfirmation) {
          setDeleteConfirmation(null);
        } else if (fileAnalysis) {
          setFileAnalysis(null);
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [aiChatOpen, deleteConfirmation, fileAnalysis]);

  // File Upload Zone Component
  const FileUploadZone = () => (
    <div
      onDrop={handleDrop}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      style={{
        position: 'fixed',
        top: dragActive ? '0' : '20px',
        left: dragActive ? '0' : '20px',
        right: dragActive ? '0' : '20px',
        bottom: dragActive ? '0' : 'auto',
        height: dragActive ? '100vh' : '120px',
        background: dragActive 
          ? 'rgba(59, 130, 246, 0.1)' 
          : 'rgba(15, 23, 42, 0.9)',
        border: dragActive 
          ? '3px dashed rgba(59, 130, 246, 0.8)' 
          : '2px dashed rgba(59, 130, 246, 0.3)',
        borderRadius: dragActive ? '0' : '12px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: dragActive ? 2000 : 1001,
        backdropFilter: 'blur(12px)',
        transition: 'all 0.3s ease',
        cursor: 'pointer'
      }}
      onClick={() => {
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = '.csv,.xlsx,.xls,.json,.txt';
        input.onchange = (e) => handleFileUpload(e.target.files[0]);
        input.click();
      }}
    >
      {isAnalyzing ? (
        <>
