          {/* Module Features */}
          {module.features && module.features.length > 0 && (
            <div style={{ marginBottom: '32px' }}>
              <h3 style={{ 
                color: '#e2e8f0', 
                fontSize: '20px', 
                marginBottom: '16px',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}>
                âš¡ Module Features
              </h3>
              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
                gap: '12px' 
              }}>
                {module.features.map((feature, idx) => (
                  <div key={idx} style={{
                    padding: '16px',
                    background: 'rgba(34, 197, 94, 0.1)',
                    border: '1px solid rgba(34, 197, 94, 0.3)',
                    borderRadius: '8px',
                    color: '#e2e8f0',
                    fontSize: '14px'
                  }}>
                    âœ… {feature}
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Module Content Placeholder */}
          <div style={{
            background: 'rgba(59, 130, 246, 0.1)',
            border: '1px solid rgba(59, 130, 246, 0.3)',
            borderRadius: '8px',
            padding: '48px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '48px', marginBottom: '16px' }}>ðŸš§</div>
            <h3 style={{ color: '#3b82f6', fontSize: '24px', marginBottom: '8px' }}>
              Module Interface Coming Soon
            </h3>
            <p style={{ color: '#94a3b8', fontSize: '16px', marginBottom: '24px' }}>
              This {module.type === 'ai-generated' ? 'AI-generated' : 'system'} module is active and ready for development.
              The user interface will be built based on your specific requirements.
            </p>
            
            <div style={{ display: 'flex', gap: '12px', justifyContent: 'center' }}>
              <button
                onClick={() => setAiChatOpen(true)}
                style={{
                  padding: '12px 24px',
                  background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                  border: 'none',
                  borderRadius: '8px',
                  color: 'white',
                  fontSize: '14px',
                  cursor: 'pointer',
                  fontWeight: '600'
                }}
              >
                ðŸ§  Enhance This Module
              </button>
              
              {module.type === 'ai-generated' && (
                <button
                  onClick={() => deleteModule(module.id)}
                  style={{
                    padding: '12px 24px',
                    background: 'rgba(239, 68, 68, 0.2)',
                    border: '1px solid rgba(239, 68, 68, 0.3)',
                    borderRadius: '8px',
                    color: '#ef4444',
                    fontSize: '14px',
                    cursor: 'pointer',
                    fontWeight: '600'
                  }}
                >
                  ðŸ—‘ï¸ Delete Module
                </button>
              )}
            </div>
          </div>

          {/* Module Metadata */}
          <div style={{
            marginTop: '32px',
            paddingTop: '16px',
            borderTop: '1px solid rgba(59, 130, 246, 0.2)'
          }}>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
              gap: '16px'
            }}>
              <div style={{
                padding: '12px',
                background: 'rgba(59, 130, 246, 0.1)',
                borderRadius: '6px'
              }}>
                <div style={{ color: '#3b82f6', fontSize: '12px', fontWeight: '600' }}>
                  MODULE TYPE
                </div>
                <div style={{ color: '#e2e8f0', fontSize: '14px' }}>
                  {module.type === 'ai-generated' ? 'AI-Generated' : 
                   module.type === 'ai' ? 'AI Core' :
                   module.type === 'system' ? 'System' : 'Core'}
                </div>
              </div>
              
              <div style={{
                padding: '12px',
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '6px'
              }}>
                <div style={{ color: '#22c55e', fontSize: '12px', fontWeight: '600' }}>
                  STATUS
                </div>
                <div style={{ color: '#e2e8f0', fontSize: '14px' }}>
                  {module.status || 'Active'}
                </div>
              </div>
              
              {module.created && (
                <div style={{
                  padding: '12px',
                  background: 'rgba(139, 92, 246, 0.1)',
                  borderRadius: '6px'
                }}>
                  <div style={{ color: '#8b5cf6', fontSize: '12px', fontWeight: '600' }}>
                    CREATED
                  </div>
                  <div style={{ color: '#e2e8f0', fontSize: '14px' }}>
                    {new Date(module.created).toLocaleDateString()}
                  </div>
                </div>
              )}
              
              {module.hasGPS && (
                <div style={{
                  padding: '12px',
                  background: 'rgba(245, 158, 11, 0.1)',
                  borderRadius: '6px'
                }}>
                  <div style={{ color: '#f59e0b', fontSize: '12px', fontWeight: '600' }}>
                    CAPABILITIES
                  </div>
                  <div style={{ color: '#e2e8f0', fontSize: '14px' }}>
                    GPS Tracking Enabled
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Enhanced Delete Confirmation Dialog
  const DeleteConfirmationDialog = () => {
    if (!deleteConfirmation) return null;

    return (
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'rgba(0, 0, 0, 0.8)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 2000,
        backdropFilter: 'blur(8px)'
      }}>
        <div style={{
          background: 'rgba(15, 23, 42, 0.95)',
          border: '1px solid rgba(239, 68, 68, 0.5)',
          borderRadius: '12px',
          padding: '32px',
          maxWidth: '500px',
          width: '90%',
          textAlign: 'center'
        }}>
          <div style={{ fontSize: '64px', marginBottom: '16px' }}>âš ï¸</div>
          
          <h2 style={{
            color: '#ef4444',
            fontSize: '24px',
            marginBottom: '8px',
            fontWeight: '600'
          }}>
            Delete Module
          </h2>
          
          <p style={{
            color: '#e2e8f0',
            fontSize: '16px',
            marginBottom: '8px'
          }}>
            Are you sure you want to delete:
          </p>
          
          <div style={{
            background: 'rgba(239, 68, 68, 0.1)',
            border: '1px solid rgba(239, 68, 68, 0.3)',
            borderRadius: '8px',
            padding: '16px',
            margin: '16px 0'
          }}>
            <div style={{
              color: '#ef4444',
              fontSize: '18px',
              fontWeight: '600',
              marginBottom: '4px'
            }}>
              {deleteConfirmation.moduleName}
            </div>
            <div style={{
              color: '#94a3b8',
              fontSize: '14px'
            }}>
              Type: {deleteConfirmation.moduleType}
            </div>
          </div>
          
          <p style={{
            color: '#94a3b8',
            fontSize: '14px',
            marginBottom: '24px'
          }}>
            This action cannot be undone. All module data will be permanently deleted.
          </p>
          
          <div style={{
            display: 'flex',
            gap: '12px',
            justifyContent: 'center'
          }}>
            <button
              onClick={() => setDeleteConfirmation(null)}
              style={{
                padding: '12px 24px',
                background: 'rgba(107, 114, 128, 0.2)',
                border: '1px solid rgba(107, 114, 128, 0.3)',
                borderRadius: '8px',
                color: '#6b7280',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '600'
              }}
            >
              Cancel
            </button>
            
            <button
              onClick={confirmDeleteModule}
              style={{
                padding: '12px 24px',
                background: 'linear-gradient(135deg, #ef4444, #dc2626)',
                border: 'none',
                borderRadius: '8px',
                color: 'white',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '600'
              }}
            >
              Delete Forever
            </button>
          </div>
        </div>
      </div>
    );
  };

  // AI Chat Sidebar Component
  const AIChatSidebar = () => {
    if (!aiChatOpen) return null;

    return (
      <div style={{
        position: 'fixed',
        top: 0,
        right: 0,
        width: `${chatWidth}px`,
        height: `${chatHeight}px`,
        background: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(12px)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '12px 0 0 12px',
        display: 'flex',
        flexDirection: 'column',
        zIndex: 1500,
        maxWidth: '90vw',
        maxHeight: '100vh',
        resize: 'both',
        overflow: 'hidden'
      }}>
        {/* Resize Handle */}
        <div style={{
          position: 'absolute',
          bottom: 0,
          left: 0,
          width: '12px',
          height: '12px',
          background: 'rgba(59, 130, 246, 0.3)',
          cursor: 'nw-resize',
          borderRadius: '12px 0 0 0'
        }} />

        {/* Chat Header */}
        <div style={{
          padding: '16px',
          borderBottom: '1px solid rgba(59, 130, 246, 0.3)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}>
          <div>
            <h3 style={{
              color: '#3b82f6',
              fontSize: '18px',
              fontWeight: '600',
              margin: 0,
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              ðŸ§  Aetheris AI
              <div style={{
                width: '8px',
                height: '8px',
                borderRadius: '50%',
                background: '#22c55e'
              }} />
            </h3>
            <p style={{
              color: '#94a3b8',
              fontSize: '12px',
              margin: 0
            }}>
              Your AI Business Assistant
            </p>
          </div>
          
          <button
            onClick={() => setAiChatOpen(false)}
            style={{
              background: 'rgba(239, 68, 68, 0.2)',
              border: '1px solid rgba(239, 68, 68, 0.3)',
              borderRadius: '6px',
              color: '#ef4444',
              padding: '6px',
              cursor: 'pointer',
              fontSize: '14px'
            }}
          >
            <X size={16} />
          </button>
        </div>

        {/* File Analysis Status */}
        {fileAnalysis && (
          <div style={{
            padding: '12px 16px',
            background: 'rgba(34, 197, 94, 0.1)',
            border: '1px solid rgba(34, 197, 94, 0.3)',
            borderTop: 'none',
            color: '#22c55e',
            fontSize: '12px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            <FileText size={14} />
            File Analysis Ready: {fileAnalysis.fileName}
            <button
              onClick={() => setFileAnalysis(null)}
              style={{
                background: 'none',
                border: 'none',
                color: '#22c55e',
                cursor: 'pointer',
                marginLeft: 'auto'
              }}
            >
              <X size={12} />
            </button>
          </div>
        )}

        {/* Chat Messages */}
        <div 
          ref={chatMessagesRef}
          style={{
            flex: 1,
            padding: '16px',
            overflowY: 'auto',
            display: 'flex',
            flexDirection: 'column',
            gap: '12px'
          }}
        >
          {chatMessages.length === 0 ? (
            <div style={{
              textAlign: 'center',
              color: '#94a3b8',
              fontSize: '14px',
              marginTop: '40px'
            }}>
              <div style={{ fontSize: '48px', marginBottom: '16px' }}>ðŸ§ </div>
              <p style={{ marginBottom: '8px' }}>Hello! I'm Aetheris.</p>
              <p style={{ fontSize: '12px', color: '#6b7280', marginBottom: '16px' }}>
                Ask me to build modules, analyze data, or help with your business!
              </p>
              <div style={{
                padding: '8px 12px',
                background: 'rgba(34, 197, 94, 0.1)',
                border: '1px solid rgba(34, 197, 94, 0.3)',
                borderRadius: '6px',
                fontSize: '11px',
                color: '#22c55e'
              }}>
                ðŸ’¡ Try: "Build employee module" or drag & drop files
              </div>
            </div>
          ) : (
            chatMessages.map(msg => (
              <div key={msg.id} style={{
                display: 'flex',
                justifyContent: msg.sender === 'user' ? 'flex-end' : 'flex-start'
              }}>
                <div style={{
                  maxWidth: '85%',
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'rgba(139, 92, 246, 0.3)',
                  border: `1px solid ${msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.5)' 
                    : 'rgba(139, 92, 246, 0.5)'}`,
                  color: '#e2e8f0',
                  fontSize: '13px',
                  lineHeight: '1.4',
                  whiteSpace: 'pre-wrap',
                  wordWrap: 'break-word'
                }}>
                  {msg.message}
                  {msg.timestamp && (
                    <div style={{ 
                      fontSize: '10px', 
                      color: '#94a3b8', 
                      marginTop: '4px',
                      opacity: 0.7
                    }}>
                      {new Date(msg.timestamp).toLocaleTimeString()}
                    </div>
                  )}
                </div>
              </div>
            ))
          )}
          
          {isProcessing && (
            <div style={{
              display: 'flex',
              justifyContent: 'flex-start'
            }}>
              <div style={{
                padding: '12px 16px',
                borderRadius: '12px',
                background: 'rgba(139, 92, 246, 0.3)',
                border: '1px solid rgba(139, 92, 246, 0.5)',
                color: '#e2e8f0',
                fontSize: '13px',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}>
                <Loader size={14} className="animate-spin" />
                Aetheris is thinking...
              </div>
            </div>
          )}
        </div>

        {/* Chat Input */}
        <div style={{
          padding: '16px',
          borderTop: '1px solid rgba(59, 130, 246, 0.3)'
        }}>
          <div style={{ display: 'flex', gap: '8px', marginBottom: '8px' }}>
            <textarea
              ref={chatInputRef}
              value={currentMessage}
              onChange={(e) => setCurrentMessage(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  processChatMessage();
                }
              }}
              placeholder="Ask me anything..."
              style={{
                flex: 1,
                padding: '10px',
                background: 'rgba(15, 23, 42, 0.6)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '6px',
                color: '#e2e8f0',
                fontSize: '13px',
                resize: 'none',
                height: '60px',
                fontFamily: 'inherit',
                outline: 'none'
              }}
              disabled={isProcessing}
            />
            <button
              onClick={processChatMessage}
              disabled={isProcessing || !currentMessage.trim()}
              style={{
                padding: '10px 12px',
                background: isProcessing || !currentMessage.trim()
                  ? 'rgba(107, 114, 128, 0.3)' 
                  : 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                border: 'none',
                borderRadius: '6px',
                color: 'white',
                fontSize: '13px',
                cursor: isProcessing || !currentMessage.trim() ? 'not-allowed' : 'pointer',
                fontWeight: '500',
                minWidth: '50px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
            >
              {isProcessing ? <Loader size={14} className="animate-spin" /> : 'ðŸš€'}
            </button>
          </div>
          
          <div style={{ 
            fontSize: '10px',
            color: '#6b7280',
            textAlign: 'center'
          }}>
            Shift+Enter for new line â€¢ Escape to close
          </div>
        </div>
      </div>
    );
  };

  // Main render logic
  const renderActiveModule = () => {
    switch (activeModule) {
      case 'dashboard':
        return <Dashboard />;
      case 'business-setup':
        return <BusinessSetup />;
      case 'module-library':
        return <ModuleLibrary />;
      default:
        return <GenericModule moduleId={activeModule} />;
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f172a, #1e293b)',
      color: '#e2e8f0',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      position: 'relative'
    }}>
      {/* Connection Status */}
      <ConnectionStatus />

      {/* File Upload Zone - Always present but invisible unless dragging */}
      <FileUploadZone />

      {/* Main Content */}
      {renderActiveModule()}

      {/* AI Chat Sidebar */}
      <AIChatSidebar />

      {/* Delete Confirmation Dialog */}
      <DeleteConfirmationDialog />

      {/* Global Floating Action Button */}
      <button
        onClick={() => setAiChatOpen(true)}
        style={{
          position: 'fixed',
          bottom: '24px',
          right: '24px',
          width: '64px',
          height: '64px',
          borderRadius: '50%',
          background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
          border: 'none',
          color: 'white',
          fontSize: '24px',
          cursor: 'pointer',
          boxShadow: '0 8px 25px rgba(59, 130, 246, 0.4)',
          zIndex: 1000,
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => {
          e.target.style.transform = 'scale(1.1)';
          e.target.style.boxShadow = '0 12px 35px rgba(59, 130, 246, 0.6)';
        }}
        onMouseLeave={(e) => {
          e.target.style.transform = 'scale(1)';
          e.target.style.boxShadow = '0 8px 25px rgba(59, 130, 246, 0.4)';
        }}
        title="Chat with Aetheris (Ctrl+K)"
      >
        ðŸ§ 
      </button>

      {/* Module Navigation */}
      <div style={{
        position: 'fixed',
        bottom: '24px',
        left: '50%',
        transform: 'translateX(-50%)',
        background: 'rgba(15, 23, 42, 0.9)',
        backdropFilter: 'blur(12px)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '50px',
        padding: '12px 20px',
        display: 'flex',
        gap: '16px',
        zIndex: 999
      }}>
        {[
          { id: 'dashboard', icon: 'ðŸ“Š', label: 'Dashboard' },
          { id: 'business-setup', icon: 'ðŸ§ ', label: 'AI Setup' },
          { id: 'module-library', icon: 'ðŸ“¦', label: 'Modules' }
        ].map(nav => (
          <button
            key={nav.id}
            onClick={() => setActiveModule(nav.id)}
            style={{
              background: activeModule === nav.id 
                ? 'linear-gradient(135deg, #3b82f6, #8b5cf6)' 
                : 'transparent',
              border: activeModule === nav.id 
                ? 'none' 
                : '1px solid rgba(59, 130, 246, 0.3)',
              borderRadius: '25px',
              color: activeModule === nav.id ? 'white' : '#94a3b8',
              padding: '8px 16px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '500',
              transition: 'all 0.3s ease',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}
            title={nav.label}
          >
            <span style={{ fontSize: '16px' }}>{nav.icon}</span>
            <span style={{ fontSize: '12px' }}>{nav.label}</span>
          </button>
        ))}
      </div>
    </div>
  );
};

export default SentariOS;
