import React, { useState, useEffect, useRef } from 'react';
import { Calendar, Users, MapPin, BarChart3, Clock, Shield, Zap, DollarSign, TrendingUp, Activity, X, MessageSquare, Wrench, Code } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area } from 'recharts';

const SentariOS = () => {
  // Core Platform State
  const [activeModule, setActiveModule] = useState('dashboard');
  const [platformModules, setPlatformModules] = useState([]);
  const [aiBuilderOpen, setAiBuilderOpen] = useState(false);
  const [buildHistory, setBuildHistory] = useState([]);
  
  // AI Module Builder State
  const [buildRequest, setBuildRequest] = useState('');
  const [buildResponse, setBuildResponse] = useState('');
  const [isBuilding, setIsBuilding] = useState(false);
  const builderRef = useRef(null);

  // Initialize platform with default modules
  useEffect(() => {
    setPlatformModules([
      { 
        id: 'dashboard', 
        name: 'Dashboard', 
        type: 'core', 
        status: 'active',
        description: 'Executive overview and KPIs'
      },
      { 
        id: 'ai-builder', 
        name: 'Aetheris AI Builder', 
        type: 'ai', 
        status: 'active',
        description: 'Build any module with natural language'
      },
      { 
        id: 'module-library', 
        name: 'Module Library', 
        type: 'system', 
        status: 'active',
        description: 'Manage and view all platform modules'
      }
    ]);
  }, []);

  // Sample performance data
  const performanceData = [
    { name: 'Mon', revenue: 12400, efficiency: 95 },
    { name: 'Tue', revenue: 13800, efficiency: 88 },
    { name: 'Wed', revenue: 15200, efficiency: 92 },
    { name: 'Thu', revenue: 14100, efficiency: 89 },
    { name: 'Fri', revenue: 16800, efficiency: 97 },
    { name: 'Sat', revenue: 18200, efficiency: 94 },
    { name: 'Sun', revenue: 11500, efficiency: 91 }
  ];

  // Aetheris AI Module Builder Component
  const AetherisAIBuilder = () => {
    const submitBuildRequest = async () => {
      if (!buildRequest.trim()) return;
      
      setIsBuilding(true);
      setBuildResponse('🧠 Aetheris is analyzing your request...');
      
      try {
        const response = await fetch('http://localhost:3000/api/aetheris/build-module', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            request: buildRequest,
            existingModules: platformModules,
            businessContext: 'security_services' // From your MIP context
          })
        });
        
        const result = await response.json();
        setBuildResponse(result.response);
        
        // If AI successfully built a module, add it to platform
        if (result.success && result.newModule) {
          setPlatformModules(prev => [...prev, result.newModule]);
          setBuildHistory(prev => [...prev, {
            request: buildRequest,
            module: result.newModule,
            timestamp: new Date().toISOString()
          }]);
        }
        
      } catch (error) {
        setBuildResponse('❌ **Error**: Could not connect to Aetheris AI backend. Make sure your server is running!');
      }
      
      setIsBuilding(false);
      setBuildRequest('');
    };

    const handleKeyPress = (e) => {
      if (e.key === 'Enter' && !e.shiftKey) {
        e.preventDefault();
        submitBuildRequest();
      }
    };

    return (
      <div style={{ 
        padding: '24px',
        background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(147, 51, 234, 0.1))',
        minHeight: '100vh'
      }}>
        <div style={{
          background: 'rgba(15, 23, 42, 0.8)',
          backdropFilter: 'blur(12px)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '12px',
          padding: '32px',
          maxWidth: '800px',
          margin: '0 auto'
        }}>
          <div style={{ textAlign: 'center', marginBottom: '32px' }}>
            <h1 style={{ 
              fontSize: '32px', 
              fontWeight: 'bold', 
              color: '#3b82f6',
              marginBottom: '8px',
              textShadow: '0 0 20px rgba(59, 130, 246, 0.5)'
            }}>
              🧠 Aetheris AI Builder
            </h1>
            <p style={{ color: '#94a3b8', fontSize: '18px' }}>
              Tell me what module you need and I'll build it for your platform
            </p>
          </div>

          {/* AI Request Input */}
          <div style={{ marginBottom: '24px' }}>
            <label style={{ 
              display: 'block', 
              marginBottom: '8px', 
              color: '#e2e8f0', 
              fontWeight: '500' 
            }}>
              What would you like me to build?
            </label>
            <textarea
              ref={builderRef}
              value={buildRequest}
              onChange={(e) => setBuildRequest(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Example: Build a complete billing and invoicing module with vendor payment support, or Create inventory management with equipment check-in/out..."
              rows={4}
              style={{
                width: '100%',
                padding: '16px',
                background: 'rgba(15, 23, 42, 0.6)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '8px',
                color: '#e2e8f0',
                fontSize: '16px',
                resize: 'vertical',
                fontFamily: 'inherit'
              }}
            />
          </div>

          {/* Build Button */}
          <button
            onClick={submitBuildRequest}
            disabled={isBuilding || !buildRequest.trim()}
            style={{
              width: '100%',
              padding: '16px',
              background: isBuilding 
                ? 'rgba(59, 130, 246, 0.3)' 
                : 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
              border: 'none',
              borderRadius: '8px',
              color: 'white',
              fontSize: '18px',
              fontWeight: '600',
              cursor: isBuilding ? 'not-allowed' : 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px',
              boxShadow: '0 4px 20px rgba(59, 130, 246, 0.3)',
              transition: 'all 0.3s ease'
            }}
          >
            {isBuilding ? (
              <>
                <div style={{
                  width: '20px',
                  height: '20px',
                  border: '2px solid rgba(255,255,255,0.3)',
                  borderRadius: '50%',
                  borderTopColor: 'white',
                  animation: 'spin 1s linear infinite'
                }}></div>
                Building Module...
              </>
            ) : (
              <>🚀 Build Module</>
            )}
          </button>

          {/* AI Response */}
          {buildResponse && (
            <div style={{
              marginTop: '24px',
              padding: '20px',
              background: 'rgba(15, 23, 42, 0.6)',
              border: '1px solid rgba(34, 197, 94, 0.3)',
              borderRadius: '8px'
            }}>
              <h3 style={{ color: '#22c55e', marginBottom: '16px', fontSize: '18px' }}>
                🧠 Aetheris Response:
              </h3>
              <div style={{ 
                color: '#e2e8f0', 
                lineHeight: '1.6',
                whiteSpace: 'pre-wrap'
              }}>
                {buildResponse}
              </div>
            </div>
          )}

          {/* Build History */}
          {buildHistory.length > 0 && (
            <div style={{ marginTop: '32px' }}>
              <h3 style={{ color: '#3b82f6', marginBottom: '16px', fontSize: '20px' }}>
                📊 Recent Builds
              </h3>
              {buildHistory.slice(-3).map((build, index) => (
                <div key={index} style={{
                  padding: '16px',
                  background: 'rgba(15, 23, 42, 0.4)',
                  border: '1px solid rgba(59, 130, 246, 0.2)',
                  borderRadius: '8px',
                  marginBottom: '12px'
                }}>
                  <div style={{ color: '#3b82f6', fontWeight: '500' }}>
                    📦 {build.module?.name || 'Custom Module'}
                  </div>
                  <div style={{ color: '#94a3b8', fontSize: '14px' }}>
                    {build.request}
                  </div>
                  <div style={{ color: '#6b7280', fontSize: '12px', marginTop: '4px' }}>
                    {new Date(build.timestamp).toLocaleString()}
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Quick Templates */}
          <div style={{ marginTop: '32px' }}>
            <h3 style={{ color: '#8b5cf6', marginBottom: '16px', fontSize: '20px' }}>
              ⚡ Quick Build Templates
            </h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '12px' }}>
              {[
                'Build complete billing and invoicing module',
                'Create inventory management system',
                'Build employee performance tracking',
                'Create client portal with reporting',
                'Build vendor management system',
                'Create payroll processing module'
              ].map((template, index) => (
                <button
                  key={index}
                  onClick={() => setBuildRequest(template)}
                  style={{
                    padding: '12px',
                    background: 'rgba(139, 92, 246, 0.1)',
                    border: '1px solid rgba(139, 92, 246, 0.3)',
                    borderRadius: '6px',
                    color: '#c4b5fd',
                    fontSize: '14px',
                    cursor: 'pointer',
                    textAlign: 'left',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.background = 'rgba(139, 92, 246, 0.2)';
                    e.target.style.borderColor = 'rgba(139, 92, 246, 0.5)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.background = 'rgba(139, 92, 246, 0.1)';
                    e.target.style.borderColor = 'rgba(139, 92, 246, 0.3)';
                  }}
                >
                  {template}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Spinning animation */}
        <style>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  };

  // Module Library Component
  const ModuleLibrary = () => {
    const activeModules = platformModules.filter(m => m.status === 'active');
    const archivedModules = buildHistory;

    return (
      <div style={{ 
        padding: '24px',
        background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(147, 51, 234, 0.1))',
        minHeight: '100vh'
      }}>
        <h1 style={{ 
          fontSize: '32px', 
          fontWeight: 'bold', 
          color: '#3b82f6',
          marginBottom: '32px',
          textAlign: 'center'
        }}>
          📦 Module Library
        </h1>

        {/* Active Modules */}
        <div style={{ marginBottom: '32px' }}>
          <h2 style={{ color: '#22c55e', fontSize: '24px', marginBottom: '16px' }}>
            ✅ Active Modules ({activeModules.length})
          </h2>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '16px' }}>
            {activeModules.map(module => (
              <div key={module.id} style={{
                padding: '20px',
                background: 'rgba(15, 23, 42, 0.8)',
                border: '1px solid rgba(34, 197, 94, 0.3)',
                borderRadius: '12px',
                backdropFilter: 'blur(12px)'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '8px' }}>
                  <div style={{ 
                    width: '40px', 
                    height: '40px', 
                    background: 'rgba(34, 197, 94, 0.2)',
                    borderRadius: '8px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center'
                  }}>
                    {module.type === 'ai' ? '🧠' : 
                     module.type === 'core' ? '⚡' : '📦'}
                  </div>
                  <div>
                    <h3 style={{ color: '#e2e8f0', fontSize: '18px', fontWeight: '600' }}>
                      {module.name}
                    </h3>
                    <div style={{ color: '#94a3b8', fontSize: '14px' }}>
                      {module.type} • {module.status}
                    </div>
                  </div>
                </div>
                <p style={{ color: '#94a3b8', fontSize: '14px', marginBottom: '16px' }}>
                  {module.description}
                </p>
                <div style={{ display: 'flex', gap: '8px' }}>
                  <button
                    onClick={() => setActiveModule(module.id)}
                    style={{
                      padding: '8px 16px',
                      background: 'rgba(59, 130, 246, 0.2)',
                      border: '1px solid rgba(59, 130, 246, 0.3)',
                      borderRadius: '6px',
                      color: '#3b82f6',
                      fontSize: '14px',
                      cursor: 'pointer'
                    }}
                  >
                    Open
                  </button>
                  {module.type !== 'core' && (
                    <button
                      style={{
                        padding: '8px 16px',
                        background: 'rgba(239, 68, 68, 0.2)',
                        border: '1px solid rgba(239, 68, 68, 0.3)',
                        borderRadius: '6px',
                        color: '#ef4444',
                        fontSize: '14px',
                        cursor: 'pointer'
                      }}
                    >
                      Archive
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Build History */}
        {buildHistory.length > 0 && (
          <div>
            <h2 style={{ color: '#8b5cf6', fontSize: '24px', marginBottom: '16px' }}>
              🏗️ Build History ({buildHistory.length})
            </h2>
            <div style={{ display: 'grid', gap: '12px' }}>
              {buildHistory.slice(-5).reverse().map((build, index) => (
                <div key={index} style={{
                  padding: '16px',
                  background: 'rgba(15, 23, 42, 0.6)',
                  border: '1px solid rgba(139, 92, 246, 0.3)',
                  borderRadius: '8px',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center'
                }}>
                  <div>
                    <div style={{ color: '#e2e8f0', fontWeight: '500' }}>
                      📦 {build.module?.name || 'Custom Module'}
                    </div>
                    <div style={{ color: '#94a3b8', fontSize: '14px' }}>
                      "{build.request}"
                    </div>
                    <div style={{ color: '#6b7280', fontSize: '12px' }}>
                      {new Date(build.timestamp).toLocaleDateString()}
                    </div>
                  </div>
                  <button
                    onClick={() => setActiveModule(build.module?.id)}
                    style={{
                      padding: '8px 16px',
                      background: 'rgba(139, 92, 246, 0.2)',
                      border: '1px solid rgba(139, 92, 246, 0.3)',
                      borderRadius: '6px',
                      color: '#8b5cf6',
                      fontSize: '14px',
                      cursor: 'pointer'
                    }}
                  >
                    View
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  };

  // Dashboard Component
  const Dashboard = () => (
    <div style={{ 
      padding: '24px',
      background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(147, 51, 234, 0.1))',
      minHeight: '100vh'
    }}>
      <h1 style={{ 
        fontSize: '32px', 
        fontWeight: 'bold', 
        color: '#3b82f6',
        marginBottom: '32px',
        textAlign: 'center'
      }}>
        🧠 Sentari OS - AI-Powered Business Platform
      </h1>

      {/* KPI Cards */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '24px', marginBottom: '32px' }}>
        {[
          { label: 'Active Modules', value: platformModules.filter(m => m.status === 'active').length, icon: '📦', color: '#22c55e' },
          { label: 'AI Builds Today', value: buildHistory.length, icon: '🏗️', color: '#3b82f6' },
          { label: 'Platform Health', value: '98%', icon: '💚', color: '#10b981' },
          { label: 'Aetheris Status', value: 'Online', icon: '🧠', color: '#8b5cf6' }
        ].map((kpi, index) => (
          <div key={index} style={{
            padding: '24px',
            background: 'rgba(15, 23, 42, 0.8)',
            border: '1px solid rgba(59, 130, 246, 0.3)',
            borderRadius: '12px',
            backdropFilter: 'blur(12px)',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '32px', marginBottom: '8px' }}>{kpi.icon}</div>
            <div style={{ fontSize: '28px', fontWeight: 'bold', color: kpi.color, marginBottom: '4px' }}>
              {kpi.value}
            </div>
            <div style={{ color: '#94a3b8', fontSize: '14px' }}>{kpi.label}</div>
          </div>
        ))}
      </div>

      {/* Performance Chart */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.8)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '12px',
        padding: '24px',
        backdropFilter: 'blur(12px)',
        marginBottom: '32px'
      }}>
        <h2 style={{ color: '#e2e8f0', fontSize: '20px', marginBottom: '20px' }}>
          📈 Platform Performance
        </h2>
        <ResponsiveContainer width="100%" height={300}>
          <AreaChart data={performanceData}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(59, 130, 246, 0.1)" />
            <XAxis dataKey="name" stroke="#94a3b8" />
            <YAxis stroke="#94a3b8" />
            <Tooltip 
              contentStyle={{
                background: 'rgba(15, 23, 42, 0.9)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '8px',
                color: '#e2e8f0'
              }}
            />
            <Area 
              type="monotone" 
              dataKey="revenue" 
              stroke="#3b82f6" 
              fill="rgba(59, 130, 246, 0.2)"
              strokeWidth={2}
            />
          </AreaChart>
        </ResponsiveContainer>
      </div>

      {/* Quick Actions */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.8)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '12px',
        padding: '24px',
        backdropFilter: 'blur(12px)'
      }}>
        <h2 style={{ color: '#e2e8f0', fontSize: '20px', marginBottom: '20px' }}>
          🚀 Quick Actions
        </h2>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px' }}>
          {[
            { label: 'Build New Module', action: () => setActiveModule('ai-builder'), icon: '🏗️' },
            { label: 'View Module Library', action: () => setActiveModule('module-library'), icon: '📦' },
            { label: 'Open AI Builder', action: () => setAiBuilderOpen(true), icon: '🧠' },
          ].map((action, index) => (
            <button
              key={index}
              onClick={action.action}
              style={{
                padding: '16px',
                background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.2), rgba(139, 92, 246, 0.2))',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '8px',
                color: '#e2e8f0',
                cursor: 'pointer',
                textAlign: 'center',
                transition: 'all 0.3s ease'
              }}
              onMouseEnter={(e) => {
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 8px 25px rgba(59, 130, 246, 0.3)';
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = 'none';
              }}
            >
              <div style={{ fontSize: '24px', marginBottom: '8px' }}>{action.icon}</div>
              <div style={{ fontSize: '14px', fontWeight: '500' }}>{action.label}</div>
            </button>
          ))}
        </div>
      </div>
    </div>
  );

  // Render active module
  const renderActiveModule = () => {
    switch (activeModule) {
      case 'dashboard':
        return <Dashboard />;
      case 'ai-builder':
        return <AetherisAIBuilder />;
      case 'module-library':
        return <ModuleLibrary />;
      default:
        return (
          <div style={{ 
            padding: '48px', 
            textAlign: 'center',
            background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(147, 51, 234, 0.1))',
            minHeight: '100vh'
          }}>
            <h2 style={{ color: '#e2e8f0', fontSize: '24px', marginBottom: '16px' }}>
              🚧 Module Under Construction
            </h2>
            <p style={{ color: '#94a3b8', fontSize: '16px', marginBottom: '24px' }}>
              This module hasn't been built yet. Use Aetheris AI Builder to create it!
            </p>
            <button
              onClick={() => setActiveModule('ai-builder')}
              style={{
                padding: '12px 24px',
                background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                border: 'none',
                borderRadius: '8px',
                color: 'white',
                fontSize: '16px',
                cursor: 'pointer'
              }}
            >
              🧠 Build This Module with AI
            </button>
          </div>
        );
    }
  };

  // Get icon for module
  const getModuleIcon = (moduleId, moduleType) => {
    if (moduleId === 'dashboard') return BarChart3;
    if (moduleId === 'ai-builder') return MessageSquare;
    if (moduleId === 'module-library') return Code;
    if (moduleType === 'ai') return Wrench;
    return Shield;
  };

  return (
    <div style={{ 
      display: 'flex', 
      height: '100vh', 
      background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #0f172a 100%)',
      fontFamily: 'system-ui, -apple-system, sans-serif'
    }}>
      {/* Sidebar Navigation */}
      <div style={{
        width: '280px',
        background: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(20px)',
        borderRight: '1px solid rgba(59, 130, 246, 0.2)',
        display: 'flex',
        flexDirection: 'column',
        boxShadow: '4px 0 20px rgba(0, 0, 0, 0.3)'
      }}>
        {/* Header */}
        <div style={{ 
          padding: '24px', 
          borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
          textAlign: 'center'
        }}>
          <h1 style={{ 
            fontSize: '24px', 
            fontWeight: 'bold', 
            color: '#3b82f6',
            textShadow: '0 0 20px rgba(59, 130, 246, 0.5)',
            marginBottom: '8px'
          }}>
            🧠 Sentari OS
          </h1>
          <p style={{ color: '#94a3b8', fontSize: '14px' }}>
            AI-Powered Business Platform
          </p>
        </div>

        {/* Navigation */}
        <div style={{ flex: 1, padding: '16px' }}>
          {platformModules.filter(m => m.status === 'active').map(module => {
            const IconComponent = getModuleIcon(module.id, module.type);
            return (
              <button
                key={module.id}
                onClick={() => setActiveModule(module.id)}
                style={{
                  width: '100%',
                  padding: '16px',
                  marginBottom: '8px',
                  background: activeModule === module.id 
                    ? 'rgba(59, 130, 246, 0.2)' 
                    : 'transparent',
                  border: activeModule === module.id
                    ? '1px solid rgba(59, 130, 246, 0.5)'
                    : '1px solid transparent',
                  borderRadius: '8px',
                  color: activeModule === module.id ? '#3b82f6' : '#94a3b8',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '12px',
                  textAlign: 'left',
                  fontSize: '14px',
                  fontWeight: '500',
                  transition: 'all 0.2s ease'
                }}
                onMouseEnter={(e) => {
                  if (activeModule !== module.id) {
                    e.target.style.background = 'rgba(59, 130, 246, 0.1)';
                    e.target.style.borderColor = 'rgba(59, 130, 246, 0.3)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (activeModule !== module.id) {
                    e.target.style.background = 'transparent';
                    e.target.style.borderColor = 'transparent';
                  }
                }}
              >
                <IconComponent size={20} />
                <span>{module.name}</span>
                {module.type === 'ai' && (
                  <span style={{ 
                    background: 'rgba(139, 92, 246, 0.3)',
                    padding: '2px 6px',
                    borderRadius: '4px',
                    fontSize: '10px',
                    marginLeft: 'auto'
                  }}>
                    AI
                  </span>
                )}
              </button>
            );
          })}
        </div>

        {/* Footer */}
        <div style={{ 
          padding: '16px', 
          borderTop: '1px solid rgba(59, 130, 246, 0.2)',
          textAlign: 'center'
        }}>
          <div style={{ color: '#6b7280', fontSize: '12px' }}>
            🚀 Powered by Enhanced Aetheris
          </div>
          <div style={{ color: '#6b7280', fontSize: '12px' }}>
            12 AI Cores Active
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div style={{ flex: 1, overflow: 'auto' }}>
        {renderActiveModule()}
      </div>

      {/* Sticky AI Builder Button */}
      <button
        onClick={() => setAiBuilderOpen(!aiBuilderOpen)}
        style={{
          position: 'fixed',
          bottom: '24px',
          right: '24px',
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
          border: 'none',
          color: 'white',
          fontSize: '24px',
          cursor: 'pointer',
          boxShadow: '0 8px 25px rgba(59, 130, 246, 0.4)',
          zIndex: 1000,
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => {
          e.target.style.transform = 'scale(1.1)';
          e.target.style.boxShadow = '0 12px 35px rgba(59, 130, 246, 0.6)';
        }}
        onMouseLeave={(e) => {
          e.target.style.transform = 'scale(1)';
          e.target.style.boxShadow = '0 8px 25px rgba(59, 130, 246, 0.4)';
        }}
      >
        🤖
      </button>

      {/* Sticky AI Builder Panel */}
      {aiBuilderOpen && (
        <div style={{
          position: 'fixed',
          bottom: '100px',
          right: '24px',
          width: '400px',
          maxHeight: '500px',
          background: 'rgba(15, 23, 42, 0.95)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '12px',
          padding: '20px',
          zIndex: 1000,
          boxShadow: '0 20px 50px rgba(0, 0, 0, 0.5)'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '16px' }}>
            <h3 style={{ color: '#3b82f6', fontSize: '18px', fontWeight: '600' }}>
              🧠 Quick AI Builder
            </h3>
            <button
              onClick={() => setAiBuilderOpen(false)}
              style={{
                background: 'none',
                border: 'none',
                color: '#94a3b8',
                cursor: 'pointer',
                padding: '4px'
              }}
            >
              <X size={20} />
            </button>
          </div>
          
          <div style={{ marginBottom: '16px' }}>
            <textarea
              placeholder="What module do you need? Example: Build billing and invoicing module with vendor support..."
              rows={3}
              style={{
                width: '100%',
                padding: '12px',
                background: 'rgba(15, 23, 42, 0.6)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '6px',
                color: '#e2e8f0',
                fontSize: '14px',
                resize: 'vertical'
              }}
            />
          </div>
          
          <button
            style={{
              width: '100%',
              padding: '12px',
              background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
              border: 'none',
              borderRadius: '6px',
              color: 'white',
              fontSize: '14px',
              cursor: 'pointer',
              fontWeight: '500'
            }}
          >
            🚀 Build Module
          </button>

          <div style={{ marginTop: '16px', textAlign: 'center' }}>
            <button
              onClick={() => {
                setAiBuilderOpen(false);
                setActiveModule('ai-builder');
              }}
              style={{
                background: 'none',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '6px',
                color: '#3b82f6',
                padding: '8px 16px',
                fontSize: '12px',
                cursor: 'pointer'
              }}
            >
              Open Full AI Builder
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SentariOS;
