sourcesContent:
import React, { useState, useEffect, useRef } from 'react';
import { Calendar, Users, MapPin, BarChart3, Clock, Shield, Zap, DollarSign, TrendingUp, Activity, X, MessageSquare, FileText, Link, Building } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  const [activeModule, setActiveModule] = useState('dashboard');
  const [aetherisStatus, setAetherisStatus] = useState('online');
  const [aiCores, setAiCores] = useState(12);
  
  // AI Editor State
  const [aiEditorOpen, setAiEditorOpen] = useState(false);
  const [aiResponse, setAiResponse] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [platformModules, setPlatformModules] = useState([]);

  // Interview State
  const [interviewStep, setInterviewStep] = useState(0);
  const [interviewData, setInterviewData] = useState({});
  const [isInterviewComplete, setIsInterviewComplete] = useState(false);

  // Relationship Manager State
  const [relationships, setRelationships] = useState([]);
  const [selectedEntity, setSelectedEntity] = useState(null);

  // Chart Data
  const performanceData = [
    { month: 'Jan', efficiency: 78, profit: 45000, revenue: 85000, satisfaction: 92 },
    { month: 'Feb', efficiency: 82, profit: 52000, revenue: 92000, satisfaction: 88 },
    { month: 'Mar', efficiency: 85, profit: 58000, revenue: 98000, satisfaction: 91 },
    { month: 'Apr', efficiency: 88, profit: 64000, revenue: 105000, satisfaction: 94 },
    { month: 'May', efficiency: 91, profit: 71000, revenue: 112000, satisfaction: 96 },
    { month: 'Jun', efficiency: 94, profit: 78000, revenue: 118000, satisfaction: 93 }
  ];

  const industryData = [
    { name: 'Security Services', value: 450000, color: '#3b82f6' },
    { name: 'Healthcare', value: 320000, color: '#10b981' },
    { name: 'Construction', value: 280000, color: '#f59e0b' },
    { name: 'Retail', value: 180000, color: '#8b5cf6' }
  ];

  const dailyMetrics = [
    { time: '00:00', guards: 45, incidents: 2, efficiency: 78 },
    { time: '04:00', guards: 52, incidents: 1, efficiency: 85 },
    { time: '08:00', guards: 127, incidents: 0, efficiency: 92 },
    { time: '12:00', guards: 134, incidents: 3, efficiency: 89 },
    { time: '16:00', guards: 142, incidents: 1, efficiency: 94 },
    { time: '20:00', guards: 98, incidents: 2, efficiency: 87 }
  ];

  // Initialize platform
  useEffect(() => {
    setPlatformModules([
      { id: 'guard-management', name: 'Guard Management', type: 'core', status: 'active', description: 'Manage security guards and assignments' },
      { id: 'site-management', name: 'Site Management', type: 'core', status: 'active', description: 'Manage client sites and locations' },
      { id: 'scheduling', name: 'AI Scheduling', type: 'core', status: 'active', description: 'Intelligent scheduling system' },
      { id: 'analytics', name: 'Business Intelligence', type: 'feature', status: 'active', description: 'KPIs and performance analytics' },
      { id: 'aetheris-interview', name: 'Business Setup', type: 'core', status: 'active', description: 'Configure your platform with Aetheris AI' },
      { id: 'form-builder', name: 'Form Builder', type: 'feature', status: 'active', description: 'AI-generated custom forms' },
      { id: 'integration-hub', name: 'Integrations', type: 'feature', status: 'active', description: 'Connect existing software' },
      { id: 'relationship-manager', name: 'Relationship Manager', type: 'core', status: 'active', description: 'Complex business relationships with competitive protection' },
      { id: 'invoice-settings', name: 'Invoice Settings', type: 'feature', status: 'active', description: 'Configure invoice display options' }
    ]);
  }, []);

  // Test data
  const guards = [
    { id: 1, name: 'John Smith', status: 'on-duty', site: 'McDonalds Downtown', shift: '6AM-2PM', phone: '(555) 123-4567' },
    { id: 2, name: 'Sarah Johnson', status: 'available', site: 'Hospital Complex', shift: '2PM-10PM', phone: '(555) 234-5678' },
    { id: 3, name: 'Mike Wilson', status: 'off-duty', site: 'Retail Center', shift: '10PM-6AM', phone: '(555) 345-6789' },
    { id: 4, name: 'Lisa Davis', status: 'on-duty', site: 'Office Building', shift: '6AM-2PM', phone: '(555) 456-7890' }
  ];

  const sites = [
    { id: 1, name: 'McDonalds Downtown', address: '123 Main St', guards: 2, status: 'secured' },
    { id: 2, name: 'Hospital Complex', address: '456 Health Ave', guards: 3, status: 'secured' },
    { id: 3, name: 'Retail Center', address: '789 Shopping Blvd', guards: 1, status: 'understaffed' },
    { id: 4, name: 'Office Building', address: '321 Business Dr', guards: 2, status: 'secured' }
  ];

  const schedules = [
    { id: 1, guard: 'John Smith', site: 'McDonalds Downtown', date: '2025-08-13', shift: '6AM-2PM', status: 'confirmed' },
    { id: 2, guard: 'Sarah Johnson', site: 'Hospital Complex', date: '2025-08-13', shift: '2PM-10PM', status: 'confirmed' },
    { id: 3, guard: 'Mike Wilson', site: 'Retail Center', date: '2025-08-14', shift: '10PM-6AM', status: 'pending' },
    { id: 4, guard: 'Lisa Davis', site: 'Office Building', date: '2025-08-14', shift: '6AM-2PM', status: 'confirmed' }
  ];

  // AI EDITOR COMPONENT
  const AIEditor = () => {
    const textareaRef = useRef(null);
    
    const processPrompt = () => {
      const prompt = textareaRef.current?.value || '';
      if (!prompt.trim()) return;
      
      setIsProcessing(true);
      setAiResponse('');

      const makeAPICall = async () => {
        try {
          const response = await fetch('http://localhost:3000/api/ai/edit-platform', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              action: 'process_prompt',
              userPrompt: prompt
            })
          });
          
          const result = await response.json();
          setAiResponse(result.response || 'AI processing complete');
          
          if (result.success && result.moduleUpdates) {
            setPlatformModules(result.moduleUpdates);
          }
          
        } catch (error) {
          setAiResponse('âŒ **Error**: Could not connect to Enhanced Aetheris backend.');
        }

        if (textareaRef.current) {
          textareaRef.current.value = '';
        }
        setIsProcessing(false);
      };

      makeAPICall();
    };

    return (
      <div style={{
        position: 'fixed',
        top: aiEditorOpen ? '10px' : 'auto',
        bottom: aiEditorOpen ? '10px' : '20px',
        right: '20px',
        width: aiEditorOpen ? '400px' : '60px',
        height: aiEditorOpen ? 'calc(100vh - 20px)' : '60px',
        background: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(20px)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '12px',
        zIndex: 1000,
        transition: 'all 0.3s ease',
        boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.3)'
      }}>
        {!aiEditorOpen && (
          <button
            onClick={() => setAiEditorOpen(true)}
            style={{
              width: '100%',
              height: '100%',
              background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
              border: 'none',
              borderRadius: '12px',
              color: 'white',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '24px'
            }}
            title="Open AI Platform Editor"
          >
            ðŸ¤–
          </button>
        )}

        {aiEditorOpen && (
          <div style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <div style={{
              padding: '15px',
              borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}>
              <div>
                <h3 style={{ color: '#3b82f6', margin: 0, fontSize: '16px' }}>ðŸ¤– AI Editor</h3>
                <p style={{ color: '#64748b', margin: '2px 0 0 0', fontSize: '11px' }}>
                  Modify your platform with natural language
                </p>
              </div>
              <button
                onClick={() => setAiEditorOpen(false)}
                style={{
                  background: 'transparent',
                  border: 'none',
                  color: '#94a3b8',
                  cursor: 'pointer',
                  padding: '4px'
                }}
              >
                <X size={16} />
              </button>
            </div>

            <div style={{ flex: 1, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
              <div style={{
                flex: 1,
                padding: '15px',
                overflowY: 'auto',
                fontSize: '13px'
              }}>
                {aiResponse ? (
                  <div style={{
                    background: 'rgba(16, 185, 129, 0.1)',
                    border: '1px solid rgba(16, 185, 129, 0.3)',
                    borderRadius: '8px',
                    padding: '12px',
                    marginBottom: '15px'
                  }}>
                    <div style={{ 
                      color: '#e2e8f0', 
                      lineHeight: '1.4',
                      whiteSpace: 'pre-line'
                    }}>
                      {aiResponse}
                    </div>
                  </div>
                ) : (
                  <div style={{ color: '#64748b', textAlign: 'center', padding: '20px' }}>
                    Tell me what you would like to change about your platform...
                  </div>
                )}
              </div>

              <div style={{
                padding: '15px',
                borderTop: '1px solid rgba(59, 130, 246, 0.2)'
              }}>
                <div style={{ display: 'flex', gap: '8px' }}>
                  <textarea
                    ref={textareaRef}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        processPrompt();
                      }
                    }}
                    placeholder="e.g., 'Remove guard management module' or 'Create inventory management system'"
                    style={{
                      flex: 1,
                      background: 'rgba(255, 255, 255, 0.1)',
                      border: '1px solid rgba(59, 130, 246, 0.3)',
                      borderRadius: '6px',
                      padding: '8px',
                      color: '#e2e8f0',
                      fontSize: '12px',
                      resize: 'none',
                      minHeight: '60px',
                      outline: 'none',
                      fontFamily: 'inherit'
                    }}
                    autoComplete="off"
                    spellCheck="false"
                  />
                  <button
                    onClick={processPrompt}
                    disabled={isProcessing}
                    style={{
                      background: isProcessing ? '#6b7280' : 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
                      border: 'none',
                      color: 'white',
                      padding: '8px 12px',
                      borderRadius: '6px',
                      cursor: isProcessing ? 'default' : 'pointer',
                      fontSize: '12px',
                      alignSelf: 'flex-end'
                    }}
                  >
                    {isProcessing ? 'ðŸ§ ...' : 'ðŸš€'}
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  // INVOICE SETTINGS COMPONENT - FIXED
  const InvoiceSettings = () => {
    const [invoiceSettings, setInvoiceSettings] = useState({
      showVendorCompanies: true,
      showVendorEmployeeNames: true,
      groupByVendor: true,
      displayMode: 'detailed',
      clientOverrides: {}
    });

    const [selectedClient, setSelectedClient] = useState('default');
    const [loading, setLoading] = useState(false);
    const [saveMessage, setSaveMessage] = useState('');

    const updateSetting = (key, value) => {
      if (selectedClient === 'default') {
        setInvoiceSettings(prev => ({
          ...prev,
          [key]: value
        }));
      } else {
        setInvoiceSettings(prev => ({
          ...prev,
          clientOverrides: {
            ...prev.clientOverrides,
            [selectedClient]: {
              ...prev.clientOverrides[selectedClient],
              [key]: value
            }
          }
        }));
      }
    };

    const getCurrentValue = (key) => {
      if (selectedClient === 'default') {
        return invoiceSettings[key];
      } else {
        return invoiceSettings.clientOverrides[selectedClient]?.[key] ?? invoiceSettings[key];
      }
    };

    const saveSettings = async () => {
      try {
        setLoading(true);
        setSaveMessage('');
        
        const response = await fetch('http://localhost:3000/api/invoice/settings', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            defaultSettings: selectedClient === 'default' ? invoiceSettings : null,
            clientId: selectedClient !== 'default' ? selectedClient : null,
            specificSettings: selectedClient !== 'default' ? getCurrentValue() : null
          })
        });

        const data = await response.json();
        
        if (data.success) {
          setSaveMessage('âœ… Settings saved successfully!');
          setTimeout(() => setSaveMessage(''), 3000);
        } else {
          setSaveMessage('âŒ Error saving settings');
        }
      } catch (error) {
        setSaveMessage('âŒ Error saving settings');
      } finally {
        setLoading(false);
      }
    };

    return (
      <div style={{ padding: '20px' }}>
        <div style={{ marginBottom: '30px' }}>
          <h2 style={{ color: '#2563eb', margin: '0 0 10px 0' }}>ðŸ§¾ Invoice Display Settings</h2>
          <p style={{ color: '#94a3b8', fontSize: '14px' }}>
            Configure how vendor information appears on client invoices
          </p>
        </div>

        {saveMessage && (
          <div style={{
            background: saveMessage.includes('âœ…') ? 'rgba(16, 185, 129, 0.1)' : 'rgba(239, 68, 68, 0.1)',
            border: `1px solid ${saveMessage.includes('âœ…') ? 'rgba(16, 185, 129, 0.3)' : 'rgba(239, 68, 68, 0.3)'}`,
            borderRadius: '8px',
            padding: '12px',
            marginBottom: '20px',
            color: saveMessage.includes('âœ…') ? '#10b981' : '#ef4444'
          }}>
            {saveMessage}
          </div>
        )}

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '12px',
          padding: '25px'
        }}>
          <h3 style={{ color: '#3b82f6', marginBottom: '20px' }}>âš™ï¸ Display Options</h3>
          
          <div style={{ marginBottom: '20px' }}>
            <label style={{ color: '#e2e8f0', fontSize: '14px', display: 'block', marginBottom: '8px' }}>
              Configure for Client:
            </label>
            <select
              value={selectedClient}
              onChange={(e) => setSelectedClient(e.target.value)}
              style={{
                width: '100%',
                padding: '10px',
                background: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '6px',
                color: '#e2e8f0',
                fontSize: '14px'
              }}
            >
              <option value="default">Default Settings</option>
              <option value="abc-construction">ABC Construction</option>
              <option value="xyz-manufacturing">XYZ Manufacturing</option>
              <option value="seattle-hospital">Seattle Hospital</option>
            </select>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '10px', 
              color: '#e2e8f0', 
              cursor: 'pointer',
              marginBottom: '10px'
            }}>
              <input
                type="checkbox"
                checked={getCurrentValue('showVendorCompanies')}
                onChange={(e) => updateSetting('showVendorCompanies', e.target.checked)}
                style={{ accentColor: '#3b82f6' }}
              />
              <span>Show Vendor Company Names</span>
            </label>
            <p style={{ color: '#94a3b8', fontSize: '12px', margin: '0 0 0 30px' }}>
              Display "ADS Security" as section headers
            </p>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '10px', 
              color: '#e2e8f0', 
              cursor: 'pointer',
              marginBottom: '10px'
            }}>
              <input
                type="checkbox"
                checked={getCurrentValue('showVendorEmployeeNames')}
                onChange={(e) => updateSetting('showVendorEmployeeNames', e.target.checked)}
                style={{ accentColor: '#3b82f6' }}
              />
              <span>Show Individual Employee Names</span>
            </label>
            <p style={{ color: '#94a3b8', fontSize: '12px', margin: '0 0 0 30px' }}>
              Display "John Smith" instead of "Security Guard #1"
            </p>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '10px', 
              color: '#e2e8f0', 
              cursor: 'pointer',
              marginBottom: '10px'
            }}>
              <input
                type="checkbox"
                checked={getCurrentValue('displayAsInternal')}
                onChange={(e) => updateSetting('displayAsInternal', e.target.checked)}
                style={{ accentColor: '#3b82f6' }}
              />
              <span>Display All as Internal Staff</span>
            </label>
            <p style={{ color: '#94a3b8', fontSize: '12px', margin: '0 0 0 30px' }}>
              Hide all vendor information, show as "MIP Internal Staff"
            </p>
          </div>

          <div style={{
            background: 'rgba(16, 185, 129, 0.1)',
            border: '1px solid rgba(16, 185, 129, 0.3)',
            borderRadius: '8px',
            padding: '15px',
            marginBottom: '20px'
          }}>
            <h4 style={{ color: '#10b981', margin: '0 0 10px 0', fontSize: '14px' }}>ðŸ“‹ Example Invoice Display:</h4>
            <div style={{ color: '#94a3b8', fontSize: '12px', fontFamily: 'monospace' }}>
              {getCurrentValue('showVendorCompanies') ? (
                <div>
                  ðŸ¢ ADS Security - $5,400.00<br />
                  &nbsp;&nbsp;&nbsp;&nbsp;{getCurrentValue('showVendorEmployeeNames') ? 'John Smith' : 'Security Guard'} - $4,000.00<br />
                  ðŸ¢ PPG Security - $3,200.00<br />
                  &nbsp;&nbsp;&nbsp;&nbsp;{getCurrentValue('showVendorEmployeeNames') ? 'Mike Wilson' : 'Security Guard'} - $3,200.00
                </div>
              ) : getCurrentValue('displayAsInternal') ? (
                <div>
                  ðŸ¢ MIP Internal Staff - $8,600.00<br />
                  &nbsp;&nbsp;&nbsp;&nbsp;Security Officer - $4,000.00<br />
                  &nbsp;&nbsp;&nbsp;&nbsp;Security Officer - $3,200.00
                </div>
              ) : (
                <div>
                  {getCurrentValue('showVendorEmployeeNames') ? 'John Smith' : 'Security Officer'} - $4,000.00<br />
                  {getCurrentValue('showVendorEmployeeNames') ? 'Mike Wilson' : 'Security Officer'} - $3,200.00
                </div>
              )}
            </div>
          </div>
        </div>

        <div style={{ 
          marginTop: '30px', 
          padding: '20px', 
          background: 'rgba(139, 92, 246, 0.1)',
          border: '1px solid rgba(139, 92, 246, 0.3)',
          borderRadius: '12px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <div>
            <h4 style={{ color: '#8b5cf6', margin: '0 0 5px 0' }}>Settings Ready</h4>
            <p style={{ color: '#94a3b8', fontSize: '14px', margin: 0 }}>
              Invoice display preferences configured for {selectedClient === 'default' ? 'all clients' : selectedClient}
            </p>
          </div>
          <button
            onClick={saveSettings}
            disabled={loading}
            style={{
              background: loading ? 'rgba(139, 92, 246, 0.5)' : 'linear-gradient(135deg, #8b5cf6, #7c3aed)',
              color: 'white',
              padding: '12px 24px',
              border: 'none',
              borderRadius: '8px',
              cursor: loading ? 'not-allowed' : 'pointer',
              fontSize: '14px',
              opacity: loading ? 0.6 : 1
            }}
          >
            {loading ? 'ðŸ”„ Saving...' : 'ðŸ’¾ Save Settings'}
          </button>
        </div>
      </div>
    );
  };

  // Functional Modules
  const GuardManagement = () => (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ color: '#2563eb', margin: 0 }}>Guard Management</h2>
        <button style={{
          background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}>
          + Add Guard
        </button>
      </div>
      
      <div style={{ display: 'grid', gap: '15px' }}>
        {guards.map(guard => (
          <div key={guard.id} style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(59, 130, 246, 0.3)',
            borderRadius: '12px',
            padding: '15px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <div>
              <h3 style={{ color: '#3b82f6', margin: '0 0 5px 0' }}>{guard.name}</h3>
              <p style={{ color: '#94a3b8', margin: '0 0 5px 0' }}>{guard.site}</p>
              <p style={{ color: '#64748b', margin: 0, fontSize: '14px' }}>{guard.shift} â€¢ {guard.phone}</p>
            </div>
            <div style={{
              padding: '6px 12px',
              borderRadius: '20px',
              background: guard.status === 'on-duty' ? '#10b981' : 
                          guard.status === 'available' ? '#f59e0b' : '#6b7280',
              color: 'white',
              fontSize: '12px',
              fontWeight: 'bold'
            }}>
              {guard.status.toUpperCase()}
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const SiteManagement = () => (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ color: '#2563eb', margin: 0 }}>Site Management</h2>
        <button style={{
          background: 'linear-gradient(135deg, #10b981, #059669)',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}>
          + Add Site
        </button>
      </div>
      
      <div style={{ display: 'grid', gap: '15px' }}>
        {sites.map(site => (
          <div key={site.id} style={{
