import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import { Calendar, Users, MapPin, BarChart3, Clock, Shield, Zap, DollarSign, TrendingUp, Activity, X, MessageSquare, Wrench, Code, Settings, Loader, RefreshCw, Upload, Play, Pause, RotateCcw, Eye, Brain, Heart, Compass, Mic, Globe, Search, Target, BookOpen, Cog, Building2, Plus, Star, CheckCircle, AlertTriangle } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, BarChart, Bar, RadialBarChart, RadialBar, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  // Core State Management
  const [activeModule, setActiveModule] = useState('dashboard');
  const [businessMetrics, setBusinessMetrics] = useState({
    monthlyRevenue: 125000,
    activeGuards: 23,
    totalClients: 8,
    totalSites: 12,
    grossProfit: 45000,
    profitMargin: 36,
    loading: false
  });

  // Enhanced Chat State - FIXED: No refresh on typing
  const [chatMessages, setChatMessages] = useState([
    { 
      role: 'assistant', 
      content: `🧠 **Aetheris AI Business Partner Online**

Welcome to your AI-powered business intelligence platform! I'm here to help you:

• **Analyze your business performance**
• **Build custom modules for your needs** 
• **Set up new client onboarding**
• **Optimize operations with AI insights**

What would you like to accomplish today?`, 
      timestamp: new Date()
    }
  ]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [chatOpen, setChatOpen] = useState(false);

  // Client Setup Interview State
  const [setupMode, setSetupMode] = useState(false);
  const [clientSetup, setClientSetup] = useState({
    step: 0,
    data: {},
    questions: []
  });

  // Module Management
  const [modules, setModules] = useState([]);
  const [isConnected, setIsConnected] = useState(true);

  // Refs for stable input handling
  const chatInputRef = useRef(null);
  const chatMessagesRef = useRef(null);

  // Sample analytics data for dashboard
  const analyticsData = useMemo(() => ({
    revenue: [
      { month: 'Jan', actual: 98000, projected: 95000 },
      { month: 'Feb', actual: 112000, projected: 108000 },
      { month: 'Mar', actual: 125000, projected: 118000 },
      { month: 'Apr', actual: 138000, projected: 128000 },
      { month: 'May', actual: 142000, projected: 135000 },
      { month: 'Jun', actual: 155000, projected: 145000 }
    ],
    performance: [
      { metric: 'Client Satisfaction', value: 94, target: 90 },
      { metric: 'Guard Performance', value: 87, target: 85 },
      { metric: 'Site Coverage', value: 98, target: 95 },
      { metric: 'Response Time', value: 92, target: 90 }
    ],
    clientBreakdown: [
      { name: 'McDonald\'s', value: 45000, color: '#FF6B6B' },
      { name: 'Seattle Hospital', value: 38000, color: '#4ECDC4' },
      { name: 'Office Complex', value: 32000, color: '#45B7D1' },
      { name: 'Retail Centers', value: 28000, color: '#96CEB4' },
      { name: 'Other', value: 12000, color: '#FFEAA7' }
    ]
  }), []);

  // Fixed chat handling - no state refresh on input
  const handleChatSubmit = useCallback(async () => {
    if (!currentMessage.trim() || isProcessing) return;

    const userMessage = currentMessage.trim();
    setCurrentMessage(''); // Clear immediately
    setIsProcessing(true);

    // Add user message immediately
    setChatMessages(prev => [...prev, { 
      role: 'user', 
      content: userMessage, 
      timestamp: new Date() 
    }]);

    try {
      // Check if this is a setup request
      if (userMessage.toLowerCase().includes('setup') || 
          userMessage.toLowerCase().includes('new client') ||
          userMessage.toLowerCase().includes('onboard')) {
        
        setSetupMode(true);
        setClientSetup({
          step: 0,
          data: {},
          questions: [
            "What's the client's company name?",
            "What industry are they in? (healthcare, retail, manufacturing, etc.)",
            "How many employees do they have?",
            "What's their monthly budget for our services?",
            "What specific services do they need? (security, scheduling, payroll, etc.)",
            "Do they have any existing systems we need to integrate with?"
          ]
        });

        setTimeout(() => {
          setChatMessages(prev => [...prev, {
            role: 'assistant',
            content: `🎯 **Client Setup Interview Initiated**

I'll help you set up a new client with custom modules tailored to their needs.

**Question 1/6:** What's the client's company name?

*This interview will help me understand their business and build the perfect module suite for them.*`,
            timestamp: new Date()
          }]);
          setIsProcessing(false);
        }, 1000);
        return;
      }

      // Handle setup interview responses
      if (setupMode && clientSetup.step < clientSetup.questions.length) {
        const updatedData = { ...clientSetup.data };
        const step = clientSetup.step;
        
        // Store answer
        switch (step) {
          case 0: updatedData.companyName = userMessage; break;
          case 1: updatedData.industry = userMessage; break;
          case 2: updatedData.employees = userMessage; break;
          case 3: updatedData.budget = userMessage; break;
          case 4: updatedData.services = userMessage; break;
          case 5: updatedData.systems = userMessage; break;
        }

        const nextStep = step + 1;
        setClientSetup(prev => ({
          ...prev,
          step: nextStep,
          data: updatedData
        }));

        setTimeout(() => {
          if (nextStep < clientSetup.questions.length) {
            // Ask next question
            setChatMessages(prev => [...prev, {
              role: 'assistant',
              content: `✅ **Recorded:** ${userMessage}

**Question ${nextStep + 1}/6:** ${clientSetup.questions[nextStep]}`,
              timestamp: new Date()
            }]);
          } else {
            // Setup complete - generate modules
            setChatMessages(prev => [...prev, {
              role: 'assistant',
              content: `🎉 **Client Setup Complete!**

**📋 Client Profile:**
• **Company:** ${updatedData.companyName}
• **Industry:** ${updatedData.industry}
• **Employees:** ${updatedData.employees}
• **Budget:** ${updatedData.budget}
• **Services:** ${updatedData.services}
• **Systems:** ${updatedData.systems}

🤖 **Generating Custom Modules...**

Based on their needs, I'm building:
1. **${updatedData.industry.charAt(0).toUpperCase() + updatedData.industry.slice(1)} CRM** - Client management for ${updatedData.companyName}
2. **Employee Scheduling** - For ${updatedData.employees} employees
3. **Billing & Invoicing** - Budget: ${updatedData.budget}
4. **${updatedData.services} Management** - Core service delivery
5. **Integration Hub** - Connect with ${updatedData.systems}

*Modules will appear in your sidebar in 3-5 seconds...*`,
              timestamp: new Date()
            }]);

            // Generate actual modules
            setTimeout(() => {
              const newModules = [
                {
                  id: `crm_${Date.now()}`,
                  name: `${updatedData.companyName} CRM`,
                  description: `Custom CRM for ${updatedData.industry} industry`,
                  icon: '🏢',
                  client: updatedData.companyName,
                  status: 'active'
                },
                {
                  id: `scheduling_${Date.now() + 1}`,
                  name: 'Smart Scheduling',
                  description: `Employee scheduling for ${updatedData.employees} staff`,
                  icon: '📅',
                  client: updatedData.companyName,
                  status: 'active'
                },
                {
                  id: `billing_${Date.now() + 2}`,
                  name: 'Billing System',
                  description: `Invoicing system with ${updatedData.budget} budget tracking`,
                  icon: '💰',
                  client: updatedData.companyName,
                  status: 'active'
                }
              ];

              setModules(prev => [...prev, ...newModules]);
              setSetupMode(false);
              
              setChatMessages(prev => [...prev, {
                role: 'assistant',
                content: `✅ **Custom Modules Generated Successfully!**

I've created 3 specialized modules for ${updatedData.companyName}:

🏢 **${updatedData.companyName} CRM** - Industry-specific client management
📅 **Smart Scheduling** - Optimized for ${updatedData.employees} employees  
💰 **Billing System** - Budget-aware invoicing

**Next Steps:**
• Click any module in the sidebar to configure it
• Ask me to "enhance [module name]" for advanced features
• Say "setup another client" to onboard more clients

Ready to customize these modules for ${updatedData.companyName}?`,
                timestamp: new Date()
              }]);
            }, 3000);
          }
          setIsProcessing(false);
        }, 1500);
        return;
      }

      // Regular AI chat
      setTimeout(() => {
        let response = '';
        
        if (userMessage.toLowerCase().includes('dashboard') || userMessage.toLowerCase().includes('analytics')) {
          response = `📊 **Business Analytics Summary**

**Current Performance:**
• Monthly Revenue: $${businessMetrics.monthlyRevenue.toLocaleString()}
• Active Guards: ${businessMetrics.activeGuards}
• Total Clients: ${businessMetrics.totalClients}
• Gross Profit: $${businessMetrics.grossProfit.toLocaleString()} (${businessMetrics.profitMargin}% margin)

**Quick Insights:**
• Revenue trending +12% month-over-month
• Client satisfaction at 94% (above industry average)
• Guard utilization optimized at 89%

What specific metrics would you like to dive deeper into?`;
        } else if (userMessage.toLowerCase().includes('build') || userMessage.toLowerCase().includes('create')) {
          response = `🔨 **AI Module Builder Ready**

I can build custom modules for your business! What type of module do you need?

**Popular Options:**
• **CRM System** - Client relationship management
• **Employee Portal** - Staff scheduling and communication
• **Financial Dashboard** - Profit tracking and billing
• **Analytics Suite** - Custom reporting and insights
• **Mobile App** - Field operations management

Or describe what you need and I'll design it custom for your business!`;
        } else if (userMessage.toLowerCase().includes('setup') || userMessage.toLowerCase().includes('client')) {
          response = `🎯 **Client Setup Assistant**

I can help you set up new clients with custom module suites!

Say **"setup new client"** and I'll start an interview to:
• Understand their business needs
• Identify required modules
• Configure custom solutions
• Build their dedicated platform

Ready to onboard a new client?`;
        } else {
          response = `🧠 **Aetheris AI Response**

I'm here to help with:
• **Business Analytics** - "show me dashboard" or "calculate profit"
• **Module Building** - "build [module type]" or "create custom solution"
• **Client Setup** - "setup new client" for guided onboarding
• **Performance Insights** - Ask about any business metric

What would you like me to help you with?`;
        }

        setChatMessages(prev => [...prev, {
          role: 'assistant',
          content: response,
          timestamp: new Date()
        }]);
        setIsProcessing(false);
      }, 1000);

    } catch (error) {
      setChatMessages(prev => [...prev, {
        role: 'assistant',
        content: `❌ Error: ${error.message}. Please try again!`,
        timestamp: new Date()
      }]);
      setIsProcessing(false);
    }
  }, [currentMessage, isProcessing, setupMode, clientSetup]);

  // Fixed: Handle Enter key without refresh
  const handleKeyPress = useCallback((e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleChatSubmit();
    }
  }, [handleChatSubmit]);

  // Auto-scroll chat
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [chatMessages]);

  // Core modules with visual icons
  const coreModules = useMemo(() => [
    { 
      id: 'dashboard', 
      name: 'Executive Dashboard', 
      icon: BarChart3, 
      description: 'Business analytics and KPI monitoring',
      color: 'blue'
    },
    { 
      id: 'ai-chat', 
      name: 'AI Assistant', 
      icon: Brain, 
      description: 'Aetheris AI business partner',
      color: 'purple'
    },
    { 
      id: 'client-setup', 
      name: 'Client Onboarding', 
      icon: Users, 
      description: 'Guided client setup with custom modules',
      color: 'green'
    },
    { 
      id: 'module-builder', 
      name: 'Module Builder', 
      icon: Wrench, 
      description: 'Create custom business modules',
      color: 'orange'
    }
  ], []);

  const allModules = useMemo(() => [...coreModules, ...modules], [coreModules, modules]);

  // Render Functions
  const renderDashboard = () => (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
            Executive Command Center
          </h1>
          <p className="text-gray-600 mt-1">AI-powered business intelligence and real-time analytics</p>
        </div>
        <div className="flex items-center space-x-3">
          <div className="flex items-center space-x-2 px-3 py-1.5 bg-green-100 text-green-800 rounded-full text-sm font-medium">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
            <span>AI Cores Online</span>
          </div>
          <button 
            onClick={() => setChatOpen(true)}
            className="flex items-center space-x-2 px-4 py-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all shadow-lg hover:shadow-xl"
          >
            <Brain size={16} />
            <span>Ask Aetheris</span>
          </button>
        </div>
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-gradient-to-br from-blue-50 to-blue-100 rounded-xl p-6 border border-blue-200 shadow-sm hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-blue-700 text-sm font-medium">Monthly Revenue</p>
              <p className="text-2xl font-bold text-blue-900">${businessMetrics.monthlyRevenue.toLocaleString()}</p>
              <p className="text-xs text-blue-600 mt-1">+12% from last month</p>
            </div>
            <DollarSign className="text-blue-600" size={24} />
          </div>
        </div>

        <div className="bg-gradient-to-br from-green-50 to-green-100 rounded-xl p-6 border border-green-200 shadow-sm hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-green-700 text-sm font-medium">Active Guards</p>
              <p className="text-2xl font-bold text-green-900">{businessMetrics.activeGuards}</p>
              <p className="text-xs text-green-600 mt-1">94% utilization rate</p>
            </div>
            <Shield className="text-green-600" size={24} />
          </div>
        </div>

        <div className="bg-gradient-to-br from-purple-50 to-purple-100 rounded-xl p-6 border border-purple-200 shadow-sm hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-purple-700 text-sm font-medium">Total Clients</p>
              <p className="text-2xl font-bold text-purple-900">{businessMetrics.totalClients}</p>
              <p className="text-xs text-purple-600 mt-1">2 new this month</p>
            </div>
            <Building2 className="text-purple-600" size={24} />
          </div>
        </div>

        <div className="bg-gradient-to-br from-orange-50 to-orange-100 rounded-xl p-6 border border-orange-200 shadow-sm hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-orange-700 text-sm font-medium">Gross Profit</p>
              <p className="text-2xl font-bold text-orange-900">${businessMetrics.grossProfit.toLocaleString()}</p>
              <p className="text-xs text-orange-600 mt-1">{businessMetrics.profitMargin}% margin</p>
            </div>
            <TrendingUp className="text-orange-600" size={24} />
          </div>
        </div>
      </div>

      {/* Analytics Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Revenue Chart */}
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">Revenue Trend</h3>
            <div className="text-sm text-green-600 font-medium">↗ +18% growth</div>
          </div>
          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              <AreaChart data={analyticsData.revenue}>
                <CartesianGrid strokeDasharray="3 3" stroke="#f1f5f9" />
                <XAxis dataKey="month" stroke="#64748b" />
                <YAxis stroke="#64748b" />
                <Tooltip 
                  formatter={(value) => [`$${value.toLocaleString()}`, '']}
                  labelStyle={{ color: '#374151' }}
                  contentStyle={{ 
                    backgroundColor: 'white', 
                    border: '1px solid #e5e7eb',
                    borderRadius: '8px'
                  }}
                />
                <Area 
                  type="monotone" 
                  dataKey="actual" 
                  stroke="#3b82f6" 
                  fill="url(#blueGradient)" 
                  strokeWidth={2}
                />
                <Area 
                  type="monotone" 
                  dataKey="projected" 
                  stroke="#8b5cf6" 
                  fill="url(#purpleGradient)" 
                  strokeWidth={2}
                  strokeDasharray="5 5"
                />
                <defs>
                  <linearGradient id="blueGradient" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.3}/>
                    <stop offset="95%" stopColor="#3b82f6" stopOpacity={0.05}/>
                  </linearGradient>
                  <linearGradient id="purpleGradient" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="#8b5cf6" stopOpacity={0.2}/>
                    <stop offset="95%" stopColor="#8b5cf6" stopOpacity={0.05}/>
                  </linearGradient>
                </defs>
              </AreaChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Performance Metrics */}
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Performance Metrics</h3>
          <div className="space-y-4">
            {analyticsData.performance.map((metric, index) => (
              <div key={index} className="flex items-center justify-between">
                <span className="text-sm font-medium text-gray-700">{metric.metric}</span>
                <div className="flex items-center space-x-3">
                  <div className="w-32 bg-gray-200 rounded-full h-2">
                    <div 
                      className={`h-2 rounded-full transition-all duration-500 ${
                        metric.value >= metric.target ? 'bg-green-500' : 'bg-yellow-500'
                      }`}
                      style={{ width: `${Math.min(metric.value, 100)}%` }}
                    ></div>
                  </div>
                  <span className="text-sm font-semibold text-gray-900 w-10 text-right">
                    {metric.value}%
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Client Revenue Breakdown */}
      <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Client Revenue Breakdown</h3>
        <div className="h-64">
          <ResponsiveContainer width="100%" height="100%">
            <PieChart>
              <Pie
                data={analyticsData.clientBreakdown}
                cx="50%"
                cy="50%"
                innerRadius={60}
                outerRadius={100}
                paddingAngle={2}
                dataKey="value"
              >
                {analyticsData.clientBreakdown.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip formatter={(value) => [`$${value.toLocaleString()}`, 'Revenue']} />
            </PieChart>
          </ResponsiveContainer>
        </div>
        <div className="grid grid-cols-2 gap-4 mt-4">
          {analyticsData.clientBreakdown.map((client, index) => (
            <div key={index} className="flex items-center space-x-2">
              <div 
                className="w-3 h-3 rounded-full" 
                style={{ backgroundColor: client.color }}
              ></div>
              <span className="text-sm text-gray-700">{client.name}</span>
              <span className="text-sm font-medium text-gray-900 ml-auto">
                ${client.value.toLocaleString()}
              </span>
            </div>
          ))}
        </div>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <button 
          onClick={() => {
            setChatOpen(true);
            setTimeout(() => setCurrentMessage('setup new client'), 100);
          }}
          className="bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-xl p-6 hover:from-blue-600 hover:to-blue-700 transition-all shadow-lg hover:shadow-xl"
        >
          <div className="flex items-center space-x-3 mb-2">
            <Plus size={24} />
            <span className="text-lg font-semibold">Setup New Client</span>
          </div>
          <p className="text-blue-100 text-sm">Start guided client onboarding</p>
        </button>

        <button 
          onClick={() => {
            setChatOpen(true);
            setTimeout(() => setCurrentMessage('build custom module'), 100);
          }}
          className="bg-gradient-to-br from-purple-500 to-purple-600 text-white rounded-xl p-6 hover:from-purple-600 hover:to-purple-700 transition-all shadow-lg hover:shadow-xl"
        >
          <div className="flex items-center space-x-3 mb-2">
            <Wrench size={24} />
            <span className="text-lg font-semibold">Build Module</span>
          </div>
          <p className="text-purple-100 text-sm">Create custom business solutions</p>
        </button>

        <button 
          onClick={() => {
            setChatOpen(true);
            setTimeout(() => setCurrentMessage('analyze my business performance'), 100);
          }}
          className="bg-gradient-to-br from-green-500 to-green-600 text-white rounded-xl p-6 hover:from-green-600 hover:to-green-700 transition-all shadow-lg hover:shadow-xl"
        >
          <div className="flex items-center space-x-3 mb-2">
            <TrendingUp size={24} />
            <span className="text-lg font-semibold">AI Analysis</span>
          </div>
          <p className="text-green-100 text-sm">Get AI-powered insights</p>
        </button>
      </div>
    </div>
  );

  const renderModuleContent = (moduleId) => {
    const module = modules.find(m => m.id === moduleId);
    if (!module) return renderDashboard();

    return (
      <div className="space-y-6">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">{module.name}</h1>
            <p className="text-gray-600">{module.description}</p>
          </div>
          <div className="flex items-center space-x-3">
            <div className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm">
              {module.status}
            </div>
            <button className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
              <Settings size={16} />
              <span>Configure</span>
            </button>
          </div>
        </div>

        {/* Module Content */}
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-8">
          <div className="text-center">
            <div className="text-6xl mb-4">{module.icon}</div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">{module.name}</h3>
            <p className="text-gray-600 mb-6">{module.description}</p>
            
            <div className="inline-flex items-center space-x-2 px-4 py-2 bg-blue-50 text-blue-700 rounded-lg">
              <Cog size={16} />
              <span>Module Active - Ready for Configuration</span>
            </div>
            
            <div className="mt-6">
              <button 
                onClick={() => {
                  setChatOpen(true);
                  setTimeout(() => setCurrentMessage(`enhance ${module.name} with advanced features`), 100);
                }}
                className="px-6 py-3 bg-gradient-to-r from-purple-600 to-blue-600 text-white rounded-lg hover:from-purple-700 hover:to-blue-700 transition-all"
              >
                Enhance with AI
              </button>
            </div>
          </div>
        </div>

        {/* Module Features */}
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Module Features</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {['Real-time Updates', 'AI Integration', 'Custom Workflows', 'Analytics Dashboard', 'Mobile Access', 'API Integration'].map((feature, index) => (
              <div key={index} className="flex items-center space-x-2 p-3 bg-gray-50 rounded-lg">
                <CheckCircle size={16} className="text-green-600" />
                <span className="text-sm text-gray-700">{feature}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-blue-50 to-purple-50">
      {/* Sidebar */}
      <div className="fixed left-0 top-0 w-72 h-full bg-white shadow-xl border-r border-gray-200 z-40">
        {/* Logo Header */}
        <div className="p-6 bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 text-white">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-white bg-opacity-20 rounded-xl flex items-center justify-center backdrop-blur-sm">
              <Brain size={28} className="text-white" />
            </div>
            <div>
              <h1 className="text-xl font-bold">Sentari.os</h1>
              <p className="text-xs text-blue-100">AI Business Intelligence Platform</p>
            </div>
          </div>
        </div>

        {/* Navigation */}
        <nav className="p-4 space-y-2">
          {allModules.map((module) => {
            const IconComponent = module.icon || Cog;
            const isActive = activeModule === module.id;
            
            return (
              <button
                key={module.id}
                onClick={() => setActiveModule(module.id)}
                className={`w-full flex items-center space-x-3 px-4 py-3 rounded-xl text-left transition-all group ${
                  isActive
                    ? 'bg-gradient-to-r from-blue-50 to-purple-50 text-blue-700 border border-blue-200 shadow-sm'
                    : 'text-gray-700 hover:bg-gray-50 hover:shadow-sm'
                }`}
              >
                <div className={`w-10 h-10 rounded-lg flex items-center justify-center transition-colors ${
                  isActive 
                    ? 'bg-gradient-to-r from-blue-500 to-purple-500 text-white' 
                    : `bg-${module.color || 'gray'}-100 text-${module.color || 'gray'}-600 group-hover:bg-${module.color || 'gray'}-200`
                }`}>
                  <IconComponent size={20} />
                </div>
                <div className="flex-1 min-w-0">
                  <div className="text-sm font-medium truncate">{module.name}</div>
                  <div className="text-xs text-gray-500 truncate">{module.description}</div>
                  {module.client && (
                    <div className="text-xs text-blue-600 font-medium">Client: {module.client}</div>
                  )}
                </div>
              </button>
            );
          })}
        </nav>

        {/* AI Status */}
        <div className="absolute bottom-20 left-4 right-4">
          <div className="bg-gradient-to-r from-gray-900 to-blue-900 rounded-xl p-4 text-white">
            <div className="flex items-center space-x-2 mb-2">
              <Brain size={16} />
              <span className="text-sm font-medium">Aetheris AI</span>
            </div>
            <div className="text-xs text-gray-300">
              12 AI cores monitoring your business
            </div>
            <div className="text-xs text-green-300 mt-1">
              ● All systems operational
            </div>
          </div>
        </div>

        {/* Chat Toggle */}
        <div className="absolute bottom-4 left-4 right-4">
          <button
            onClick={() => setChatOpen(true)}
            className="w-full flex items-center justify-center space-x-2 px-4 py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-xl hover:from-blue-700 hover:to-purple-700 transition-all shadow-lg hover:shadow-xl"
          >
            <MessageSquare size={20} />
            <span>Chat with Aetheris</span>
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="ml-72 min-h-screen">
        <main className="p-8">
          {activeModule === 'dashboard' ? renderDashboard() : renderModuleContent(activeModule)}
        </main>
      </div>

      {/* Enhanced Chat Panel - FIXED: No refresh on typing */}
      {chatOpen && (
        <div className="fixed right-0 top-0 w-96 h-full bg-white shadow-2xl border-l border-gray-200 z-50 flex flex-col">
          {/* Chat Header */}
          <div className="p-4 bg-gradient-to-r from-blue-600 to-purple-600 text-white">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-white bg-opacity-20 rounded-lg flex items-center justify-center">
                  <Brain size={24} />
                </div>
                <div>
                  <h3 className="font-semibold">Aetheris AI</h3>
                  <p className="text-xs text-blue-100">Business Intelligence Partner</p>
                </div>
              </div>
              <button
                onClick={() => setChatOpen(false)}
                className="text-white hover:text-gray-200 transition-colors"
              >
                <X size={20} />
              </button>
            </div>
          </div>

          {/* Setup Mode Indicator */}
          {setupMode && (
            <div className="p-3 bg-gradient-to-r from-green-50 to-blue-50 border-b border-gray-200">
              <div className="flex items-center space-x-2">
                <Users size={16} className="text-green-600" />
                <span className="text-sm font-medium text-green-800">
                  Client Setup Interview - Question {clientSetup.step + 1}/{clientSetup.questions.length}
                </span>
              </div>
            </div>
          )}

          {/* Messages */}
          <div ref={chatMessagesRef} className="flex-1 overflow-y-auto p-4 space-y-4">
            {chatMessages.map((message, index) => (
              <div
                key={index}
                className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div
                  className={`max-w-[85%] rounded-lg p-3 ${
                    message.role === 'user'
                      ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg'
                      : 'bg-white border border-gray-200 shadow-sm'
                  }`}
                >
                  <div className="text-sm whitespace-pre-wrap">{message.content}</div>
                  <div className={`text-xs mt-2 opacity-70 ${
                    message.role === 'user' ? 'text-blue-100' : 'text-gray-500'
                  }`}>
                    {message.timestamp.toLocaleTimeString()}
                  </div>
                </div>
              </div>
            ))}

            {isProcessing && (
              <div className="flex justify-start">
                <div className="bg-gradient-to-r from-blue-50 to-purple-50 border border-blue-200 rounded-lg p-3 shadow-sm">
                  <div className="flex items-center space-x-2">
                    <Loader className="animate-spin text-blue-600" size={16} />
                    <span className="text-sm text-blue-800">Aetheris is thinking...</span>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Chat Input - FIXED: No refresh on typing */}
          <div className="p-4 border-t border-gray-200 bg-gray-50">
            <div className="flex space-x-2">
              <textarea
                ref={chatInputRef}
                value={currentMessage}
                onChange={(e) => setCurrentMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={setupMode ? "Answer the question above..." : "Ask Aetheris anything..."}
                className="flex-1 px-3 py-2 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                rows="2"
                disabled={isProcessing}
              />
              <button
                onClick={handleChatSubmit}
                disabled={isProcessing || !currentMessage.trim()}
                className="px-4 py-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg hover:from-blue-700 hover:to-purple-700 disabled:opacity-50 transition-all shadow-md hover:shadow-lg flex items-center justify-center"
              >
                {isProcessing ? <Loader className="animate-spin" size={16} /> : <Brain size={16} />}
              </button>
            </div>
            <div className="flex justify-between items-center mt-2">
              <p className="text-xs text-gray-500">
                {setupMode ? 'Interview mode active' : 'Press Enter to send • Shift+Enter for new line'}
              </p>
              <div className="flex items-center space-x-2 text-xs text-gray-500">
                <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                <span>AI Online</span>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Quick Action FAB */}
      {!chatOpen && (
        <div className="fixed bottom-6 right-6 z-40">
          <button
            onClick={() => setChatOpen(true)}
            className="w-16 h-16 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-full shadow-xl hover:shadow-2xl transition-all flex items-center justify-center group hover:scale-105"
          >
            <Brain size={28} className="group-hover:rotate-12 transition-transform" />
          </button>
        </div>
      )}

      {/* Status Bar */}
      <div className="fixed bottom-0 left-72 right-0 bg-white bg-opacity-90 backdrop-blur-sm border-t border-gray-200 px-8 py-3 z-30">
        <div className="flex items-center justify-between text-sm">
          <div className="flex items-center space-x-6">
            <div className="flex items-center space-x-2 text-green-600">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span>System Online</span>
            </div>
            <div className="text-gray-600">
              Modules: {modules.length} Active
            </div>
            <div className="text-gray-600">
              AI Cores: 12 Online
            </div>
          </div>
          
          <div className="flex items-center space-x-6 text-gray-600">
            <span>Revenue: ${businessMetrics.monthlyRevenue.toLocaleString()}</span>
            <span>Guards: {businessMetrics.activeGuards}</span>
            <span>Clients: {businessMetrics.totalClients}</span>
          </div>
        </div>
      </div>

      {/* Custom Styles */}
      <style jsx>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        
        .animate-spin {
          animation: spin 1s linear infinite;
        }

        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.5; }
        }
        
        .animate-pulse {
          animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
        }

        .bg-clip-text {
          background-clip: text;
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }

        /* Smooth scrollbar */
        .overflow-y-auto::-webkit-scrollbar {
          width: 6px;
        }
        
        .overflow-y-auto::-webkit-scrollbar-track {
          background: #f1f5f9;
        }
        
        .overflow-y-auto::-webkit-scrollbar-thumb {
          background: #cbd5e1;
          border-radius: 3px;
        }
        
        .overflow-y-auto::-webkit-scrollbar-thumb:hover {
          background: #94a3b8;
        }

        /* Enhanced gradients */
        .backdrop-blur-sm {
          backdrop-filter: blur(4px);
        }
      `}</style>
    </div>
  );
};

export default SentariOS;
