import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, PieChart, Pie, Cell, LineChart, Line } from 'recharts';

const UniversalBusinessDashboard = () => {
  const [activeModule, setActiveModule] = useState('dashboard');
  const [selectedIndustry, setSelectedIndustry] = useState('security');
  const [employees, setEmployees] = useState([]);
  const [schedules, setSchedules] = useState([]);
  const [clients, setClients] = useState([]);
  const [incidents, setIncidents] = useState([]);
  const [newEmployee, setNewEmployee] = useState({ name: '', role: '', department: '', salary: '' });
  const [newClient, setNewClient] = useState({ name: '', industry: '', contact: '', status: 'Active' });
  const [newIncident, setNewIncident] = useState({ title: '', severity: 'Low', status: 'Open', description: '' });

  // Sample data for different industries
  const industryData = {
    security: {
      name: 'Security Services',
      icon: '🛡️',
      kpis: [
        { name: 'Active Guards', value: 45, color: '#2196F3' },
        { name: 'Sites Covered', value: 23, color: '#4CAF50' },
        { name: 'Incidents', value: 3, color: '#FF9800' },
        { name: 'Client Satisfaction', value: '98%', color: '#9C27B0' }
      ]
    },
    healthcare: {
      name: 'Healthcare Services',
      icon: '🏥',
      kpis: [
        { name: 'Active Staff', value: 120, color: '#2196F3' },
        { name: 'Patients Today', value: 340, color: '#4CAF50' },
        { name: 'Bed Occupancy', value: '89%', color: '#FF9800' },
        { name: 'Quality Score', value: '95%', color: '#9C27B0' }
      ]
    },
    construction: {
      name: 'Construction',
      icon: '🏗️',
      kpis: [
        { name: 'Active Workers', value: 78, color: '#2196F3' },
        { name: 'Projects', value: 12, color: '#4CAF50' },
        { name: 'Safety Incidents', value: 1, color: '#FF9800' },
        { name: 'On Schedule', value: '94%', color: '#9C27B0' }
      ]
    }
  };

  const modules = [
    { id: 'dashboard', name: 'Dashboard', icon: '📊' },
    { id: 'scheduling', name: 'Scheduling', icon: '📅' },
    { id: 'hr', name: 'HR Management', icon: '👥' },
    { id: 'clients', name: 'Clients', icon: '🏢' },
    { id: 'incidents', name: 'Incidents', icon: '⚠️' },
    { id: 'reports', name: 'Reports', icon: '📈' },
    { id: 'billing', name: 'Billing', icon: '💰' },
    { id: 'compliance', name: 'Compliance', icon: '✅' }
  ];

  const industries = [
    { id: 'security', name: 'Security Services', icon: '🛡️' },
    { id: 'healthcare', name: 'Healthcare', icon: '🏥' },
    { id: 'construction', name: 'Construction', icon: '🏗️' },
    { id: 'delivery', name: 'Delivery', icon: '🚚' },
    { id: 'retail', name: 'Retail', icon: '🛍️' },
    { id: 'hospitality', name: 'Hospitality', icon: '🏨' }
  ];

  // Sample chart data
  const monthlyData = [
    { month: 'Jan', revenue: 45000, costs: 32000, profit: 13000 },
    { month: 'Feb', revenue: 52000, costs: 35000, profit: 17000 },
    { month: 'Mar', revenue: 48000, costs: 33000, profit: 15000 },
    { month: 'Apr', revenue: 61000, costs: 38000, profit: 23000 },
    { month: 'May', revenue: 55000, costs: 36000, profit: 19000 },
    { month: 'Jun', revenue: 67000, costs: 41000, profit: 26000 }
  ];

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

  // Initialize with sample data
  useEffect(() => {
    setEmployees([
      { id: 1, name: 'John Smith', role: 'Security Guard', department: 'Operations', salary: '45000', status: 'Active' },
      { id: 2, name: 'Sarah Johnson', role: 'Supervisor', department: 'Management', salary: '65000', status: 'Active' },
      { id: 3, name: 'Mike Wilson', role: 'Security Guard', department: 'Operations', salary: '47000', status: 'Active' }
    ]);
    
    setClients([
      { id: 1, name: 'McDonald\'s Downtown', industry: 'Retail', contact: 'manager@mcdonalds.com', status: 'Active' },
      { id: 2, name: 'Memorial Hospital', industry: 'Healthcare', contact: 'security@memorial.com', status: 'Active' },
      { id: 3, name: 'City Mall', industry: 'Retail', contact: 'ops@citymall.com', status: 'Pending' }
    ]);
    
    setIncidents([
      { id: 1, title: 'Late Arrival - Site 3', severity: 'Low', status: 'Resolved', description: 'Guard arrived 15 minutes late due to traffic' },
      { id: 2, title: 'Suspicious Activity', severity: 'Medium', status: 'In Progress', description: 'Unknown individual loitering near entrance' },
      { id: 3, title: 'Equipment Malfunction', severity: 'High', status: 'Open', description: 'Security camera system offline at main entrance' }
    ]);
  }, []);

  const addEmployee = () => {
    if (newEmployee.name && newEmployee.role) {
      setEmployees([...employees, { 
        id: employees.length + 1, 
        ...newEmployee, 
        status: 'Active' 
      }]);
      setNewEmployee({ name: '', role: '', department: '', salary: '' });
    }
  };

  const addClient = () => {
    if (newClient.name) {
      setClients([...clients, { 
        id: clients.length + 1, 
        ...newClient 
      }]);
      setNewClient({ name: '', industry: '', contact: '', status: 'Active' });
    }
  };

  const addIncident = () => {
    if (newIncident.title) {
      setIncidents([...incidents, { 
        id: incidents.length + 1, 
        ...newIncident 
      }]);
      setNewIncident({ title: '', severity: 'Low', status: 'Open', description: '' });
    }
  };

  const renderDashboard = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold mb-4 flex items-center">
          {industryData[selectedIndustry].icon} {industryData[selectedIndustry].name} Dashboard
        </h2>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
          {industryData[selectedIndustry].kpis.map((kpi, index) => (
            <div key={index} className="bg-gradient-to-r from-blue-500 to-purple-600 text-white p-4 rounded-lg">
              <h3 className="text-lg font-semibold">{kpi.name}</h3>
              <p className="text-3xl font-bold">{kpi.value}</p>
            </div>
          ))}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <div className="bg-gray-50 p-4 rounded-lg">
            <h3 className="text-xl font-bold mb-4">Monthly Revenue</h3>
            <BarChart width={400} height={300} data={monthlyData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="revenue" fill="#4CAF50" />
              <Bar dataKey="costs" fill="#FF9800" />
              <Bar dataKey="profit" fill="#2196F3" />
            </BarChart>
          </div>

          <div className="bg-gray-50 p-4 rounded-lg">
            <h3 className="text-xl font-bold mb-4">Department Distribution</h3>
            <PieChart width={400} height={300}>
              <Pie
                data={[
                  { name: 'Operations', value: 65 },
                  { name: 'Management', value: 20 },
                  { name: 'Administration', value: 10 },
                  { name: 'Training', value: 5 }
                ]}
                cx={200}
                cy={150}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
                label
              >
                {monthlyData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </div>
        </div>
      </div>
    </div>
  );

  const renderHR = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold mb-4">👥 HR Management</h2>
        
        <div className="mb-6">
          <h3 className="text-xl font-semibold mb-4">Add New Employee</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
            <input
              type="text"
              placeholder="Employee Name"
              className="p-2 border rounded"
              value={newEmployee.name}
              onChange={(e) => setNewEmployee({...newEmployee, name: e.target.value})}
            />
            <input
              type="text"
              placeholder="Role"
              className="p-2 border rounded"
              value={newEmployee.role}
              onChange={(e) => setNewEmployee({...newEmployee, role: e.target.value})}
            />
            <input
              type="text"
              placeholder="Department"
              className="p-2 border rounded"
              value={newEmployee.department}
              onChange={(e) => setNewEmployee({...newEmployee, department: e.target.value})}
            />
            <input
              type="text"
              placeholder="Salary"
              className="p-2 border rounded"
              value={newEmployee.salary}
              onChange={(e) => setNewEmployee({...newEmployee, salary: e.target.value})}
            />
          </div>
          <button 
            onClick={addEmployee}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Add Employee
          </button>
        </div>

        <div className="overflow-x-auto">
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="border border-gray-300 p-2">Name</th>
                <th className="border border-gray-300 p-2">Role</th>
                <th className="border border-gray-300 p-2">Department</th>
                <th className="border border-gray-300 p-2">Salary</th>
                <th className="border border-gray-300 p-2">Status</th>
              </tr>
            </thead>
            <tbody>
              {employees.map(employee => (
                <tr key={employee.id}>
                  <td className="border border-gray-300 p-2">{employee.name}</td>
                  <td className="border border-gray-300 p-2">{employee.role}</td>
                  <td className="border border-gray-300 p-2">{employee.department}</td>
                  <td className="border border-gray-300 p-2">${employee.salary}</td>
                  <td className="border border-gray-300 p-2">
                    <span className={`px-2 py-1 rounded text-sm ${
                      employee.status === 'Active' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                    }`}>
                      {employee.status}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderClients = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold mb-4">🏢 Client Management</h2>
        
        <div className="mb-6">
          <h3 className="text-xl font-semibold mb-4">Add New Client</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
            <input
              type="text"
              placeholder="Client Name"
              className="p-2 border rounded"
              value={newClient.name}
              onChange={(e) => setNewClient({...newClient, name: e.target.value})}
            />
            <input
              type="text"
              placeholder="Industry"
              className="p-2 border rounded"
              value={newClient.industry}
              onChange={(e) => setNewClient({...newClient, industry: e.target.value})}
            />
            <input
              type="email"
              placeholder="Contact Email"
              className="p-2 border rounded"
              value={newClient.contact}
              onChange={(e) => setNewClient({...newClient, contact: e.target.value})}
            />
            <select 
              className="p-2 border rounded"
              value={newClient.status}
              onChange={(e) => setNewClient({...newClient, status: e.target.value})}
            >
              <option value="Active">Active</option>
              <option value="Pending">Pending</option>
              <option value="Inactive">Inactive</option>
            </select>
          </div>
          <button 
            onClick={addClient}
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Add Client
          </button>
        </div>

        <div className="overflow-x-auto">
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="border border-gray-300 p-2">Client Name</th>
                <th className="border border-gray-300 p-2">Industry</th>
                <th className="border border-gray-300 p-2">Contact</th>
                <th className="border border-gray-300 p-2">Status</th>
              </tr>
            </thead>
            <tbody>
              {clients.map(client => (
                <tr key={client.id}>
                  <td className="border border-gray-300 p-2">{client.name}</td>
                  <td className="border border-gray-300 p-2">{client.industry}</td>
                  <td className="border border-gray-300 p-2">{client.contact}</td>
                  <td className="border border-gray-300 p-2">
                    <span className={`px-2 py-1 rounded text-sm ${
                      client.status === 'Active' ? 'bg-green-100 text-green-800' : 
                      client.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' : 
                      'bg-red-100 text-red-800'
                    }`}>
                      {client.status}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderIncidents = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold mb-4">⚠️ Incident Management</h2>
        
        <div className="mb-6">
          <h3 className="text-xl font-semibold mb-4">Report New Incident</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <input
              type="text"
              placeholder="Incident Title"
              className="p-2 border rounded"
              value={newIncident.title}
              onChange={(e) => setNewIncident({...newIncident, title: e.target.value})}
            />
            <select 
              className="p-2 border rounded"
              value={newIncident.severity}
              onChange={(e) => setNewIncident({...newIncident, severity: e.target.value})}
            >
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
              <option value="Critical">Critical</option>
            </select>
            <select 
              className="p-2 border rounded"
              value={newIncident.status}
              onChange={(e) => setNewIncident({...newIncident, status: e.target.value})}
            >
              <option value="Open">Open</option>
              <option value="In Progress">In Progress</option>
              <option value="Resolved">Resolved</option>
            </select>
          </div>
          <textarea
            placeholder="Incident Description"
            className="w-full p-2 border rounded mb-4"
            rows={3}
            value={newIncident.description}
            onChange={(e) => setNewIncident({...newIncident, description: e.target.value})}
          />
          <button 
            onClick={addIncident}
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Report Incident
          </button>
        </div>

        <div className="space-y-4">
          {incidents.map(incident => (
            <div key={incident.id} className="border rounded-lg p-4">
              <div className="flex justify-between items-start mb-2">
                <h4 className="text-lg font-semibold">{incident.title}</h4>
                <div className="flex space-x-2">
                  <span className={`px-2 py-1 rounded text-sm ${
                    incident.severity === 'Critical' ? 'bg-red-100 text-red-800' :
                    incident.severity === 'High' ? 'bg-orange-100 text-orange-800' :
                    incident.severity === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-blue-100 text-blue-800'
                  }`}>
                    {incident.severity}
                  </span>
                  <span className={`px-2 py-1 rounded text-sm ${
                    incident.status === 'Resolved' ? 'bg-green-100 text-green-800' :
                    incident.status === 'In Progress' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-red-100 text-red-800'
                  }`}>
                    {incident.status}
                  </span>
                </div>
              </div>
              <p className="text-gray-600">{incident.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const renderReports = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold mb-4">📈 Reports & Analytics</h2>
        
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <div className="bg-gray-50 p-4 rounded-lg">
            <h3 className="text-xl font-bold mb-4">Performance Trends</h3>
            <LineChart width={400} height={300} data={monthlyData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="profit" stroke="#4CAF50" strokeWidth={2} />
              <Line type="monotone" dataKey="revenue" stroke="#2196F3" strokeWidth={2} />
            </LineChart>
          </div>
          
          <div className="bg-gray-50 p-4 rounded-lg">
            <h3 className="text-xl font-bold mb-4">Key Metrics Summary</h3>
            <div className="space-y-4">
              <div className="flex justify-between items-center p-3 bg-white rounded">
                <span className="font-medium">Total Employees</span>
                <span className="text-xl font-bold text-blue-600">{employees.length}</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-white rounded">
                <span className="font-medium">Active Clients</span>
                <span className="text-xl font-bold text-green-600">{clients.filter(c => c.status === 'Active').length}</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-white rounded">
                <span className="font-medium">Open Incidents</span>
                <span className="text-xl font-bold text-red-600">{incidents.filter(i => i.status !== 'Resolved').length}</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-white rounded">
                <span className="font-medium">Monthly Revenue</span>
                <span className="text-xl font-bold text-purple-600">$67,000</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch(activeModule) {
      case 'hr': return renderHR();
      case 'clients': return renderClients();
      case 'incidents': return renderIncidents();
      case 'reports': return renderReports();
      case 'scheduling': 
        return (
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold mb-4">📅 Scheduling Module</h2>
            <p className="text-gray-600">Advanced scheduling features coming soon. This would include:</p>
            <ul className="list-disc list-inside mt-4 space-y-2">
              <li>Drag & drop shift management</li>
              <li>Employee availability tracking</li>
              <li>Automatic schedule optimization</li>
              <li>Time-off request management</li>
            </ul>
          </div>
        );
      case 'billing':
        return (
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold mb-4">💰 Billing & Invoicing</h2>
            <p className="text-gray-600">Billing features coming soon. This would include:</p>
            <ul className="list-disc list-inside mt-4 space-y-2">
              <li>Automated invoice generation</li>
              <li>Payment processing integration</li>
              <li>Recurring billing setup</li>
              <li>Financial reporting</li>
            </ul>
          </div>
        );
      case 'compliance':
        return (
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold mb-4">✅ Compliance Management</h2>
            <p className="text-gray-600">Compliance features coming soon. This would include:</p>
            <ul className="list-disc list-inside mt-4 space-y-2">
              <li>Regulatory requirement tracking</li>
              <li>Audit trail management</li>
              <li>Document compliance verification</li>
              <li>Automated compliance reporting</li>
            </ul>
          </div>
        );
      default: return renderDashboard();
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow-lg p-4">
        <div className="flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <h1 className="text-2xl font-bold text-gray-800">🚀 Universal Business Platform</h1>
            <select 
              className="p-2 border rounded-lg"
              value={selectedIndustry}
              onChange={(e) => setSelectedIndustry(e.target.value)}
            >
              {industries.map(industry => (
                <option key={industry.id} value={industry.id}>
                  {industry.icon} {industry.name}
                </option>
              ))}
            </select>
          </div>
          <div className="text-sm text-gray-600">
            Backend: ✅ localhost:3000 | Frontend: ✅ localhost:3001
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Sidebar */}
        <aside className="w-64 bg-white shadow-lg min-h-screen">
          <nav className="p-4">
            <ul className="space-y-2">
              {modules.map(module => (
                <li key={module.id}>
                  <button
                    onClick={() => setActiveModule(module.id)}
                    className={`w-full text-left p-3 rounded-lg flex items-center space-x-3 transition-colors ${
                      activeModule === module.id 
                        ? 'bg-blue-500 text-white' 
                        : 'hover:bg-gray-100 text-gray-700'
                    }`}
                  >
                    <span className="text-xl">{module.icon}</span>
                    <span className="font-medium">{module.name}</span>
                  </button>
                </li>
              ))}
            </ul>
          </nav>
        </aside>

        {/* Main Content */}
        <main className="flex-1 p-6">
          {renderContent()}
        </main>
      </div>
    </div>
  );
};

export default UniversalBusinessDashboard;
