import React, { useState } from 'react';

const AIPlatformBuilder = () => {
  const [currentStep, setCurrentStep] = useState('welcome');
  const [chatHistory, setChatHistory] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [platformConfig, setPlatformConfig] = useState({});
  const [generatedModules, setGeneratedModules] = useState([]);
  const [isGenerating, setIsGenerating] = useState(false);

  const aiQuestions = {
    welcome: {
      message: "ðŸ‘‹ Hello! I'm your AI Platform Builder. I'll create a custom business management platform tailored specifically for your business. Let's start with the basics - what type of business do you run?",
      options: ["Security Services", "Healthcare", "Construction", "Cleaning Services", "Delivery/Logistics", "Restaurant/Hospitality", "Retail", "Manufacturing", "Other"]
    },
    industry_specific: {
      security: "Perfect! Security services need specialized features. Do you primarily handle: Static Guard Services, Mobile Patrols, Event Security, or Executive Protection?",
      healthcare: "Great! For healthcare, do you need: Patient Management, Staff Scheduling, HIPAA Compliance, or Medical Equipment Tracking?",
      construction: "Excellent! Construction businesses typically need: Project Management, Safety Compliance, Equipment Tracking, or Worker Certification Management?",
      cleaning: "Perfect for cleaning services! Do you focus on: Residential Cleaning, Commercial Buildings, Post-Construction, or Specialized Cleaning?",
      delivery: "Great choice! For delivery/logistics, what's your main focus: Last-Mile Delivery, Route Optimization, Fleet Management, or Warehouse Operations?",
      restaurant: "Perfect! Restaurant operations need: Table Management, Staff Scheduling, Inventory Control, or Delivery Coordination?",
      retail: "Excellent! Retail businesses typically need: Inventory Management, Staff Scheduling, Customer Management, or Multi-Location Coordination?",
      manufacturing: "Great for manufacturing! Do you need: Production Scheduling, Quality Control, Equipment Maintenance, or Supply Chain Management?"
    },
    core_features: "Based on your business type, I recommend these core modules. Which ones are most important to you?",
    advanced_features: "Now let's talk about advanced features. Do you need:",
    customization: "Great! Now I'll generate your custom platform with all the features you've selected."
  };

  const industryModules = {
    security: [
      { id: 'dashboard', name: 'Live Operations Dashboard', description: 'Real-time monitoring of guards, sites, and incidents', icon: 'ðŸ“Š', essential: true },
      { id: 'scheduling', name: 'Guard Scheduling', description: 'Shift management and guard assignments', icon: 'ðŸ“…', essential: true },
      { id: 'gps_tracking', name: 'GPS Tracking', description: 'Real-time location tracking of security personnel', icon: 'ðŸ“', essential: true },
      { id: 'incident_reports', name: 'Incident Reporting', description: 'Digital incident reports with photos and signatures', icon: 'ðŸ“‹', essential: true },
      { id: 'patrol_management', name: 'Patrol Tours', description: 'Checkpoint scanning and patrol route management', icon: 'ðŸš¶', essential: true },
      { id: 'client_portal', name: 'Client Portal', description: 'Client access to reports and communications', icon: 'ðŸ¢', essential: false },
      { id: 'billing', name: 'Billing & Invoicing', description: 'Automated billing and invoice generation', icon: 'ðŸ’°', essential: false },
      { id: 'hr_management', name: 'HR Management', description: 'Employee records and certification tracking', icon: 'ðŸ‘¥', essential: false },
      { id: 'dispatch', name: 'Dispatch Center', description: 'Central command for managing security operations', icon: 'ðŸ“¡', essential: false }
    ],
    healthcare: [
      { id: 'dashboard', name: 'Patient Care Dashboard', description: 'Overview of patient status and staff assignments', icon: 'ðŸ“Š', essential: true },
      { id: 'patient_management', name: 'Patient Management', description: 'Patient records and care coordination', icon: 'ðŸ¥', essential: true },
      { id: 'staff_scheduling', name: 'Staff Scheduling', description: 'Nurse and doctor shift management', icon: 'ðŸ“…', essential: true },
      { id: 'compliance', name: 'HIPAA Compliance', description: 'Privacy and compliance management', icon: 'ðŸ”’', essential: true },
      { id: 'medication_tracking', name: 'Medication Management', description: 'Track medication administration and schedules', icon: 'ðŸ’Š', essential: false },
      { id: 'equipment_tracking', name: 'Medical Equipment', description: 'Track and maintain medical equipment', icon: 'ðŸ©º', essential: false },
      { id: 'billing', name: 'Medical Billing', description: 'Insurance claims and patient billing', icon: 'ðŸ’°', essential: false }
    ],
    construction: [
      { id: 'dashboard', name: 'Project Dashboard', description: 'Overview of all construction projects and progress', icon: 'ðŸ“Š', essential: true },
      { id: 'project_management', name: 'Project Management', description: 'Track project timelines and milestones', icon: 'ðŸ—ï¸', essential: true },
      { id: 'worker_scheduling', name: 'Worker Scheduling', description: 'Manage construction crew schedules', icon: 'ðŸ“…', essential: true },
      { id: 'safety_compliance', name: 'Safety Compliance', description: 'OSHA compliance and safety incident tracking', icon: 'â›‘ï¸', essential: true },
      { id: 'equipment_management', name: 'Equipment Management', description: 'Track heavy machinery and tools', icon: 'ðŸš›', essential: false },
      { id: 'material_tracking', name: 'Material Management', description: 'Inventory and supply chain management', icon: 'ðŸ“¦', essential: false },
      { id: 'quality_control', name: 'Quality Control', description: 'Inspections and quality assurance', icon: 'âœ…', essential: false }
    ]
  };

  const advancedFeatures = [
    { id: 'gps_tracking', name: 'GPS Tracking', description: 'Real-time location tracking', icon: 'ðŸ“' },
    { id: 'mobile_app', name: 'Mobile App', description: 'iOS and Android apps for field workers', icon: 'ðŸ“±' },
    { id: 'crm', name: 'CRM System', description: 'Customer relationship management', icon: 'ðŸ¤' },
    { id: 'reporting', name: 'Advanced Reporting', description: 'Custom reports and analytics', icon: 'ðŸ“ˆ' },
    { id: 'integrations', name: 'Third-party Integrations', description: 'Connect with existing software', icon: 'ðŸ”—' },
    { id: 'ai_insights', name: 'AI Insights', description: 'Predictive analytics and recommendations', icon: 'ðŸ¤–' },
    { id: 'multi_location', name: 'Multi-Location Support', description: 'Manage multiple sites or branches', icon: 'ðŸŒ' },
    { id: 'compliance_automation', name: 'Compliance Automation', description: 'Automated regulatory compliance', icon: 'ðŸ”’' }
  ];

  const addToChat = (message, isUser = false, options = null) => {
    setChatHistory(prev => [...prev, { message, isUser, options, timestamp: new Date() }]);
  };

  const handleWelcomeSelection = (industry) => {
    const industryKey = industry.toLowerCase().replace(/[^a-z]/g, '');
    addToChat(industry, true);
    
    setPlatformConfig(prev => ({ ...prev, industry: industry, industryKey }));
    
    const aiResponse = aiQuestions.industry_specific[industryKey] || 
                      "Great choice! Let me understand your specific needs better. What are the main challenges you face in your daily operations?";
    
    setTimeout(() => {
      if (industryModules[industryKey]) {
        addToChat(aiResponse);
        setCurrentStep('modules');
        setTimeout(() => {
          addToChat("Based on your industry, here are the modules I recommend for your platform:", false, industryModules[industryKey]);
        }, 1000);
      } else {
        addToChat("I'd love to learn more about your specific business. Can you tell me what your main operational challenges are?");
        setCurrentStep('custom_questions');
      }
    }, 1000);
  };

  const handleModuleSelection = (selectedModules) => {
    setPlatformConfig(prev => ({ ...prev, selectedModules }));
    addToChat(`Selected modules: ${selectedModules.map(m => m.name).join(', ')}`, true);
    
    setTimeout(() => {
      addToChat("Excellent choices! Now let's add some advanced features to make your platform even more powerful:", false, advancedFeatures);
      setCurrentStep('advanced_features');
    }, 1000);
  };

  const handleAdvancedFeatures = (selectedFeatures) => {
    setPlatformConfig(prev => ({ ...prev, advancedFeatures: selectedFeatures }));
    addToChat(`Selected features: ${selectedFeatures.map(f => f.name).join(', ')}`, true);
    
    setTimeout(() => {
      addToChat("Perfect! Now I have everything I need. Let me generate your custom business platform...");
      setCurrentStep('generating');
      generatePlatform();
    }, 1000);
  };

  const generatePlatform = () => {
    setIsGenerating(true);
    
    // Get modules based on industry if no modules selected
    const modulesToGenerate = platformConfig.selectedModules?.length > 0 
      ? platformConfig.selectedModules 
      : industryModules[platformConfig.industryKey] || [
          { id: 'dashboard', name: 'Business Dashboard', description: 'Overview of your business operations', icon: 'ðŸ“Š' },
          { id: 'scheduling', name: 'Staff Scheduling', description: 'Manage employee schedules and shifts', icon: 'ðŸ“…' },
          { id: 'reports', name: 'Reports & Analytics', description: 'Business insights and reporting', icon: 'ðŸ“ˆ' }
        ];
    
    // Simulate AI generation process
    const steps = [
      "ðŸ—ï¸ Creating database architecture...",
      "âš¡ Building backend APIs...",
      "ðŸŽ¨ Designing user interface...",
      "ðŸ“± Generating mobile app...",
      "ðŸ”— Setting up integrations...",
      "âœ… Finalizing your platform..."
    ];

    steps.forEach((step, index) => {
      setTimeout(() => {
        addToChat(step);
        
        if (index === steps.length - 1) {
          setTimeout(() => {
            setIsGenerating(false);
            setCurrentStep('complete');
            
            // Generate the actual modules
            const modules = modulesToGenerate.map(module => ({
              ...module,
              status: 'generated',
              code: `// Generated ${module.name} Module\n// This module handles ${module.description.toLowerCase()}\n\nconst ${module.id}Module = {\n  name: "${module.name}",\n  description: "${module.description}",\n  // Module implementation here\n};`
            }));
            
            setGeneratedModules(modules);
            
            addToChat("ðŸŽ‰ Your custom business platform is ready! You can now preview each module and start using your platform.");
          }, 1000);
        }
      }, index * 2000);
    });
  };

  const handleUserInput = () => {
    if (!userInput.trim()) return;
    
    addToChat(userInput, true);
    
    // Simple AI responses based on context
    setTimeout(() => {
      if (currentStep === 'custom_questions') {
        addToChat("I understand. Based on what you've told me, I'll create a custom solution. Let me start building your platform with the essential modules you need.");
        setCurrentStep('generating');
        generatePlatform();
      }
    }, 1000);
    
    setUserInput('');
  };

  const ModuleSelector = ({ modules, onSelect, allowMultiple = true }) => {
    const [selected, setSelected] = useState([]);

    const toggleSelection = (module) => {
      if (allowMultiple) {
        setSelected(prev => {
          const isSelected = prev.find(m => m.id === module.id);
          if (isSelected) {
            return prev.filter(m => m.id !== module.id);
          } else {
            return [...prev, module];
          }
        });
      } else {
        setSelected([module]);
      }
    };

    return (
      <div className="space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {modules.map(module => (
            <div
              key={module.id}
              onClick={() => toggleSelection(module)}
              className={`p-4 border-2 rounded-lg cursor-pointer transition-all ${
                selected.find(m => m.id === module.id)
                  ? 'border-blue-500 bg-blue-50'
                  : 'border-gray-200 hover:border-blue-300'
              } ${module.essential ? 'ring-2 ring-green-200' : ''}`}
            >
              <div className="flex items-start space-x-3">
                <span className="text-2xl">{module.icon}</span>
                <div className="flex-1">
                  <h3 className="font-semibold text-gray-800">
                    {module.name}
                    {module.essential && <span className="text-green-600 text-sm ml-2">(Recommended)</span>}
                  </h3>
                  <p className="text-sm text-gray-600 mt-1">{module.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
        
        {selected.length > 0 && (
          <div className="flex justify-center">
            <button
              onClick={() => onSelect(selected)}
              className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors"
            >
              Continue with {selected.length} {allowMultiple && selected.length > 1 ? 'modules' : 'module'}
            </button>
          </div>
        )}
      </div>
    );
  };

  const PlatformPreview = () => (
    <div className="space-y-6">
      <div className="text-center">
        <h2 className="text-3xl font-bold text-gray-800 mb-2">
          ðŸŽ‰ Your {platformConfig.industry} Platform is Ready!
        </h2>
        <p className="text-gray-600">
          Here's what I've built for you based on your requirements:
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {generatedModules.map(module => (
          <div key={module.id} className="bg-white border rounded-lg p-6 shadow-sm">
            <div className="flex items-center space-x-3 mb-4">
              <span className="text-3xl">{module.icon}</span>
              <div>
                <h3 className="font-semibold text-gray-800">{module.name}</h3>
                <span className="inline-block bg-green-100 text-green-800 text-xs px-2 py-1 rounded-full">
                  Generated
                </span>
              </div>
            </div>
            <p className="text-sm text-gray-600 mb-4">{module.description}</p>
            <div className="space-y-2">
              <button className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition-colors">
                Launch Module
              </button>
              <button className="w-full bg-gray-100 text-gray-700 py-2 px-4 rounded hover:bg-gray-200 transition-colors">
                View Code
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="text-center space-y-4">
        <div className="bg-gray-50 p-6 rounded-lg">
          <h3 className="font-semibold text-gray-800 mb-2">Platform Statistics</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div>
              <div className="text-2xl font-bold text-blue-600">{generatedModules.length}</div>
              <div className="text-sm text-gray-600">Modules Generated</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-green-600">100%</div>
              <div className="text-sm text-gray-600">Customized</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-purple-600">Ready</div>
              <div className="text-sm text-gray-600">Status</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-orange-600">Live</div>
              <div className="text-sm text-gray-600">Deployment</div>
            </div>
          </div>
        </div>

        <div className="space-x-4">
          <button className="bg-green-500 text-white px-6 py-3 rounded-lg hover:bg-green-600 transition-colors">
            ðŸš€ Launch Platform
          </button>
          <button 
            onClick={() => {
              setCurrentStep('welcome');
              setChatHistory([]);
              setPlatformConfig({});
              setGeneratedModules([]);
            }}
            className="bg-gray-500 text-white px-6 py-3 rounded-lg hover:bg-gray-600 transition-colors"
          >
            Build Another Platform
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-800 mb-4">
            ðŸ¤– AI Platform Builder
          </h1>
          <p className="text-xl text-gray-600">
            Tell me about your business, and I'll build a custom management platform for you
          </p>
        </div>

        {/* Chat Interface */}
        {currentStep !== 'complete' && (
          <div className="max-w-4xl mx-auto bg-white rounded-xl shadow-lg p-6 mb-8">
            <div className="h-96 overflow-y-auto mb-6 space-y-4">
              {chatHistory.map((chat, index) => (
                <div key={index} className={`flex ${chat.isUser ? 'justify-end' : 'justify-start'}`}>
                  <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                    chat.isUser 
                      ? 'bg-blue-500 text-white' 
                      : 'bg-gray-100 text-gray-800'
                  }`}>
                    {chat.message}
                    
                    {/* Industry Selection Options */}
                    {chat.options && currentStep === 'welcome' && (
                      <div className="grid grid-cols-2 gap-2 mt-3">
                        {chat.options.map(option => (
                          <button
                            key={option}
                            onClick={() => handleWelcomeSelection(option)}
                            className="bg-white text-gray-800 px-3 py-2 rounded text-sm hover:bg-blue-50 transition-colors"
                          >
                            {option}
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              ))}
              
              {isGenerating && (
                <div className="flex justify-start">
                  <div className="bg-gray-100 text-gray-800 px-4 py-2 rounded-lg">
                    <div className="flex items-center space-x-2">
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                      <span>AI is building your platform...</span>
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Module Selection Interface */}
            {currentStep === 'modules' && chatHistory.length > 0 && !isGenerating && (
              <ModuleSelector 
                modules={chatHistory[chatHistory.length - 1].options || []} 
                onSelect={handleModuleSelection}
              />
            )}

            {/* Advanced Features Selection */}
            {currentStep === 'advanced_features' && chatHistory.length > 0 && !isGenerating && (
              <ModuleSelector 
                modules={chatHistory[chatHistory.length - 1].options || []} 
                onSelect={handleAdvancedFeatures}
              />
            )}

            {/* Input Field */}
            {(currentStep === 'custom_questions' || currentStep === 'welcome') && !isGenerating && (
              <div className="flex space-x-4">
                <input
                  type="text"
                  value={userInput}
                  onChange={(e) => setUserInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleUserInput()}
                  placeholder="Tell me about your business needs..."
                  className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                <button
                  onClick={handleUserInput}
                  className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors"
                >
                  Send
                </button>
              </div>
            )}
          </div>
        )}

        {/* Initial Welcome */}
        {currentStep === 'welcome' && chatHistory.length === 0 && (
          <div className="max-w-4xl mx-auto bg-white rounded-xl shadow-lg p-8">
            <div className="text-center space-y-6">
              <div className="text-6xl">ðŸ¤–</div>
              <div>
                <h2 className="text-2xl font-bold text-gray-800 mb-4">
                  Welcome to AI Platform Builder!
                </h2>
                <p className="text-gray-600 mb-6">
                  I'll ask you a few questions about your business and build a custom management platform tailored to your exact needs. 
                  Think TrackTik, but built specifically for YOU.
                </p>
                
                <button 
                  onClick={() => addToChat(aiQuestions.welcome.message, false, aiQuestions.welcome.options)}
                  className="bg-blue-500 text-white px-8 py-3 rounded-lg hover:bg-blue-600 transition-colors text-lg"
                >
                  ðŸš€ Start Building My Platform
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-8">
                <div className="text-center">
                  <div className="text-3xl mb-2">ðŸŽ¯</div>
                  <h3 className="font-semibold">Tailored to You</h3>
                  <p className="text-sm text-gray-600">Custom features based on your industry and needs</p>
                </div>
                <div className="text-center">
                  <div className="text-3xl mb-2">âš¡</div>
                  <h3 className="font-semibold">Built in Minutes</h3>
                  <p className="text-sm text-gray-600">What takes months, AI builds in minutes</p>
                </div>
                <div className="text-center">
                  <div className="text-3xl mb-2">ðŸš€</div>
                  <h3 className="font-semibold">Ready to Use</h3>
                  <p className="text-sm text-gray-600">Launch immediately with full functionality</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Platform Preview */}
        {currentStep === 'complete' && <PlatformPreview />}
      </div>
    </div>
  );
};

export default AIPlatformBuilder;
