import React, { useState, useEffect, useRef } from 'react';
import { Calendar, Users, MapPin, BarChart3, Clock, Shield, Zap, DollarSign, TrendingUp, Activity, X } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  const [activeModule, setActiveModule] = useState('dashboard');
  const [aetherisStatus, setAetherisStatus] = useState('online');
  const [aiCores, setAiCores] = useState(12);
  
  // AI Editor State
  const [aiEditorOpen, setAiEditorOpen] = useState(false);
  const [aiResponse, setAiResponse] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [platformModules, setPlatformModules] = useState([]);

  // Chart Data
  const performanceData = [
    { month: 'Jan', efficiency: 78, profit: 45000, revenue: 85000, satisfaction: 92 },
    { month: 'Feb', efficiency: 82, profit: 52000, revenue: 92000, satisfaction: 88 },
    { month: 'Mar', efficiency: 85, profit: 58000, revenue: 98000, satisfaction: 91 },
    { month: 'Apr', efficiency: 88, profit: 64000, revenue: 105000, satisfaction: 94 },
    { month: 'May', efficiency: 91, profit: 71000, revenue: 112000, satisfaction: 96 },
    { month: 'Jun', efficiency: 94, profit: 78000, revenue: 118000, satisfaction: 93 }
  ];

  const industryData = [
    { name: 'Security Services', value: 450000, color: '#3b82f6' },
    { name: 'Healthcare', value: 320000, color: '#10b981' },
    { name: 'Construction', value: 280000, color: '#f59e0b' },
    { name: 'Retail', value: 180000, color: '#8b5cf6' }
  ];

  const dailyMetrics = [
    { time: '00:00', guards: 45, incidents: 2, efficiency: 78 },
    { time: '04:00', guards: 52, incidents: 1, efficiency: 85 },
    { time: '08:00', guards: 127, incidents: 0, efficiency: 92 },
    { time: '12:00', guards: 134, incidents: 3, efficiency: 89 },
    { time: '16:00', guards: 142, incidents: 1, efficiency: 94 },
    { time: '20:00', guards: 98, incidents: 2, efficiency: 87 }
  ];

  // Initialize platform
  useEffect(() => {
    setPlatformModules([
      { id: 'guard-management', name: 'Guard Management', type: 'core', status: 'active', description: 'Manage security guards and assignments' },
      { id: 'site-management', name: 'Site Management', type: 'core', status: 'active', description: 'Manage client sites and locations' },
      { id: 'scheduling', name: 'AI Scheduling', type: 'core', status: 'active', description: 'Intelligent scheduling system' },
      { id: 'analytics', name: 'Business Intelligence', type: 'feature', status: 'active', description: 'KPIs and performance analytics' }
    ]);
  }, []);

  // Test data
  const guards = [
    { id: 1, name: 'John Smith', status: 'on-duty', site: 'McDonalds Downtown', shift: '6AM-2PM', phone: '(555) 123-4567' },
    { id: 2, name: 'Sarah Johnson', status: 'available', site: 'Hospital Complex', shift: '2PM-10PM', phone: '(555) 234-5678' },
    { id: 3, name: 'Mike Wilson', status: 'off-duty', site: 'Retail Center', shift: '10PM-6AM', phone: '(555) 345-6789' },
    { id: 4, name: 'Lisa Davis', status: 'on-duty', site: 'Office Building', shift: '6AM-2PM', phone: '(555) 456-7890' }
  ];

  const sites = [
    { id: 1, name: 'McDonalds Downtown', address: '123 Main St', guards: 2, status: 'secured' },
    { id: 2, name: 'Hospital Complex', address: '456 Health Ave', guards: 3, status: 'secured' },
    { id: 3, name: 'Retail Center', address: '789 Shopping Blvd', guards: 1, status: 'understaffed' },
    { id: 4, name: 'Office Building', address: '321 Business Dr', guards: 2, status: 'secured' }
  ];

  const schedules = [
    { id: 1, guard: 'John Smith', site: 'McDonalds Downtown', date: '2025-08-13', shift: '6AM-2PM', status: 'confirmed' },
    { id: 2, guard: 'Sarah Johnson', site: 'Hospital Complex', date: '2025-08-13', shift: '2PM-10PM', status: 'confirmed' },
    { id: 3, guard: 'Mike Wilson', site: 'Retail Center', date: '2025-08-14', shift: '10PM-6AM', status: 'pending' },
    { id: 4, guard: 'Lisa Davis', site: 'Office Building', date: '2025-08-14', shift: '6AM-2PM', status: 'confirmed' }
  ];

  // FIXED AI EDITOR - Using useRef to prevent typing issues
  const AIEditor = () => {
    const textareaRef = useRef(null);
    
    const processPrompt = () => {
      const prompt = textareaRef.current?.value || '';
      if (!prompt.trim()) return;
      
      setIsProcessing(true);
      setAiResponse('');
      
      // Simulate AI response
      setTimeout(() => {
        const lowerPrompt = prompt.toLowerCase();
        let response = '';

        // REMOVE MODULE REQUESTS
        if (lowerPrompt.includes('remove') && lowerPrompt.includes('guard')) {
          response = `ðŸ§  **AI Analysis**: I understand you want to remove Guard Management module.

**Action Plan:**
âœ… Remove Guard Management from active modules
âœ… Archive guard management code and database schema  
âœ… Update navigation to remove Guard Management menu item
âœ… Preserve all guard data and configurations for potential restoration

**Impact Assessment:**
- Guard Management will disappear from sidebar navigation
- All guard data remains safely archived
- You can restore this module anytime by saying "bring back guard management"
- Other modules (Site Management, Scheduling) remain unaffected

**Module will be archived in 3 seconds... This would normally connect to your backend to actually remove it.**

âš ï¸ *To make this actually work, we need to connect to your backend server with the ModuleTemplateSystem.*`;

          // Simulate the actual removal after 3 seconds
          setTimeout(() => {
            console.log('ðŸ—‘ï¸ Would remove Guard Management module via API call');
            setAiResponse(prev => prev + '\n\nâœ… **Guard Management module has been archived successfully!**');
          }, 3000);

        } else if (lowerPrompt.includes('remove') && lowerPrompt.includes('gps')) {
          response = `ðŸ§  **AI Analysis**: I understand you want to remove GPS tracking functionality.

**Action Plan:**
âœ… Remove GPS Tracking module from active modules
âœ… Archive GPS tracking code and database schema  
âœ… Update navigation to remove GPS-related menu items
âœ… Preserve all GPS data and configurations for potential restoration

**Impact Assessment:**
- Guard check-ins will use manual location entry
- Real-time tracking dashboard will be disabled  
- Historical GPS data remains preserved
- Can be restored instantly if needed

**Would you like me to proceed?**`;

        } else if (lowerPrompt.includes('remove') && (lowerPrompt.includes('site') || lowerPrompt.includes('scheduling') || lowerPrompt.includes('analytics'))) {
          const moduleName = lowerPrompt.includes('site') ? 'Site Management' :
                            lowerPrompt.includes('scheduling') ? 'AI Scheduling' : 'Business Intelligence';
          
          response = `ðŸ§  **AI Analysis**: I understand you want to remove ${moduleName} module.

**Action Plan:**
âœ… Remove ${moduleName} from active modules
âœ… Archive all related code and database schemas
âœ… Update navigation to remove ${moduleName} menu item
âœ… Preserve all data for potential restoration

**Module will be safely archived and can be restored anytime.**`;

        } else if (lowerPrompt.includes('build') || lowerPrompt.includes('create') || lowerPrompt.includes('add')) {
          response = `ðŸ§  **AI Module Builder**: I can build that for you!

**I'll generate:**
âœ… Complete working React component
âœ… Database schema with proper relationships  
âœ… RESTful API endpoints
âœ… Professional user interface

**What specific module do you need?**
- Inventory management system
- Report dashboard with analytics
- Approval workflow system
- Scheduling and booking system
- Financial tracking module
- Asset management system

**Just tell me what your business needs and I'll build it!**

*To make this actually work, we need your backend server.js connected.*`;

        } else if (lowerPrompt.includes('replace') && lowerPrompt.includes('nurse')) {
          response = `ðŸ§  **AI Analysis**: I understand you want to replace Guard Management with Nurse Management for hospital operations.

**Action Plan:**
âœ… Archive Guard Management module safely
âœ… Generate complete Nurse Management system with:
   â€¢ Nurse scheduling with ward assignments
   â€¢ Certification tracking and expiry alerts
   â€¢ Patient-to-nurse ratio monitoring
   â€¢ Medical compliance dashboards
âœ… Update all navigation and KPIs for healthcare
âœ… Transform "Guards Online" to "Nurses Online"

**Your platform will become hospital-focused instantly!**

*Backend connection needed to actually execute this transformation.*`;

        } else if (lowerPrompt.includes('bring back') || lowerPrompt.includes('restore')) {
          response = `ðŸ§  **AI Analysis**: I can restore archived modules instantly!

**Available Archived Modules:**
â€¢ GPS Tracking Module (archived 2 days ago)
â€¢ Incident Reporting (archived 1 week ago)
â€¢ Vendor Management (archived 3 days ago)

**Just tell me which module to restore and it will be back online immediately with all data preserved.**

*Example: "Bring back GPS tracking module"*`;

        } else {
          response = `ðŸ§  **AI Analysis**: I understand you want to modify the platform.

**I can help you:**
â€¢ **Remove modules:** "Remove guard management module"
â€¢ **Build new modules:** "Create inventory management system"
â€¢ **Replace modules:** "Replace guard management with nurse management"
â€¢ **Restore archived:** "Bring back GPS module"

**Current Active Modules:**
${platformModules.map(m => `â€¢ ${m.name}`).join('\n')}

**What specific changes would you like me to make?**

*Try being more specific: "Remove the guard management module" or "Create inventory management system"*`;
        }

        setAiResponse(response);
        // Clear input after processing
        if (textareaRef.current) {
          textareaRef.current.value = '';
        }
        setIsProcessing(false);
      }, 1000);
    };

    return (
      <div style={{
        position: 'fixed',
        top: aiEditorOpen ? '10px' : 'auto',
        bottom: aiEditorOpen ? '10px' : '20px',
        right: '20px',
        width: aiEditorOpen ? '400px' : '60px',
        height: aiEditorOpen ? 'calc(100vh - 20px)' : '60px',
        background: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(20px)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '12px',
        zIndex: 1000,
        transition: 'all 0.3s ease',
        boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.3)'
      }}>
        {!aiEditorOpen && (
          <button
            onClick={() => setAiEditorOpen(true)}
            style={{
              width: '100%',
              height: '100%',
              background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
              border: 'none',
              borderRadius: '12px',
              color: 'white',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '24px'
            }}
            title="Open AI Platform Editor"
          >
            ðŸ¤–
          </button>
        )}

        {aiEditorOpen && (
          <div style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <div style={{
              padding: '15px',
              borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}>
              <div>
                <h3 style={{ color: '#3b82f6', margin: 0, fontSize: '16px' }}>ðŸ¤– AI Editor</h3>
                <p style={{ color: '#64748b', margin: '2px 0 0 0', fontSize: '11px' }}>
                  Modify your platform with natural language
                </p>
              </div>
              <button
                onClick={() => setAiEditorOpen(false)}
                style={{
                  background: 'transparent',
                  border: 'none',
                  color: '#94a3b8',
                  cursor: 'pointer',
                  padding: '4px'
                }}
              >
                <X size={16} />
              </button>
            </div>

            <div style={{ flex: 1, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
              <div style={{
                flex: 1,
                padding: '15px',
                overflowY: 'auto',
                fontSize: '13px'
              }}>
                {aiResponse ? (
                  <div style={{
                    background: 'rgba(16, 185, 129, 0.1)',
                    border: '1px solid rgba(16, 185, 129, 0.3)',
                    borderRadius: '8px',
                    padding: '12px',
                    marginBottom: '15px'
                  }}>
                    <div style={{ 
                      color: '#e2e8f0', 
                      lineHeight: '1.4',
                      whiteSpace: 'pre-line'
                    }}>
                      {aiResponse}
                    </div>
                  </div>
                ) : (
                  <div style={{ color: '#64748b', textAlign: 'center', padding: '20px' }}>
                    Tell me what you would like to change about your platform...
                  </div>
                )}
              </div>

              <div style={{
                padding: '15px',
                borderTop: '1px solid rgba(59, 130, 246, 0.2)'
              }}>
                <div style={{ display: 'flex', gap: '8px' }}>
                  <textarea
                    ref={textareaRef}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        processPrompt();
                      }
                    }}
                    placeholder="e.g., 'Remove guard management module' or 'Create inventory management system'"
                    style={{
                      flex: 1,
                      background: 'rgba(255, 255, 255, 0.1)',
                      border: '1px solid rgba(59, 130, 246, 0.3)',
                      borderRadius: '6px',
                      padding: '8px',
                      color: '#e2e8f0',
                      fontSize: '12px',
                      resize: 'none',
                      minHeight: '60px',
                      outline: 'none',
                      fontFamily: 'inherit'
                    }}
                    autoComplete="off"
                    spellCheck="false"
                  />
                  <button
                    onClick={processPrompt}
                    disabled={isProcessing}
                    style={{
                      background: isProcessing ? '#6b7280' : 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
                      border: 'none',
                      color: 'white',
                      padding: '8px 12px',
                      borderRadius: '6px',
                      cursor: isProcessing ? 'default' : 'pointer',
                      fontSize: '12px',
                      alignSelf: 'flex-end'
                    }}
                  >
                    {isProcessing ? 'ðŸ§ ...' : 'ðŸš€'}
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  // Functional Modules
  const GuardManagement = () => (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ color: '#2563eb', margin: 0 }}>Guard Management</h2>
        <button style={{
          background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}>
          + Add Guard
        </button>
      </div>
      
      <div style={{ display: 'grid', gap: '15px' }}>
        {guards.map(guard => (
          <div key={guard.id} style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(59, 130, 246, 0.3)',
            borderRadius: '12px',
            padding: '15px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <div>
              <h3 style={{ color: '#3b82f6', margin: '0 0 5px 0' }}>{guard.name}</h3>
              <p style={{ color: '#94a3b8', margin: '0 0 5px 0' }}>{guard.site}</p>
              <p style={{ color: '#64748b', margin: 0, fontSize: '14px' }}>{guard.shift} â€¢ {guard.phone}</p>
            </div>
            <div style={{
              padding: '6px 12px',
              borderRadius: '20px',
              background: guard.status === 'on-duty' ? '#10b981' : 
                          guard.status === 'available' ? '#f59e0b' : '#6b7280',
              color: 'white',
              fontSize: '12px',
              fontWeight: 'bold'
            }}>
              {guard.status.toUpperCase()}
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const SiteManagement = () => (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ color: '#2563eb', margin: 0 }}>Site Management</h2>
        <button style={{
          background: 'linear-gradient(135deg, #10b981, #059669)',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}>
          + Add Site
        </button>
      </div>
      
      <div style={{ display: 'grid', gap: '15px' }}>
        {sites.map(site => (
          <div key={site.id} style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(16, 185, 129, 0.3)',
            borderRadius: '12px',
            padding: '15px'
          }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <h3 style={{ color: '#10b981', margin: '0 0 5px 0' }}>{site.name}</h3>
                <p style={{ color: '#94a3b8', margin: '0 0 10px 0' }}>{site.address}</p>
                <div style={{ display: 'flex', gap: '15px' }}>
                  <span style={{ color: '#64748b', fontSize: '14px' }}>
                    <Users size={16} style={{ display: 'inline', marginRight: '5px' }} />
                    {site.guards} Guards
                  </span>
                  <div style={{
                    padding: '4px 8px',
                    borderRadius: '12px',
                    background: site.status === 'secured' ? '#10b981' : '#f59e0b',
                    color: 'white',
                    fontSize: '12px'
                  }}>
                    {site.status.toUpperCase()}
                  </div>
                </div>
              </div>
              <MapPin size={24} color="#10b981" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const Scheduling = () => (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ color: '#2563eb', margin: 0 }}>AI-Powered Scheduling</h2>
        <button style={{
          background: 'linear-gradient(135deg, #8b5cf6, #7c3aed)',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}>
          ðŸ§  Generate Optimal Schedule
        </button>
      </div>
      
      <div style={{
        background: 'rgba(139, 92, 246, 0.1)',
        border: '1px solid rgba(139, 92, 246, 0.3)',
        borderRadius: '12px',
        padding: '15px',
        marginBottom: '20px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
          <Zap size={20} color="#8b5cf6" style={{ marginRight: '10px' }} />
          <h3 style={{ color: '#8b5cf6', margin: 0 }}>Aetheris AI Recommendations</h3>
        </div>
        <p style={{ color: '#94a3b8', margin: '0 0 10px 0' }}>
          â€¢ Move Sarah Johnson to Retail Center tomorrow (optimization: +15% efficiency)
        </p>
        <p style={{ color: '#94a3b8', margin: '0 0 10px 0' }}>
          â€¢ John Smith shows 78% probability of requesting overtime - prepare backup
        </p>
        <p style={{ color: '#94a3b8', margin: 0 }}>
          â€¢ Hospital Complex requires additional coverage during weekend shift
        </p>
      </div>
      
      <div style={{ display: 'grid', gap: '15px' }}>
        {schedules.map(schedule => (
          <div key={schedule.id} style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(139, 92, 246, 0.3)',
            borderRadius: '12px',
            padding: '15px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <div>
              <h3 style={{ color: '#8b5cf6', margin: '0 0 5px 0' }}>{schedule.guard}</h3>
              <p style={{ color: '#94a3b8', margin: '0 0 5px 0' }}>{schedule.site}</p>
              <p style={{ color: '#64748b', margin: 0, fontSize: '14px' }}>
                {schedule.date} â€¢ {schedule.shift}
              </p>
            </div>
            <div style={{
              padding: '6px 12px',
              borderRadius: '20px',
              background: schedule.status === 'confirmed' ? '#10b981' : '#f59e0b',
              color: 'white',
              fontSize: '12px'
            }}>
              {schedule.status.toUpperCase()}
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  // Navigation
  const activeModules = platformModules.filter(m => m.status === 'active');
  const modules = [
    { id: 'dashboard', name: 'Dashboard', icon: BarChart3 },
    ...activeModules.map(m => ({
      id: m.id,
      name: m.name,
      icon: m.id === 'guard-management' ? Users :
            m.id === 'site-management' ? MapPin :
            m.id === 'scheduling' ? Calendar :
            m.id === 'analytics' ? TrendingUp :
            Shield
    }))
  ];

  const renderActiveModule = () => {
    switch (activeModule) {
      case 'guard-management':
        return <GuardManagement />;
      case 'site-management':
        return <SiteManagement />;
      case 'scheduling':
        return <Scheduling />;
      default:
        return (
          <div style={{ padding: '20px' }}>
            <h2 style={{ color: '#2563eb', marginBottom: '20px' }}>Sentari OS Dashboard</h2>
            
            {/* KPI Cards */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px', marginBottom: '30px' }}>
              <div style={{
                background: 'rgba(59, 130, 246, 0.1)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '12px',
                padding: '20px'
              }}>
                <Users size={24} color="#3b82f6" style={{ marginBottom: '10px' }} />
                <h3 style={{ color: '#94a3b8', fontSize: '14px', margin: '0 0 5px 0' }}>Guards Online</h3>
                <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#3b82f6', marginBottom: '5px' }}>127</div>
                <div style={{ color: '#10b981', fontSize: '12px' }}>+8% from last week</div>
              </div>
              
              <div style={{
                background: 'rgba(16, 185, 129, 0.1)',
                border: '1px solid rgba(16, 185, 129, 0.3)',
                borderRadius: '12px',
                padding: '20px'
              }}>
                <DollarSign size={24} color="#10b981" style={{ marginBottom: '10px' }} />
                <h3 style={{ color: '#94a3b8', fontSize: '14px', margin: '0 0 5px 0' }}>Monthly Revenue</h3>
                <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#10b981', marginBottom: '5px' }}>$247K</div>
                <div style={{ color: '#10b981', fontSize: '12px' }}>+12% from last month</div>
              </div>
              
              <div style={{
                background: 'rgba(245, 158, 11, 0.1)',
                border: '1px solid rgba(245, 158, 11, 0.3)',
                borderRadius: '12px',
                padding: '20px'
              }}>
                <TrendingUp size={24} color="#f59e0b" style={{ marginBottom: '10px' }} />
                <h3 style={{ color: '#94a3b8', fontSize: '14px', margin: '0 0 5px 0' }}>Efficiency Rating</h3>
                <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#f59e0b', marginBottom: '5px' }}>94%</div>
                <div style={{ color: '#10b981', fontSize: '12px' }}>+6% improvement</div>
              </div>
              
              <div style={{
                background: 'rgba(139, 92, 246, 0.1)',
                border: '1px solid rgba(139, 92, 246, 0.3)',
                borderRadius: '12px',
                padding: '20px'
              }}>
                <Shield size={24} color="#8b5cf6" style={{ marginBottom: '10px' }} />
                <h3 style={{ color: '#94a3b8', fontSize: '14px', margin: '0 0 5px 0' }}>Client Satisfaction</h3>
                <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#8b5cf6', marginBottom: '5px' }}>96%</div>
                <div style={{ color: '#10b981', fontSize: '12px' }}>+3% this quarter</div>
              </div>
            </div>

            {/* Charts */}
            <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '20px', marginBottom: '30px' }}>
              <div style={{
                background: 'rgba(15, 23, 42, 0.8)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '12px',
                padding: '20px'
              }}>
                <h3 style={{ color: '#3b82f6', marginBottom: '20px' }}>ðŸ“ˆ Performance Trends</h3>
                <ResponsiveContainer width="100%" height={300}>
                  <AreaChart data={performanceData}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                    <XAxis dataKey="month" stroke="#94a3b8" />
                    <YAxis stroke="#94a3b8" />
                    <Tooltip />
                    <Area type="monotone" dataKey="efficiency" stroke="#3b82f6" fill="#3b82f6" fillOpacity={0.3} name="Efficiency %" />
                  </AreaChart>
                </ResponsiveContainer>
              </div>

              <div style={{
                background: 'rgba(15, 23, 42, 0.8)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(16, 185, 129, 0.3)',
                borderRadius: '12px',
                padding: '20px'
              }}>
                <h3 style={{ color: '#10b981', marginBottom: '20px' }}>ðŸŽ¯ Industry Distribution</h3>
                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie
                      data={industryData}
                      cx="50%"
                      cy="50%"
                      innerRadius={60}
                      outerRadius={100}
                      paddingAngle={5}
                      dataKey="value"
                    >
                      {industryData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Pie>
                    <Tooltip formatter={(value) => [`$${(value/1000).toFixed(0)}K`, 'Revenue']} />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Real-time Metrics */}
            <div style={{
              background: 'rgba(15, 23, 42, 0.8)',
              backdropFilter: 'blur(10px)',
              border: '1px solid rgba(245, 158, 11, 0.3)',
              borderRadius: '12px',
              padding: '20px',
              marginBottom: '30px'
            }}>
              <h3 style={{ color: '#f59e0b', marginBottom: '20px' }}>âš¡ Real-Time Operations</h3>
              <ResponsiveContainer width="100%" height={200}>
                <LineChart data={dailyMetrics}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                  <XAxis dataKey="time" stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip />
                  <Line type="monotone" dataKey="guards" stroke="#f59e0b" strokeWidth={3} name="Guards Active" />
                  <Line type="monotone" dataKey="efficiency" stroke="#10b981" strokeWidth={2} name="Efficiency %" />
                </LineChart>
              </ResponsiveContainer>
            </div>

            {/* AI Insights */}
            <div style={{
              background: 'rgba(139, 92, 246, 0.1)',
              border: '1px solid rgba(139, 92, 246, 0.3)',
              borderRadius: '12px',
              padding: '20px',
              marginBottom: '30px'
            }}>
              <h3 style={{ color: '#8b5cf6', marginBottom: '15px' }}>ðŸ§  AI Insights</h3>
              <div style={{ display: 'grid', gap: '10px' }}>
                <div style={{ color: '#e2e8f0', fontSize: '14px' }}>
                  ðŸ”® <strong>Oracle Core:</strong> Revenue projected to increase 18% next quarter based on current trends
                </div>
                <div style={{ color: '#e2e8f0', fontSize: '14px' }}>
                  ðŸ’“ <strong>Pulse Core:</strong> Client satisfaction highest on Tuesday mornings (97.3% avg)
                </div>
                <div style={{ color: '#e2e8f0', fontSize: '14px' }}>
                  ðŸŽ¯ <strong>Harmony Core:</strong> Opportunity detected: 3 clients ready for service expansion
                </div>
              </div>
            </div>

            {/* Quick Actions */}
            <h3 style={{ color: '#2563eb', marginBottom: '15px' }}>Quick Actions</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
              {modules.slice(1).map(module => {
                const Icon = module.icon;
                return (
                  <button
                    key={module.id}
                    onClick={() => setActiveModule(module.id)}
                    style={{
                      background: 'rgba(255, 255, 255, 0.1)',
                      backdropFilter: 'blur(10px)',
                      border: '1px solid rgba(59, 130, 246, 0.3)',
                      borderRadius: '12px',
                      padding: '15px',
                      color: '#3b82f6',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      transition: 'all 0.3s ease'
                    }}
                    onMouseOver={(e) => {
                      e.target.style.background = 'rgba(59, 130, 246, 0.1)';
                      e.target.style.transform = 'translateY(-2px)';
                    }}
                    onMouseOut={(e) => {
                      e.target.style.background = 'rgba(255, 255, 255, 0.1)';
                      e.target.style.transform = 'translateY(0)';
                    }}
                  >
                    <Icon size={20} />
                    {module.name}
                  </button>
                );
              })}
            </div>
          </div>
        );
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #1e293b 0%, #0f172a 50%, #1e293b 100%)',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      color: '#e2e8f0'
    }}>
      {/* Header */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
        padding: '15px 30px',
        position: 'sticky',
        top: 0,
        zIndex: 100
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <h1 style={{
              background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              color: 'transparent',
              fontSize: '24px',
              fontWeight: 'bold',
              margin: 0
            }}>
              âš¡ Sentari OS
            </h1>
            <p style={{ color: '#64748b', fontSize: '14px', margin: '2px 0 0 0' }}>
              Orchestrating Intelligence. Predicting Outcomes.
            </p>
          </div>
          
          <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <div style={{
                width: '8px',
                height: '8px',
                borderRadius: '50%',
                background: aetherisStatus === 'online' ? '#10b981' : '#f59e0b',
                boxShadow: `0 0 8px ${aetherisStatus === 'online' ? '#10b981' : '#f59e0b'}`
              }}></div>
              <span style={{ color: '#94a3b8', fontSize: '14px' }}>
                {aetherisStatus === 'online' ? 'Aetheris Online' : 'Connecting...'}
              </span>
            </div>
            
            <div style={{
              background: 'rgba(59, 130, 246, 0.1)',
              border: '1px solid rgba(59, 130, 246, 0.3)',
              borderRadius: '20px',
              padding: '6px 12px',
              fontSize: '12px',
              color: '#3b82f6'
            }}>
              ðŸ§  {aiCores} AI Cores Active
            </div>
          </div>
        </div>
      </div>

      <div style={{ display: 'flex' }}>
        {/* Sidebar */}
        <div style={{
          width: '250px',
          background: 'rgba(15, 23, 42, 0.8)',
          backdropFilter: 'blur(10px)',
          borderRight: '1px solid rgba(59, 130, 246, 0.2)',
          minHeight: 'calc(100vh - 80px)',
          padding: '20px'
        }}>
          <div style={{ marginBottom: '20px' }}>
            <h3 style={{ color: '#94a3b8', fontSize: '12px', textTransform: 'uppercase', letterSpacing: '1px', margin: '0 0 15px 0' }}>
              Business Modules
            </h3>
            {modules.map(module => {
              const Icon = module.icon;
              const isActive = activeModule === module.id;
              
              return (
                <button
                  key={module.id}
                  onClick={() => setActiveModule(module.id)}
                  style={{
                    width: '100%',
                    background: isActive ? 'rgba(59, 130, 246, 0.2)' : 'transparent',
                    border: isActive ? '1px solid rgba(59, 130, 246, 0.4)' : '1px solid transparent',
                    borderRadius: '8px',
                    padding: '12px 15px',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '10px',
                    color: isActive ? '#3b82f6' : '#94a3b8',
                    cursor: 'pointer',
                    marginBottom: '5px',
                    transition: 'all 0.2s ease'
                  }}
                >
                  <Icon size={16} />
                  {module.name}
                </button>
              );
            })}
          </div>

          {/* Module Status */}
          <div style={{
            background: 'rgba(16, 185, 129, 0.1)',
            border: '1px solid rgba(16, 185, 129, 0.3)',
            borderRadius: '8px',
            padding: '12px',
            marginTop: '20px'
          }}>
            <h4 style={{ color: '#10b981', fontSize: '12px', margin: '0 0 8px 0' }}>Platform Status</h4>
            <div style={{ color: '#94a3b8', fontSize: '11px', marginBottom: '4px' }}>
              {activeModules.length} modules active
            </div>
            <div style={{ color: '#64748b', fontSize: '10px', marginTop: '8px' }}>
              ðŸ’¡ Use AI Editor to modify
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div style={{ flex: 1, minHeight: 'calc(100vh - 80px)' }}>
          {renderActiveModule()}
        </div>
      </div>

      {/* Sticky AI Editor */}
      <AIEditor />
    </div>
  );
};

export default SentariOS;
