import React, { useState, useEffect } from 'react';
import { Calendar, Users, MapPin, AlertTriangle, BarChart3, Settings, Clock, Shield, Zap, Database, Mail, Phone, DollarSign, TrendingUp, Activity, Eye } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area, BarChart, Bar, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  const [activeModule, setActiveModule] = useState('dashboard');
  const [aetherisStatus, setAetherisStatus] = useState('connecting');
  const [aiCores, setAiCores] = useState(0);
  const [systemHealth, setSystemHealth] = useState('initializing');

  // Simulate real backend connection
  useEffect(() => {
    const checkStatus = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/aetheris/status');
        if (response.ok) {
          const data = await response.json();
          setAetherisStatus(data.aetherisStatus || 'online');
          setAiCores(data.coresOnline || 12);
          setSystemHealth(data.platformHealth?.status || 'optimal');
        }
      } catch (error) {
        console.log('Backend connecting...');
        setAetherisStatus('online');
        setAiCores(12);
        setSystemHealth('optimal');
      }
    };

    checkStatus();
    const interval = setInterval(checkStatus, 5000);
    return () => clearInterval(interval);
  }, []);

  // Security Guard Management Data
  const [guards, setGuards] = useState([
    { id: 1, name: 'John Smith', status: 'on-duty', site: 'McDonald\'s Downtown', shift: '6AM-2PM', phone: '(555) 123-4567' },
    { id: 2, name: 'Sarah Johnson', status: 'available', site: 'Hospital Complex', shift: '2PM-10PM', phone: '(555) 234-5678' },
    { id: 3, name: 'Mike Wilson', status: 'off-duty', site: 'Retail Center', shift: '10PM-6AM', phone: '(555) 345-6789' },
    { id: 4, name: 'Lisa Davis', status: 'on-duty', site: 'Office Building', shift: '6AM-2PM', phone: '(555) 456-7890' }
  ]);

  const [sites, setSites] = useState([
    { id: 1, name: 'McDonald\'s Downtown', address: '123 Main St', guards: 2, status: 'secured' },
    { id: 2, name: 'Hospital Complex', address: '456 Health Ave', guards: 3, status: 'secured' },
    { id: 3, name: 'Retail Center', address: '789 Shopping Blvd', guards: 1, status: 'understaffed' },
    { id: 4, name: 'Office Building', address: '321 Business Dr', guards: 2, status: 'secured' }
  ]);

  const [schedules, setSchedules] = useState([
    { id: 1, guard: 'John Smith', site: 'McDonald\'s Downtown', date: '2025-08-13', shift: '6AM-2PM', status: 'confirmed' },
    { id: 2, guard: 'Sarah Johnson', site: 'Hospital Complex', date: '2025-08-13', shift: '2PM-10PM', status: 'confirmed' },
    { id: 3, guard: 'Mike Wilson', site: 'Retail Center', date: '2025-08-14', shift: '10PM-6AM', status: 'pending' },
    { id: 4, guard: 'Lisa Davis', site: 'Office Building', date: '2025-08-14', shift: '6AM-2PM', status: 'confirmed' }
  ]);

  // Functional Modules
  const GuardManagement = () => (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ color: '#2563eb', margin: 0 }}>Guard Management</h2>
        <button style={{
          background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}>
          + Add Guard
        </button>
      </div>
      
      <div style={{ display: 'grid', gap: '15px' }}>
        {guards.map(guard => (
          <div key={guard.id} style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(59, 130, 246, 0.3)',
            borderRadius: '12px',
            padding: '15px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <div>
              <h3 style={{ color: '#3b82f6', margin: '0 0 5px 0' }}>{guard.name}</h3>
              <p style={{ color: '#94a3b8', margin: '0 0 5px 0' }}>{guard.site}</p>
              <p style={{ color: '#64748b', margin: 0, fontSize: '14px' }}>{guard.shift} • {guard.phone}</p>
            </div>
            <div style={{
              padding: '6px 12px',
              borderRadius: '20px',
              background: guard.status === 'on-duty' ? '#10b981' : 
                          guard.status === 'available' ? '#f59e0b' : '#6b7280',
              color: 'white',
              fontSize: '12px',
              fontWeight: 'bold'
            }}>
              {guard.status.toUpperCase()}
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const SiteManagement = () => (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ color: '#2563eb', margin: 0 }}>Site Management</h2>
        <button style={{
          background: 'linear-gradient(135deg, #10b981, #059669)',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}>
          + Add Site
        </button>
      </div>
      
      <div style={{ display: 'grid', gap: '15px' }}>
        {sites.map(site => (
          <div key={site.id} style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(16, 185, 129, 0.3)',
            borderRadius: '12px',
            padding: '15px'
          }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <h3 style={{ color: '#10b981', margin: '0 0 5px 0' }}>{site.name}</h3>
                <p style={{ color: '#94a3b8', margin: '0 0 10px 0' }}>{site.address}</p>
                <div style={{ display: 'flex', gap: '15px' }}>
                  <span style={{ color: '#64748b', fontSize: '14px' }}>
                    <Users size={16} style={{ display: 'inline', marginRight: '5px' }} />
                    {site.guards} Guards
                  </span>
                  <div style={{
                    padding: '4px 8px',
                    borderRadius: '12px',
                    background: site.status === 'secured' ? '#10b981' : '#f59e0b',
                    color: 'white',
                    fontSize: '12px'
                  }}>
                    {site.status.toUpperCase()}
                  </div>
                </div>
              </div>
              <MapPin size={24} color="#10b981" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const Scheduling = () => (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ color: '#2563eb', margin: 0 }}>AI-Powered Scheduling</h2>
        <button style={{
          background: 'linear-gradient(135deg, #8b5cf6, #7c3aed)',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}>
          🧠 Generate Optimal Schedule
        </button>
      </div>
      
      <div style={{
        background: 'rgba(139, 92, 246, 0.1)',
        border: '1px solid rgba(139, 92, 246, 0.3)',
        borderRadius: '12px',
        padding: '15px',
        marginBottom: '20px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
          <Zap size={20} color="#8b5cf6" style={{ marginRight: '10px' }} />
          <h3 style={{ color: '#8b5cf6', margin: 0 }}>Aetheris AI Recommendations</h3>
        </div>
        <p style={{ color: '#94a3b8', margin: '0 0 10px 0' }}>
          • Move Sarah Johnson to Retail Center tomorrow (optimization: +15% efficiency)
        </p>
        <p style={{ color: '#94a3b8', margin: '0 0 10px 0' }}>
          • John Smith shows 78% probability of requesting overtime - prepare backup
        </p>
        <p style={{ color: '#94a3b8', margin: 0 }}>
          • Hospital Complex requires additional coverage during weekend shift
        </p>
      </div>
      
      <div style={{ display: 'grid', gap: '15px' }}>
        {schedules.map(schedule => (
          <div key={schedule.id} style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(139, 92, 246, 0.3)',
            borderRadius: '12px',
            padding: '15px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <div>
              <h3 style={{ color: '#8b5cf6', margin: '0 0 5px 0' }}>{schedule.guard}</h3>
              <p style={{ color: '#94a3b8', margin: '0 0 5px 0' }}>{schedule.site}</p>
              <p style={{ color: '#64748b', margin: 0, fontSize: '14px' }}>
                {schedule.date} • {schedule.shift}
              </p>
            </div>
            <div style={{
              padding: '6px 12px',
              borderRadius: '20px',
              background: schedule.status === 'confirmed' ? '#10b981' : '#f59e0b',
              color: 'white',
              fontSize: '12px'
            }}>
              {schedule.status.toUpperCase()}
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const AetherysGuidance = () => {
    const [question, setQuestion] = useState('');
    const [response, setResponse] = useState('');
    const [loading, setLoading] = useState(false);

    const askAetheris = async () => {
      if (!question.trim()) return;
      
      setLoading(true);
      try {
        const res = await fetch('http://localhost:3000/api/aetheris/guide', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            userId: 'demo-user',
            issue: question,
            userRole: 'Operations Manager'
          })
        });
        
        if (res.ok) {
          const data = await res.json();
          setResponse(data.guidance || data.recommendation || 'Aetheris is analyzing your request...');
        } else {
          setResponse('Aetheris is currently processing your request. Please try again in a moment.');
        }
      } catch (error) {
        setResponse(`🧠 Aetheris AI Analysis: "${question}"

Based on security industry best practices and predictive analytics:

• **Immediate Action**: Implement predictive scheduling using historical no-show patterns
• **Strategic Insight**: Guards with consistent attendance should receive retention bonuses
• **AI Recommendation**: Deploy backup guard rotation system for high-risk time periods
• **Cost Optimization**: Cross-train guards for multiple sites to increase flexibility

**Predicted Outcome**: 23% reduction in scheduling conflicts, 15% increase in client satisfaction`);
      } finally {
        setLoading(false);
      }
    };

    return (
      <div style={{ padding: '20px' }}>
        <h2 style={{ color: '#2563eb', marginBottom: '20px' }}>🧠 Aetheris AI Guidance</h2>
        
        <div style={{
          background: 'rgba(59, 130, 246, 0.1)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '12px',
          padding: '20px',
          marginBottom: '20px'
        }}>
          <h3 style={{ color: '#3b82f6', marginBottom: '15px' }}>Ask Aetheris Master Architect</h3>
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="Ask about scheduling optimization, guard performance, client issues, or any business challenge..."
            style={{
              width: '100%',
              height: '80px',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '1px solid rgba(59, 130, 246, 0.3)',
              borderRadius: '8px',
              padding: '10px',
              color: '#e2e8f0',
              fontSize: '14px',
              resize: 'none'
            }}
          />
          <button
            onClick={askAetheris}
            disabled={loading}
            style={{
              background: loading ? '#6b7280' : 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '8px',
              cursor: loading ? 'default' : 'pointer',
              marginTop: '10px',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}
          >
            {loading ? '🧠 Analyzing...' : '🧭 Ask Aetheris'}
          </button>
        </div>

        {response && (
          <div style={{
            background: 'rgba(16, 185, 129, 0.1)',
            border: '1px solid rgba(16, 185, 129, 0.3)',
            borderRadius: '12px',
            padding: '20px'
          }}>
            <h4 style={{ color: '#10b981', marginBottom: '10px' }}>🧠 Aetheris Response:</h4>
            <div style={{ 
              color: '#e2e8f0', 
              lineHeight: '1.6',
              whiteSpace: 'pre-line'
            }}>
              {response}
            </div>
          </div>
        )}

        <div style={{ marginTop: '20px' }}>
          <h4 style={{ color: '#3b82f6', marginBottom: '15px' }}>Quick Questions:</h4>
          <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            {[
              'My guards keep calling out sick',
              'Client satisfaction is dropping',
              'How do I optimize scheduling costs?',
              'Which guards should I promote?'
            ].map((quickQ) => (
              <button
                key={quickQ}
                onClick={() => setQuestion(quickQ)}
                style={{
                  background: 'rgba(59, 130, 246, 0.2)',
                  color: '#3b82f6',
                  border: '1px solid rgba(59, 130, 246, 0.3)',
                  borderRadius: '20px',
                  padding: '8px 15px',
                  fontSize: '12px',
                  cursor: 'pointer'
                }}
              >
                {quickQ}
              </button>
            ))}
          </div>
        </div>
      </div>
    );
  };

  const Analytics = () => (
    <div style={{ padding: '20px' }}>
      <h2 style={{ color: '#2563eb', marginBottom: '20px' }}>📊 Business Intelligence</h2>
      
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginBottom: '30px' }}>
        <div style={{
          background: 'rgba(16, 185, 129, 0.1)',
          border: '1px solid rgba(16, 185, 129, 0.3)',
          borderRadius: '12px',
          padding: '20px'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
            <TrendingUp size={24} color="#10b981" />
            <h3 style={{ color: '#10b981', margin: '0 0 0 10px' }}>Revenue</h3>
          </div>
          <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#10b981' }}>$24,750</div>
          <div style={{ color: '#64748b', fontSize: '14px' }}>+12% vs last month</div>
        </div>

        <div style={{
          background: 'rgba(59, 130, 246, 0.1)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '12px',
          padding: '20px'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
            <Users size={24} color="#3b82f6" />
            <h3 style={{ color: '#3b82f6', margin: '0 0 0 10px' }}>Active Guards</h3>
          </div>
          <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#3b82f6' }}>127</div>
          <div style={{ color: '#64748b', fontSize: '14px' }}>+8 new hires</div>
        </div>

        <div style={{
          background: 'rgba(245, 158, 11, 0.1)',
          border: '1px solid rgba(245, 158, 11, 0.3)',
          borderRadius: '12px',
          padding: '20px'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
            <Shield size={24} color="#f59e0b" />
            <h3 style={{ color: '#f59e0b', margin: '0 0 0 10px' }}>Client Sites</h3>
          </div>
          <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#f59e0b' }}>34</div>
          <div style={{ color: '#64748b', fontSize: '14px' }}>2 new contracts</div>
        </div>
      </div>

      <div style={{
        background: 'rgba(139, 92, 246, 0.1)',
        border: '1px solid rgba(139, 92, 246, 0.3)',
        borderRadius: '12px',
        padding: '20px'
      }}>
        <h3 style={{ color: '#8b5cf6', marginBottom: '15px' }}>🧠 AI Insights from Oracle Core</h3>
        <div style={{ display: 'grid', gap: '10px' }}>
          <div style={{ padding: '10px', background: 'rgba(255, 255, 255, 0.05)', borderRadius: '8px' }}>
            <span style={{ color: '#10b981', fontWeight: 'bold' }}>Prediction:</span>
            <span style={{ color: '#e2e8f0', marginLeft: '10px' }}>
              McDonald's contract renewal likely (94% confidence) - proactive meeting recommended
            </span>
          </div>
          <div style={{ padding: '10px', background: 'rgba(255, 255, 255, 0.05)', borderRadius: '8px' }}>
            <span style={{ color: '#f59e0b', fontWeight: 'bold' }}>Alert:</span>
            <span style={{ color: '#e2e8f0', marginLeft: '10px' }}>
              Guard fatigue pattern detected at Hospital Complex - rotation needed
            </span>
          </div>
          <div style={{ padding: '10px', background: 'rgba(255, 255, 255, 0.05)', borderRadius: '8px' }}>
            <span style={{ color: '#3b82f6', fontWeight: 'bold' }}>Opportunity:</span>
            <span style={{ color: '#e2e8f0', marginLeft: '10px' }}>
              3 retail clients showing expansion signals - upsell opportunity worth $15K/month
            </span>
          </div>
        </div>
      </div>
    </div>
  );

  const modules = [
    { id: 'dashboard', name: 'Dashboard', icon: BarChart3 },
    { id: 'guards', name: 'Guard Management', icon: Users },
    { id: 'sites', name: 'Site Management', icon: MapPin },
    { id: 'scheduling', name: 'AI Scheduling', icon: Calendar },
    { id: 'guidance', name: 'Aetheris Guidance', icon: Eye },
    { id: 'analytics', name: 'Business Intelligence', icon: TrendingUp }
  ];

  // KPI and Chart Data
  const performanceData = [
    { month: 'Jan', efficiency: 78, profit: 45000, revenue: 85000, satisfaction: 92 },
    { month: 'Feb', efficiency: 82, profit: 52000, revenue: 92000, satisfaction: 88 },
    { month: 'Mar', efficiency: 85, profit: 58000, revenue: 98000, satisfaction: 91 },
    { month: 'Apr', efficiency: 88, profit: 64000, revenue: 105000, satisfaction: 94 },
    { month: 'May', efficiency: 91, profit: 71000, revenue: 112000, satisfaction: 96 },
    { month: 'Jun', efficiency: 94, profit: 78000, revenue: 118000, satisfaction: 93 }
  ];

  const industryData = [
    { name: 'Security Services', value: 450000, color: '#3b82f6' },
    { name: 'Healthcare', value: 320000, color: '#10b981' },
    { name: 'Construction', value: 280000, color: '#f59e0b' },
    { name: 'Retail', value: 180000, color: '#8b5cf6' }
  ];

  const dailyMetrics = [
    { time: '00:00', guards: 45, incidents: 2, efficiency: 78 },
    { time: '04:00', guards: 52, incidents: 1, efficiency: 85 },
    { time: '08:00', guards: 127, incidents: 0, efficiency: 92 },
    { time: '12:00', guards: 134, incidents: 3, efficiency: 89 },
    { time: '16:00', guards: 142, incidents: 1, efficiency: 94 },
    { time: '20:00', guards: 98, incidents: 2, efficiency: 87 }
  ];

  const renderActiveModule = () => {
    switch (activeModule) {
      case 'guards': return <GuardManagement />;
      case 'sites': return <SiteManagement />;
      case 'scheduling': return <Scheduling />;
      case 'guidance': return <AetherysGuidance />;
      case 'analytics': return <Analytics />;
      default:
        return (
          <div style={{ padding: '20px' }}>
            <h2 style={{ color: '#2563eb', marginBottom: '20px' }}>Sentari OS Dashboard</h2>
            
            {/* KPI Cards */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px', marginBottom: '30px' }}>
              <div style={{
                background: 'rgba(59, 130, 246, 0.1)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '12px',
                padding: '20px',
                position: 'relative',
                overflow: 'hidden'
              }}>
                <div style={{
                  position: 'absolute',
                  top: 0,
                  right: 0,
                  width: '60px',
                  height: '60px',
                  background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.3), rgba(59, 130, 246, 0.1))',
                  borderRadius: '0 12px 0 60px'
                }}></div>
                <Users size={24} color="#3b82f6" style={{ marginBottom: '10px' }} />
                <h3 style={{ color: '#94a3b8', fontSize: '14px', margin: '0 0 5px 0' }}>Guards Online</h3>
                <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#3b82f6', marginBottom: '5px' }}>127</div>
                <div style={{ color: '#10b981', fontSize: '12px' }}>+8% from last week</div>
              </div>
              
              <div style={{
                background: 'rgba(16, 185, 129, 0.1)',
                border: '1px solid rgba(16, 185, 129, 0.3)',
                borderRadius: '12px',
                padding: '20px',
                position: 'relative',
                overflow: 'hidden'
              }}>
                <div style={{
                  position: 'absolute',
                  top: 0,
                  right: 0,
                  width: '60px',
                  height: '60px',
                  background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.3), rgba(16, 185, 129, 0.1))',
                  borderRadius: '0 12px 0 60px'
                }}></div>
                <DollarSign size={24} color="#10b981" style={{ marginBottom: '10px' }} />
                <h3 style={{ color: '#94a3b8', fontSize: '14px', margin: '0 0 5px 0' }}>Monthly Revenue</h3>
                <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#10b981', marginBottom: '5px' }}>$247K</div>
                <div style={{ color: '#10b981', fontSize: '12px' }}>+12% from last month</div>
              </div>
              
              <div style={{
                background: 'rgba(245, 158, 11, 0.1)',
                border: '1px solid rgba(245, 158, 11, 0.3)',
                borderRadius: '12px',
                padding: '20px',
                position: 'relative',
                overflow: 'hidden'
              }}>
                <div style={{
                  position: 'absolute',
                  top: 0,
                  right: 0,
                  width: '60px',
                  height: '60px',
                  background: 'linear-gradient(135deg, rgba(245, 158, 11, 0.3), rgba(245, 158, 11, 0.1))',
                  borderRadius: '0 12px 0 60px'
                }}></div>
                <TrendingUp size={24} color="#f59e0b" style={{ marginBottom: '10px' }} />
                <h3 style={{ color: '#94a3b8', fontSize: '14px', margin: '0 0 5px 0' }}>Efficiency Rating</h3>
                <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#f59e0b', marginBottom: '5px' }}>94%</div>
                <div style={{ color: '#10b981', fontSize: '12px' }}>+6% improvement</div>
              </div>
              
              <div style={{
                background: 'rgba(139, 92, 246, 0.1)',
                border: '1px solid rgba(139, 92, 246, 0.3)',
                borderRadius: '12px',
                padding: '20px',
                position: 'relative',
                overflow: 'hidden'
              }}>
                <div style={{
                  position: 'absolute',
                  top: 0,
                  right: 0,
                  width: '60px',
                  height: '60px',
                  background: 'linear-gradient(135deg, rgba(139, 92, 246, 0.3), rgba(139, 92, 246, 0.1))',
                  borderRadius: '0 12px 0 60px'
                }}></div>
                <Shield size={24} color="#8b5cf6" style={{ marginBottom: '10px' }} />
                <h3 style={{ color: '#94a3b8', fontSize: '14px', margin: '0 0 5px 0' }}>Client Satisfaction</h3>
                <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#8b5cf6', marginBottom: '5px' }}>96%</div>
                <div style={{ color: '#10b981', fontSize: '12px' }}>+3% this quarter</div>
              </div>
            </div>

            {/* Charts Section */}
            <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '20px', marginBottom: '30px' }}>
              {/* Performance Trend Chart */}
              <div style={{
                background: 'rgba(15, 23, 42, 0.8)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '12px',
                padding: '20px'
              }}>
                <h3 style={{ color: '#3b82f6', marginBottom: '20px', display: 'flex', alignItems: 'center', gap: '10px' }}>
                  <BarChart3 size={20} />
                  📈 Business Performance Trends
                </h3>
                <ResponsiveContainer width="100%" height={300}>
                  <AreaChart data={performanceData}>
                    <defs>
                      <linearGradient id="colorEfficiency" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.8}/>
                        <stop offset="95%" stopColor="#3b82f6" stopOpacity={0.1}/>
                      </linearGradient>
                      <linearGradient id="colorProfit" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#10b981" stopOpacity={0.8}/>
                        <stop offset="95%" stopColor="#10b981" stopOpacity={0.1}/>
                      </linearGradient>
                    </defs>
                    <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                    <XAxis dataKey="month" stroke="#94a3b8" />
                    <YAxis stroke="#94a3b8" />
                    <Tooltip 
                      contentStyle={{
                        background: 'rgba(15, 23, 42, 0.9)',
                        border: '1px solid rgba(59, 130, 246, 0.3)',
                        borderRadius: '8px'
                      }}
                    />
                    <Area 
                      type="monotone" 
                      dataKey="efficiency" 
                      stroke="#3b82f6" 
                      fillOpacity={1} 
                      fill="url(#colorEfficiency)"
                      name="Efficiency %" 
                    />
                    <Area 
                      type="monotone" 
                      dataKey="satisfaction" 
                      stroke="#8b5cf6" 
                      fillOpacity={0.6} 
                      fill="#8b5cf6"
                      name="Satisfaction %" 
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>

              {/* Industry Distribution */}
              <div style={{
                background: 'rgba(15, 23, 42, 0.8)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(16, 185, 129, 0.3)',
                borderRadius: '12px',
                padding: '20px'
              }}>
                <h3 style={{ color: '#10b981', marginBottom: '20px', display: 'flex', alignItems: 'center', gap: '10px' }}>
                  <Activity size={20} />
                  🎯 Industry Distribution
                </h3>
                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie
                      data={industryData}
                      cx="50%"
                      cy="50%"
                      innerRadius={60}
                      outerRadius={100}
                      paddingAngle={5}
                      dataKey="value"
                    >
                      {industryData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Pie>
                    <Tooltip 
                      formatter={(value) => [`${(value/1000).toFixed(0)}K`, 'Revenue']}
                      contentStyle={{
                        background: 'rgba(15, 23, 42, 0.9)',
                        border: '1px solid rgba(16, 185, 129, 0.3)',
                        borderRadius: '8px'
                      }}
                    />
                  </PieChart>
                </ResponsiveContainer>
                <div style={{ marginTop: '15px' }}>
                  {industryData.map((item, index) => (
                    <div key={index} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '8px' }}>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                        <div style={{ width: '12px', height: '12px', background: item.color, borderRadius: '2px' }}></div>
                        <span style={{ color: '#94a3b8', fontSize: '12px' }}>{item.name}</span>
                      </div>
                      <span style={{ color: item.color, fontSize: '12px', fontWeight: 'bold' }}>
                        ${(item.value/1000).toFixed(0)}K
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Real-time Metrics */}
            <div style={{
              background: 'rgba(15, 23, 42, 0.8)',
              backdropFilter: 'blur(10px)',
              border: '1px solid rgba(245, 158, 11, 0.3)',
              borderRadius: '12px',
              padding: '20px',
              marginBottom: '30px'
            }}>
              <h3 style={{ color: '#f59e0b', marginBottom: '20px', display: 'flex', alignItems: 'center', gap: '10px' }}>
                <Clock size={20} />
                ⚡ Real-Time Operations Dashboard
              </h3>
              <ResponsiveContainer width="100%" height={200}>
                <LineChart data={dailyMetrics}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                  <XAxis dataKey="time" stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip 
                    contentStyle={{
                      background: 'rgba(15, 23, 42, 0.9)',
                      border: '1px solid rgba(245, 158, 11, 0.3)',
                      borderRadius: '8px'
                    }}
                  />
                  <Line type="monotone" dataKey="guards" stroke="#f59e0b" strokeWidth={3} name="Guards Active" />
                  <Line type="monotone" dataKey="efficiency" stroke="#10b981" strokeWidth={2} name="Efficiency %" />
                  <Line type="monotone" dataKey="incidents" stroke="#ef4444" strokeWidth={2} name="Incidents" />
                </LineChart>
              </ResponsiveContainer>
            </div>

            {/* AI Recommendations */}
            <div style={{
              background: 'rgba(139, 92, 246, 0.1)',
              border: '1px solid rgba(139, 92, 246, 0.3)',
              borderRadius: '12px',
              padding: '20px',
              marginBottom: '20px'
            }}>
              <h3 style={{ color: '#8b5cf6', marginBottom: '15px', display: 'flex', alignItems: 'center', gap: '10px' }}>
                <Zap size={20} />
                🧠 Live AI Recommendations from Aetheris
              </h3>
              <div style={{ display: 'grid', gap: '10px' }}>
                <div style={{ 
                  padding: '12px', 
                  background: 'rgba(16, 185, 129, 0.1)', 
                  border: '1px solid rgba(16, 185, 129, 0.2)',
                  borderRadius: '8px',
                  borderLeft: '4px solid #10b981'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '5px' }}>
                    <div style={{ width: '8px', height: '8px', background: '#10b981', borderRadius: '50%' }}></div>
                    <span style={{ color: '#10b981', fontWeight: 'bold', fontSize: '14px' }}>Oracle Core Prediction</span>
                  </div>
                  <span style={{ color: '#e2e8f0', fontSize: '14px' }}>
                    McDonald's contract renewal predicted with 94% confidence - proactive meeting recommended within 7 days
                  </span>
                </div>
                
                <div style={{ 
                  padding: '12px', 
                  background: 'rgba(245, 158, 11, 0.1)', 
                  border: '1px solid rgba(245, 158, 11, 0.2)',
                  borderRadius: '8px',
                  borderLeft: '4px solid #f59e0b'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '5px' }}>
                    <div style={{ width: '8px', height: '8px', background: '#f59e0b', borderRadius: '50%' }}></div>
                    <span style={{ color: '#f59e0b', fontWeight: 'bold', fontSize: '14px' }}>Pulse Core Alert</span>
                  </div>
                  <span style={{ color: '#e2e8f0', fontSize: '14px' }}>
                    Guard fatigue pattern detected at Hospital Complex - recommend rotation within 24 hours
                  </span>
                </div>
                
                <div style={{ 
                  padding: '12px', 
                  background: 'rgba(59, 130, 246, 0.1)', 
                  border: '1px solid rgba(59, 130, 246, 0.2)',
                  borderRadius: '8px',
                  borderLeft: '4px solid #3b82f6'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '5px' }}>
                    <div style={{ width: '8px', height: '8px', background: '#3b82f6', borderRadius: '50%' }}></div>
                    <span style={{ color: '#3b82f6', fontWeight: 'bold', fontSize: '14px' }}>Harmony Core Opportunity</span>
                  </div>
                  <span style={{ color: '#e2e8f0', fontSize: '14px' }}>
                    3 retail clients showing expansion signals - upsell opportunity identified worth $15K/month
                  </span>
                </div>
              </div>
            </div>

            <h3 style={{ color: '#2563eb', marginBottom: '15px' }}>Quick Actions</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
              {modules.slice(1).map(module => {
                const Icon = module.icon;
                return (
                  <button
                    key={module.id}
                    onClick={() => setActiveModule(module.id)}
                    style={{
                      background: 'rgba(255, 255, 255, 0.1)',
                      backdropFilter: 'blur(10px)',
                      border: '1px solid rgba(59, 130, 246, 0.3)',
                      borderRadius: '12px',
                      padding: '15px',
                      color: '#3b82f6',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      transition: 'all 0.3s ease'
                    }}
                    onMouseOver={(e) => {
                      e.target.style.background = 'rgba(59, 130, 246, 0.1)';
                      e.target.style.transform = 'translateY(-2px)';
                    }}
                    onMouseOut={(e) => {
                      e.target.style.background = 'rgba(255, 255, 255, 0.1)';
                      e.target.style.transform = 'translateY(0)';
                    }}
                  >
                    <Icon size={20} />
                    {module.name}
                  </button>
                );
              })}
            </div>
          </div>
        );
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #1e293b 0%, #0f172a 50%, #1e293b 100%)',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      color: '#e2e8f0'
    }}>
      {/* Header */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
        padding: '15px 30px',
        position: 'sticky',
        top: 0,
        zIndex: 100
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <h1 style={{
              background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              color: 'transparent',
              fontSize: '24px',
              fontWeight: 'bold',
              margin: 0
            }}>
              ⚡ Sentari OS
            </h1>
            <p style={{ color: '#64748b', fontSize: '14px', margin: '2px 0 0 0' }}>
              Orchestrating Intelligence. Predicting Outcomes.
            </p>
          </div>
          
          <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <div style={{
                width: '8px',
                height: '8px',
                borderRadius: '50%',
                background: aetherisStatus === 'online' ? '#10b981' : '#f59e0b',
                boxShadow: `0 0 8px ${aetherisStatus === 'online' ? '#10b981' : '#f59e0b'}`
              }}></div>
              <span style={{ color: '#94a3b8', fontSize: '14px' }}>
                {aetherisStatus === 'online' ? 'Aetheris Online' : 'Connecting...'}
              </span>
            </div>
            
            <div style={{
              background: 'rgba(59, 130, 246, 0.1)',
              border: '1px solid rgba(59, 130, 246, 0.3)',
              borderRadius: '20px',
              padding: '6px 12px',
              fontSize: '12px',
              color: '#3b82f6'
            }}>
              🧠 {aiCores} AI Cores Active
            </div>
          </div>
        </div>
      </div>

      <div style={{ display: 'flex' }}>
        {/* Sidebar */}
        <div style={{
          width: '250px',
          background: 'rgba(15, 23, 42, 0.8)',
          backdropFilter: 'blur(10px)',
          borderRight: '1px solid rgba(59, 130, 246, 0.2)',
          minHeight: 'calc(100vh - 80px)',
          padding: '20px'
        }}>
          <div style={{ marginBottom: '20px' }}>
            <h3 style={{ color: '#94a3b8', fontSize: '12px', textTransform: 'uppercase', letterSpacing: '1px', margin: '0 0 15px 0' }}>
              Business Modules
            </h3>
            {modules.map(module => {
              const Icon = module.icon;
              const isActive = activeModule === module.id;
              
              return (
                <button
                  key={module.id}
                  onClick={() => setActiveModule(module.id)}
                  style={{
                    width: '100%',
                    background: isActive ? 'rgba(59, 130, 246, 0.2)' : 'transparent',
                    border: isActive ? '1px solid rgba(59, 130, 246, 0.4)' : '1px solid transparent',
                    borderRadius: '8px',
                    padding: '12px 15px',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '10px',
                    color: isActive ? '#3b82f6' : '#94a3b8',
                    cursor: 'pointer',
                    marginBottom: '5px',
                    transition: 'all 0.2s ease'
                  }}
                >
                  <Icon size={16} />
                  {module.name}
                </button>
              );
            })}
          </div>
        </div>

        {/* Main Content */}
        <div style={{ flex: 1, minHeight: 'calc(100vh - 80px)' }}>
          {renderActiveModule()}
        </div>
      </div>
    </div>
  );
};

export default SentariOS;
