// src/components/Dashboard/PerformanceCharts.js
import React, { useMemo } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, PieChart, Pie, Cell } from 'recharts';

const PerformanceCharts = ({ data }) => {
  // Generate performance data based on current metrics
  const performanceData = useMemo(() => {
    if (data?.analytics && data.analytics.monthlyRevenue > 0) {
      const currentMonth = {
        name: 'Current',
        revenue: data.analytics.monthlyRevenue || 0,
        employees: data.employees?.length || 0,
        sites: data.analytics.totalSites || 0,
        efficiency: data.analytics.profitMargin || 0
      };
      
      // Generate historical data based on current month
      const baseData = [];
      for (let i = 5; i >= 1; i--) {
        const variation = 0.8 + (Math.random() * 0.4); // 80-120% variation
        baseData.push({
          name: new Date(Date.now() - i * 30 * 24 * 60 * 60 * 1000).toLocaleDateString('en', { month: 'short' }),
          revenue: Math.round(currentMonth.revenue * variation),
          employees: Math.max(1, Math.round(currentMonth.employees * variation)),
          sites: Math.max(1, Math.round(currentMonth.sites * variation)),
          efficiency: Math.round(currentMonth.efficiency * variation)
        });
      }
      
      return [...baseData, currentMonth];
    }
    
    // Fallback data
    return [
      { name: 'Jan', revenue: 425000, employees: 89, sites: 12, efficiency: 94 },
      { name: 'Feb', revenue: 468000, employees: 92, sites: 14, efficiency: 91 },
      { name: 'Mar', revenue: 510000, employees: 96, sites: 15, efficiency: 96 },
      { name: 'Apr', revenue: 485000, employees: 94, sites: 16, efficiency: 89 },
      { name: 'May', revenue: 534000, employees: 98, sites: 18, efficiency: 93 },
      { name: 'Current', revenue: 612000, employees: 105, sites: 21, efficiency: 97 }
    ];
  }, [data]);

  const industryData = [
    { name: 'Security Services', value: 45, color: '#3b82f6' },
    { name: 'Healthcare', value: 25, color: '#10b981' },
    { name: 'Manufacturing', value: 20, color: '#f59e0b' },
    { name: 'Retail', value: 10, color: '#ef4444' }
  ];

  return (
    <div style={{ 
      display: 'grid', 
      gridTemplateColumns: '2fr 1fr', 
      gap: '24px', 
      marginBottom: '32px' 
    }}>
      {/* Performance Trend Chart */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.8)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '16px',
        padding: '24px',
        backdropFilter: 'blur(12px)'
      }}>
        <h2 style={{ 
          color: '#e2e8f0', 
          fontSize: '22px', 
          marginBottom: '20px', 
          fontWeight: '600'
        }}>
          üìà Performance Trends
        </h2>
        <ResponsiveContainer width="100%" height={280}>
          <AreaChart data={performanceData}>
            <defs>
              <linearGradient id="revenueGradient" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.3}/>
                <stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/>
              </linearGradient>
            </defs>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(59, 130, 246, 0.1)" />
            <XAxis dataKey="name" stroke="#94a3b8" fontSize={12} />
            <YAxis stroke="#94a3b8" fontSize={12} />
            <Tooltip 
              contentStyle={{
                background: 'rgba(15, 23, 42, 0.95)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '8px',
                color: '#e2e8f0',
                boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)'
              }}
              formatter={(value, name) => [
                name === 'revenue' ? `$${value.toLocaleString()}` : value,
                name === 'revenue' ? 'Revenue' : name
              ]}
            />
            <Area 
              type="monotone" 
              dataKey="revenue" 
              stroke="#3b82f6" 
              fill="url(#revenueGradient)"
              strokeWidth={3}
            />
          </AreaChart>
        </ResponsiveContainer>
      </div>

      {/* Industry Mix Chart */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.8)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '16px',
        padding: '24px',
        backdropFilter: 'blur(12px)'
      }}>
        <h2 style={{ 
          color: '#e2e8f0', 
          fontSize: '22px', 
          marginBottom: '20px', 
          fontWeight: '600' 
        }}>
          üè≠ Industry Mix
        </h2>
        
        {data?.analytics?.totalClients > 0 ? (
          <>
            <ResponsiveContainer width="100%" height={200}>
              <PieChart>
                <Pie
                  data={industryData}
                  cx="50%"
                  cy="50%"
                  innerRadius={50}
                  outerRadius={80}
                  paddingAngle={5}
                  dataKey="value"
                >
                  {industryData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip 
                  contentStyle={{
                    background: 'rgba(15, 23, 42, 0.95)',
                    border: '1px solid rgba(59, 130, 246, 0.3)',
                    borderRadius: '8px',
                    color: '#e2e8f0'
                  }}
                  formatter={(value) => [`${value}%`, 'Share']}
                />
              </PieChart>
            </ResponsiveContainer>
            
            <div style={{ marginTop: '16px' }}>
              {industryData.map((item, index) => (
                <div key={index} style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                  padding: '8px 0',
                  borderBottom: index < industryData.length - 1 ? '1px solid rgba(59, 130, 246, 0.1)' : 'none'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                    <div style={{
                      width: '12px',
                      height: '12px',
                      borderRadius: '50%',
                      background: item.color
                    }} />
                    <span style={{ color: '#e2e8f0', fontSize: '14px' }}>{item.name}</span>
                  </div>
                  <span style={{ color: '#94a3b8', fontSize: '14px', fontWeight: '500' }}>
                    {item.value}%
                  </span>
                </div>
              ))}
            </div>
          </>
        ) : (
          <div style={{ textAlign: 'center', padding: '32px 0', color: '#6b7280' }}>
            <div style={{ fontSize: '48px', marginBottom: '16px' }}>üìä</div>
            <p>No client data available</p>
            <p style={{ fontSize: '12px', marginTop: '8px' }}>
              Add clients to see industry breakdown
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default PerformanceCharts;
