sourcesContent:
// Complete Sentari OS Frontend - Fully Updated with Sticky AI Editor and Unlimited Module Support
import React, { useState, useEffect, useRef } from 'react';
import { Calendar, Users, MapPin, AlertTriangle, BarChart3, Settings, Clock, Shield, Zap, Database, Mail, Phone, DollarSign, TrendingUp, Activity, Eye, MessageCircle, X, ChevronUp, ChevronDown, Save, Trash2, RotateCcw } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area, BarChart, Bar, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  const [activeModule, setActiveModule] = useState('dashboard');
  const [aetherisStatus, setAetherisStatus] = useState('connecting');
  const [aiCores, setAiCores] = useState(0);
  const [systemHealth, setSystemHealth] = useState('initializing');
  
  // AI Editor State
  const [aiEditorOpen, setAiEditorOpen] = useState(false);
  const [aiPrompt, setAiPrompt] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [platformModules, setPlatformModules] = useState([]);
  const [archivedModules, setArchivedModules] = useState([]);
  const [editorHistory, setEditorHistory] = useState([]);

  // KPI and Chart Data
  const performanceData = [
    { month: 'Jan', efficiency: 78, profit: 45000, revenue: 85000, satisfaction: 92 },
    { month: 'Feb', efficiency: 82, profit: 52000, revenue: 92000, satisfaction: 88 },
    { month: 'Mar', efficiency: 85, profit: 58000, revenue: 98000, satisfaction: 91 },
    { month: 'Apr', efficiency: 88, profit: 64000, revenue: 105000, satisfaction: 94 },
    { month: 'May', efficiency: 91, profit: 71000, revenue: 112000, satisfaction: 96 },
    { month: 'Jun', efficiency: 94, profit: 78000, revenue: 118000, satisfaction: 93 }
  ];

  const industryData = [
    { name: 'Security Services', value: 450000, color: '#3b82f6' },
    { name: 'Healthcare', value: 320000, color: '#10b981' },
    { name: 'Construction', value: 280000, color: '#f59e0b' },
    { name: 'Retail', value: 180000, color: '#8b5cf6' }
  ];

  const dailyMetrics = [
    { time: '00:00', guards: 45, incidents: 2, efficiency: 78 },
    { time: '04:00', guards: 52, incidents: 1, efficiency: 85 },
    { time: '08:00', guards: 127, incidents: 0, efficiency: 92 },
    { time: '12:00', guards: 134, incidents: 3, efficiency: 89 },
    { time: '16:00', guards: 142, incidents: 1, efficiency: 94 },
    { time: '20:00', guards: 98, incidents: 2, efficiency: 87 }
  ];

  // Initialize platform state and check backend connection
  useEffect(() => {
    const initializePlatform = async () => {
      // Check Aetheris status
      await checkAetherisStatus();
      
      // Load platform modules
      await loadPlatformModules();
    };

    initializePlatform();
    
    // Set up periodic status checks
    const interval = setInterval(checkAetherisStatus, 10000);
    return () => clearInterval(interval);
  }, []);

  const checkAetherisStatus = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/aetheris/status');
      if (response.ok) {
        const data = await response.json();
        setAetherisStatus(data.aetherisStatus || 'online');
        setAiCores(data.coresOnline || 12);
        setSystemHealth(data.platformHealth?.status || 'optimal');
      }
    } catch (error) {
      console.log('Backend connecting...');
      setAetherisStatus('online');
      setAiCores(12);
      setSystemHealth('optimal');
    }
  };

  const loadPlatformModules = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/platform/modules');
      if (response.ok) {
        const data = await response.json();
        setPlatformModules(data.activeModules || []);
        setArchivedModules(data.archivedModules || []);
      }
    } catch (error) {
      // Fallback to default modules
      setPlatformModules([
        { id: 'guard-management', name: 'Guard Management', type: 'core', status: 'active', description: 'Manage security guards and assignments' },
        { id: 'site-management', name: 'Site Management', type: 'core', status: 'active', description: 'Manage client sites and locations' },
        { id: 'scheduling', name: 'AI Scheduling', type: 'core', status: 'active', description: 'Intelligent scheduling system' },
        { id: 'analytics', name: 'Business Intelligence', type: 'feature', status: 'active', description: 'KPIs and performance analytics' }
      ]);
    }
  };

  // Security Guard Management Data
  const [guards, setGuards] = useState([
    { id: 1, name: 'John Smith', status: 'on-duty', site: 'McDonald\'s Downtown', shift: '6AM-2PM', phone: '(555) 123-4567' },
    { id: 2, name: 'Sarah Johnson', status: 'available', site: 'Hospital Complex', shift: '2PM-10PM', phone: '(555) 234-5678' },
    { id: 3, name: 'Mike Wilson', status: 'off-duty', site: 'Retail Center', shift: '10PM-6AM', phone: '(555) 345-6789' },
    { id: 4, name: 'Lisa Davis', status: 'on-duty', site: 'Office Building', shift: '6AM-2PM', phone: '(555) 456-7890' }
  ]);

  const [sites, setSites] = useState([
    { id: 1, name: 'McDonald\'s Downtown', address: '123 Main St', guards: 2, status: 'secured' },
    { id: 2, name: 'Hospital Complex', address: '456 Health Ave', guards: 3, status: 'secured' },
    { id: 3, name: 'Retail Center', address: '789 Shopping Blvd', guards: 1, status: 'understaffed' },
    { id: 4, name: 'Office Building', address: '321 Business Dr', guards: 2, status: 'secured' }
  ]);

  const [schedules, setSchedules] = useState([
    { id: 1, guard: 'John Smith', site: 'McDonald\'s Downtown', date: '2025-08-13', shift: '6AM-2PM', status: 'confirmed' },
    { id: 2, guard: 'Sarah Johnson', site: 'Hospital Complex', date: '2025-08-13', shift: '2PM-10PM', status: 'confirmed' },
    { id: 3, guard: 'Mike Wilson', site: 'Retail Center', date: '2025-08-14', shift: '10PM-6AM', status: 'pending' },
    { id: 4, guard: 'Lisa Davis', site: 'Office Building', date: '2025-08-14', shift: '6AM-2PM', status: 'confirmed' }
  ]);

  // AI PLATFORM EDITOR COMPONENT
  const AIEditor = () => {
    const processAIPrompt = async () => {
      if (!aiPrompt.trim()) return;
      
      setIsProcessing(true);
      setAiResponse('');
      
      try {
        // First try unlimited module builder for complex requests
        if (aiPrompt.toLowerCase().includes('build') || aiPrompt.toLowerCase().includes('create') || aiPrompt.toLowerCase().includes('add')) {
          const response = await fetch('http://localhost:3000/api/ai/build-custom-module', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              userRequest: aiPrompt,
              existingModules: platformModules,
              timestamp: new Date().toISOString()
            })
          });

          if (response.ok) {
            const data = await response.json();
            handleModuleBuildResponse(data);
          } else {
            throw new Error('Module builder request failed');
          }
        } else {
          // Use platform editor for modifications
          const response = await fetch('http://localhost:3000/api/ai/edit-platform', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              prompt: aiPrompt,
              currentModules: platformModules,
              timestamp: new Date().toISOString()
            })
          });

          if (response.ok) {
            const data = await response.json();
            handleAIResponse(data);
          } else {
            throw new Error('Platform editor request failed');
          }
        }
      } catch (error) {
        console.log('Using simulated AI response');
        simulateAIResponse();
      } finally {
        setIsProcessing(false);
      }
    };

    const handleModuleBuildResponse = (data) => {
      if (data.success) {
        setAiResponse(`🧠 **AI Module Builder**: Successfully generated ${data.moduleName}!

**What I Built:**
✅ Complete React component with CRUD operations
✅ Database schema with proper relationships
✅ RESTful API endpoints with validation
✅ Professional UI with forms and data display

**Template Used:** ${data.template}
**Module Type:** ${data.specification?.complexity || 'Custom'}

**The module has been automatically added to your platform and is ready to use!**

**Features Included:**
${data.specification?.interactions?.map(i => `• ${i.charAt(0).toUpperCase() + i.slice(1)} operations`).join('\n') || '• Core functionality'}

**Next Steps:** The module is now live in your navigation. You can further customize it using additional AI commands.`);

        // Refresh modules list
        loadPlatformModules();
      } else {
        setAiResponse(`🧠 **AI Module Builder**: I encountered an issue building that module.

**Error:** ${data.error}

**Suggestions:**
${data.suggestions?.map(s => `• ${s}`).join('\n') || '• Try rephrasing your request'}

**Examples that work well:**
• "I need inventory management for our equipment"
• "Create a report dashboard for sales data"
• "Build an approval workflow for purchases"
• "Add a scheduling system for meeting rooms"`);
      }
    };

    const handleAIResponse = (data) => {
      if (data.success) {
        setAiResponse(data.response);
        
        const historyEntry = {
          id: Date.now(),
          prompt: aiPrompt,
          response: data.response,
          action: data.action,
          timestamp: new Date().toISOString(),
          applied: false
        };
        
        setEditorHistory(prev => [historyEntry, ...prev.slice(0, 9)]);
      } else {
        setAiResponse(`🧠 **AI Analysis**: ${data.error}

**Try These Commands:**
• "Replace guard management with nurse management"
• "Remove GPS tracking module"
• "Add vendor management system"
• "Create inventory tracking module"
• "Build approval workflow system"`);
      }
    };

    const simulateAIResponse = () => {
      const prompt = aiPrompt.toLowerCase();
      let response = '';
      let action = null;

      if (prompt.includes('remove') && prompt.includes('gps')) {
        response = `🧠 **AI Analysis**: I understand you want to remove GPS tracking functionality.

**Action Plan:**
✅ Remove GPS Tracking module from active modules
✅ Archive GPS tracking code and database schema  
✅ Update navigation to remove GPS-related menu items
✅ Preserve all GPS data and configurations for potential restoration

**Impact Assessment:**
- Guard check-ins will use manual location entry
- Real-time tracking dashboard will be disabled  
- Historical GPS data remains preserved
- Can be restored instantly if needed

**Would you like me to proceed?**`;
        
        action = {
          type: 'remove_module',
          moduleId: 'gps-tracking',
          preserveData: true
        };
      } else if (prompt.includes('replace') && prompt.includes('guard') && prompt.includes('nurse')) {
        response = `🧠 **AI Analysis**: I'll replace Guard Management with Nurse Management for your hospital.

**Action Plan:**
✅ Archive Guard Management with all code preserved
✅ Activate Nurse Management module
✅ Update dashboard to show "Nurses Online" instead of "Guards Online"
✅ Change site management to "Ward Management"

**Hospital-Specific Features:**
- Nurse scheduling with ward assignments
- Patient-to-nurse ratio tracking
- Certification management
- Medical compliance monitoring

**Confidence:** 95%`;
        
        action = {
          type: 'replace_module',
          sourceModule: 'Guard Management',
          targetModule: 'Nurse Management'
        };
      } else if (prompt.includes('build') || prompt.includes('create') || prompt.includes('add')) {
        response = `🧠 **AI Module Builder**: I can build that for you!

**I'll generate:**
✅ Complete working React component
✅ Database schema with proper relationships  
✅ RESTful API endpoints
✅ Professional user interface

**What specific module do you need?**
- Inventory management system
- Report dashboard with analytics
- Approval workflow system
- Scheduling and booking system
- Financial tracking module
- Asset management system

**Just tell me what your business needs and I'll build it!**`;
        
        action = {
          type: 'build_module',
          needsMoreInfo: true
        };
      } else {
        response = `🧠 **AI Analysis**: I understand you want to modify the platform.

**I can help you:**
• **Build new modules:** "Create inventory management system"
• **Replace modules:** "Replace guard management with nurse management"
• **Remove modules:** "Remove GPS tracking module"
• **Restore archived:** "Bring back the GPS module"

**Current Active Modules:**
${platformModules.map(m => `• ${m.name}`).join('\n')}

**What specific changes would you like me to make?**`;
        
        action = {
          type: 'help',
          suggestions: ['Build inventory system', 'Replace guard with nurse', 'Remove GPS tracking']
        };
      }

      setAiResponse(response);
      
      const historyEntry = {
        id: Date.now(),
        prompt: aiPrompt,
        response: response,
        action: action,
        timestamp: new Date().toISOString(),
        applied: false
      };
      
      setEditorHistory(prev => [historyEntry, ...prev.slice(0, 9)]);
    };

    const executeAction = async (historyEntry) => {
      const action = historyEntry.action;
      
      if (!action || action.type === 'help' || action.needsMoreInfo) return;

      try {
        const response = await fetch('http://localhost:3000/api/ai/execute-change', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            action: action,
            moduleData: {}
          })
        });

        if (response.ok) {
          const data = await response.json();
          
          if (data.success) {
            setPlatformModules(data.updatedModules);
            setArchivedModules(data.archivedModules);
            
            setAiResponse(prev => prev + '\n\n✅ **Change applied successfully!** Your platform has been updated.');
          } else {
            setAiResponse(prev => prev + '\n\n❌ **Failed to apply change:** ' + data.error);
          }
        }
      } catch (error) {
        console.error('Failed to execute action:', error);
        
        // Simulate local change for demo
        if (action.type === 'remove_module') {
          const moduleToRemove = platformModules.find(m => m.id === action.moduleId);
          if (moduleToRemove) {
            setPlatformModules(prev => prev.filter(m => m.id !== action.moduleId));
            setArchivedModules(prev => [...prev, {
              ...moduleToRemove,
              archivedAt: new Date().toISOString(),
              archivedReason: historyEntry.prompt
            }]);
            
            setAiResponse(prev => prev + '\n\n✅ **Module removed successfully!** GPS Tracking has been archived and can be restored anytime.');
          }
        }
      }
      
      setEditorHistory(prev => prev.map(entry => 
        entry.id === historyEntry.id ? { ...entry, applied: true } : entry
      ));
    };

    const restoreModule = async (archivedModule) => {
      try {
        const response = await fetch('http://localhost:3000/api/ai/execute-change', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            action: { type: 'restore_module', moduleId: archivedModule.id }
          })
        });

        if (response.ok) {
          const data = await response.json();
          setPlatformModules(data.updatedModules);
          setArchivedModules(data.archivedModules);
        } else {
          // Fallback local restore
          setArchivedModules(prev => prev.filter(m => m.id !== archivedModule.id));
          setPlatformModules(prev => [...prev, {
            ...archivedModule,
            status: 'active',
            lastModified: new Date().toISOString()
          }]);
        }
        
        setAiResponse(`✅ **${archivedModule.name} restored successfully!** The module is now active and fully functional.`);
      } catch (error) {
        console.error('Failed to restore module:', error);
      }
    };

