                {/* History */}
                {editorHistory.length > 0 && (
                  <div style={{ marginTop: '15px' }}>
                    <h4 style={{ color: '#8b5cf6', fontSize: '12px', marginBottom: '10px' }}>Recent Changes</h4>
                    {editorHistory.slice(0, 3).map(entry => (
                      <div key={entry.id} style={{
                        background: 'rgba(255, 255, 255, 0.05)',
                        borderRadius: '6px',
                        padding: '8px',
                        marginBottom: '8px',
                        fontSize: '11px'
                      }}>
                        <div style={{ color: '#94a3b8', marginBottom: '4px' }}>
