                          }}>
                            {entry.applied ? '✅ Applied' : '⏳ Pending'}
                          </span>
                          {!entry.applied && entry.action && entry.action.type !== 'help' && !entry.action.needsMoreInfo && (
                            <button
                              onClick={() => executeAction(entry)}
                              style={{
                                background: 'linear-gradient(135deg, #10b981, #059669)',
                                border: 'none',
                                color: 'white',
                                padding: '2px 6px',
                                borderRadius: '4px',
                                fontSize: '9px',
                                cursor: 'pointer'
                              }}
                            >
                              Apply
                            </button>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                )}

                {/* Archived Modules */}
                {archivedModules.length > 0 && (
                  <div style={{ marginTop: '15px' }}>
                    <h4 style={{ color: '#f59e0b', fontSize: '12px', marginBottom: '10px' }}>
                      📦 Archived Modules ({archivedModules.length})
                    </h4>
                    {archivedModules.map(module => (
                      <div key={module.id} style={{
                        background: 'rgba(245, 158, 11, 0.1)',
                        border: '1px solid rgba(245, 158, 11, 0.2)',
                        borderRadius: '6px',
                        padding: '8px',
                        marginBottom: '6px',
                        fontSize: '11px'
                      }}>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
