                            style={{
                              background: 'transparent',
                              border: '1px solid rgba(245, 158, 11, 0.5)',
                              color: '#f59e0b',
                              padding: '2px 6px',
                              borderRadius: '4px',
                              fontSize: '9px',
                              cursor: 'pointer'
