  // Enhanced confirmation processing
  const processConfirmationResponse = useCallback(async (message, confirmationId = null) => {
    const messageLower = message.toLowerCase().trim();
    
    const confirmKeywords = ['yes', 'confirm', 'ok', 'correct', 'looks good', 'approve', 'yep', 'yeah', 'sure', 'affirmative', 'add it', 'go ahead', 'proceed'];
    const cancelKeywords = ['no', 'cancel', 'wrong', 'incorrect', 'stop', 'nope', 'not right', 'abort'];
    const correctionPatterns = ['actually', 'change', 'update', 'fix', 'should be', 'meant to', 'correct'];
    
    const isConfirm = confirmKeywords.some(keyword => messageLower.includes(keyword));
    const isCancel = cancelKeywords.some(keyword => messageLower.includes(keyword));
    const isCorrection = correctionPatterns.some(pattern => messageLower.includes(pattern));
    
    // Handle corrections
    if (isCorrection && !isConfirm && !isCancel) {
      try {
        const response = await fetch(`${API_BASE_URL}/api/aetheris/chat`, {
          method: 'POST',
          headers: { 
            'Content-Type': 'application/json',
            'X-Company-Id': 'sentari',
            'X-User-Id': userName || 'tristen'
          },
          body: JSON.stringify({ 
            message: `apply_correction: ${message}`,
            userId: userName || 'tristen',
            companyId: companyName || 'sentari',
            correctionContext: {
              originalMessage: message,
              confirmationId: activeConfirmation?.id
            }
          })
        });
        
        const result = await response.json();
        
        if (result.requiresConfirmation) {
          setActiveConfirmation({
            id: result.confirmationId,
            message: result.response,
            type: 'correction',
            timestamp: Date.now()
          });
          
          return {
            type: 'correction_applied',
            message: result.response,
            newConfirmation: true
          };
        } else {
          return {
            type: 'correction_completed',
            message: result.response
          };
        }
      } catch (error) {
        return {
          type: 'error',
          message: `Correction failed: ${error.message}`
        };
      }
    }
    
    // Handle confirm/cancel
    if (!isConfirm && !isCancel) {
      return null; // Not a confirmation response
    }
    
    try {
      const response = await fetch(`${API_BASE_URL}/api/aetheris/chat`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'X-Company-Id': 'sentari',
          'X-User-Id': userName || 'tristen'
        },
        body: JSON.stringify({ 
          message: isConfirm ? 'yes confirm' : 'no cancel',
          userId: userName || 'tristen',
          companyId: companyName || 'sentari',
          confirmationResponse: {
            approved: isConfirm,
            confirmationId: activeConfirmation?.id || confirmationId
          }
        })
      });
      
      const result = await response.json();
      
      // Clear active confirmation
      setActiveConfirmation(null);
      
      return {
        type: isConfirm ? 'confirmed' : 'cancelled',
        message: result.response,
        dataChanges: result.dataChanges
      };
      
    } catch (error) {
      return {
        type: 'error',
        message: `Confirmation failed: ${error.message}`
      };
    }
  }, [activeConfirmation, userName, companyName]);

