async function replaceModule(sourceModule, targetModule) {
  try {
    console.log(`ðŸ”„ Replacing ${sourceModule} with ${targetModule}`);
    
    const sourceIdx = platformModules.findIndex(m => m.name === sourceModule);
    const targetIdx = platformModules.findIndex(m => m.name === targetModule);
    
    if (sourceIdx === -1) {
      throw new Error(`Source module ${sourceModule} not found`);
    }
    
    const archivedModule = { ...platformModules[sourceIdx] };
    archivedModule.status = 'archived';
    archivedModule.archivedAt = new Date().toISOString();
    archivedModule.archivedReason = `Replaced with ${targetModule}`;
    
    archivedModules.push(archivedModule);
    
    if (targetIdx !== -1) {
      platformModules[targetIdx].status = 'active';
      platformModules[targetIdx].lastModified = new Date().toISOString();
      platformModules.splice(sourceIdx, 1);
    } else {
      const newModule = await generateNewModule(targetModule);
      platformModules[sourceIdx] = newModule;
    }
    
    return {
      success: true,
      message: `Successfully replaced ${sourceModule} with ${targetModule}`,
      archivedModule: archivedModule,
      newModule: platformModules.find(m => m.name === targetModule)
    };
    
  } catch (error) {
    console.error('âŒ Module replacement failed:', error);
    throw error;
  }
}

