async function restoreModule(moduleId) {
  const archivedIdx = archivedModules.findIndex(m => m.id === moduleId);
  if (archivedIdx === -1) {
    throw new Error(`Archived module ${moduleId} not found`);
  }
  
  const module = archivedModules[archivedIdx];
  module.status = 'active';
  module.lastModified = new Date().toISOString();
  delete module.archivedAt;
  delete module.archivedReason;
  
  platformModules.push({ ...module });
  archivedModules.splice(archivedIdx, 1);
  
  return { success: true, message: `${module.name} restored successfully` };
}

