import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell, LineChart, Line } from 'recharts';

export default function AetherisMasterDashboard() {
  const [aetherisStatus, setAetherisStatus] = useState(null);
  const [aiCores, setAiCores] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [guidance, setGuidance] = useState(null);
  const [loading, setLoading] = useState(false);
  const [activeModule, setActiveModule] = useState('dashboard');
  const [systemLogs, setSystemLogs] = useState([]);

  // Connect to your Aetheris backend
  useEffect(() => {
    fetchAetherisStatus();
    fetchAiCores();
    
    // Poll for updates every 30 seconds
    const interval = setInterval(() => {
      fetchAetherisStatus();
    }, 30000);
    
    return () => clearInterval(interval);
  }, []);

  const fetchAetherisStatus = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/aetheris/status');
      const data = await response.json();
      setAetherisStatus(data);
      if (data.recentActivity) {
        setSystemLogs(data.recentActivity);
      }
    } catch (error) {
      console.error('Failed to fetch Aetheris status:', error);
    }
  };

  const fetchAiCores = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/aetheris/cores');
      const data = await response.json();
      setAiCores(data.cores || []);
    } catch (error) {
      console.error('Failed to fetch AI cores:', error);
    }
  };

  const askAetheris = async () => {
    if (!userInput.trim()) return;
    
    setLoading(true);
    try {
      const response = await fetch('http://localhost:3000/api/aetheris/guide', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: 'dashboard-user',
          issue: userInput
        })
      });
      const data = await response.json();
      setGuidance(data);
    } catch (error) {
      console.error('Failed to get Aetheris guidance:', error);
      setGuidance({ 
        guidance: 'Sorry, I encountered an issue. Please check if Aetheris is running on localhost:3000',
        timestamp: new Date()
      });
    }
    setLoading(false);
  };

  const coreColors = {
    'sentinel': '#3b82f6',
    'jarvis': '#10b981', 
    'visual': '#f59e0b',
    'security': '#ef4444',
    'kpi': '#8b5cf6',
    'integration': '#06b6d4',
    'training': '#84cc16',
    'finance': '#f97316',
    'client': '#ec4899'
  };

  const mockBusinessData = [
    { name: 'Security Services', revenue: 450000, clients: 12, guards: 85 },
    { name: 'Healthcare', revenue: 280000, clients: 8, employees: 45 },
    { name: 'Construction', revenue: 320000, clients: 6, workers: 60 },
    { name: 'Delivery', revenue: 180000, clients: 15, drivers: 35 }
  ];

  const performanceData = [
    { name: 'Jan', efficiency: 85, profit: 120000, issues: 12 },
    { name: 'Feb', efficiency: 88, profit: 135000, issues: 8 },
    { name: 'Mar', efficiency: 92, profit: 150000, issues: 5 },
    { name: 'Apr', efficiency: 89, profit: 142000, issues: 7 },
    { name: 'May', efficiency: 94, profit: 165000, issues: 3 }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-800">
      {/* Header */}
      <div className="bg-slate-800/50 backdrop-blur border-b border-blue-400/20">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                <span className="text-2xl font-bold text-white">‚ö°</span>
              </div>
              <div>
                <h1 className="text-2xl font-bold text-white">MIPFullSystem</h1>
                <p className="text-blue-300">Powered by Aetheris Master Architect AI</p>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <div className={`w-3 h-3 rounded-full ${aetherisStatus?.aetherisStatus === 'online' ? 'bg-green-400' : 'bg-red-400'}`}></div>
                <span className="text-sm text-gray-300">
                  {aetherisStatus?.aetherisStatus === 'online' ? 'Aetheris Online' : 'Connecting...'}
                </span>
              </div>
              <div className="bg-blue-600 px-3 py-1 rounded-full text-sm text-white">
                {aiCores.length} AI Cores Active
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <div className="bg-slate-800/30 backdrop-blur border-b border-slate-700/50">
        <div className="max-w-7xl mx-auto px-6">
          <nav className="flex space-x-8">
            {[
              { id: 'dashboard', name: 'üìä Dashboard', icon: 'üìä' },
              { id: 'ai-cores', name: 'üß† AI Cores', icon: 'üß†' },
              { id: 'guidance', name: 'üß≠ Aetheris Guidance', icon: 'üß≠' },
              { id: 'modules', name: 'üõ†Ô∏è Business Modules', icon: 'üõ†Ô∏è' },
              { id: 'analytics', name: 'üìà Analytics', icon: 'üìà' }
            ].map(item => (
              <button
                key={item.id}
                onClick={() => setActiveModule(item.id)}
                className={`px-4 py-3 text-sm font-medium border-b-2 transition-colors ${
                  activeModule === item.id
                    ? 'border-blue-400 text-blue-400'
                    : 'border-transparent text-gray-400 hover:text-white'
                }`}
              >
                {item.name}
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-6 py-8">
        {activeModule === 'dashboard' && (
          <div className="space-y-8">
            {/* System Overview */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              <div className="bg-gradient-to-br from-blue-600 to-blue-700 rounded-xl p-6 text-white">
                <h3 className="text-lg font-semibold mb-2">üß† Aetheris Status</h3>
                <p className="text-3xl font-bold">{aetherisStatus?.aetherisStatus || 'Loading...'}</p>
                <p className="text-blue-200 text-sm">Master Architect AI</p>
              </div>
              
              <div className="bg-gradient-to-br from-green-600 to-green-700 rounded-xl p-6 text-white">
                <h3 className="text-lg font-semibold mb-2">ü§ñ AI Cores</h3>
                <p className="text-3xl font-bold">{aiCores.length}</p>
                <p className="text-green-200 text-sm">Specialized Agents</p>
              </div>
              
              <div className="bg-gradient-to-br from-purple-600 to-purple-700 rounded-xl p-6 text-white">
                <h3 className="text-lg font-semibold mb-2">üè¢ Platform Health</h3>
                <p className="text-3xl font-bold">{aetherisStatus?.platformHealth?.status || 'Optimal'}</p>
                <p className="text-purple-200 text-sm">System Status</p>
              </div>
              
              <div className="bg-gradient-to-br from-orange-600 to-orange-700 rounded-xl p-6 text-white">
                <h3 className="text-lg font-semibold mb-2">üìä Active Modules</h3>
                <p className="text-3xl font-bold">15</p>
                <p className="text-orange-200 text-sm">Business Modules</p>
              </div>
            </div>

            {/* Business Performance */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="bg-slate-800/50 rounded-xl p-6 border border-slate-700">
                <h3 className="text-xl font-bold text-white mb-4">üìà Business Performance</h3>
                <ResponsiveContainer width="100%" height={300}>
                  <LineChart data={performanceData}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                    <XAxis dataKey="name" stroke="#9ca3af" />
                    <YAxis stroke="#9ca3af" />
                    <Tooltip contentStyle={{ backgroundColor: '#1f2937', border: '1px solid #374151' }} />
                    <Legend />
                    <Line type="monotone" dataKey="efficiency" stroke="#3b82f6" strokeWidth={2} />
                    <Line type="monotone" dataKey="profit" stroke="#10b981" strokeWidth={2} />
                  </LineChart>
                </ResponsiveContainer>
              </div>

              <div className="bg-slate-800/50 rounded-xl p-6 border border-slate-700">
                <h3 className="text-xl font-bold text-white mb-4">üéØ Industry Distribution</h3>
                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie
                      data={mockBusinessData}
                      cx="50%"
                      cy="50%"
                      outerRadius={100}
                      fill="#8884d8"
                      dataKey="revenue"
                      label
                    >
                      {mockBusinessData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={Object.values(coreColors)[index]} />
                      ))}
                    </Pie>
                    <Tooltip contentStyle={{ backgroundColor: '#1f2937', border: '1px solid #374151' }} />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        )}

        {activeModule === 'ai-cores' && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold text-white">üß† AI Core Network</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {aiCores.map((core, index) => (
                <div key={core.id} className="bg-slate-800/50 rounded-xl p-6 border border-slate-700 hover:border-blue-400/50 transition-colors">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-lg font-bold text-white">{core.name}</h3>
                    <div className="w-3 h-3 bg-green-400 rounded-full"></div>
                  </div>
                  <p className="text-gray-300 text-sm mb-3">{core.role}</p>
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-gray-400">Health:</span>
                    <span className="text-green-400 font-medium">{core.health || 'Optimal'}</span>
                  </div>
                  <div className="flex items-center justify-between text-sm mt-2">
                    <span className="text-gray-400">Last Active:</span>
                    <span className="text-blue-400 font-medium">
                      {core.lastActivity ? new Date(core.lastActivity).toLocaleTimeString() : 'Now'}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeModule === 'guidance' && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold text-white">üß≠ Ask Aetheris for Guidance</h2>
            
            <div className="bg-slate-800/50 rounded-xl p-6 border border-slate-700">
              <div className="flex space-x-4 mb-6">
                <input
                  type="text"
                  value={userInput}
                  onChange={(e) => setUserInput(e.target.value)}
                  placeholder="Ask Aetheris anything about your business platform..."
                  className="flex-1 bg-slate-700 border border-slate-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:outline-none focus:border-blue-400"
                  onKeyPress={(e) => e.key === 'Enter' && askAetheris()}
                />
                <button
                  onClick={askAetheris}
                  disabled={loading}
                  className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-600 px-6 py-3 rounded-lg text-white font-medium transition-colors"
                >
                  {loading ? 'ü§î Thinking...' : 'üß≠ Ask Aetheris'}
                </button>
              </div>
              
              {guidance && (
                <div className="bg-slate-900/50 rounded-lg p-6 border border-blue-400/20">
                  <div className="flex items-center mb-3">
                    <span className="text-2xl mr-2">üåü</span>
                    <h3 className="text-lg font-bold text-blue-400">Aetheris Guidance</h3>
                    <span className="ml-auto text-sm text-gray-400">
                      {new Date(guidance.timestamp).toLocaleString()}
                    </span>
                  </div>
                  <p className="text-gray-200 leading-relaxed whitespace-pre-line">{guidance.guidance}</p>
                  
                  {guidance.recommendedCores && guidance.recommendedCores.length > 0 && (
                    <div className="mt-4">
                      <p className="text-sm font-medium text-blue-300 mb-2">Recommended AI Cores:</p>
                      <div className="flex flex-wrap gap-2">
                        {guidance.recommendedCores.map(coreId => (
                          <span key={coreId} className="bg-blue-600/20 text-blue-300 px-3 py-1 rounded-full text-sm">
                            {aiCores.find(c => c.id === coreId)?.name || coreId}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Quick Questions */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {[
                "My security guards keep calling out sick",
                "How can I optimize my scheduling for maximum profit?",
                "What's the best way to track employee performance?",
                "How do I prevent client churn?"
              ].map(question => (
                <button
                  key={question}
                  onClick={() => {
                    setUserInput(question);
                    askAetheris();
                  }}
                  className="bg-slate-700/50 hover:bg-slate-600/50 border border-slate-600 rounded-lg p-4 text-left text-gray-300 hover:text-white transition-colors"
                >
                  <span className="text-blue-400 mr-2">üí°</span>
                  {question}
                </button>
              ))}
            </div>
          </div>
        )}

        {activeModule === 'modules' && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold text-white">üõ†Ô∏è Business Modules</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {[
                { name: 'GPS Tracking', icon: 'üìç', status: 'Ready', description: 'Real-time location tracking' },
                { name: 'HR Management', icon: 'üë•', status: 'Ready', description: 'Employee records and management' },
                { name: 'Scheduling', icon: 'üìÖ', status: 'Ready', description: 'Intelligent shift scheduling' },
                { name: 'Incident Reporting', icon: 'üìã', status: 'Ready', description: 'Digital incident reports' },
