import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import { Calendar, Users, MapPin, BarChart3, Clock, Shield, Zap, DollarSign, TrendingUp, Activity, X, MessageSquare, Wrench, Code, Settings, Loader, RefreshCw, Upload, FileText, Eye, Brain, Search, Target, Compass, BookOpen, Users2, Heart, User, Lightbulb, Gauge } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  // Core Platform State
  const [activeModule, setActiveModule] = useState('dashboard');
  const [platformModules, setPlatformModules] = useState([]);
  const [aiChatOpen, setAiChatOpen] = useState(false);
  const [buildHistory, setBuildHistory] = useState([]);
  
  // Real Dashboard Data State
  const [realDashboardData, setRealDashboardData] = useState(null);
  const [dashboardLoading, setDashboardLoading] = useState(true);
  const [loadingError, setLoadingError] = useState(null);
  
  // Delete confirmation state
  const [deleteConfirmation, setDeleteConfirmation] = useState(null);
  
  // AI Chat State
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [userName, setUserName] = useState(null);
  const [companyName, setCompanyName] = useState('');
  const chatInputRef = useRef(null);
  const chatMessagesRef = useRef(null);

  // Chat UI State
  const [chatWidth, setChatWidth] = useState(450);
  const [chatHeight, setChatHeight] = useState(600);
  const [isResizing, setIsResizing] = useState(false);

  // Connection status
  const [backendConnected, setBackendConnected] = useState(false);

  // File Upload State
  const [isDragOver, setIsDragOver] = useState(false);
  const [uploadingFile, setUploadingFile] = useState(false);
  const [fileAnalysisResult, setFileAnalysisResult] = useState(null);
  const fileInputRef = useRef(null);

  // UI Customization State
  const [uiCustomization, setUiCustomization] = useState({
    backgroundColor: 'linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #0f172a 100%)',
    sidebarPosition: 'left',
    sidebarWidth: '280px',
    primaryColor: '#3b82f6',
    secondaryColor: '#8b5cf6',
    accentColor: '#22c55e',
    textColor: '#e2e8f0',
    cardBackground: 'rgba(15, 23, 42, 0.8)',
    borderColor: 'rgba(59, 130, 246, 0.3)',
    buttonStyle: 'modern',
    fontSize: '14px'
  });

  // 12 AI Cores State
  const [aiCores, setAiCores] = useState([
    { id: 'oracle', name: 'Oracle Core', status: 'active', type: 'strategic', icon: Eye, description: 'Strategic Foresight Engine', personality: 'Wise oracle who sees the big picture' },
    { id: 'pulse', name: 'Pulse Core', status: 'active', type: 'monitoring', icon: Activity, description: 'Real-Time KPI Monitoring', personality: 'Vigilant health monitor' },
    { id: 'echo', name: 'Echo Core', status: 'active', type: 'detection', icon: Search, description: 'Pattern Recognition & Anomaly Detection', personality: 'Sharp detective' },
    { id: 'mentor', name: 'Mentor Core', status: 'active', type: 'coaching', icon: Users2, description: 'Predictive Modeling & Coaching', personality: 'Wise mentor focused on human development' },
    { id: 'relay', name: 'Relay Core', status: 'active', type: 'automation', icon: Zap, description: 'Workflow Automation & Orchestration', personality: 'Efficient conductor' },
    { id: 'atlas', name: 'Atlas Core', status: 'active', type: 'operations', icon: Compass, description: 'Operational Intelligence & Optimization', personality: 'Strategic military commander' },
    { id: 'lexicon', name: 'Lexicon Core', status: 'active', type: 'communication', icon: MessageSquare, description: 'Proposal & Communication Engine', personality: 'Eloquent communicator' },
    { id: 'harmony', name: 'Harmony Core', status: 'active', type: 'relationship', icon: Heart, description: 'Client Success & Retention Engine', personality: 'Caring relationship counselor' },
    { id: 'empath', name: 'Empath Core', status: 'active', type: 'behavioral', icon: User, description: 'Human Behavior & Sentiment Analysis', personality: 'Empathetic psychologist' },
    { id: 'persona', name: 'Persona Core', status: 'active', type: 'security', icon: Shield, description: 'Executive Protection Intelligence', personality: 'Elite security professional' },
    { id: 'scholar', name: 'Scholar Core', status: 'active', type: 'knowledge', icon: BookOpen, description: 'Knowledge Management & Learning', personality: 'Wise librarian' },
    { id: 'forge', name: 'Forge Core', status: 'active', type: 'innovation', icon: Lightbulb, description: 'AI Development & Customization Hub', personality: 'Mad scientist innovator' }
  ]);

  // API URL
  const API_URL = 'http://localhost:3000';

  // Check backend connection
  const checkBackendConnection = useCallback(async () => {
    try {
      const response = await fetch(`${API_URL}/api/health`);
      setBackendConnected(response.ok);
    } catch (error) {
      setBackendConnected(false);
    }
  }, []);

 useEffect(() => {
  const loadData = async () => {
    try {
      const response = await fetch(`${API_URL}/api/modules`);
      if (response.ok) {
        const data = await response.json();
        const modules = Array.isArray(data) ? data : (data.modules || []);
        setPlatformModules(prev => {
          const coreModules = prev.filter(m => m.type === 'core' || m.type === 'ai' || m.type === 'system');
          return [...coreModules, ...modules];
        });
      }
    } catch (error) {
      console.error('Failed to load modules:', error);
    }
  };

  loadData(); // Call the async function
}, []);

  // Load real dashboard data
  const loadDashboardData = useCallback(async () => {
    setDashboardLoading(true);
    try {
      const response = await fetch(`${API_URL}/api/business-data`);
      if (response.ok) {
        const data = await response.json();
        setRealDashboardData(data);
        setLoadingError(null);
      } else {
        throw new Error(`HTTP ${response.status}`);
      }
    } catch (error) {
      console.error('Failed to load dashboard data:', error);
      setLoadingError(error.message);
      setRealDashboardData({
        totalRevenue: 125000,
        activeClients: 8,
        totalEmployees: 23,
        completedTasks: 156,
        monthlyRevenue: 45000,
        activeGuards: 12,
        totalSites: 8,
        totalClients: 6,
        guardsOnLeave: 2,
        averageHourlyRate: 22.50,
        lastUpdated: new Date().toISOString(),
        clients: [
          { name: "McDonald's", monthlyRevenue: 15000, satisfaction: 4.8, activeContracts: 3 },
          { name: "Hospital District", monthlyRevenue: 25000, satisfaction: 4.9, activeContracts: 5 },
          { name: "ABC Manufacturing", monthlyRevenue: 12000, satisfaction: 4.7, activeContracts: 2 }
        ],
        employees: [
          { name: "John Smith", role: "Security Guard", status: "Active", hoursThisWeek: 40 },
          { name: "Sarah Johnson", role: "Patrol Officer", status: "Active", hoursThisWeek: 35 },
          { name: "Mike Wilson", role: "Supervisor", status: "Active", hoursThisWeek: 45 }
        ]
      });
    } finally {
      setDashboardLoading(false);
    }
  }, []);

  // Initialize platform on startup
  useEffect(() => {
    const initializePlatform = async () => {
      setPlatformModules([
        { id: 'dashboard', name: 'Executive Dashboard', type: 'core', status: 'active', description: 'Real-time business metrics and AI insights powered by all 12 cores', aiCore: 'pulse' },
        { id: 'business-setup', name: 'AI Business Setup', type: 'ai', status: 'active', description: 'Aetheris-powered business configuration wizard with 12-core intelligence', aiCore: 'aetheris' },
        { id: 'module-library', name: 'Module Library', type: 'system', status: 'active', description: 'Manage and deploy AI-built business modules across all 12 cores', aiCore: 'forge' },
        { id: 'ai-cores', name: '12 AI Cores Control', type: 'system', status: 'active', description: 'Monitor and control all 12 specialized AI cores', aiCore: 'oracle' }
      ]);
      
      await Promise.all([
        checkBackendConnection(),
        loadExistingModules(),
        loadDashboardData()
      ]);
    };
    
    initializePlatform();
  }, [checkBackendConnection, loadExistingModules, loadDashboardData]);

  // Auto-scroll effect for chat
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [chatMessages, isProcessing]);

  // File upload handling
  const handleFileUpload = useCallback(async (files) => {
    setUploadingFile(true);
    
    for (const file of files) {
      try {
        const formData = new FormData();
        formData.append('file', file);
        
        const response = await fetch(`${API_URL}/api/upload`, {
          method: 'POST',
          body: formData
        });
        
        if (response.ok) {
          const result = await response.json();
          setFileAnalysisResult(result);
          
          const analysisMessage = {
            role: 'assistant',
            content: `File Analysis Complete: ${file.name}\n\nData Type: ${result.analysis?.dataType || 'Unknown'}\nRecords: ${result.recordCount || 0}\nSuggested Module: ${result.analysis?.suggestedModuleName || 'Custom Module'}\n\nWhat would you like me to do with this data?`
          };
          setChatMessages(prev => [...prev, analysisMessage]);
          setAiChatOpen(true);
        }
      } catch (error) {
        console.error('Upload failed:', error);
      }
    }
    
    setUploadingFile(false);
  }, []);

  // Process chat messages with AI
  const processChatMessage = useCallback(async () => {
    if (!currentMessage.trim() || isProcessing) return;

    const userMessage = currentMessage.trim();
    setCurrentMessage('');
    setIsProcessing(true);

    const newUserMessage = { role: 'user', content: userMessage };
    setChatMessages(prev => [...prev, newUserMessage]);

    try {
      // Check for UI customization commands
      if (userMessage.toLowerCase().includes('background') || 
          userMessage.toLowerCase().includes('sidebar') ||
          userMessage.toLowerCase().includes('color')) {
        
        let response = '';
        let newCustomization = { ...uiCustomization };
        let hasChanges = false;

        if (userMessage.toLowerCase().includes('background') && userMessage.toLowerCase().includes('pink')) {
          newCustomization.backgroundColor = 'linear-gradient(135deg, #db2777 0%, #ec4899 50%, #db2777 100%)';
          hasChanges = true;
          response += 'Background changed to pink gradient! ';
        } else if (userMessage.toLowerCase().includes('background') && userMessage.toLowerCase().includes('blue')) {
          newCustomization.backgroundColor = 'linear-gradient(135deg, #1e40af 0%, #3b82f6 50%, #1e40af 100%)';
          hasChanges = true;
          response += 'Background changed to blue gradient! ';
        }

        if (userMessage.toLowerCase().includes('sidebar')) {
          if (userMessage.toLowerCase().includes('right')) {
            newCustomization.sidebarPosition = 'right';
            hasChanges = true;
            response += 'Sidebar moved to right side! ';
          } else if (userMessage.toLowerCase().includes('left')) {
            newCustomization.sidebarPosition = 'left';
            hasChanges = true;
            response += 'Sidebar moved to left side! ';
          }
        }

        if (hasChanges) {
          setUiCustomization(newCustomization);
          const aiMessage = { role: 'assistant', content: response + '\n\nUI customization applied successfully!' };
          setChatMessages(prev => [...prev, aiMessage]);
          setIsProcessing(false);
          return;
        }
      }

      const response = await fetch(`${API_URL}/api/chat`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          message: userMessage,
          userName: userName || 'User',
          companyName: companyName || 'Your Company',
          chatHistory: chatMessages,
          aiCores: aiCores,
          fileAnalysisContext: fileAnalysisResult
        })
      });

      if (response.ok) {
        const data = await response.json();
        const aiMessage = { role: 'assistant', content: data.response };
        setChatMessages(prev => [...prev, aiMessage]);

        if (data.userName && !userName) {
          setUserName(data.userName);
        }
        if (data.companyName && !companyName) {
          setCompanyName(data.companyName);
        }
        if (data.newModule) {
          await loadExistingModules();
        }
        if (data.dataChanges) {
          setTimeout(() => loadDashboardData(), 500);
        }
      } else {
        throw new Error(`HTTP ${response.status}`);
      }
    } catch (error) {
      console.error('Chat error:', error);
      const errorMessage = { 
        role: 'assistant', 
        content: `Connection Error: ${error.message}. Please check if the backend server is running on port 3000.`
      };
      setChatMessages(prev => [...prev, errorMessage]);
    }

    setIsProcessing(false);
  }, [currentMessage, isProcessing, userName, companyName, chatMessages, aiCores, fileAnalysisResult, uiCustomization, loadExistingModules, loadDashboardData]);

  // Handle chat input keypress
  const handleChatKeyPress = useCallback((e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      processChatMessage();
    }
  }, [processChatMessage]);

  // Delete module with confirmation
  const handleDeleteModule = useCallback(async (moduleId, moduleName) => {
    try {
      const response = await fetch(`${API_URL}/api/modules/${moduleId}`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' }
      });

      if (response.ok) {
        await loadExistingModules();
        if (activeModule === moduleId) {
          setActiveModule('dashboard');
        }
        console.log(`Module "${moduleName}" deleted successfully`);
      } else {
        console.error('Failed to delete module');
      }
    } catch (error) {
      console.error('Error deleting module:', error);
    }
    setDeleteConfirmation(null);
  }, [activeModule, loadExistingModules]);

  // Drag and drop handlers
  const handleDragOver = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragOver(true);
  }, []);

  const handleDragLeave = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragOver(false);
  }, []);

  const handleDrop = useCallback(async (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragOver(false);

    const files = Array.from(e.dataTransfer.files);
    if (files.length > 0) {
      await handleFileUpload(files);
    }
  }, [handleFileUpload]);

  // Chart colors for consistency
  const chartColors = ['#3b82f6', '#8b5cf6', '#10b981', '#f59e0b', '#ef4444', '#6366f1'];

  // Memoize dashboard metrics for performance
  const dashboardMetrics = useMemo(() => {
    const baseMetrics = [
      {
        label: 'Monthly Revenue',
        value: realDashboardData?.monthlyRevenue ? `$${realDashboardData.monthlyRevenue.toLocaleString()}` : '$0',
        change: realDashboardData?.monthlyRevenue > 0 ? '+14.6%' : '+0%',
        icon: 'ðŸ’°',
        color: uiCustomization.accentColor,
        gradient: `linear-gradient(135deg, ${uiCustomization.accentColor}33, ${uiCustomization.accentColor}11)`
      },
      {
        label: 'Active Guards',
        value: realDashboardData?.activeGuards?.toString() || '0',
        change: realDashboardData?.activeGuards > 0 ? '+7.1%' : '+0%',
        icon: 'ðŸ‘®',
        color: uiCustomization.primaryColor,
        gradient: `linear-gradient(135deg, ${uiCustomization.primaryColor}33, ${uiCustomization.primaryColor}11)`
      },
      {
        label: 'Client Sites',
        value: realDashboardData?.totalSites?.toString() || '0',
        change: realDashboardData?.totalSites > 0 ? '+16.7%' : '+0%',
        icon: 'ðŸ¢',
        color: '#f59e0b',
        gradient: 'linear-gradient(135deg, rgba(245, 158, 11, 0.2), rgba(245, 158, 11, 0.1))'
      },
      {
        label: 'Total Clients',
        value: realDashboardData?.totalClients?.toString() || '0',
        change: realDashboardData?.totalClients > 0 ? '+4.3%' : '+0%',
        icon: 'ðŸ¤',
        color: uiCustomization.secondaryColor,
        gradient: `linear-gradient(135deg, ${uiCustomization.secondaryColor}33, ${uiCustomization.secondaryColor}11)`
      }
    ];
    return baseMetrics;
  }, [realDashboardData, uiCustomization]);

  // Enhanced performance data
  const performanceData = useMemo(() => {
    const baseData = [
      { name: 'Jan', revenue: 425000, guards: 89, sites: 12, efficiency: 94 },
      { name: 'Feb', revenue: 468000, guards: 92, sites: 14, efficiency: 91 },
      { name: 'Mar', revenue: 510000, guards: 96, sites: 15, efficiency: 96 },
      { name: 'Apr', revenue: 485000, guards: 94, sites: 16, efficiency: 89 },
      { name: 'May', revenue: 534000, guards: 98, sites: 18, efficiency: 93 }
    ];

    const currentMonth = {
      name: 'Current',
      revenue: realDashboardData?.monthlyRevenue || 612000,
      guards: realDashboardData?.activeGuards || 105,
      sites: realDashboardData?.totalSites || 21,
      efficiency: 97
    };

    return [...baseData, currentMonth];
  }, [realDashboardData]);

  const industryData = [
    { name: 'Security Services', value: 45, color: uiCustomization.primaryColor },
    { name: 'Healthcare', value: 25, color: uiCustomization.accentColor },
    { name: 'Manufacturing', value: 20, color: '#f59e0b' },
    { name: 'Retail', value: 10, color: '#ef4444' }
  ];

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
        e.preventDefault();
        setAiChatOpen(prev => !prev);
      }
      
      if (e.key === 'Escape') {
        if (aiChatOpen) {
          setAiChatOpen(false);
        } else if (deleteConfirmation) {
          setDeleteConfirmation(null);
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [aiChatOpen, deleteConfirmation]);

  // Drag and drop listeners
  useEffect(() => {
    const handleGlobalDragOver = (e) => {
      e.preventDefault();
      e.stopPropagation();
    };

    const handleGlobalDrop = (e) => {
      e.preventDefault();
      e.stopPropagation();
    };

    document.addEventListener('dragover', handleGlobalDragOver);
    document.addEventListener('drop', handleGlobalDrop);

    return () => {
      document.removeEventListener('dragover', handleGlobalDragOver);
      document.removeEventListener('drop', handleGlobalDrop);
    };
  }, []);

  // Module icon helper
  const getModuleIcon = (moduleId, moduleType, aiCore) => {
    if (moduleId === 'dashboard') return BarChart3;
    if (moduleId === 'business-setup') return MessageSquare;
