        return <AICoresControl />;
      default:
        if (currentModule && currentModule.type === 'ai-generated') {
          return <AIGeneratedModule module={currentModule} />;
        }
        return (
          <div style={{ 
            padding: '48px', 
            textAlign: 'center',
            background: uiCustomization.backgroundColor,
            minHeight: '100vh'
          }}>
            <h2 style={{ color: '#e2e8f0', fontSize: '28px', marginBottom: '16px' }}>
              ðŸš§ Module Not Found
            </h2>
            <p style={{ color: '#94a3b8', fontSize: '18px', marginBottom: '24px' }}>
              This module hasn't been built yet. Ask Enhanced Aetheris to create it for you!
            </p>
            <button
              onClick={() => setAiChatOpen(true)}
              style={{
                padding: '16px 32px',
                background: `linear-gradient(135deg, ${uiCustomization.primaryColor}, ${uiCustomization.secondaryColor})`,
                border: 'none',
                borderRadius: '8px',
                color: 'white',
                fontSize: '16px',
                cursor: 'pointer',
                fontWeight: '600'
              }}
            >
              ðŸ§  Ask Enhanced Aetheris to Build This
            </button>
          </div>
        );
    }
  };

  return (
    <div style={getContainerStyles()}
         onDragOver={handleDragOver}
         onDragLeave={handleDragLeave}
         onDrop={handleDrop}>
      
      <input
        ref={fileInputRef}
        type="file"
        accept=".csv,.xlsx,.xls,.json,.txt"
        style={{ display: 'none' }}
        onChange={(e) => {
          if (e.target.files && e.target.files[0]) {
            handleFileUpload([e.target.files[0]]);
            e.target.value = '';
          }
        }}
      />

      <ConnectionStatus />

      {uploadingFile && (
        <div style={{
          position: 'fixed',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          background: uiCustomization.cardBackground,
          border: `1px solid ${uiCustomization.borderColor}`,
          borderRadius: '12px',
          padding: '24px',
          zIndex: 1500,
          display: 'flex',
          alignItems: 'center',
          gap: '12px'
        }}>
          <Loader size={24} className="animate-spin" style={{ color: uiCustomization.primaryColor }} />
          <span style={{ color: uiCustomization.textColor }}>Analyzing file with AI...</span>
        </div>
      )}

      {/* Sidebar Navigation */}
      <div style={getSidebarStyles()}>
        <div style={{ 
          padding: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '16px' : '24px', 
          borderBottom: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'none' : `1px solid ${uiCustomization.borderColor}`,
          borderRight: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? `1px solid ${uiCustomization.borderColor}` : 'none',
          textAlign: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'left' : 'center',
          flexShrink: 0,
          minWidth: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '200px' : 'auto'
        }}>
          <h1 style={{ 
            fontSize: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '18px' : '24px', 
            fontWeight: 'bold', 
            color: uiCustomization.primaryColor,
            textShadow: `0 0 20px ${uiCustomization.primaryColor}50`,
            marginBottom: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '4px' : '8px'
          }}>
            ðŸ§  SentariOS
          </h1>
          <p style={{ color: '#94a3b8', fontSize: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '12px' : '14px' }}>
            12-Core AI Platform
          </p>
          {userName && (
            <div style={{ 
              marginTop: '8px',
              padding: '4px 8px',
              background: `${uiCustomization.accentColor}33`,
              borderRadius: '12px',
              color: uiCustomization.accentColor,
              fontSize: '12px'
            }}>
              Welcome, {userName}!
            </div>
          )}
          {companyName && (
            <div style={{ 
              marginTop: '4px',
              padding: '4px 8px',
              background: 'rgba(245, 158, 11, 0.2)',
              borderRadius: '12px',
              color: '#f59e0b',
              fontSize: '12px'
            }}>
              ðŸ¢ {companyName}
            </div>
          )}
        </div>

        <div style={{ 
          flex: 1, 
          padding: '16px',
          display: 'flex',
          flexDirection: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'row' : 'column',
          gap: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '8px' : '0',
          overflowY: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'hidden' : 'auto',
          overflowX: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'auto' : 'hidden'
        }}>
          {platformModules.filter(m => m.status === 'active').map(module => {
            const IconComponent = getModuleIcon(module.id, module.type, module.aiCore);
            return (
              <button
                key={module.id}
                onClick={() => setActiveModule(module.id)}
                style={{
                  width: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'auto' : '100%',
                  minWidth: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '120px' : 'auto',
                  padding: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '12px' : '16px',
                  marginBottom: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '0' : '8px',
                  background: activeModule === module.id 
                    ? `${uiCustomization.primaryColor}33` 
                    : 'transparent',
                  border: activeModule === module.id
                    ? `1px solid ${uiCustomization.primaryColor}80`
                    : '1px solid transparent',
                  borderRadius: '8px',
                  color: activeModule === module.id ? uiCustomization.primaryColor : '#94a3b8',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '12px',
                  fontSize: '14px',
                  fontWeight: '500',
                  transition: 'all 0.2s ease',
                  flexDirection: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'column' : 'row',
                  textAlign: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'center' : 'left'
                }}
                onMouseEnter={(e) => {
                  if (activeModule !== module.id) {
                    e.currentTarget.style.background = `${uiCustomization.primaryColor}20`;
                  }
                }}
                onMouseLeave={(e) => {
                  if (activeModule !== module.id) {
                    e.currentTarget.style.background = 'transparent';
                  }
                }}
              >
                <IconComponent size={20} />
                <span style={{ 
                  flex: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'none' : 1,
                  fontSize: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '12px' : '14px'
                }}>
                  {uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' 
                    ? module.name.split(' ').slice(0, 2).join(' ') 
                    : module.name}
                </span>
                {module.type === 'ai-generated' && (
                  <span style={{ 
                    background: `${uiCustomization.accentColor}50`,
                    padding: '2px 6px',
                    borderRadius: '4px',
                    fontSize: '10px'
                  }}>
                    AI
                  </span>
                )}
              </button>
            );
          })}
        </div>

        <div style={{ 
          padding: '16px', 
          borderTop: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? 'none' : `1px solid ${uiCustomization.borderColor}`,
          borderLeft: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? `1px solid ${uiCustomization.borderColor}` : 'none',
          textAlign: 'center',
          flexShrink: 0,
          minWidth: uiCustomization.sidebarPosition === 'top' || uiCustomization.sidebarPosition === 'bottom' ? '150px' : 'auto'
        }}>
          <div style={{ color: '#6b7280', fontSize: '12px', marginBottom: '4px' }}>
            ðŸš€ Enhanced AI System
          </div>
          <div style={{ 
            color: backendConnected ? uiCustomization.accentColor : '#ef4444', 
            fontSize: '12px', 
            fontWeight: '500' 
          }}>
            {backendConnected ? '12 Cores Online' : 'Backend Offline'}
          </div>
          <div style={{ color: uiCustomization.primaryColor, fontSize: '11px', marginTop: '2px' }}>
            Press Ctrl+K for AI chat
          </div>
        </div>
      </div>

      <div style={getMainContentStyles()}>
        {renderActiveModule()}
      </div>

      <button
        onClick={() => setAiChatOpen(!aiChatOpen)}
        style={{
          position: 'fixed',
          bottom: '24px',
          right: '24px',
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          background: aiChatOpen 
            ? 'linear-gradient(135deg, #ef4444, #dc2626)' 
            : `linear-gradient(135deg, ${uiCustomization.primaryColor}, ${uiCustomization.secondaryColor})`,
          border: 'none',
          color: 'white',
          fontSize: '24px',
          cursor: 'pointer',
          boxShadow: `0 8px 25px ${uiCustomization.primaryColor}40`,
          zIndex: 1000,
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.transform = 'scale(1.1)';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.transform = 'scale(1)';
        }}
        title={aiChatOpen ? 'Close Chat' : 'Open Enhanced AI Chat (Ctrl+K)'}
      >
        {aiChatOpen ? <X size={24} /> : 'ðŸ§ '}
      </button>

      {aiChatOpen && (
        <div style={{
          position: 'fixed',
          bottom: '100px',
          right: '24px',
          width: `${chatWidth}px`,
          height: `${chatHeight}px`,
          minWidth: '400px',
          maxWidth: '800px',
          minHeight: '400px',
          maxHeight: '80vh',
          background: 'rgba(15, 23, 42, 0.95)',
          backdropFilter: 'blur(20px)',
          border: `1px solid ${uiCustomization.borderColor}`,
          borderRadius: '12px',
          zIndex: 1000,
          boxShadow: '0 20px 50px rgba(0, 0, 0, 0.5)',
          display: 'flex',
          flexDirection: 'column',
          animation: 'slideUp 0.3s ease-out'
        }}>
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between', 
            padding: '20px',
            borderBottom: `1px solid ${uiCustomization.borderColor}`
          }}>
            <div>
              <h3 style={{ 
                color: uiCustomization.primaryColor, 
                fontSize: '18px', 
                fontWeight: '600', 
                margin: 0,
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}>
                ðŸ§  Enhanced Aetheris - 12 AI Cores
                {isProcessing && <Loader size={16} className="animate-spin" />}
              </h3>
              {userName && (
                <p style={{ color: '#94a3b8', fontSize: '12px', marginTop: '2px', margin: 0 }}>
                  Building for {userName} â€¢ {companyName || 'Your Company'} â€¢ All cores online
                </p>
              )}
            </div>
            <button
              onClick={() => setAiChatOpen(false)}
              style={{
                background: 'rgba(239, 68, 68, 0.2)',
                border: '1px solid rgba(239, 68, 68, 0.3)',
                borderRadius: '6px',
                color: '#ef4444',
                cursor: 'pointer',
                padding: '8px',
                transition: 'all 0.2s ease'
              }}
            >
              <X size={16} />
            </button>
          </div>
          
          <div 
            ref={chatMessagesRef}
            style={{
              flex: 1,
              padding: '16px',
              overflowY: 'auto',
              scrollBehavior: 'smooth'
            }}
          >
            {chatMessages.length === 0 && (
              <div style={{
                textAlign: 'center',
                color: '#94a3b8',
                fontSize: '14px',
                marginTop: '40px'
              }}>
                <div style={{ fontSize: '48px', marginBottom: '16px' }}>ðŸ§ </div>
                <p style={{ marginBottom: '8px' }}>Enhanced Aetheris with 12 specialized AI cores ready!</p>
                <div style={{ 
                  fontSize: '12px', 
                  marginTop: '12px',
                  display: 'flex',
                  flexDirection: 'column',
                  gap: '4px'
                }}>
                  <span>"Build all modules" â€¢ "My name is Tristen" â€¢ "Turn background pink"</span>
                  <span>"Add client McDonald's" â€¢ "Upload employee data" â€¢ "Move sidebar right"</span>
                </div>
              </div>
            )}
            
            {chatMessages.map((msg, index) => (
              <div key={index} style={{
                marginBottom: '16px',
                display: 'flex',
                justifyContent: msg.role === 'user' ? 'flex-end' : 'flex-start'
              }}>
                <div style={{
                  maxWidth: '85%',
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: msg.role === 'user' 
                    ? `${uiCustomization.primaryColor}50` 
                    : `${uiCustomization.secondaryColor}50`,
                  border: `1px solid ${msg.role === 'user' 
                    ? `${uiCustomization.primaryColor}80` 
                    : `${uiCustomization.secondaryColor}80`}`,
                  color: '#e2e8f0',
                  fontSize: '14px',
                  lineHeight: '1.5',
                  whiteSpace: 'pre-wrap',
                  wordWrap: 'break-word'
                }}>
                  {msg.content}
                </div>
              </div>
            ))}
            
            {isProcessing && (
              <div style={{
                display: 'flex',
                justifyContent: 'flex-start',
                marginBottom: '16px'
              }}>
                <div style={{
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: `${uiCustomization.secondaryColor}50`,
                  border: `1px solid ${uiCustomization.secondaryColor}80`,
                  color: '#e2e8f0',
                  fontSize: '14px',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}>
                  <Loader size={16} className="animate-spin" />
                  Enhanced Aetheris processing across 12 cores...
                </div>
              </div>
            )}
          </div>

          <div style={{
            padding: '16px',
            borderTop: `1px solid ${uiCustomization.borderColor}`
          }}>
            <div style={{ display: 'flex', gap: '8px', marginBottom: '8px' }}>
              <textarea
                ref={chatInputRef}
                value={currentMessage}
                onChange={(e) => setCurrentMessage(e.target.value)}
                onKeyPress={handleChatKeyPress}
                placeholder="Ask me anything: 'Build modules', 'Turn background pink', 'Move sidebar right', 'Upload CSV data'..."
                style={{
                  flex: 1,
                  padding: '12px',
                  background: 'rgba(15, 23, 42, 0.6)',
                  border: `1px solid ${uiCustomization.borderColor}`,
                  borderRadius: '6px',
                  color: '#e2e8f0',
                  fontSize: '14px',
                  resize: 'vertical',
                  minHeight: '44px',
                  maxHeight: '120px',
                  fontFamily: 'inherit',
                  outline: 'none'
                }}
                disabled={isProcessing}
                rows="1"
              />
              <button
                onClick={processChatMessage}
                disabled={isProcessing || !currentMessage.trim() || !backendConnected}
                style={{
                  padding: '12px 16px',
                  background: isProcessing || !currentMessage.trim() || !backendConnected
                    ? 'rgba(107, 114, 128, 0.3)' 
                    : `linear-gradient(135deg, ${uiCustomization.primaryColor}, ${uiCustomization.secondaryColor})`,
                  border: 'none',
                  borderRadius: '6px',
                  color: 'white',
                  fontSize: '14px',
                  cursor: isProcessing || !currentMessage.trim() || !backendConnected ? 'not-allowed' : 'pointer',
                  fontWeight: '500',
                  minWidth: '60px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}
              >
                {isProcessing ? <Loader size={16} className="animate-spin" /> : 'Send'}
              </button>
            </div>
            
            <div style={{ 
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              fontSize: '12px',
              color: '#6b7280'
            }}>
              <span>12-Core AI + File Upload + UI Customization + Real Data</span>
              <span>Shift+Enter for new line â€¢ Esc to close</span>
            </div>
          </div>
        </div>
      )}

      <DeleteConfirmationDialog />

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        
        @keyframes fadeIn {
          0% { opacity: 0; }
          100% { opacity: 1; }
        }
        
        @keyframes slideUp {
          0% { transform: translateY(20px); opacity: 0; }
          100% { transform: translateY(0); opacity: 1; }
        }
        
        .animate-spin {
          animation: spin 1s linear infinite;
        }
        
        *::-webkit-scrollbar {
          width: 8px;
        }
        
        *::-webkit-scrollbar-track {
          background: rgba(55, 65, 81, 0.3);
          border-radius: 4px;
        }
        
        *::-webkit-scrollbar-thumb {
          background: ${uiCustomization.primaryColor}80;
          border-radius: 4px;
        }
        
        *::-webkit-scrollbar-thumb:hover {
          background: ${uiCustomization.primaryColor};
        }

        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.5; }
        }
        
        .pulse {
          animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
        }

        .card-hover {
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        
        .card-hover:hover {
          transform: translateY(-4px);
          box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
        }
      `}</style>
    </div>
  );
};

export default SentariOS;
