import React, { useState, useEffect, useRef } from 'react';
import { Calendar, Users, MapPin, BarChart3, Clock, Shield, Zap, DollarSign, TrendingUp, Activity, X, MessageSquare, Wrench, Code, Settings } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  // Core Platform State
  const [activeModule, setActiveModule] = useState('dashboard');
  const [platformModules, setPlatformModules] = useState([]);
  const [aiChatOpen, setAiChatOpen] = useState(false);
  const [buildHistory, setBuildHistory] = useState([]);
  const [setupComplete, setSetupComplete] = useState(false);
  
  // AI Chat State (Fixed cursor jumping)
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [userName, setUserName] = useState(null);
  const chatInputRef = useRef(null);

  // Interview State
  const [interviewActive, setInterviewActive] = useState(false);
  const [interviewStep, setInterviewStep] = useState(0);
  const [interviewData, setInterviewData] = useState({});

  // ================================
  // PERSISTENT MODULE LOADING
  // ================================

  // Load existing modules on startup
  useEffect(() => {
    loadExistingModules();
    initializeDefaultModules();
  }, []);

  const loadExistingModules = async () => {
    try {
      console.log('📂 Loading existing modules...');
      const response = await fetch('http://localhost:3000/api/modules');
      const data = await response.json();
      
      if (data.success) {
        // Load built modules from server
        const builtModules = data.modules || [];
        const serverUserName = data.userName;
        
        console.log(`✅ Loaded ${builtModules.length} persistent modules`);
        
        // Convert built modules to platform modules format
        const convertedModules = builtModules.map(module => ({
          id: module.id,
          name: module.name,
          type: 'ai-generated',
          status: 'active',
          description: module.description,
          features: module.features || [],
          created: module.created,
          hasGPS: module.hasGPS || false,
          originalType: module.type
        }));
        
        // Add to existing platform modules (after default ones)
        setPlatformModules(prev => {
          const coreModules = prev.filter(m => m.type === 'core' || m.type === 'ai' || m.type === 'system');
          return [...coreModules, ...convertedModules];
        });
        
        // Set user name if available
        if (serverUserName) {
          setUserName(serverUserName);
        }
        
        console.log(`👤 User: ${serverUserName || 'Anonymous'}`);
      }
    } catch (error) {
      console.error('Failed to load existing modules:', error);
    }
  };

  const initializeDefaultModules = () => {
    setPlatformModules(prev => {
      // Only add defaults if not already present
      const hasDefaults = prev.some(m => m.id === 'dashboard');
      if (hasDefaults) return prev;
      
      return [
        { 
          id: 'dashboard', 
          name: 'Dashboard', 
          type: 'core', 
          status: 'active',
          description: 'Executive overview and analytics'
        },
        { 
          id: 'business-setup', 
          name: 'Business Setup', 
          type: 'ai', 
          status: 'active',
          description: 'Aetheris interview to customize platform'
        },
        { 
          id: 'module-library', 
          name: 'Module Library', 
          type: 'system', 
          status: 'active',
          description: 'Manage all platform modules'
        }
      ];
    });

    // Start interview if first time setup and no user name
    if (!setupComplete && !userName) {
      setChatMessages([{
        id: 1,
        sender: 'aetheris',
        message: `🧠 **Welcome to Sentari OS!**

I'm Aetheris, your AI business architect. I'll help you build a platform perfectly tailored to your business.

Let's start with a few questions to understand your needs:

**Question 1:** What industry are you in? (Security, Healthcare, Manufacturing, Retail, etc.)`
      }]);
      setInterviewActive(true);
    }
  };

  // Sample analytics data (your beautiful dashboard data)
  const performanceData = [
    { name: 'Jan', revenue: 425000, guards: 89, sites: 12, efficiency: 94 },
    { name: 'Feb', revenue: 468000, guards: 92, sites: 14, efficiency: 91 },
    { name: 'Mar', revenue: 510000, guards: 96, sites: 15, efficiency: 96 },
    { name: 'Apr', revenue: 485000, guards: 94, sites: 16, efficiency: 89 },
    { name: 'May', revenue: 534000, guards: 98, sites: 18, efficiency: 93 },
    { name: 'Jun', revenue: 612000, guards: 105, sites: 21, efficiency: 97 }
  ];

  const industryData = [
    { name: 'Security Services', value: 45, color: '#3b82f6' },
    { name: 'Healthcare', value: 25, color: '#10b981' },
    { name: 'Manufacturing', value: 20, color: '#f59e0b' },
    { name: 'Retail', value: 10, color: '#ef4444' }
  ];

  // Interview Questions
  const interviewQuestions = [
    "What industry are you in? (Security, Healthcare, Manufacturing, Retail, etc.)",
    "How many employees do you currently manage?",
    "What software do you currently use for business operations?",
    "What are your biggest daily operational challenges?",
    "Do you handle scheduling, payroll, invoicing, or reporting manually?",
    "Do you work with multiple client sites or focus on one location?",
    "Do you use vendors or subcontractors in your business?",
    "What reports do you generate most often, and who receives them?",
    "Do your clients need access to any information or reports?",
    "Would you like your company logo on all forms and documents?"
  ];

  // ================================
  // ENHANCED AI CHAT PROCESSING
  // ================================

  // Process AI Chat Messages (Fixed to prevent cursor jumping)
  const processChatMessage = async () => {
    if (!currentMessage.trim()) return;
    
    const userMessage = currentMessage;
    setCurrentMessage(''); // Clear immediately to prevent jumping
    setIsProcessing(true);
    
    // Add user message to chat
    const newUserMessage = {
      id: Date.now(),
      sender: 'user',
      message: userMessage
    };
    setChatMessages(prev => [...prev, newUserMessage]);
    
    try {
      let endpoint = '/api/aetheris/chat';
      let requestBody = { message: userMessage };
      
      // If interview is active, process as interview response
      if (interviewActive && interviewStep < interviewQuestions.length) {
        endpoint = '/api/aetheris/interview';
        requestBody = {
          step: interviewStep,
          answer: userMessage,
          previousData: interviewData
        };
      }
      
      const response = await fetch(`http://localhost:3000${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody)
      });
      
      const result = await response.json();
      
      // Add AI response to chat
      const aiMessage = {
        id: Date.now() + 1,
        sender: 'aetheris',
        message: result.response
      };
      setChatMessages(prev => [...prev, aiMessage]);
      
      // Handle interview progression
      if (result.interviewData) {
        setInterviewData(prev => ({ ...prev, ...result.interviewData }));
        setInterviewStep(prev => prev + 1);
        
        if (result.setupComplete) {
          setInterviewActive(false);
          setSetupComplete(true);
          
          // Add generated modules to platform
          if (result.generatedModules) {
            setPlatformModules(prev => [...prev, ...result.generatedModules]);
          }
        }
      }
      
      // Handle NEW MODULE BUILDING (Enhanced)
      if (result.newModule) {
        const newPlatformModule = {
          id: result.newModule.id,
          name: result.newModule.name,
          type: 'ai-generated',
          status: 'active',
          description: result.newModule.description,
          features: result.newModule.features || [],
          created: result.newModule.created,
          hasGPS: result.newModule.hasGPS || false,
          originalType: result.newModule.type
        };
        
        // Add to platform modules
        setPlatformModules(prev => {
          // Check if module already exists
          const exists = prev.some(m => m.id === newPlatformModule.id);
          if (exists) return prev;
          
          return [...prev, newPlatformModule];
        });
        
        // Add to build history
        setBuildHistory(prev => [...prev, {
          request: userMessage,
          module: newPlatformModule,
          timestamp: new Date().toISOString()
        }]);
        
        console.log('🆕 New module built:', result.newModule.name);
      }
      
      // Update user name if learned
      if (result.userName && result.userName !== userName) {
        setUserName(result.userName);
        console.log('👤 User name updated:', result.userName);
      }
      
    } catch (error) {
      console.error('Chat error:', error);
      const errorMessage = {
        id: Date.now() + 1,
        sender: 'aetheris',
        message: '❌ **Connection Error**: Could not reach Aetheris backend. Make sure your server is running on port 3000!'
      };
      setChatMessages(prev => [...prev, errorMessage]);
    }
    
    setIsProcessing(false);
  };

  // Handle Enter key (without cursor jumping)
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      processChatMessage();
    }
  };

  // Delete module with server sync
  const deleteModule = async (moduleId) => {
    try {
      const response = await fetch(`http://localhost:3000/api/modules/${moduleId}`, {
        method: 'DELETE'
      });
      
      const data = await response.json();
      if (data.success) {
        // Remove from local state
        setPlatformModules(prev => prev.filter(m => m.id !== moduleId));
        
        console.log('✅ Module deleted:', moduleId);
        
        // If we're viewing the deleted module, go back to dashboard
        if (activeModule === moduleId) {
          setActiveModule('dashboard');
        }
      }
    } catch (error) {
      console.error('Failed to delete module:', error);
    }
  };

  // Business Setup Interview Component (keeping your existing design)
  const BusinessSetup = () => (
    <div style={{ 
      padding: '24px',
      background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(147, 51, 234, 0.1))',
      minHeight: '100vh'
    }}>
      <div style={{
        background: 'rgba(15, 23, 42, 0.8)',
        backdropFilter: 'blur(12px)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '12px',
        padding: '32px',
        maxWidth: '800px',
        margin: '0 auto'
      }}>
        <div style={{ textAlign: 'center', marginBottom: '32px' }}>
          <h1 style={{ 
            fontSize: '32px', 
            fontWeight: 'bold', 
            color: '#3b82f6',
            marginBottom: '8px',
            textShadow: '0 0 20px rgba(59, 130, 246, 0.5)'
          }}>
            🧠 Aetheris Business Setup
          </h1>
          <p style={{ color: '#94a3b8', fontSize: '18px' }}>
            Let me understand your business and build a custom platform for you
          </p>
        </div>

        {/* Interview Progress */}
        {interviewActive && (
          <div style={{ marginBottom: '24px' }}>
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              marginBottom: '8px'
            }}>
              <span style={{ color: '#94a3b8', fontSize: '14px' }}>
                Interview Progress
              </span>
              <span style={{ color: '#3b82f6', fontSize: '14px', fontWeight: '500' }}>
                {interviewStep + 1} / {interviewQuestions.length}
              </span>
            </div>
            <div style={{
              width: '100%',
              height: '6px',
              background: 'rgba(59, 130, 246, 0.2)',
              borderRadius: '3px',
              overflow: 'hidden'
            }}>
              <div style={{
                width: `${((interviewStep + 1) / interviewQuestions.length) * 100}%`,
                height: '100%',
                background: 'linear-gradient(90deg, #3b82f6, #8b5cf6)',
                transition: 'width 0.5s ease'
              }} />
            </div>
          </div>
        )}

        {/* Chat Interface */}
        <div style={{
          background: 'rgba(15, 23, 42, 0.6)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '8px',
          height: '400px',
          marginBottom: '16px',
          display: 'flex',
          flexDirection: 'column'
        }}>
          {/* Messages */}
          <div style={{
            flex: 1,
            padding: '16px',
            overflowY: 'auto'
          }}>
            {chatMessages.map(msg => (
              <div key={msg.id} style={{
                marginBottom: '16px',
                display: 'flex',
                justifyContent: msg.sender === 'user' ? 'flex-end' : 'flex-start'
              }}>
                <div style={{
                  maxWidth: '80%',
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'rgba(139, 92, 246, 0.3)',
                  border: `1px solid ${msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.5)' 
                    : 'rgba(139, 92, 246, 0.5)'}`,
                  color: '#e2e8f0',
                  fontSize: '14px',
                  lineHeight: '1.4',
                  whiteSpace: 'pre-wrap'
                }}>
                  {msg.message}
                </div>
              </div>
            ))}
            {isProcessing && (
              <div style={{
                display: 'flex',
                justifyContent: 'flex-start',
                marginBottom: '16px'
              }}>
                <div style={{
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: 'rgba(139, 92, 246, 0.3)',
                  border: '1px solid rgba(139, 92, 246, 0.5)',
                  color: '#e2e8f0',
                  fontSize: '14px'
                }}>
                  🧠 Aetheris is thinking...
                </div>
              </div>
            )}
          </div>

          {/* Input Area */}
