            value: '$612,000', 
            change: '+14.6%',
            icon: '💰', 
            color: '#22c55e',
            gradient: 'linear-gradient(135deg, rgba(34, 197, 94, 0.2), rgba(34, 197, 94, 0.1))'
          },
          { 
            label: 'Active Guards', 
            value: '105', 
            change: '+7.1%',
