              <div key={index} style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                padding: '8px 0',
                borderBottom: index < industryData.length - 1 ? '1px solid rgba(59, 130, 246, 0.1)' : 'none'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                  <div style={{
                    width: '12px',
                    height: '12px',
                    borderRadius: '50%',
                    background: item.color
                  }} />
                  <span style={{ color: '#e2e8f0', fontSize: '14px' }}>{item.name}</span>
                </div>
                <span style={{ color: '#94a3b8', fontSize: '14px', fontWeight: '500' }}>
                  {item.value}%
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.8)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '16px',
        padding: '28px',
        backdropFilter: 'blur(12px)'
      }}>
        <h2 style={{ color: '#e2e8f0', fontSize: '22px', marginBottom: '20px', fontWeight: '600' }}>
          🚀 AI-Powered Quick Actions
        </h2>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px' }}>
          {[
            { label: 'Setup New Business', action: () => setActiveModule('business-setup'), icon: '🎤', color: '#8b5cf6' },
            { label: 'Ask Aetheris Anything', action: () => setAiChatOpen(true), icon: '🧠', color: '#3b82f6' },
            { label: 'View All Modules', action: () => setActiveModule('module-library'), icon: '📦', color: '#10b981' },
            { label: 'Platform Settings', action: () => alert('Settings coming soon!'), icon: '⚙️', color: '#f59e0b' }
          ].map((action, index) => (
            <button
              key={index}
              onClick={action.action}
              style={{
                padding: '20px',
                background: `linear-gradient(135deg, ${action.color}20, ${action.color}10)`,
                border: `1px solid ${action.color}30`,
                borderRadius: '12px',
