                        background: 'rgba(239, 68, 68, 0.2)',
                        border: '1px solid rgba(239, 68, 68, 0.3)',
                        borderRadius: '6px',
                        color: '#ef4444',
                        padding: '6px 8px',
                        fontSize: '12px',
                        cursor: 'pointer',
                        fontWeight: '500'
