                      onClick={() => {
                        setAiChatOpen(true);
                        setCurrentMessage(`Enhance my ${module.name} module with additional features`);
                      }}
                      style={{
                        padding: '10px 16px',
                        background: 'rgba(139, 92, 246, 0.2)',
                        border: '1px solid rgba(139, 92, 246, 0.3)',
                        borderRadius: '8px',
                        color: '#8b5cf6',
                        fontSize: '14px',
                        cursor: 'pointer',
                        fontWeight: '500'
                      }}
                    >
                      Enhance
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

