                <p style={{ color: '#94a3b8', fontSize: '14px', marginBottom: '20px', lineHeight: '1.5' }}>
                  {module.description}
                </p>
                <button
                  onClick={() => setActiveModule(module.id)}
                  style={{
                    width: '100%',
                    padding: '10px 16px',
                    background: 'rgba(59, 130, 246, 0.2)',
                    border: '1px solid rgba(59, 130, 246, 0.3)',
                    borderRadius: '8px',
                    color: '#3b82f6',
                    fontSize: '14px',
                    cursor: 'pointer',
                    fontWeight: '500'
                  }}
                >
                  Open Module
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Build History */}
        {buildHistory.length > 0 && (
          <div>
            <h2 style={{ color: '#8b5cf6', fontSize: '24px', marginBottom: '16px' }}>
              🏗️ AI Build History ({buildHistory.length})
            </h2>
            <div style={{ display: 'grid', gap: '12px' }}>
              {buildHistory.slice(-5).reverse().map((build, index) => (
                <div key={index} style={{
                  padding: '20px',
                  background: 'rgba(15, 23, 42, 0.6)',
                  border: '1px solid rgba(139, 92, 246, 0.3)',
                  borderRadius: '12px',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center'
                }}>
                  <div>
                    <div style={{ color: '#e2e8f0', fontWeight: '600', fontSize: '16px' }}>
                      🤖 {build.module?.name || 'Custom Module'}
                    </div>
                    <div style={{ color: '#94a3b8', fontSize: '14px', marginTop: '4px' }}>
                      "{build.request}"
                    </div>
                    <div style={{ color: '#6b7280', fontSize: '12px', marginTop: '4px' }}>
                      Built: {new Date(build.timestamp).toLocaleString()}
                    </div>
                  </div>
                  <button
                    onClick={() => setActiveModule(build.module?.id)}
                    style={{
                      padding: '10px 16px',
                      background: 'rgba(139, 92, 246, 0.2)',
                      border: '1px solid rgba(139, 92, 246, 0.3)',
                      borderRadius: '8px',
                      color: '#8b5cf6',
                      fontSize: '14px',
                      cursor: 'pointer',
                      fontWeight: '500'
                    }}
                  >
                    Open
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Empty State */}
        {aiGeneratedModules.length === 0 && (
          <div style={{
            textAlign: 'center',
            padding: '48px',
            background: 'rgba(15, 23, 42, 0.6)',
            border: '2px dashed rgba(59, 130, 246, 0.3)',
            borderRadius: '12px'
          }}>
            <div style={{ fontSize: '48px', marginBottom: '16px' }}>🤖</div>
            <h2 style={{ color: '#e2e8f0', fontSize: '24px', marginBottom: '8px' }}>
              No AI Modules Built Yet
            </h2>
            <p style={{ color: '#94a3b8', fontSize: '16px', marginBottom: '24px' }}>
              Chat with Aetheris to build your first custom business module!
            </p>
            <button
              onClick={() => setAiChatOpen(true)}
