        {module.features && module.features.length > 0 && (
          <div style={{ marginBottom: '32px' }}>
            <h3 style={{ color: '#22c55e', fontSize: '20px', marginBottom: '16px' }}>
              ✨ AI-Generated Features
            </h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '12px' }}>
              {module.features.map((feature, index) => (
                <div key={index} style={{
                  padding: '16px',
                  background: 'rgba(34, 197, 94, 0.1)',
                  border: '1px solid rgba(34, 197, 94, 0.3)',
                  borderRadius: '8px',
                  color: '#22c55e'
                }}>
                  ✅ {feature.replace(/_/g, ' ').toUpperCase()}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* GPS Status */}
        {module.hasGPS && (
          <div style={{
            padding: '16px',
            background: 'rgba(245, 158, 11, 0.1)',
            border: '1px solid rgba(245, 158, 11, 0.3)',
            borderRadius: '8px',
            marginBottom: '24px'
          }}>
            <h3 style={{ color: '#f59e0b', marginBottom: '8px' }}>📍 GPS Tracking Enabled</h3>
            <p style={{ color: '#94a3b8', fontSize: '14px' }}>
              This module includes GPS location tracking capabilities that can be configured per user or location.
            </p>
          </div>
        )}

        <div style={{
          padding: '20px',
          background: 'rgba(59, 130, 246, 0.1)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '8px',
          marginBottom: '24px'
        }}>
          <h3 style={{ color: '#3b82f6', marginBottom: '12px' }}>🧠 Aetheris Intelligence</h3>
          <p style={{ color: '#94a3b8', fontSize: '14px' }}>
            This module was generated by Enhanced Aetheris with persistent storage. 
            It's saved permanently and will remain in your platform until you delete it.
            Ask Aetheris to enhance it with additional features or integrations.
          </p>
        </div>

        <div style={{ display: 'flex', gap: '16px', justifyContent: 'center' }}>
          <button
            onClick={() => setAiChatOpen(true)}
            style={{
              padding: '16px 32px',
              background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
              border: 'none',
              borderRadius: '8px',
              color: 'white',
              fontSize: '16px',
              cursor: 'pointer',
              fontWeight: '600'
            }}
          >
            🧠 Enhance This Module
          </button>
          <button
            onClick={() => setActiveModule('dashboard')}
            style={{
              padding: '16px 32px',
              background: 'rgba(59, 130, 246, 0.2)',
              border: '1px solid rgba(59, 130, 246, 0.3)',
              borderRadius: '8px',
              color: '#3b82f6',
              fontSize: '16px',
              cursor: 'pointer',
              fontWeight: '600'
            }}
          >
            ← Back to Dashboard
          </button>
        </div>
      </div>
    </div>
  );

  // Render active module
  const renderActiveModule = () => {
    const currentModule = platformModules.find(m => m.id === activeModule);
    
    switch (activeModule) {
      case 'dashboard':
        return <Dashboard />;
      case 'business-setup':
        return <BusinessSetup />;
      case 'module-library':
        return <ModuleLibrary />;
      default:
        if (currentModule && currentModule.type === 'ai-generated') {
          return <AIGeneratedModule module={currentModule} />;
        }
        return (
          <div style={{ 
            padding: '48px', 
            textAlign: 'center',
            background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(147, 51, 234, 0.1))',
            minHeight: '100vh'
          }}>
            <h2 style={{ color: '#e2e8f0', fontSize: '28px', marginBottom: '16px' }}>
              🚧 Module Not Found
            </h2>
            <p style={{ color: '#94a3b8', fontSize: '18px', marginBottom: '24px' }}>
              This module hasn't been built yet. Ask Aetheris to create it for you!
            </p>
            <button
              onClick={() => setAiChatOpen(true)}
              style={{
                padding: '16px 32px',
                background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                border: 'none',
                borderRadius: '8px',
                color: 'white',
                fontSize: '16px',
                cursor: 'pointer',
                fontWeight: '600'
              }}
            >
              🧠 Ask Aetheris to Build This
            </button>
          </div>
        );
    }
  };

  // Get icon for module
  const getModuleIcon = (moduleId, moduleType) => {
    if (moduleId === 'dashboard') return BarChart3;
    if (moduleId === 'business-setup') return MessageSquare;
    if (moduleId === 'module-library') return Code;
    if (moduleId === 'billing-invoicing') return DollarSign;
    if (moduleId === 'inventory-management') return MapPin;
    if (moduleId === 'hr-payroll') return Users;
    if (moduleId === 'client-portal') return Activity;
    if (moduleType === 'ai') return Wrench;
    if (moduleType === 'ai-generated') return Zap;
    return Shield;
  };

  return (
    <div style={{ 
      display: 'flex', 
      height: '100vh', 
      background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #0f172a 100%)',
      fontFamily: 'system-ui, -apple-system, sans-serif'
    }}>
      {/* Sidebar Navigation */}
      <div style={{
        width: '280px',
        background: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(20px)',
        borderRight: '1px solid rgba(59, 130, 246, 0.2)',
        display: 'flex',
        flexDirection: 'column',
        boxShadow: '4px 0 20px rgba(0, 0, 0, 0.3)'
      }}>
        {/* Header */}
        <div style={{ 
          padding: '24px', 
          borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
          textAlign: 'center'
        }}>
          <h1 style={{ 
            fontSize: '24px', 
            fontWeight: 'bold', 
            color: '#3b82f6',
            textShadow: '0 0 20px rgba(59, 130, 246, 0.5)',
            marginBottom: '8px'
          }}>
            🧠 Sentari OS
          </h1>
          <p style={{ color: '#94a3b8', fontSize: '14px' }}>
            AI-Powered Business Platform
          </p>
          {userName && (
            <div style={{ 
              marginTop: '8px',
              padding: '4px 8px',
              background: 'rgba(34, 197, 94, 0.2)',
              borderRadius: '12px',
              color: '#22c55e',
              fontSize: '12px'
            }}>
              Welcome, {userName}!
            </div>
          )}
        </div>

        {/* Navigation */}
        <div style={{ flex: 1, padding: '16px' }}>
          {platformModules.filter(m => m.status === 'active').map(module => {
            const IconComponent = getModuleIcon(module.id, module.type);
            return (
              <button
                key={module.id}
                onClick={() => setActiveModule(module.id)}
                style={{
                  width: '100%',
                  padding: '16px',
                  marginBottom: '8px',
                  background: activeModule === module.id 
                    ? 'rgba(59, 130, 246, 0.2)' 
                    : 'transparent',
                  border: activeModule === module.id
                    ? '1px solid rgba(59, 130, 246, 0.5)'
                    : '1px solid transparent',
                  borderRadius: '8px',
                  color: activeModule === module.id ? '#3b82f6' : '#94a3b8',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '12px',
                  textAlign: 'left',
                  fontSize: '14px',
                  fontWeight: '500',
                  transition: 'all 0.2s ease'
                }}
                onMouseEnter={(e) => {
                  if (activeModule !== module.id) {
                    e.target.style.background = 'rgba(59, 130, 246, 0.1)';
                    e.target.style.borderColor = 'rgba(59, 130, 246, 0.3)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (activeModule !== module.id) {
                    e.target.style.background = 'transparent';
                    e.target.style.borderColor = 'transparent';
                  }
                }}
              >
                <IconComponent size={20} />
                <span>{module.name}</span>
                {module.type === 'ai-generated' && (
                  <span style={{ 
                    background: 'rgba(34, 197, 94, 0.3)',
                    padding: '2px 6px',
                    borderRadius: '4px',
                    fontSize: '10px',
                    marginLeft: 'auto'
                  }}>
                    AI
                  </span>
                )}
              </button>
            );
          })}
        </div>

        {/* Footer */}
        <div style={{ 
          padding: '16px', 
          borderTop: '1px solid rgba(59, 130, 246, 0.2)',
          textAlign: 'center'
        }}>
          <div style={{ color: '#6b7280', fontSize: '12px' }}>
            🚀 Powered by Enhanced Aetheris
          </div>
          <div style={{ color: '#22c55e', fontSize: '12px', fontWeight: '500' }}>
            Persistent Storage Active
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div style={{ flex: 1, overflow: 'auto' }}>
        {renderActiveModule()}
      </div>

      {/* Sticky AI Chat Button (Fixed) */}
      <button
        onClick={() => setAiChatOpen(!aiChatOpen)}
        style={{
          position: 'fixed',
          bottom: '24px',
          right: '24px',
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
          border: 'none',
          color: 'white',
          fontSize: '24px',
          cursor: 'pointer',
          boxShadow: '0 8px 25px rgba(59, 130, 246, 0.4)',
          zIndex: 1000,
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => {
          e.target.style.transform = 'scale(1.1)';
          e.target.style.boxShadow = '0 12px 35px rgba(59, 130, 246, 0.6)';
        }}
        onMouseLeave={(e) => {
          e.target.style.transform = 'scale(1)';
          e.target.style.boxShadow = '0 8px 25px rgba(59, 130, 246, 0.4)';
        }}
      >
        🧠
      </button>

      {/* Sticky AI Chat Panel (Fixed cursor jumping) */}
      {aiChatOpen && (
        <div style={{
          position: 'fixed',
          bottom: '100px',
          right: '24px',
          width: '450px',
          height: '600px',
          background: 'rgba(15, 23, 42, 0.95)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '12px',
          zIndex: 1000,
          boxShadow: '0 20px 50px rgba(0, 0, 0, 0.5)',
          display: 'flex',
          flexDirection: 'column'
        }}>
          {/* Header */}
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between', 
            padding: '20px',
            borderBottom: '1px solid rgba(59, 130, 246, 0.3)'
          }}>
            <div>
              <h3 style={{ color: '#3b82f6', fontSize: '18px', fontWeight: '600' }}>
                🧠 Chat with Aetheris
              </h3>
              {userName && (
                <p style={{ color: '#94a3b8', fontSize: '12px', marginTop: '2px' }}>
                  Building for {userName}
                </p>
              )}
            </div>
            <button
              onClick={() => setAiChatOpen(false)}
              style={{
                background: 'none',
                border: 'none',
                color: '#94a3b8',
                cursor: 'pointer',
                padding: '4px'
              }}
            >
              <X size={20} />
            </button>
          </div>
          
          {/* Messages */}
          <div style={{
            flex: 1,
            padding: '16px',
            overflowY: 'auto'
          }}>
            {chatMessages.length === 0 && (
              <div style={{
                textAlign: 'center',
                color: '#94a3b8',
                fontSize: '14px',
                marginTop: '40px'
              }}>
                <div style={{ fontSize: '48px', marginBottom: '16px' }}>🧠</div>
                <p>Ask me anything about your business:</p>
                <p style={{ fontSize: '12px', marginTop: '8px' }}>
                  "Build billing module" • "How many guards do we have?" • "Create inventory system"
                </p>
              </div>
            )}
            
            {chatMessages.map(msg => (
              <div key={msg.id} style={{
                marginBottom: '16px',
                display: 'flex',
                justifyContent: msg.sender === 'user' ? 'flex-end' : 'flex-start'
              }}>
                <div style={{
                  maxWidth: '85%',
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'rgba(139, 92, 246, 0.3)',
                  border: `1px solid ${msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.5)' 
                    : 'rgba(139, 92, 246, 0.5)'}`,
                  color: '#e2e8f0',
                  fontSize: '14px',
                  lineHeight: '1.5',
                  whiteSpace: 'pre-wrap'
                }}>
                  {msg.message}
                </div>
              </div>
            ))}
            
            {isProcessing && (
              <div style={{
                display: 'flex',
                justifyContent: 'flex-start',
                marginBottom: '16px'
              }}>
                <div style={{
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: 'rgba(139, 92, 246, 0.3)',
                  border: '1px solid rgba(139, 92, 246, 0.5)',
                  color: '#e2e8f0',
                  fontSize: '14px',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}>
                  <div style={{
                    width: '16px',
                    height: '16px',
                    border: '2px solid rgba(255,255,255,0.3)',
                    borderRadius: '50%',
                    borderTopColor: 'white',
                    animation: 'spin 1s linear infinite'
                  }}></div>
                  Aetheris is thinking...
                </div>
              </div>
            )}
          </div>

          {/* Input Area (Fixed cursor jumping) */}
          <div style={{
            padding: '16px',
            borderTop: '1px solid rgba(59, 130, 246, 0.3)'
          }}>
            <div style={{ display: 'flex', gap: '8px' }}>
              <input
                key="ai-chat-input" // Force React to treat as static
                ref={chatInputRef}
                value={currentMessage}
                onChange={(e) => setCurrentMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask me anything: 'Build billing module', 'How many guards?', 'Create forms'..."
                style={{
                  flex: 1,
                  padding: '12px',
                  background: 'rgba(15, 23, 42, 0.6)',
                  border: '1px solid rgba(59, 130, 246, 0.3)',
                  borderRadius: '6px',
                  color: '#e2e8f0',
                  fontSize: '14px'
                }}
                disabled={isProcessing}
              />
              <button
                onClick={processChatMessage}
                disabled={isProcessing || !currentMessage.trim()}
                style={{
                  padding: '12px 16px',
                  background: isProcessing 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                  border: 'none',
                  borderRadius: '6px',
                  color: 'white',
                  fontSize: '14px',
                  cursor: isProcessing ? 'not-allowed' : 'pointer',
                  fontWeight: '500'
                }}
              >
                {isProcessing ? '⏳' : '🚀'}
              </button>
            </div>
            
            <div style={{ 
              marginTop: '8px', 
              textAlign: 'center',
              fontSize: '12px',
              color: '#6b7280'
            }}>
              💡 Modules save permanently - they survive browser refresh!
            </div>
          </div>
        </div>
      )}

      {/* Spinning animation */}
      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default SentariOS;
