import React, { useState, useEffect, useRef, useMemo } from 'react';
import { Calendar, Users, MapPin, BarChart3, Clock, Shield, Zap, DollarSign, TrendingUp, Activity, X, MessageSquare, Wrench, Code, Settings } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  // Core Platform State
  const [activeModule, setActiveModule] = useState('dashboard');
  const [platformModules, setPlatformModules] = useState([]);
  const [aiChatOpen, setAiChatOpen] = useState(false);
  const [buildHistory, setBuildHistory] = useState([]);
  const [setupComplete, setSetupComplete] = useState(false);
  
  // Real Dashboard Data State
  const [realDashboardData, setRealDashboardData] = useState(null);
  const [dashboardLoading, setDashboardLoading] = useState(true);
  
  // Delete confirmation state
  const [deleteConfirmation, setDeleteConfirmation] = useState(null);
  
  // AI Chat State
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [userName, setUserName] = useState(null);
  const chatInputRef = useRef(null);
  const chatMessagesRef = useRef(null);

  // Fixed Chat UI State
  const [chatWidth, setChatWidth] = useState('450px');
  const [chatHeight, setChatHeight] = useState('600px');

  // Interview State
  const [interviewActive, setInterviewActive] = useState(false);
  const [interviewStep, setInterviewStep] = useState(0);
  const [interviewData, setInterviewData] = useState({});

  // Auto-scroll effect
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [chatMessages, isProcessing]);

  // Load dashboard data and modules on startup
  useEffect(() => {
    loadRealDashboardData();
    loadExistingModules();
    initializeDefaultModules();
  }, []);

  // Load real dashboard data from backend
  const loadRealDashboardData = async () => {
    try {
      setDashboardLoading(true);
      const response = await fetch('http://localhost:3000/api/business-metrics');
      const data = await response.json();
      
      if (data.success) {
        setRealDashboardData(data.metrics);
        console.log('✅ Real dashboard data loaded:', data.metrics);
      } else {
        console.error('Failed to load dashboard data:', data.error);
      }
    } catch (error) {
      console.error('Dashboard data fetch error:', error);
    } finally {
      setDashboardLoading(false);
    }
  };

  // 🔧 FIXED: Enhanced loadExistingModules with better error handling
  const loadExistingModules = async () => {
    try {
      console.log('🔄 Loading existing modules...');
      const response = await fetch('http://localhost:3000/api/modules');
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
      
      const data = await response.json();
      
      if (data.success) {
        const builtModules = data.modules || [];
        const serverUserName = data.userName;
        
        console.log(`📦 Loaded ${builtModules.length} persistent modules from backend`);
        
        const convertedModules = builtModules.map(module => ({
          id: module.id,
          name: module.name,
          type: 'ai-generated',
          status: 'active',
          description: module.description,
          features: module.features || [],
          created: module.created,
          hasGPS: module.hasGPS || false,
          originalType: module.type
        }));
        
        setPlatformModules(prev => {
          const coreModules = prev.filter(m => m.type === 'core' || m.type === 'ai' || m.type === 'system');
          const newTotal = [...coreModules, ...convertedModules];
          console.log(`📊 Total modules after reload: ${newTotal.length} (${coreModules.length} core + ${convertedModules.length} AI)`);
          return newTotal;
        });
        
        if (serverUserName && serverUserName !== userName) {
          setUserName(serverUserName);
        }
        
        console.log(`👤 User: ${serverUserName || 'Anonymous'}`);
      } else {
        console.error('❌ Failed to load modules from backend:', data.error);
      }
    } catch (error) {
      console.error('💥 Failed to load existing modules:', error);
    }
  };

  const initializeDefaultModules = () => {
    setPlatformModules(prev => {
      const hasDefaults = prev.some(m => m.id === 'dashboard');
      if (hasDefaults) return prev;
      
      return [
        { 
          id: 'dashboard', 
          name: 'Dashboard', 
          type: 'core', 
          status: 'active',
          description: 'Executive overview and analytics'
        },
        { 
          id: 'business-setup', 
          name: 'Business Setup', 
          type: 'ai', 
          status: 'active',
          description: 'Aetheris interview to customize platform'
        },
        { 
          id: 'module-library', 
          name: 'Module Library', 
          type: 'system', 
          status: 'active',
          description: 'Manage all platform modules'
        }
      ];
    });

    setSetupComplete(true);
  };

  // Memoize dashboard data to prevent unnecessary re-renders
  const dashboardMetrics = useMemo(() => {
    if (!realDashboardData) {
      return [
        { label: 'Monthly Revenue', value: '$612,000', change: '+14.6%', icon: '💰', color: '#22c55e' },
        { label: 'Active Guards', value: '105', change: '+7.1%', icon: '👮', color: '#3b82f6' },
        { label: 'Client Sites', value: '21', change: '+16.7%', icon: '🏢', color: '#f59e0b' },
        { label: 'Efficiency Score', value: '97%', change: '+4.3%', icon: '⚡', color: '#8b5cf6' }
      ];
    }

    return [
      {
        label: 'Monthly Revenue',
        value: `$${realDashboardData.monthlyRevenue?.toLocaleString() || '0'}`,
        change: '+14.6%', // You can calculate this from historical data
        icon: '💰',
        color: '#22c55e',
        gradient: 'linear-gradient(135deg, rgba(34, 197, 94, 0.2), rgba(34, 197, 94, 0.1))'
      },
      {
        label: 'Active Guards',
        value: realDashboardData.activeGuards?.toString() || '0',
        change: '+7.1%',
        icon: '👮',
        color: '#3b82f6',
        gradient: 'linear-gradient(135deg, rgba(59, 130, 246, 0.2), rgba(59, 130, 246, 0.1))'
      },
      {
        label: 'Client Sites',
        value: realDashboardData.totalSites?.toString() || '0',
        change: '+16.7%',
        icon: '🏢',
        color: '#f59e0b',
        gradient: 'linear-gradient(135deg, rgba(245, 158, 11, 0.2), rgba(245, 158, 11, 0.1))'
      },
      {
        label: 'Total Clients',
        value: realDashboardData.totalClients?.toString() || '0',
        change: '+4.3%',
        icon: '🤝',
        color: '#8b5cf6',
        gradient: 'linear-gradient(135deg, rgba(139, 92, 246, 0.2), rgba(139, 92, 246, 0.1))'
      }
    ];
  }, [realDashboardData]);

  // Sample analytics data
  const performanceData = [
    { name: 'Jan', revenue: 425000, guards: 89, sites: 12, efficiency: 94 },
