    }
  };

  // 🔧 FIXED: Show delete confirmation (enhanced logging)
  const deleteModule = async (moduleId) => {
    const moduleToDelete = platformModules.find(m => m.id === moduleId);
    if (!moduleToDelete) {
      console.error('❌ Module not found for deletion:', moduleId);
      return;
    }
    
    console.log('🗑️ Showing delete confirmation for:', moduleToDelete.name, 'ID:', moduleId);
    
    // Show confirmation dialog
    setDeleteConfirmation({
      moduleId,
      moduleName: moduleToDelete.name,
      moduleType: moduleToDelete.type
    });
  };

  // 🔧 FIXED: Actually delete the module with proper reload
  const confirmDeleteModule = async () => {
    if (!deleteConfirmation) {
      console.error('❌ No delete confirmation found');
      return;
    }
    
    console.log('🔥 Attempting to delete module:', deleteConfirmation.moduleId);
    
    try {
      const response = await fetch(`http://localhost:3000/api/modules/${deleteConfirmation.moduleId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      
      console.log('🌐 Delete response status:', response.status, response.ok);
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
      
      const data = await response.json();
      console.log('📄 Delete response data:', data);
      
      if (data.success) {
        console.log('✅ Backend deletion successful, updating frontend...');
        
        // ✅ IMMEDIATE: Remove from frontend state
        setPlatformModules(prev => {
          const newModules = prev.filter(m => m.id !== deleteConfirmation.moduleId);
          console.log(`📊 Frontend modules: ${prev.length} → ${newModules.length}`);
          return newModules;
        });
        
        // ✅ FORCE RELOAD: Also reload from backend to ensure 100% sync
        console.log('🔄 Force reloading modules from backend...');
        setTimeout(async () => {
          try {
            await loadExistingModules();
            console.log('🔄 Backend reload completed');
          } catch (reloadError) {
            console.error('❌ Failed to reload modules:', reloadError);
          }
        }, 300);
        
        // Switch to dashboard if we deleted the active module
        if (activeModule === deleteConfirmation.moduleId) {
          setActiveModule('dashboard');
          console.log('🏠 Switched to dashboard (deleted active module)');
        }
        
        if (data.backupCreated) {
          console.log(`📦 Backup created: ${data.backupFile}`);
        }
        
        console.log('✅ Module deletion completed successfully');
        
      } else {
        console.error('❌ Backend deletion failed:', data.error);
        alert(`Failed to delete module: ${data.error}`);
      }
    } catch (error) {
      console.error('💥 Delete request failed:', error);
      alert(`Error deleting module: ${error.message}`);
    } finally {
      // Always close the confirmation dialog
      setDeleteConfirmation(null);
      console.log('🔒 Delete confirmation dialog closed');
    }
