    if (moduleId === 'module-library') return Code;
    if (moduleId === 'billing-invoicing') return DollarSign;
    if (moduleId === 'employee-management') return Users;
    if (moduleId === 'client-management') return Activity;
    if (moduleId === 'patrol-management') return Shield;
    if (moduleId === 'surveillance-integration') return Activity;
    if (moduleType === 'ai') return Wrench;
    if (moduleType === 'ai-generated') return Zap;
    return Shield;
