import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell, LineChart, Line } from 'recharts';

export default function AetherisMasterDashboard() {
  const [aetherisStatus, setAetherisStatus] = useState(null);
  const [aiCores, setAiCores] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [guidance, setGuidance] = useState(null);
  const [loading, setLoading] = useState(false);
  const [activeModule, setActiveModule] = useState('dashboard');
  const [systemLogs, setSystemLogs] = useState([]);

  // Connect to your Aetheris backend
  useEffect(() => {
    fetchAetherisStatus();
    fetchAiCores();
    
    // Poll for updates every 30 seconds
    const interval = setInterval(() => {
      fetchAetherisStatus();
    }, 30000);
    
    return () => clearInterval(interval);
  }, []);

  const fetchAetherisStatus = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/aetheris/status');
      const data = await response.json();
      setAetherisStatus(data);
      if (data.recentActivity) {
        setSystemLogs(data.recentActivity);
      }
    } catch (error) {
      console.error('Failed to fetch Aetheris status:', error);
    }
  };

  const fetchAiCores = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/aetheris/cores');
      const data = await response.json();
      setAiCores(data.cores || []);
    } catch (error) {
      console.error('Failed to fetch AI cores:', error);
    }
  };

  const askAetheris = async () => {
    if (!userInput.trim()) return;
    
    setLoading(true);
    try {
      const response = await fetch('http://localhost:3000/api/aetheris/guide', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: 'dashboard-user',
          issue: userInput
        })
      });
      const data = await response.json();
      setGuidance(data);
    } catch (error) {
      console.error('Failed to get Aetheris guidance:', error);
      setGuidance({ 
        guidance: 'Sorry, I encountered an issue. Please check if Aetheris is running on localhost:3000',
        timestamp: new Date()
      });
    }
    setLoading(false);
  };

  const coreColors = {
    'sentinel': '#3b82f6',
    'jarvis': '#10b981', 
    'visual': '#f59e0b',
    'security': '#ef4444',
    'kpi': '#8b5cf6',
    'integration': '#06b6d4',
    'training': '#84cc16',
    'finance': '#f97316',
    'client': '#ec4899'
  };

  const mockBusinessData = [
    { name: 'Security Services', revenue: 450000, clients: 12, guards: 85 },
    { name: 'Healthcare', revenue: 280000, clients: 8, employees: 45 },
    { name: 'Construction', revenue: 320000, clients: 6, workers: 60 },
    { name: 'Delivery', revenue: 180000, clients: 15, drivers: 35 }
  ];

  const performanceData = [
    { name: 'Jan', efficiency: 85, profit: 120000, issues: 12 },
    { name: 'Feb', efficiency: 88, profit: 135000, issues: 8 },
    { name: 'Mar', efficiency: 92, profit: 150000, issues: 5 },
    { name: 'Apr', efficiency: 89, profit: 142000, issues: 7 },
    { name: 'May', efficiency: 94, profit: 165000, issues: 3 }
  ];

  const cardStyle = {
    background: 'rgba(30, 41, 59, 0.8)',
    backdropFilter: 'blur(20px)',
    border: '1px solid rgba(71, 85, 105, 0.5)',
    borderRadius: '16px',
    padding: '24px',
    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)'
  };

  const buttonStyle = {
    background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
    border: 'none',
    padding: '12px 24px',
    borderRadius: '8px',
    color: 'white',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    boxShadow: '0 4px 15px rgba(59, 130, 246, 0.3)',
    fontSize: '14px'
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 30%, #334155 70%, #1e293b 100%)',
      fontFamily: "'Segoe UI', 'Inter', system-ui, sans-serif",
      color: '#e2e8f0'
    }}>
      {/* Header */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
        boxShadow: '0 4px 20px rgba(0, 0, 0, 0.4)',
        position: 'sticky',
        top: 0,
        zIndex: 50
      }}>
        <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '24px 32px' }}>
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
              <div style={{
                width: '64px',
                height: '64px',
                background: 'linear-gradient(135deg, #3b82f6 0%, #8b5cf6 50%, #ec4899 100%)',
                borderRadius: '16px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 10px 30px rgba(59, 130, 246, 0.4)',
                animation: 'pulseGlow 2s ease-in-out infinite alternate'
              }}>
                <span style={{ fontSize: '32px', fontWeight: 'bold', filter: 'drop-shadow(0 2px 4px rgba(0,0,0,0.5))' }}>âš¡</span>
              </div>
              <div>
                <h1 style={{ 
                  fontSize: '36px', 
                  fontWeight: '800', 
                  margin: '0 0 4px 0',
                  background: 'linear-gradient(135deg, #60a5fa, #a78bfa, #f472b6)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text',
                  letterSpacing: '-0.025em'
                }}>MIPFullSystem</h1>
                <p style={{ 
                  color: '#94a3b8', 
                  margin: '0',
                  fontSize: '16px',
                  fontWeight: '500',
                  letterSpacing: '0.025em'
                }}>Powered by Aetheris Master Architect AI</p>
              </div>
            </div>
            
            <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <div style={{
                  width: '16px',
                  height: '16px',
                  borderRadius: '50%',
                  background: aetherisStatus?.aetherisStatus === 'online' 
                    ? 'radial-gradient(circle, #10b981 0%, #059669 100%)'
                    : 'radial-gradient(circle, #ef4444 0%, #dc2626 100%)',
                  boxShadow: `0 0 20px ${aetherisStatus?.aetherisStatus === 'online' ? '#10b981' : '#ef4444'}`,
                  animation: aetherisStatus?.aetherisStatus === 'online' ? 'pulseGreen 2s ease-in-out infinite' : 'pulseRed 2s ease-in-out infinite'
                }}></div>
                <span style={{ 
                  fontSize: '16px', 
                  color: aetherisStatus?.aetherisStatus === 'online' ? '#10b981' : '#ef4444', 
                  fontWeight: '600'
                }}>
                  {aetherisStatus?.aetherisStatus === 'online' ? 'Aetheris Online' : 'Connecting...'}
                </span>
              </div>
              <div style={{
                background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
                padding: '12px 20px',
                borderRadius: '25px',
                fontSize: '16px',
                color: '#ffffff',
                fontWeight: '700',
                boxShadow: '0 6px 20px rgba(59, 130, 246, 0.4)',
                border: '1px solid rgba(255, 255, 255, 0.1)'
              }}>
                {aiCores.length} AI Cores Active
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <div style={{
        background: 'rgba(30, 41, 59, 0.7)',
        backdropFilter: 'blur(15px)',
        borderBottom: '1px solid rgba(71, 85, 105, 0.3)'
      }}>
        <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '0 32px' }}>
          <nav style={{ display: 'flex', gap: '8px' }}>
            {[
              { id: 'dashboard', name: 'ðŸ“Š Dashboard', icon: 'ðŸ“Š' },
              { id: 'ai-cores', name: 'ðŸ§  AI Cores', icon: 'ðŸ§ ' },
              { id: 'guidance', name: 'ðŸ§­ Aetheris Guidance', icon: 'ðŸ§­' },
              { id: 'modules', name: 'ðŸ› ï¸ Business Modules', icon: 'ðŸ› ï¸' },
              { id: 'analytics', name: 'ðŸ“ˆ Analytics', icon: 'ðŸ“ˆ' }
            ].map(item => (
              <button
                key={item.id}
                onClick={() => setActiveModule(item.id)}
                style={{
                  padding: '18px 24px',
                  fontSize: '16px',
                  fontWeight: '600',
                  borderBottom: activeModule === item.id ? '4px solid #3b82f6' : '4px solid transparent',
                  color: activeModule === item.id ? '#60a5fa' : '#94a3b8',
                  background: activeModule === item.id ? 'rgba(59, 130, 246, 0.1)' : 'transparent',
                  border: 'none',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  borderRadius: '8px 8px 0 0'
                }}
                onMouseEnter={(e) => {
                  if (activeModule !== item.id) {
                    e.target.style.color = '#ffffff';
                    e.target.style.background = 'rgba(59, 130, 246, 0.05)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (activeModule !== item.id) {
                    e.target.style.color = '#94a3b8';
                    e.target.style.background = 'transparent';
                  }
                }}
              >
                {item.name}
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Main Content */}
      <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '40px 32px' }}>
        {activeModule === 'dashboard' && (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '32px' }}>
            {/* System Overview */}
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', 
              gap: '24px' 
            }}>
              <div style={{
                ...cardStyle,
                background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.2), rgba(29, 78, 216, 0.1))',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                transform: 'scale(1)',
                cursor: 'pointer'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'scale(1.02) translateY(-4px)'}
              onMouseLeave={(e) => e.target.style.transform = 'scale(1) translateY(0)'}
              >
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '16px' }}>
                  <h3 style={{ fontSize: '20px', fontWeight: '700', color: '#ffffff', margin: 0 }}>ðŸ§  Aetheris Status</h3>
                  <span style={{ fontSize: '28px' }}>ðŸŒŸ</span>
                </div>
                <p style={{ fontSize: '42px', fontWeight: '800', color: '#60a5fa', margin: '8px 0' }}>
                  {aetherisStatus?.aetherisStatus || 'Loading...'}
                </p>
                <p style={{ color: '#93c5fd', fontSize: '16px', margin: 0, fontWeight: '500' }}>Master Architect AI</p>
              </div>
              
              <div style={{
                ...cardStyle,
                background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.2), rgba(5, 150, 105, 0.1))',
                border: '1px solid rgba(16, 185, 129, 0.3)',
                transform: 'scale(1)',
                cursor: 'pointer'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'scale(1.02) translateY(-4px)'}
              onMouseLeave={(e) => e.target.style.transform = 'scale(1) translateY(0)'}
              >
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '16px' }}>
                  <h3 style={{ fontSize: '20px', fontWeight: '700', color: '#ffffff', margin: 0 }}>ðŸ¤– AI Cores</h3>
                  <span style={{ fontSize: '28px' }}>ðŸ§ </span>
                </div>
                <p style={{ fontSize: '42px', fontWeight: '800', color: '#34d399', margin: '8px 0' }}>{aiCores.length}</p>
                <p style={{ color: '#86efac', fontSize: '16px', margin: 0, fontWeight: '500' }}>Specialized Agents</p>
              </div>
              
              <div style={{
                ...cardStyle,
                background: 'linear-gradient(135deg, rgba(139, 92, 246, 0.2), rgba(124, 58, 237, 0.1))',
                border: '1px solid rgba(139, 92, 246, 0.3)',
                transform: 'scale(1)',
                cursor: 'pointer'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'scale(1.02) translateY(-4px)'}
              onMouseLeave={(e) => e.target.style.transform = 'scale(1) translateY(0)'}
              >
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '16px' }}>
                  <h3 style={{ fontSize: '20px', fontWeight: '700', color: '#ffffff', margin: 0 }}>ðŸ¢ Platform Health</h3>
                  <span style={{ fontSize: '28px' }}>ðŸ’š</span>
                </div>
                <p style={{ fontSize: '42px', fontWeight: '800', color: '#a78bfa', margin: '8px 0', textTransform: 'capitalize' }}>
                  {aetherisStatus?.platformHealth?.status || 'Optimal'}
                </p>
                <p style={{ color: '#c4b5fd', fontSize: '16px', margin: 0, fontWeight: '500' }}>System Status</p>
              </div>
              
              <div style={{
                ...cardStyle,
                background: 'linear-gradient(135deg, rgba(249, 115, 22, 0.2), rgba(234, 88, 12, 0.1))',
                border: '1px solid rgba(249, 115, 22, 0.3)',
                transform: 'scale(1)',
                cursor: 'pointer'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'scale(1.02) translateY(-4px)'}
              onMouseLeave={(e) => e.target.style.transform = 'scale(1) translateY(0)'}
              >
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '16px' }}>
                  <h3 style={{ fontSize: '20px', fontWeight: '700', color: '#ffffff', margin: 0 }}>ðŸ“Š Active Modules</h3>
                  <span style={{ fontSize: '28px' }}>ðŸ› ï¸</span>
                </div>
                <p style={{ fontSize: '42px', fontWeight: '800', color: '#fb923c', margin: '8px 0' }}>15</p>
                <p style={{ color: '#fed7aa', fontSize: '16px', margin: 0, fontWeight: '500' }}>Business Modules</p>
              </div>
            </div>

            {/* Business Performance Charts */}
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: '1fr 1fr', 
              gap: '32px' 
            }}>
              <div style={{ ...cardStyle, minHeight: '400px' }}>
                <h3 style={{ fontSize: '24px', fontWeight: '700', color: '#ffffff', marginBottom: '24px', margin: '0 0 24px 0' }}>
                  ðŸ“ˆ Business Performance
                </h3>
                <ResponsiveContainer width="100%" height={320}>
                  <LineChart data={performanceData}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                    <XAxis dataKey="name" stroke="#9ca3af" fontSize={14} />
                    <YAxis stroke="#9ca3af" fontSize={14} />
                    <Tooltip 
                      contentStyle={{ 
                        backgroundColor: '#1f2937', 
                        border: '1px solid #374151', 
                        borderRadius: '8px',
                        color: '#ffffff'
                      }} 
                    />
                    <Legend />
                    <Line type="monotone" dataKey="efficiency" stroke="#3b82f6" strokeWidth={3} />
                    <Line type="monotone" dataKey="profit" stroke="#10b981" strokeWidth={3} />
                  </LineChart>
                </ResponsiveContainer>
              </div>

              <div style={{ ...cardStyle, minHeight: '400px' }}>
                <h3 style={{ fontSize: '24px', fontWeight: '700', color: '#ffffff', marginBottom: '24px', margin: '0 0 24px 0' }}>
                  ðŸŽ¯ Industry Distribution
                </h3>
                <ResponsiveContainer width="100%" height={320}>
                  <PieChart>
                    <Pie
                      data={mockBusinessData}
                      cx="50%"
                      cy="50%"
                      outerRadius={120}
                      fill="#8884d8"
                      dataKey="revenue"
                      label
                    >
                      {mockBusinessData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={Object.values(coreColors)[index]} />
                      ))}
                    </Pie>
                    <Tooltip 
                      contentStyle={{ 
                        backgroundColor: '#1f2937', 
                        border: '1px solid #374151', 
                        borderRadius: '8px',
                        color: '#ffffff'
                      }} 
                    />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        )}

        {activeModule === 'ai-cores' && (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '32px' }}>
            <h2 style={{ fontSize: '32px', fontWeight: '800', color: '#ffffff', margin: 0 }}>ðŸ§  AI Core Network</h2>
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))', 
              gap: '24px' 
            }}>
              {aiCores.map((core, index) => (
                <div key={core.id} style={{
                  ...cardStyle,
                  border: `1px solid ${coreColors[core.id] || '#71717a'}40`,
                  background: `linear-gradient(135deg, ${coreColors[core.id] || '#71717a'}15, rgba(30, 41, 59, 0.8))`,
                  transform: 'scale(1)',
                  cursor: 'pointer'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'scale(1.02) translateY(-6px)';
                  e.currentTarget.style.boxShadow = `0 15px 40px rgba(0, 0, 0, 0.4), 0 0 30px ${coreColors[core.id] || '#71717a'}30`;
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'scale(1) translateY(0)';
                  e.currentTarget.style.boxShadow = '0 10px 30px rgba(0, 0, 0, 0.3)';
                }}
                >
                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '20px' }}>
                    <h3 style={{ fontSize: '20px', fontWeight: '700', color: '#ffffff', margin: 0 }}>{core.name}</h3>
                    <div style={{
                      width: '16px',
                      height: '16px',
                      background: `radial-gradient(circle, ${coreColors[core.id] || '#10b981'}, ${coreColors[core.id] || '#059669'})`,
                      borderRadius: '50%',
                      boxShadow: `0 0 15px ${coreColors[core.id] || '#10b981'}`
                    }}></div>
                  </div>
                  <p style={{ color: '#d1d5db', fontSize: '16px', marginBottom: '20px', lineHeight: '1.6' }}>{core.role}</p>
                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', fontSize: '14px', marginBottom: '12px' }}>
                    <span style={{ color: '#94a3b8', fontWeight: '500' }}>Health:</span>
                    <span style={{ color: coreColors[core.id] || '#10b981', fontWeight: '700' }}>
                      {core.health || 'Optimal'}
                    </span>
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', fontSize: '14px' }}>
                    <span style={{ color: '#94a3b8', fontWeight: '500' }}>Last Active:</span>
                    <span style={{ color: '#60a5fa', fontWeight: '600' }}>
                      {core.lastActivity ? new Date(core.lastActivity).toLocaleTimeString() : 'Now'}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeModule === 'guidance' && (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '32px' }}>
            <h2 style={{ fontSize: '32px', fontWeight: '800', color: '#ffffff', margin: 0 }}>ðŸ§­ Ask Aetheris for Guidance</h2>
            
            <div style={{ ...cardStyle }}>
              <div style={{ display: 'flex', gap: '16px', marginBottom: '32px' }}>
                <input
                  type="text"
                  value={userInput}
                  onChange={(e) => setUserInput(e.target.value)}
                  placeholder="Ask Aetheris anything about your business platform..."
                  style={{
                    flex: 1,
                    background: 'rgba(71, 85, 105, 0.5)',
                    border: '2px solid rgba(71, 85, 105, 0.5)',
                    borderRadius: '12px',
                    padding: '16px 20px',
                    color: '#ffffff',
                    fontSize: '16px',
                    fontWeight: '500',
                    transition: 'all 0.3s ease'
                  }}
                  onFocus={(e) => e.target.style.border = '2px solid #3b82f6'}
                  onBlur={(e) => e.target.style.border = '2px solid rgba(71, 85, 105, 0.5)'}
                  onKeyPress={(e) => e.key === 'Enter' && askAetheris()}
                />
                <button
                  onClick={askAetheris}
                  disabled={loading}
                  style={{
                    ...buttonStyle,
                    padding: '16px 32px',
                    fontSize: '16px',
                    background: loading 
                      ? 'linear-gradient(135deg, #6b7280, #4b5563)' 
                      : 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
                    cursor: loading ? 'not-allowed' : 'pointer'
                  }}
                  onMouseEnter={(e) => {
                    if (!loading) {
                      e.target.style.transform = 'translateY(-2px)';
                      e.target.style.boxShadow = '0 8px 25px rgba(59, 130, 246, 0.4)';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (!loading) {
                      e.target.style.transform = 'translateY(0)';
                      e.target.style.boxShadow = '0 4px 15px rgba(59, 130, 246, 0.3)';
                    }
                  }}
                >
                  {loading ? 'ðŸ¤” Thinking...' : 'ðŸ§­ Ask Aetheris'}
                </button>
              </div>
              
              {guidance && (
                <div style={{
                  background: 'rgba(15, 23, 42, 0.8)',
                  borderRadius: '16px',
                  padding: '32px',
                  border: '2px solid rgba(59, 130, 246, 0.3)',
                  boxShadow: '0 0 30px rgba(59, 130, 246, 0.2)'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
                    <span style={{ fontSize: '32px', marginRight: '12px' }}>ðŸŒŸ</span>
                    <h3 style={{ fontSize: '24px', fontWeight: '700', color: '#60a5fa', margin: 0 }}>Aetheris Guidance</h3>
                    <span style={{ marginLeft: 'auto', fontSize: '14px', color: '#94a3b8', fontWeight: '500' }}>
                      {new Date(guidance.timestamp).toLocaleString()}
                    </span>
                  </div>
                  <p style={{ 
                    color: '#e2e8f0', 
                    lineHeight: '1.8', 
                    whiteSpace: 'pre-line', 
                    fontSize: '16px',
                    marginBottom: '24px'
                  }}>
                    {guidance.guidance}
                  </p>
                  
                  {guidance.recommendedCores && guidance.recommendedCores.length > 0 && (
                    <div>
                      <p style={{ fontSize: '16px', fontWeight: '600', color: '#93c5fd', marginBottom: '12px' }}>
                        Recommended AI Cores:
                      </p>
                      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
                        {guidance.recommendedCores.map(coreId => (
                          <span 
                            key={coreId} 
                            style={{ 
                              background: 'rgba(59, 130, 246, 0.2)', 
                              color: '#93c5fd', 
                              padding: '8px 16px', 
                              borderRadius: '20px', 
                              fontSize: '14px',
                              fontWeight: '600',
                              border: '1px solid rgba(59, 130, 246, 0.3)'
                            }}
                          >
                            {aiCores.find(c => c.id === coreId)?.name || coreId}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Quick Questions */}
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
              gap: '16px' 
            }}>
              {[
                "My security guards keep calling out sick",
                "How can I optimize my scheduling for maximum profit?", 
                "What's the best way to track employee performance?",
                "How do I prevent client churn?"
              ].map(question => (
                <button
                  key={question}
                  onClick={() => {
                    setUserInput(question);
                    askAetheris();
                  }}
                  style={{
                    background: 'rgba(71, 85, 105, 0.3)',
                    border: '2px solid rgba(71, 85, 105, 0.5)',
                    borderRadius: '12px',
                    padding: '20px',
                    textAlign: 'left',
                    color: '#d1d5db',
                    fontSize: '16px',
                    fontWeight: '500',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    lineHeight: '1.5'
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.background = 'rgba(59, 130, 246, 0.1)';
                    e.target.style.border = '2px solid rgba(59, 130, 246, 0.3)';
                    e.target.style.color = '#ffffff';
                    e.target.style.transform = 'translateY(-2px)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.background = 'rgba(71, 85, 105, 0.3)';
                    e.target.style.border = '2px solid rgba(71, 85, 105, 0.5)';
                    e.target.style.color = '#d1d5db';
                    e.target.style.transform = 'translateY(0)';
                  }}
                >
                  <span style={{ color: '#60a5fa', marginRight: '12px', fontSize: '18px' }}>ðŸ’¡</span>
                  {question}
                </button>
              ))}
            </div>
          </div>
        )}

        {activeModule === 'modules' && (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '32px' }}>
            <h2 style={{ fontSize: '32px', fontWeight: '800', color: '#ffffff', margin: 0 }}>ðŸ› ï¸ Business Modules</h2>
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', 
              gap: '24px' 
            }}>
              {[
                { name: 'GPS Tracking', icon: 'ðŸ“', status: 'Ready', description: 'Real-time location tracking', color: '#3b82f6' },
                { name: 'HR Management', icon: 'ðŸ‘¥', status: 'Ready', description: 'Employee records and management', color: '#10b981' },
                { name: 'Scheduling', icon: 'ðŸ“…', status: 'Ready', description: 'Intelligent shift scheduling', color: '#f59e0b' },
                { name: 'Incident Reporting', icon: 'ðŸ“‹', status: 'Ready', description: 'Digital incident reports', color: '#ef4444' },
                { name: 'Client Management', icon: 'ðŸ¢', status: 'Ready', description: 'CRM and client relations', color: '#8b5cf6' },
                { name: 'Payroll', icon: 'ðŸ’°', status: 'Ready', description: 'Automated payroll processing', color: '#06b6d4' },
                { name: 'Analytics', icon: 'ðŸ“Š', status: 'Ready', description: 'Business intelligence', color: '#84cc16' },
                { name: 'Mobile App', icon: 'ðŸ“±', status: 'Development', description: 'Field worker mobile app', color: '#f97316' },
                { name: 'Compliance', icon: 'âš–ï¸', status: 'Ready', description: 'Legal and compliance management', color: '#ec4899' }
              ].map(module => (
                <div key={module.name} style={{
                  ...cardStyle,
                  background: `linear-gradient(135deg, ${module.color}15, rgba(30, 41, 59, 0.8))`,
                  border: `1px solid ${module.color}40`,
                  transform: 'scale(1)',
                  cursor: 'pointer'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'scale(1.02) translateY(-4px)';
                  e.currentTarget.style.border = `1px solid ${module.color}60`;
                  e.currentTarget.style.boxShadow = `0 15px 40px rgba(0, 0, 0, 0.4), 0 0 20px ${module.color}30`;
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'scale(1) translateY(0)';
                  e.currentTarget.style.border = `1px solid ${module.color}40`;
                  e.currentTarget.style.boxShadow = '0 10px 30px rgba(0, 0, 0, 0.3)';
                }}
                >
                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '20px' }}>
                    <span style={{ fontSize: '40px', filter: 'drop-shadow(0 2px 4px rgba(0,0,0,0.5))' }}>{module.icon}</span>
                    <span style={{
                      padding: '6px 16px',
                      borderRadius: '20px',
                      fontSize: '14px',
                      fontWeight: '700',
                      background: module.status === 'Ready' 
                        ? 'linear-gradient(135deg, #10b981, #059669)'
                        : 'linear-gradient(135deg, #f59e0b, #d97706)',
                      color: '#ffffff',
                      boxShadow: `0 4px 15px ${module.status === 'Ready' ? '#10b98130' : '#f59e0b30'}`
                    }}>
                      {module.status}
                    </span>
                  </div>
                  <h3 style={{ fontSize: '22px', fontWeight: '700', color: '#ffffff', marginBottom: '12px', margin: '0 0 12px 0' }}>
                    {module.name}
                  </h3>
                  <p style={{ color: '#94a3b8', fontSize: '16px', marginBottom: '24px', lineHeight: '1.6' }}>
                    {module.description}
                  </p>
                  <button style={{
                    width: '100%',
                    background: `linear-gradient(135deg, ${module.color}, ${module.color}dd)`,
                    border: 'none',
                    padding: '14px 20px',
                    borderRadius: '10px',
                    color: '#ffffff',
                    fontWeight: '700',
                    fontSize: '16px',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: `0 4px 15px ${module.color}40`
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.transform = 'translateY(-2px)';
                    e.target.style.boxShadow = `0 8px 25px ${module.color}50`;
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = `0 4px 15px ${module.color}40`;
                  }}
                  >
                    Configure Module
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Add CSS animations */}
      <style>{`
        @keyframes pulseGlow {
          0% { box-shadow: 0 10px 30px rgba(59, 130, 246, 0.4); }
          50% { box-shadow: 0 10px 40px rgba(59, 130, 246, 0.6); }
          100% { box-shadow: 0 10px 30px rgba(59, 130, 246, 0.4); }
        }
        
        @keyframes pulseGreen {
          0% { box-shadow: 0 0 15px #10b981; }
          50% { box-shadow: 0 0 25px #10b981; }
          100% { box-shadow: 0 0 15px #10b981; }
        }
        
        @keyframes pulseRed {
          0% { box-shadow: 0 0 15px #ef4444; }
          50% { box-shadow: 0 0 25px #ef4444; }
          100% { box-shadow: 0 0 15px #ef4444; }
        }
      `}</style>
    </div>
  );
}
