// src/components/AI/AIBusinessAssistant.js
import React, { useState } from 'react';
import { Loader } from 'lucide-react';
import apiService from '../../services/apiService';

const AIBusinessAssistant = ({ userName, companyName, onChatOpen }) => {
  const [activeFeature, setActiveFeature] = useState(null);
  const [aiProcessing, setAiProcessing] = useState(false);

  const aiFeatures = [
    {
      id: 'profit-analysis',
      name: 'Profit Analysis',
      icon: 'ðŸ’°',
      description: 'AI calculates profit margins and client breakdowns',
      color: '#22c55e'
    },
    {
      id: 'business-intelligence',
      name: 'Business Intelligence',
      icon: 'ðŸ“Š',
      description: 'Comprehensive business health and recommendations',
      color: '#f59e0b'
    },
    {
      id: 'employee-analysis',
      name: 'Employee Insights',
      icon: 'ðŸ‘¥',
      description: 'Performance tracking and status management',
      color: '#8b5cf6'
    },
    {
      id: 'client-updates',
      name: 'Smart Client Updates',
      icon: 'ðŸ¢',
      description: 'Update billing rates with automatic revenue recalculation',
      color: '#3b82f6'
    }
  ];

  const executeAIFeature = async (featureId) => {
    setAiProcessing(true);
    setActiveFeature(featureId);
    
    try {
      let message;
      
      switch (featureId) {
        case 'profit-analysis':
          message = 'Give me a detailed profit analysis with client breakdowns';
          break;
        case 'business-intelligence':
          message = 'Provide a comprehensive business intelligence report with recommendations';
          break;
        case 'employee-analysis':
          message = 'Show me employee analysis with performance insights';
          break;
        case 'client-updates':
          message = 'Show me all clients with their current billing rates';
          break;
        default:
          message = 'Help me with business analytics';
      }
      
      // Simulate API call - in real implementation would use apiService
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      // Open chat to show results
      onChatOpen();
      
    } catch (error) {
      console.error('AI feature execution error:', error);
    } finally {
      setAiProcessing(false);
      setActiveFeature(null);
    }
  };

  return (
    <div style={{
      background: 'rgba(15, 23, 42, 0.8)',
      border: '1px solid rgba(59, 130, 246, 0.3)',
      borderRadius: '12px',
      padding: '24px',
      margin: '20px 0'
    }}>
      <h3 style={{ color: '#3b82f6', marginBottom: '16px' }}>
        ðŸ§  AI Business Assistant
      </h3>
      
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
        gap: '12px',
        marginBottom: '16px'
      }}>
        {aiFeatures.map(feature => (
          <button
            key={feature.id}
            onClick={() => executeAIFeature(feature.id)}
            disabled={aiProcessing}
            style={{
              padding: '16px',
              background: `linear-gradient(135deg, ${feature.color}20, ${feature.color}10)`,
              border: `1px solid ${feature.color}30`,
              borderRadius: '8px',
              color: '#e2e8f0',
              cursor: aiProcessing ? 'not-allowed' : 'pointer',
              textAlign: 'center',
              transition: 'all 0.3s ease'
            }}
          >
            <div style={{ fontSize: '24px', marginBottom: '8px' }}>{feature.icon}</div>
            <div style={{ fontWeight: '600', marginBottom: '4px' }}>{feature.name}</div>
            <div style={{ fontSize: '12px', color: '#94a3b8' }}>{feature.description}</div>
          </button>
        ))}
      </div>
      
      {aiProcessing && (
        <div style={{ textAlign: 'center', padding: '16px' }}>
          <Loader size={24} className="animate-spin" style={{ color: '#3b82f6' }} />
          <p style={{ color: '#94a3b8', marginTop: '8px', fontSize: '14px' }}>
            AI processing {aiFeatures.find(f => f.id === activeFeature)?.name}...
          </p>
        </div>
      )}
    </div>
  );
};

export default AIBusinessAssistant;
