import React, { useState, useEffect, useRef } from 'react';

const SentariOS = () => {
  // Core Application State
  const [currentView, setCurrentView] = useState('dashboard');
  const [modules, setModules] = useState([]);
  const [userName, setUserName] = useState('');
  const [companyName, setCompanyName] = useState('');
  const [businessData, setBusinessData] = useState({});
  
  // AI Chat State
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  
  // Interview State
  const [showInterview, setShowInterview] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [interviewAnswers, setInterviewAnswers] = useState({});
  const [interviewProgress, setInterviewProgress] = useState(0);
  
  const chatContainerRef = useRef(null);
  const chatInputRef = useRef(null);

  // Load data on startup
  useEffect(() => {
    loadModules();
    loadBusinessData();
  }, []);

  // Auto-scroll chat to bottom when new messages arrive
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatMessages]);

  // Load modules from backend
  const loadModules = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/modules');
      const data = await response.json();
      if (data.success) {
        setModules(data.modules || []);
        setUserName(data.userName || '');
        setCompanyName(data.companyName || '');
      }
    } catch (error) {
      console.error('Failed to load modules:', error);
    }
  };

  // Load business data
  const loadBusinessData = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/company-data');
      const data = await response.json();
      if (data.success) {
        setBusinessData(data.companyData || {});
        
        // Check if new user (no modules) and should show interview
        if ((!data.companyData.platformModules || data.companyData.platformModules.length === 0) && !showInterview) {
          setTimeout(() => setShowInterview(true), 2000);
        }
      }
    } catch (error) {
      console.error('Failed to load business data:', error);
    }
  };

  // Interview Questions
  const interviewQuestions = [
    "What industry is your business in? (e.g., Security, Healthcare, Manufacturing, Retail)",
    "How many employees do you currently have?",
    "What's your primary service offering to clients?",
    "Do you need scheduling and workforce management?",
    "Do you handle payroll internally or use external services?",
    "Do you need client relationship management (CRM) features?",
    "What types of reports do you generate for clients?",
    "Do you need mobile apps for field workers?",
    "What compliance requirements do you have? (HIPAA, SOX, etc.)",
    "What's your main business challenge we should address first?"
  ];

  // Process interview answers
  const processInterviewAnswer = async (answer) => {
    const newAnswers = { ...interviewAnswers, [currentQuestion]: answer };
    setInterviewAnswers(newAnswers);
    
    if (currentQuestion < interviewQuestions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setInterviewProgress(((currentQuestion + 1) / interviewQuestions.length) * 100);
    } else {
      // Interview complete - send to AI for module generation
      setInterviewProgress(100);
      setTimeout(async () => {
        await processInterviewCompletion(newAnswers);
        setShowInterview(false);
      }, 1000);
    }
  };

  // Complete interview and generate modules
  const processInterviewCompletion = async (answers) => {
    const interviewSummary = interviewQuestions.map((q, i) => 
      `Q: ${q}\nA: ${answers[i] || 'Not answered'}`
    ).join('\n\n');

    const systemMessage = `Based on this business interview, create 5-8 appropriate business modules for this company:

${interviewSummary}

Generate modules that specifically match their industry, size, and needs. Each module should be practical and immediately useful.`;

    try {
      const response = await fetch('http://localhost:3000/api/aetheris/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          message: systemMessage,
          userId: 'system',
          companyId: 'interview_completion'
        })
      });

      const data = await response.json();
      if (data.newModule || data.dataChanges) {
        loadModules(); // Refresh modules after AI generation
      }
    } catch (error) {
      console.error('Interview completion failed:', error);
    }
  };

  // Process AI chat message
  const processChatMessage = async () => {
    if (!currentMessage.trim() || isProcessing) return;

    const userMessage = currentMessage.trim();
    setCurrentMessage('');
    setIsProcessing(true);

    // Add user message to chat
    const newUserMessage = {
      role: 'user',
      content: userMessage,
      timestamp: new Date().toISOString()
    };

    setChatMessages(prev => [...prev, newUserMessage]);

    try {
      const response = await fetch('http://localhost:3000/api/aetheris/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          message: userMessage,
          userId: userName || 'user',
          companyId: 'markham_investigation'
        })
      });

      const data = await response.json();
      
      // Add AI response to chat
      const aiMessage = {
        role: 'assistant',
        content: data.response,
        timestamp: new Date().toISOString()
      };

      setChatMessages(prev => [...prev, aiMessage]);

      // Update data if changes were made
      if (data.newModule || data.dataChanges) {
        loadModules();
        loadBusinessData();
      }

      if (data.userName) setUserName(data.userName);
      if (data.companyName) setCompanyName(data.companyName);

    } catch (error) {
      const errorMessage = {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date().toISOString()
      };
      setChatMessages(prev => [...prev, errorMessage]);
    }

    setIsProcessing(false);
  };

  // Handle Enter key for chat
  const handleChatKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      processChatMessage();
    }
  };

  // Delete module
  const deleteModule = async (moduleId) => {
    try {
      const response = await fetch(`http://localhost:3000/api/modules/${moduleId}`, {
        method: 'DELETE'
      });
      if (response.ok) {
        loadModules();
      }
    } catch (error) {
      console.error('Failed to delete module:', error);
    }
  };

  // Calculate analytics from business data
  const analytics = {
    activeGuards: businessData?.employees?.filter(e => e.status === 'Active').length || 0,
    totalClients: businessData?.clients?.length || 0,
    monthlyRevenue: businessData?.clients?.reduce((sum, c) => sum + (c.monthly_revenue || 0), 0) || 0,
    totalSites: businessData?.sites?.length || 0,
    guardUtilization: 87,
    clientSatisfaction: 94,
    responseTime: 2.3,
    incidentResolution: 96
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #334155 100%)',
      color: '#e2e8f0',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, sans-serif"
    }}>
      
      {/* Header */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.9)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
        padding: '16px 24px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '12px'
        }}>
          <div style={{
            width: '32px',
            height: '32px',
            background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
            borderRadius: '8px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '16px',
            fontWeight: 'bold'
          }}>
            S
          </div>
          <div>
            <div style={{ fontSize: '18px', fontWeight: '600' }}>
              Sentari OS
            </div>
            <div style={{ fontSize: '12px', color: '#94a3b8' }}>
              AI Business Intelligence Platform
            </div>
          </div>
        </div>

        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <div style={{ fontSize: '14px', color: '#94a3b8' }}>
            {companyName && `${companyName} • `}
            {userName && `Welcome, ${userName}`}
          </div>
          
          <button
            onClick={() => setIsChatOpen(!isChatOpen)}
            style={{
              background: isChatOpen 
                ? 'linear-gradient(135deg, #ef4444, #dc2626)' 
                : 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
              border: 'none',
              borderRadius: '8px',
              color: 'white',
              padding: '8px 16px',
              fontSize: '14px',
              fontWeight: '500',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}
          >
            🧠 {isChatOpen ? 'Close Chat' : 'Chat with AI'}
          </button>
        </div>
      </div>

      <div style={{ display: 'flex', height: 'calc(100vh - 73px)' }}>
        
        {/* Sidebar */}
        <div style={{
          width: '280px',
          background: 'rgba(15, 23, 42, 0.8)',
          borderRight: '1px solid rgba(59, 130, 246, 0.2)',
          padding: '24px',
          overflow: 'auto'
        }}>
          <div style={{ marginBottom: '24px' }}>
            <h3 style={{ 
              fontSize: '16px', 
              fontWeight: '600', 
              marginBottom: '16px',
              color: '#f1f5f9'
            }}>
              Platform Modules
            </h3>
            
            {modules.length === 0 ? (
              <div style={{
                padding: '16px',
                background: 'rgba(59, 130, 246, 0.1)',
                borderRadius: '8px',
                border: '1px solid rgba(59, 130, 246, 0.2)',
                textAlign: 'center'
              }}>
                <div style={{ fontSize: '14px', color: '#94a3b8', marginBottom: '8px' }}>
                  No modules yet
                </div>
                <div style={{ fontSize: '12px', color: '#6b7280' }}>
                  Chat with AI to build your first module
                </div>
              </div>
            ) : (
              modules.map((module, index) => (
                <div
                  key={module.id || index}
                  style={{
                    background: 'rgba(30, 41, 59, 0.8)',
                    borderRadius: '8px',
                    padding: '12px',
                    marginBottom: '12px',
                    border: '1px solid rgba(59, 130, 246, 0.2)',
                    cursor: 'pointer'
                  }}
                  onClick={() => setCurrentView(`module-${module.id}`)}
                >
                  <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'flex-start',
                    marginBottom: '8px'
                  }}>
                    <div style={{ fontSize: '14px', fontWeight: '500', color: '#f1f5f9' }}>
                      {module.name}
                    </div>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        deleteModule(module.id);
                      }}
                      style={{
                        background: 'rgba(239, 68, 68, 0.2)',
                        border: '1px solid rgba(239, 68, 68, 0.3)',
                        borderRadius: '4px',
                        color: '#fca5a5',
                        fontSize: '10px',
                        padding: '2px 6px',
                        cursor: 'pointer'
                      }}
                    >
                      Delete
                    </button>
                  </div>
                  <div style={{ fontSize: '12px', color: '#94a3b8' }}>
                    {module.description}
                  </div>
                  {module.features && module.features.length > 0 && (
                    <div style={{ marginTop: '8px' }}>
                      <div style={{ fontSize: '10px', color: '#6b7280', marginBottom: '4px' }}>
                        Features:
                      </div>
                      <div style={{ fontSize: '11px', color: '#9ca3af' }}>
                        {module.features.slice(0, 2).join(', ')}
                        {module.features.length > 2 && '...'}
                      </div>
                    </div>
                  )}
                </div>
              ))
            )}
          </div>

          {/* Navigation */}
          <div style={{ marginTop: 'auto' }}>
            <div style={{ fontSize: '14px', fontWeight: '500', marginBottom: '12px', color: '#f1f5f9' }}>
              Navigation
            </div>
            {['dashboard', 'analytics', 'modules'].map((view) => (
              <button
                key={view}
                onClick={() => setCurrentView(view)}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  marginBottom: '4px',
                  background: currentView === view 
                    ? 'rgba(59, 130, 246, 0.2)' 
                    : 'transparent',
                  border: currentView === view 
                    ? '1px solid rgba(59, 130, 246, 0.4)' 
                    : '1px solid transparent',
                  borderRadius: '6px',
                  color: currentView === view ? '#60a5fa' : '#94a3b8',
                  fontSize: '13px',
                  cursor: 'pointer',
                  textAlign: 'left',
                  textTransform: 'capitalize'
                }}
              >
                {view}
              </button>
            ))}
          </div>
        </div>

        {/* Main Content */}
        <div style={{ 
          flex: 1, 
          background: 'rgba(30, 41, 59, 0.3)',
          position: 'relative'
        }}>
          
          {/* Dashboard View */}
          {currentView === 'dashboard' && (
            <div style={{ padding: '24px' }}>
              <div style={{ marginBottom: '24px' }}>
                <h2 style={{ fontSize: '24px', fontWeight: '700', marginBottom: '8px' }}>
                  Business Intelligence Dashboard
                </h2>
                <p style={{ color: '#94a3b8', fontSize: '14px' }}>
                  Real-time insights and performance metrics for {companyName || 'your business'}
                </p>
              </div>

              {/* KPI Cards */}
              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
                gap: '20px',
                marginBottom: '32px'
              }}>
                {[
                  { 
                    title: 'Active Guards', 
                    value: analytics.activeGuards, 
                    trend: '+8%',
                    gradient: 'linear-gradient(135deg, #10b981, #059669)'
                  },
                  { 
                    title: 'Total Clients', 
                    value: analytics.totalClients, 
                    trend: '+12%',
                    gradient: 'linear-gradient(135deg, #3b82f6, #2563eb)'
                  },
                  { 
                    title: 'Monthly Revenue', 
                    value: `$${analytics.monthlyRevenue.toLocaleString()}`, 
                    trend: '+15%',
                    gradient: 'linear-gradient(135deg, #8b5cf6, #7c3aed)'
                  },
                  { 
                    title: 'Site Coverage', 
                    value: analytics.totalSites, 
                    trend: '+5%',
                    gradient: 'linear-gradient(135deg, #f59e0b, #d97706)'
                  }
                ].map((metric, index) => (
                  <div
                    key={index}
                    style={{
                      background: 'rgba(15, 23, 42, 0.6)',
                      borderRadius: '12px',
                      padding: '20px',
                      border: '1px solid rgba(59, 130, 246, 0.2)',
                      position: 'relative',
                      overflow: 'hidden'
                    }}
                  >
                    <div style={{
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      right: 0,
                      height: '4px',
                      background: metric.gradient
                    }} />
                    
                    <div style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'flex-start',
                      marginBottom: '12px'
                    }}>
                      <div style={{ fontSize: '14px', color: '#94a3b8' }}>
                        {metric.title}
                      </div>
                      <div style={{
                        fontSize: '12px',
                        color: '#10b981',
                        background: 'rgba(16, 185, 129, 0.1)',
                        padding: '2px 6px',
                        borderRadius: '4px'
                      }}>
                        {metric.trend}
                      </div>
                    </div>
                    
                    <div style={{
                      fontSize: '28px',
                      fontWeight: '700',
                      color: '#f1f5f9'
                    }}>
                      {metric.value}
                    </div>
                  </div>
                ))}
              </div>

              {/* Performance Charts */}
              <div style={{
                display: 'grid',
                gridTemplateColumns: '2fr 1fr',
                gap: '24px'
              }}>
                {/* Revenue Trend */}
                <div style={{
                  background: 'rgba(15, 23, 42, 0.6)',
                  borderRadius: '12px',
                  padding: '24px',
                  border: '1px solid rgba(59, 130, 246, 0.2)'
                }}>
                  <h3 style={{ fontSize: '18px', fontWeight: '600', marginBottom: '16px' }}>
                    Revenue Trend
                  </h3>
                  <div style={{
                    height: '200px',
                    background: 'rgba(30, 41, 59, 0.5)',
                    borderRadius: '8px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: '#94a3b8'
                  }}>
                    📈 Revenue trending upward (+15% this quarter)
                  </div>
                </div>

                {/* Industry Distribution */}
                <div style={{
                  background: 'rgba(15, 23, 42, 0.6)',
                  borderRadius: '12px',
                  padding: '24px',
                  border: '1px solid rgba(59, 130, 246, 0.2)'
                }}>
                  <h3 style={{ fontSize: '18px', fontWeight: '600', marginBottom: '16px' }}>
                    Client Types
                  </h3>
                  <div style={{ marginTop: '16px' }}>
                    {[
                      { type: 'Retail', percentage: 45, color: '#3b82f6' },
                      { type: 'Healthcare', percentage: 30, color: '#10b981' },
                      { type: 'Corporate', percentage: 25, color: '#8b5cf6' }
                    ].map((item, index) => (
                      <div key={index} style={{ marginBottom: '12px' }}>
                        <div style={{
                          display: 'flex',
                          justifyContent: 'space-between',
                          fontSize: '13px',
                          marginBottom: '4px'
                        }}>
                          <span>{item.type}</span>
                          <span>{item.percentage}%</span>
                        </div>
                        <div style={{
                          height: '6px',
                          background: 'rgba(30, 41, 59, 0.5)',
                          borderRadius: '3px',
                          overflow: 'hidden'
                        }}>
                          <div style={{
                            width: `${item.percentage}%`,
                            height: '100%',
                            background: item.color,
                            borderRadius: '3px'
                          }} />
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Analytics View */}
          {currentView === 'analytics' && (
            <div style={{ padding: '24px' }}>
              <h2 style={{ fontSize: '24px', fontWeight: '700', marginBottom: '24px' }}>
                Advanced Analytics
              </h2>
              
              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
                gap: '20px' 
              }}>
                {[
                  { label: 'Guard Utilization', value: `${analytics.guardUtilization}%`, color: '#10b981' },
                  { label: 'Client Satisfaction', value: `${analytics.clientSatisfaction}%`, color: '#3b82f6' },
                  { label: 'Avg Response Time', value: `${analytics.responseTime}min`, color: '#f59e0b' },
                  { label: 'Incident Resolution', value: `${analytics.incidentResolution}%`, color: '#8b5cf6' }
                ].map((metric, index) => (
                  <div
                    key={index}
                    style={{
                      background: 'rgba(15, 23, 42, 0.6)',
                      borderRadius: '12px',
                      padding: '20px',
                      border: '1px solid rgba(59, 130, 246, 0.2)',
                      textAlign: 'center'
                    }}
                  >
                    <div style={{
                      fontSize: '28px',
                      fontWeight: '700',
                      color: metric.color,
                      marginBottom: '8px'
                    }}>
                      {metric.value}
                    </div>
                    <div style={{ fontSize: '14px', color: '#94a3b8' }}>
                      {metric.label}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Modules View */}
          {currentView === 'modules' && (
            <div style={{ padding: '24px' }}>
              <h2 style={{ fontSize: '24px', fontWeight: '700', marginBottom: '24px' }}>
                Module Library
              </h2>
              
              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', 
                gap: '20px' 
              }}>
                {modules.map((module, index) => (
                  <div
                    key={module.id || index}
                    style={{
                      background: 'rgba(15, 23, 42, 0.6)',
                      borderRadius: '12px',
                      padding: '20px',
                      border: '1px solid rgba(59, 130, 246, 0.2)'
                    }}
                  >
                    <div style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'flex-start',
                      marginBottom: '12px'
                    }}>
                      <h3 style={{ fontSize: '16px', fontWeight: '600', color: '#f1f5f9' }}>
                        {module.name}
                      </h3>
                      <button
                        onClick={() => deleteModule(module.id)}
                        style={{
                          background: 'rgba(239, 68, 68, 0.2)',
                          border: '1px solid rgba(239, 68, 68, 0.3)',
                          borderRadius: '6px',
                          color: '#fca5a5',
                          fontSize: '12px',
                          padding: '4px 8px',
                          cursor: 'pointer'
                        }}
                      >
                        Remove
                      </button>
                    </div>
                    <p style={{ fontSize: '14px', color: '#94a3b8', marginBottom: '12px' }}>
                      {module.description}
                    </p>
                    {module.features && module.features.length > 0 && (
                      <div>
                        <div style={{ fontSize: '12px', color: '#6b7280', marginBottom: '6px' }}>
                          Key Features:
                        </div>
                        <ul style={{ fontSize: '12px', color: '#9ca3af', margin: 0, paddingLeft: '16px' }}>
                          {module.features.slice(0, 3).map((feature, i) => (
                            <li key={i} style={{ marginBottom: '2px' }}>{feature}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Interview Modal */}
      {showInterview && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0, 0, 0, 0.8)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            background: 'linear-gradient(135deg, #1e293b, #334155)',
            borderRadius: '16px',
            padding: '32px',
            maxWidth: '500px',
            width: '90%',
            border: '1px solid rgba(59, 130, 246, 0.3)'
          }}>
            <div style={{ textAlign: 'center', marginBottom: '24px' }}>
              <h2 style={{ fontSize: '24px', fontWeight: '700', marginBottom: '8px' }}>
                Welcome to Sentari OS
              </h2>
              <p style={{ color: '#94a3b8', fontSize: '14px' }}>
                Let's customize your AI business platform
              </p>
            </div>

            {/* Progress Bar */}
            <div style={{
              width: '100%',
              height: '6px',
              background: 'rgba(30, 41, 59, 0.8)',
              borderRadius: '3px',
              marginBottom: '24px',
              overflow: 'hidden'
            }}>
              <div style={{
                width: `${interviewProgress}%`,
                height: '100%',
                background: 'linear-gradient(90deg, #3b82f6, #8b5cf6)',
                borderRadius: '3px',
                transition: 'width 0.3s ease'
              }} />
            </div>

            <div style={{ marginBottom: '24px' }}>
              <div style={{ fontSize: '14px', color: '#94a3b8', marginBottom: '8px' }}>
                Question {currentQuestion + 1} of {interviewQuestions.length}
              </div>
              <div style={{ fontSize: '16px', fontWeight: '500', lineHeight: '1.5' }}>
                {interviewQuestions[currentQuestion]}
              </div>
            </div>

            <div style={{ display: 'flex', gap: '12px' }}>
              <input
                type="text"
                placeholder="Your answer..."
                style={{
                  flex: 1,
                  padding: '12px',
                  background: 'rgba(15, 23, 42, 0.6)',
                  border: '1px solid rgba(59, 130, 246, 0.3)',
                  borderRadius: '8px',
                  color: '#e2e8f0',
                  fontSize: '14px'
                }}
                onKeyPress={(e) => {
                  if (e.key === 'Enter' && e.target.value.trim()) {
                    processInterviewAnswer(e.target.value.trim());
                    e.target.value = '';
                  }
                }}
              />
              <button
                onClick={(e) => {
                  const input = e.target.parentElement.querySelector('input');
                  if (input.value.trim()) {
                    processInterviewAnswer(input.value.trim());
                    input.value = '';
                  }
                }}
                style={{
                  background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                  border: 'none',
                  borderRadius: '8px',
                  color: 'white',
                  padding: '12px 20px',
                  fontSize: '14px',
                  fontWeight: '500',
                  cursor: 'pointer'
                }}
              >
                Next
              </button>
            </div>

            <button
              onClick={() => setShowInterview(false)}
              style={{
                background: 'transparent',
                border: '1px solid rgba(94, 124, 226, 0.3)',
                borderRadius: '8px',
                color: '#94a3b8',
                padding: '8px 16px',
                fontSize: '12px',
                cursor: 'pointer',
                width: '100%',
                marginTop: '16px'
              }}
            >
              Skip Interview (Use Default Modules)
            </button>
          </div>
        </div>
      )}

      {/* AI Chat Interface */}
      {isChatOpen && (
        <div style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          width: '400px',
          height: '500px',
          background: 'rgba(15, 23, 42, 0.95)',
          borderRadius: '16px',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          display: 'flex',
          flexDirection: 'column',
          zIndex: 1000,
          backdropFilter: 'blur(20px)'
        }}>
          {/* Chat Header */}
          <div style={{
            padding: '16px',
            borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <div>
              <div style={{ fontSize: '16px', fontWeight: '600' }}>
                🧠 Aetheris AI
              </div>
              <div style={{ fontSize: '12px', color: '#94a3b8' }}>
                Your Business Intelligence Assistant
              </div>
            </div>
            <button
              onClick={() => setIsChatOpen(false)}
              style={{
                background: 'rgba(239, 68, 68, 0.2)',
                border: '1px solid rgba(239, 68, 68, 0.3)',
                borderRadius: '6px',
                color: '#fca5a5',
                padding: '4px 8px',
                fontSize: '12px',
                cursor: 'pointer'
              }}
            >
              Close
            </button>
          </div>

          {/* Chat Messages */}
          <div
            ref={chatContainerRef}
            style={{
              flex: 1,
              padding: '16px',
              overflow: 'auto',
              display: 'flex',
              flexDirection: 'column',
              gap: '12px'
            }}
          >
            {chatMessages.length === 0 && (
              <div style={{
                textAlign: 'center',
                color: '#94a3b8',
                fontSize: '14px',
                marginTop: '40px'
              }}>
                <div style={{ fontSize: '32px', marginBottom: '12px' }}>🧠</div>
                <div style={{ fontWeight: '500', marginBottom: '8px' }}>
                  Aetheris AI Ready
                </div>
                <div style={{ fontSize: '12px', lineHeight: '1.4' }}>
                  Ask me anything about your business:<br/>
                  • "How many guards do we have?"<br/>
                  • "Build billing module"<br/>
                  • "What's our revenue this month?"
                </div>
              </div>
            )}

            {chatMessages.map((message, index) => (
              <div
                key={index}
                style={{
                  display: 'flex',
                  justifyContent: message.role === 'user' ? 'flex-end' : 'flex-start'
                }}
              >
                <div style={{
                  maxWidth: '80%',
                  padding: '12px',
                  borderRadius: '12px',
                  background: message.role === 'user' 
                    ? 'linear-gradient(135deg, #3b82f6, #8b5cf6)'
                    : 'rgba(30, 41, 59, 0.8)',
                  border: message.role === 'assistant' 
                    ? '1px solid rgba(59, 130, 246, 0.2)' 
                    : 'none',
                  fontSize: '14px',
                  lineHeight: '1.4'
                }}>
                  {message.content}
                </div>
              </div>
            ))}

            {isProcessing && (
              <div style={{
                display: 'flex',
                justifyContent: 'flex-start'
              }}>
                <div style={{
                  padding: '12px',
                  borderRadius: '12px',
                  background: 'rgba(30, 41, 59, 0.8)',
                  border: '1px solid rgba(59, 130, 246, 0.2)',
                  fontSize: '14px',
                  color: '#94a3b8'
                }}>
                  🧠 Aetheris is thinking...
                </div>
              </div>
            )}
          </div>

          {/* Chat Input */}
          <div style={{ padding: '16px', borderTop: '1px solid rgba(59, 130, 246, 0.2)' }}>
            <div style={{ display: 'flex', gap: '8px' }}>
              <textarea
                key="ai-chat-input"
                ref={chatInputRef}
                value={currentMessage}
                onChange={(e) => setCurrentMessage(e.target.value)}
                onKeyPress={handleChatKeyPress}
                placeholder="Chat with your AI assistant..."
                style={{
                  flex: 1,
                  padding: '12px',
                  background: 'rgba(15, 23, 42, 0.6)',
                  border: '1px solid rgba(59, 130, 246, 0.3)',
                  borderRadius: '6px',
                  color: '#e2e8f0',
                  fontSize: '14px',
                  resize: 'none',
                  minHeight: '44px',
                  maxHeight: '100px'
                }}
                disabled={isProcessing}
              />
              <button
                onClick={processChatMessage}
                disabled={isProcessing || !currentMessage.trim()}
                style={{
                  padding: '12px 16px',
                  background: isProcessing 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                  border: 'none',
                  borderRadius: '6px',
                  color: 'white',
                  fontSize: '14px',
                  cursor: isProcessing ? 'not-allowed' : 'pointer',
                  fontWeight: '500',
                  minWidth: '60px'
                }}
              >
                {isProcessing ? '⏳' : '🚀'}
              </button>
            </div>
            
            <div style={{ 
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              fontSize: '12px',
              color: '#6b7280',
              marginTop: '8px'
            }}>
              <span>💡 Fixed: Working AI + Module Building</span>
              <span>Shift+Enter for new line</span>
            </div>
          </div>
        </div>
      )}

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default SentariOS;
