import React, { useState, useEffect, useRef } from 'react';
import { Calendar, Users, MapPin, BarChart3, Clock, Shield, Zap, DollarSign, TrendingUp, Activity, X, MessageSquare, Wrench, Code, Settings } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, PieChart, Pie, Cell } from 'recharts';

const SentariOS = () => {
  // Core Platform State
  const [activeModule, setActiveModule] = useState('dashboard');
  const [platformModules, setPlatformModules] = useState([]);
  const [aiChatOpen, setAiChatOpen] = useState(false);
  const [buildHistory, setBuildHistory] = useState([]);
  const [setupComplete, setSetupComplete] = useState(false);
  
  // AI Chat State
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [userName, setUserName] = useState(null);
  const chatInputRef = useRef(null);
  const chatMessagesRef = useRef(null);

  // Fixed Chat UI State
  const [chatWidth, setChatWidth] = useState('450px');
  const [chatHeight, setChatHeight] = useState('600px');

  // Interview State
  const [interviewActive, setInterviewActive] = useState(false);
  const [interviewStep, setInterviewStep] = useState(0);
  const [interviewData, setInterviewData] = useState({});

  // Auto-scroll effect
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [chatMessages, isProcessing]);

  // Load existing modules on startup
  useEffect(() => {
    loadExistingModules();
    initializeDefaultModules();
  }, []);

  const loadExistingModules = async () => {
    try {
      console.log('ðŸ“‚ Loading existing modules...');
      const response = await fetch('http://localhost:3000/api/modules');
      const data = await response.json();
      
      if (data.success) {
        const builtModules = data.modules || [];
        const serverUserName = data.userName;
        
        console.log(`âœ… Loaded ${builtModules.length} persistent modules`);
        
        const convertedModules = builtModules.map(module => ({
          id: module.id,
          name: module.name,
          type: 'ai-generated',
          status: 'active',
          description: module.description,
          features: module.features || [],
          created: module.created,
          hasGPS: module.hasGPS || false,
          originalType: module.type
        }));
        
        setPlatformModules(prev => {
          const coreModules = prev.filter(m => m.type === 'core' || m.type === 'ai' || m.type === 'system');
          return [...coreModules, ...convertedModules];
        });
        
        if (serverUserName) {
          setUserName(serverUserName);
        }
        
        console.log(`ðŸ‘¤ User: ${serverUserName || 'Anonymous'}`);
      }
    } catch (error) {
      console.error('Failed to load existing modules:', error);
    }
  };

  const initializeDefaultModules = () => {
    setPlatformModules(prev => {
      const hasDefaults = prev.some(m => m.id === 'dashboard');
      if (hasDefaults) return prev;
      
      return [
        { 
          id: 'dashboard', 
          name: 'Dashboard', 
          type: 'core', 
          status: 'active',
          description: 'Executive overview and analytics'
        },
        { 
          id: 'business-setup', 
          name: 'Business Setup', 
          type: 'ai', 
          status: 'active',
          description: 'Aetheris interview to customize platform'
        },
        { 
          id: 'module-library', 
          name: 'Module Library', 
          type: 'system', 
          status: 'active',
          description: 'Manage all platform modules'
        }
      ];
    });

    setSetupComplete(true);
  };

  // Sample analytics data
  const performanceData = [
    { name: 'Jan', revenue: 425000, guards: 89, sites: 12, efficiency: 94 },
    { name: 'Feb', revenue: 468000, guards: 92, sites: 14, efficiency: 91 },
    { name: 'Mar', revenue: 510000, guards: 96, sites: 15, efficiency: 96 },
    { name: 'Apr', revenue: 485000, guards: 94, sites: 16, efficiency: 89 },
    { name: 'May', revenue: 534000, guards: 98, sites: 18, efficiency: 93 },
    { name: 'Jun', revenue: 612000, guards: 105, sites: 21, efficiency: 97 }
  ];

  const industryData = [
    { name: 'Security Services', value: 45, color: '#3b82f6' },
    { name: 'Healthcare', value: 25, color: '#10b981' },
    { name: 'Manufacturing', value: 20, color: '#f59e0b' },
    { name: 'Retail', value: 10, color: '#ef4444' }
  ];

  const interviewQuestions = [
    "What industry are you in? (Security, Healthcare, Manufacturing, Retail, etc.)",
    "How many employees do you currently manage?",
    "What software do you currently use for business operations?",
    "What are your biggest daily operational challenges?",
    "Do you handle scheduling, payroll, invoicing, or reporting manually?",
    "Do you work with multiple client sites or focus on one location?",
    "Do you use vendors or subcontractors in your business?",
    "What reports do you generate most often, and who receives them?",
    "Do your clients need access to any information or reports?",
    "Would you like your company logo on all forms and documents?"
  ];

  // Process AI Chat Messages
  const processChatMessage = async () => {
    if (!currentMessage.trim()) return;
    
    const userMessage = currentMessage;
    setCurrentMessage('');
    setIsProcessing(true);
    
    const newUserMessage = {
      id: Date.now(),
      sender: 'user',
      message: userMessage
    };
    setChatMessages(prev => [...prev, newUserMessage]);
    
    try {
      const response = await fetch(`http://localhost:3000/api/ai/chat`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userMessage })
      });
      
      const result = await response.json();
      
      const aiMessage = {
        id: Date.now() + 1,
        sender: 'aetheris',
        message: result.response
      };
      setChatMessages(prev => [...prev, aiMessage]);
      
      if (result.newModule) {
        const newPlatformModule = {
          id: result.newModule.id,
          name: result.newModule.name,
          type: 'ai-generated',
          status: 'active',
          description: result.newModule.description,
          features: result.newModule.features || [],
          created: result.newModule.created,
          hasGPS: result.newModule.hasGPS || false,
          originalType: result.newModule.type
        };
        
        setPlatformModules(prev => {
          const exists = prev.some(m => m.id === newPlatformModule.id);
          if (exists) return prev;
          
          return [...prev, newPlatformModule];
        });
        
        setBuildHistory(prev => [...prev, {
          request: userMessage,
          module: newPlatformModule,
          timestamp: new Date().toISOString()
        }]);
        
        console.log('ðŸ†• New module built:', result.newModule.name);
      }
      
      if (result.userName && result.userName !== userName) {
        setUserName(result.userName);
        console.log('ðŸ‘¤ User name updated:', result.userName);
      }
      
    } catch (error) {
      console.error('Chat error:', error);
      const errorMessage = {
        id: Date.now() + 1,
        sender: 'aetheris',
        message: 'âŒ **Connection Error**: Could not reach Aetheris backend. Make sure your server is running on port 3000!'
      };
      setChatMessages(prev => [...prev, errorMessage]);
    }
    
    setIsProcessing(false);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      processChatMessage();
    }
  };

  const deleteModule = async (moduleId) => {
    try {
      const response = await fetch(`http://localhost:3000/api/modules/${moduleId}`, {
        method: 'DELETE'
      });
      
      const data = await response.json();
      if (data.success) {
        setPlatformModules(prev => prev.filter(m => m.id !== moduleId));
        
        console.log('âœ… Module deleted:', moduleId);
        
        if (activeModule === moduleId) {
          setActiveModule('dashboard');
        }
      }
    } catch (error) {
      console.error('Failed to delete module:', error);
    }
  };

  // Business Setup Component
  const BusinessSetup = () => (
    <div style={{ 
      padding: '24px',
      background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(147, 51, 234, 0.1))',
      minHeight: '100vh'
    }}>
      <div style={{
        background: 'rgba(15, 23, 42, 0.8)',
        backdropFilter: 'blur(12px)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '12px',
        padding: '32px',
        maxWidth: '800px',
        margin: '0 auto'
      }}>
        <div style={{ textAlign: 'center', marginBottom: '32px' }}>
          <h1 style={{ 
            fontSize: '32px', 
            fontWeight: 'bold', 
            color: '#3b82f6',
            marginBottom: '8px',
            textShadow: '0 0 20px rgba(59, 130, 246, 0.5)'
          }}>
            ðŸ§  Aetheris Business Setup
          </h1>
          <p style={{ color: '#94a3b8', fontSize: '18px' }}>
            Let me understand your business and build a custom platform for you
          </p>
        </div>

        <div style={{
          background: 'rgba(15, 23, 42, 0.6)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '8px',
          height: '400px',
          marginBottom: '16px',
          display: 'flex',
          flexDirection: 'column'
        }}>
          <div style={{
            flex: 1,
            padding: '16px',
            overflowY: 'auto'
          }}>
            {chatMessages.map(msg => (
              <div key={msg.id} style={{
                marginBottom: '16px',
                display: 'flex',
                justifyContent: msg.sender === 'user' ? 'flex-end' : 'flex-start'
              }}>
                <div style={{
                  maxWidth: '80%',
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'rgba(139, 92, 246, 0.3)',
                  border: `1px solid ${msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.5)' 
                    : 'rgba(139, 92, 246, 0.5)'}`,
                  color: '#e2e8f0',
                  fontSize: '14px',
                  lineHeight: '1.4',
                  whiteSpace: 'pre-wrap'
                }}>
                  {msg.message}
                </div>
              </div>
            ))}
            {isProcessing && (
              <div style={{
                display: 'flex',
                justifyContent: 'flex-start',
                marginBottom: '16px'
              }}>
                <div style={{
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: 'rgba(139, 92, 246, 0.3)',
                  border: '1px solid rgba(139, 92, 246, 0.5)',
                  color: '#e2e8f0',
                  fontSize: '14px'
                }}>
                  ðŸ§  Aetheris is thinking...
                </div>
              </div>
            )}
          </div>

          <div style={{
            padding: '16px',
            borderTop: '1px solid rgba(59, 130, 246, 0.3)'
          }}>
            <div style={{ display: 'flex', gap: '8px' }}>
              <input
                ref={chatInputRef}
                key="business-setup-input"
                value={currentMessage}
                onChange={(e) => setCurrentMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask me anything: 'Build all modules', 'My name is...', 'Delete scheduling'..."
                style={{
                  flex: 1,
                  padding: '12px',
                  background: 'rgba(15, 23, 42, 0.6)',
                  border: '1px solid rgba(59, 130, 246, 0.3)',
                  borderRadius: '6px',
                  color: '#e2e8f0',
                  fontSize: '14px'
                }}
                disabled={isProcessing}
              />
              <button
                onClick={processChatMessage}
                disabled={isProcessing || !currentMessage.trim()}
                style={{
                  padding: '12px 16px',
                  background: isProcessing 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                  border: 'none',
                  borderRadius: '6px',
                  color: 'white',
                  fontSize: '14px',
                  cursor: isProcessing ? 'not-allowed' : 'pointer',
                  fontWeight: '500'
                }}
              >
                {isProcessing ? 'â³' : 'ðŸš€'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  // Dashboard Component
  const Dashboard = () => (
    <div style={{ 
      padding: '24px',
      background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(147, 51, 234, 0.1))',
      minHeight: '100vh'
    }}>
      <div style={{ textAlign: 'center', marginBottom: '32px' }}>
        <h1 style={{ 
          fontSize: '42px', 
          fontWeight: 'bold', 
          color: '#3b82f6',
          marginBottom: '8px',
          textShadow: '0 0 30px rgba(59, 130, 246, 0.6)'
        }}>
          ðŸ§  Sentari OS
        </h1>
        <p style={{ color: '#94a3b8', fontSize: '20px' }}>
          AI-Powered Business Intelligence Platform
        </p>
        <div style={{ 
          display: 'inline-block',
          padding: '8px 16px',
          background: 'rgba(34, 197, 94, 0.2)',
          border: '1px solid rgba(34, 197, 94, 0.3)',
          borderRadius: '20px',
          color: '#22c55e',
          fontSize: '14px',
          marginTop: '8px'
        }}>
          âœ… 12 AI Cores Online â€¢ {platformModules.filter(m => m.status === 'active').length} Modules Active
          {userName && ` â€¢ Welcome ${userName}!`}
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '24px', marginBottom: '32px' }}>
        {[
          { 
            label: 'Monthly Revenue', 
            value: '$612,000', 
            change: '+14.6%',
            icon: 'ðŸ’°', 
            color: '#22c55e',
            gradient: 'linear-gradient(135deg, rgba(34, 197, 94, 0.2), rgba(34, 197, 94, 0.1))'
          },
          { 
            label: 'Active Guards', 
            value: '105', 
            change: '+7.1%',
            icon: 'ðŸ‘®', 
            color: '#3b82f6',
            gradient: 'linear-gradient(135deg, rgba(59, 130, 246, 0.2), rgba(59, 130, 246, 0.1))'
          },
          { 
            label: 'Client Sites', 
            value: '21', 
            change: '+16.7%',
            icon: 'ðŸ¢', 
            color: '#f59e0b',
            gradient: 'linear-gradient(135deg, rgba(245, 158, 11, 0.2), rgba(245, 158, 11, 0.1))'
          },
          { 
            label: 'Efficiency Score', 
            value: '97%', 
            change: '+4.3%',
            icon: 'âš¡', 
            color: '#8b5cf6',
            gradient: 'linear-gradient(135deg, rgba(139, 92, 246, 0.2), rgba(139, 92, 246, 0.1))'
          }
        ].map((kpi, index) => (
          <div key={index} style={{
            padding: '28px',
            background: 'rgba(15, 23, 42, 0.8)',
            border: '1px solid rgba(59, 130, 246, 0.3)',
            borderRadius: '16px',
            backdropFilter: 'blur(12px)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            <div style={{
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: kpi.gradient,
              opacity: 0.5
            }} />
            
            <div style={{ position: 'relative', zIndex: 1 }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '12px' }}>
                <div style={{ fontSize: '32px' }}>{kpi.icon}</div>
                <div style={{ 
                  padding: '4px 8px',
                  background: 'rgba(34, 197, 94, 0.2)',
                  borderRadius: '12px',
                  color: '#22c55e',
                  fontSize: '12px',
                  fontWeight: '600'
                }}>
                  {kpi.change}
                </div>
              </div>
              <div style={{ fontSize: '36px', fontWeight: 'bold', color: kpi.color, marginBottom: '8px' }}>
                {kpi.value}
              </div>
              <div style={{ color: '#94a3b8', fontSize: '16px', fontWeight: '500' }}>
                {kpi.label}
              </div>
            </div>
          </div>
        ))}
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '24px', marginBottom: '32px' }}>
        <div style={{
          background: 'rgba(15, 23, 42, 0.8)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '16px',
          padding: '24px',
          backdropFilter: 'blur(12px)'
        }}>
          <h2 style={{ color: '#e2e8f0', fontSize: '22px', marginBottom: '20px', fontWeight: '600' }}>
            ðŸ“ˆ Performance Trends
          </h2>
          <ResponsiveContainer width="100%" height={280}>
            <AreaChart data={performanceData}>
              <defs>
                <linearGradient id="revenueGradient" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.3}/>
                  <stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(59, 130, 246, 0.1)" />
              <XAxis dataKey="name" stroke="#94a3b8" fontSize={12} />
              <YAxis stroke="#94a3b8" fontSize={12} />
              <Tooltip 
                contentStyle={{
                  background: 'rgba(15, 23, 42, 0.95)',
                  border: '1px solid rgba(59, 130, 246, 0.3)',
                  borderRadius: '8px',
                  color: '#e2e8f0',
                  boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)'
                }}
              />
              <Area 
                type="monotone" 
                dataKey="revenue" 
                stroke="#3b82f6" 
                fill="url(#revenueGradient)"
                strokeWidth={3}
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        <div style={{
          background: 'rgba(15, 23, 42, 0.8)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '16px',
          padding: '24px',
          backdropFilter: 'blur(12px)'
        }}>
          <h2 style={{ color: '#e2e8f0', fontSize: '22px', marginBottom: '20px', fontWeight: '600' }}>
            ðŸ­ Industry Mix
          </h2>
          <ResponsiveContainer width="100%" height={280}>
            <PieChart>
              <Pie
                data={industryData}
                cx="50%"
                cy="50%"
                innerRadius={60}
                outerRadius={90}
                paddingAngle={5}
                dataKey="value"
              >
                {industryData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip 
                contentStyle={{
                  background: 'rgba(15, 23, 42, 0.95)',
                  border: '1px solid rgba(59, 130, 246, 0.3)',
                  borderRadius: '8px',
                  color: '#e2e8f0'
                }}
              />
            </PieChart>
          </ResponsiveContainer>
          <div style={{ marginTop: '16px' }}>
            {industryData.map((item, index) => (
              <div key={index} style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                padding: '8px 0',
                borderBottom: index < industryData.length - 1 ? '1px solid rgba(59, 130, 246, 0.1)' : 'none'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                  <div style={{
                    width: '12px',
                    height: '12px',
                    borderRadius: '50%',
                    background: item.color
                  }} />
                  <span style={{ color: '#e2e8f0', fontSize: '14px' }}>{item.name}</span>
                </div>
                <span style={{ color: '#94a3b8', fontSize: '14px', fontWeight: '500' }}>
                  {item.value}%
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div style={{
        background: 'rgba(15, 23, 42, 0.8)',
        border: '1px solid rgba(59, 130, 246, 0.3)',
        borderRadius: '16px',
        padding: '28px',
        backdropFilter: 'blur(12px)'
      }}>
        <h2 style={{ color: '#e2e8f0', fontSize: '22px', marginBottom: '20px', fontWeight: '600' }}>
          ðŸš€ AI-Powered Quick Actions
        </h2>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px' }}>
          {[
            { label: 'Setup New Business', action: () => setActiveModule('business-setup'), icon: 'ðŸŽ¤', color: '#8b5cf6' },
            { label: 'Ask Aetheris Anything', action: () => setAiChatOpen(true), icon: 'ðŸ§ ', color: '#3b82f6' },
            { label: 'View All Modules', action: () => setActiveModule('module-library'), icon: 'ðŸ“¦', color: '#10b981' },
            { label: 'Platform Settings', action: () => alert('Settings coming soon!'), icon: 'âš™ï¸', color: '#f59e0b' }
          ].map((action, index) => (
            <button
              key={index}
              onClick={action.action}
              style={{
                padding: '20px',
                background: `linear-gradient(135deg, ${action.color}20, ${action.color}10)`,
                border: `1px solid ${action.color}30`,
                borderRadius: '12px',
                color: '#e2e8f0',
                cursor: 'pointer',
                textAlign: 'center',
                transition: 'all 0.3s ease',
                position: 'relative',
                overflow: 'hidden'
              }}
              onMouseEnter={(e) => {
                e.target.style.transform = 'translateY(-4px)';
                e.target.style.boxShadow = `0 12px 35px ${action.color}40`;
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = 'none';
              }}
            >
              <div style={{ fontSize: '32px', marginBottom: '12px' }}>{action.icon}</div>
              <div style={{ fontSize: '16px', fontWeight: '600' }}>{action.label}</div>
            </button>
          ))}
