// src/hooks/useChat.js
import { useState, useCallback, useRef, useEffect } from 'react';

const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:3000';

export const useChat = () => {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [pendingConfirmation, setPendingConfirmation] = useState(null);
  const [isWaitingForConfirmation, setIsWaitingForConfirmation] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to bottom when new messages arrive
  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  // Load chat history on mount
  useEffect(() => {
    const loadChatHistory = async () => {
      try {
        console.log('ðŸ”„ Loading chat history...');
        const response = await fetch(`${API_BASE}/api/chat/history`);
        
        if (!response.ok) {
          throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }
        
        const history = await response.json();
        console.log('ðŸ“œ Chat history loaded:', history.length, 'messages');
        
        // Transform backend format to frontend format
        const transformedHistory = Array.isArray(history) ? history.map(msg => ({
          role: msg.sender === 'user' ? 'user' : 'assistant',
          content: msg.message || '',
          timestamp: msg.timestamp,
          id: msg.id || Date.now() + Math.random(),
          sender: msg.sender // Keep original sender info for compatibility
        })) : [];
        
        setMessages(transformedHistory);
        console.log('âœ… Chat history transformed and loaded:', transformedHistory.length, 'messages');
      } catch (error) {
        console.error('âŒ Failed to load chat history:', error);
        setMessages([]); // Set to empty array on error
      }
    };
    
    loadChatHistory();
  }, []);

  // Send message to chat API
  const sendMessage = useCallback(async (messageText, isConfirmation = false) => {
    if (!messageText.trim() && !isConfirmation) return;

    console.log('ðŸ“¤ Sending message:', messageText);

    const userMessage = {
      role: 'user',
      content: messageText,
      timestamp: new Date().toISOString(),
      id: Date.now(),
      sender: 'user'
    };

    // Add user message to chat immediately
    if (!isConfirmation) {
      setMessages(prev => [...prev, userMessage]);
    }

    setIsLoading(true);

    try {
      const response = await fetch(`${API_BASE}/api/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: messageText,
          userId: 'tristen',
          companyId: 'Markham Investigation & Protection'
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const result = await response.json();
      console.log('ðŸ“¥ Chat response:', result);
      
      // Handle confirmation requests
      if (result.requiresConfirmation) {
        setPendingConfirmation(result.confirmationData);
        setIsWaitingForConfirmation(true);
        
        const confirmationMessage = {
          role: 'assistant',
          content: result.message,
          timestamp: new Date().toISOString(),
          id: Date.now() + 1,
          requiresConfirmation: true,
          sender: 'aetheris'
        };
        
        setMessages(prev => [...prev, confirmationMessage]);
      } else {
        // Regular response
        const assistantMessage = {
          role: 'assistant',
          content: result.message || 'No response received',
          timestamp: result.timestamp || new Date().toISOString(),
          id: Date.now() + 1,
          sender: 'aetheris'
        };
        
        setMessages(prev => [...prev, assistantMessage]);
        
        // Clear any pending confirmations
        setPendingConfirmation(null);
        setIsWaitingForConfirmation(false);
      }
    } catch (error) {
      console.error('âŒ Chat error:', error);
      
      const errorMessage = {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date().toISOString(),
        id: Date.now() + 1,
        error: true,
        sender: 'aetheris'
      };
      
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  }, []);

  // Handle confirmation responses
  const handleConfirmation = useCallback(async (confirmed) => {
    if (!pendingConfirmation) return;

    const confirmationText = confirmed ? 'yes' : 'no';
    
    // Add user's confirmation to chat
    const confirmationMessage = {
      role: 'user',
      content: `Confirmation: ${confirmationText}`,
      timestamp: new Date().toISOString(),
      id: Date.now(),
      isConfirmation: true,
      sender: 'user'
    };
    
    setMessages(prev => [...prev, confirmationMessage]);
    
    // Send confirmation to backend
    await sendMessage(confirmationText, true);
    
    // Clear confirmation state
    setPendingConfirmation(null);
    setIsWaitingForConfirmation(false);
  }, [pendingConfirmation, sendMessage]);

  // Cancel pending confirmation
  const cancelConfirmation = useCallback(() => {
    setPendingConfirmation(null);
    setIsWaitingForConfirmation(false);
    
    const cancelMessage = {
      role: 'assistant',
      content: 'Action cancelled.',
      timestamp: new Date().toISOString(),
      id: Date.now(),
      sender: 'aetheris'
    };
    
    setMessages(prev => [...prev, cancelMessage]);
  }, []);

  // Clear chat history
  const clearChat = useCallback(() => {
    setMessages([]);
    setPendingConfirmation(null);
    setIsWaitingForConfirmation(false);
  }, []);

  return {
    messages,
    isLoading,
    pendingConfirmation,
    isWaitingForConfirmation,
    messagesEndRef,
    sendMessage,
    handleConfirmation,
    cancelConfirmation,
    clearChat,
  };
};
