      }}>
        <div style={{ 
          padding: '24px', 
          borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
          textAlign: 'center'
        }}>
          <h1 style={{ 
            fontSize: '24px', 
            fontWeight: 'bold', 
            color: '#3b82f6',
            textShadow: '0 0 20px rgba(59, 130, 246, 0.5)',
            marginBottom: '8px'
          }}>
            ðŸ§  Sentari OS
          </h1>
          <p style={{ color: '#94a3b8', fontSize: '14px' }}>
            AI-Powered Business Platform
          </p>
          {/* ðŸ”§ VARIABLE-DRIVEN: User and company display */}
          {userName && (
            <div style={{ 
              marginTop: '8px',
              padding: '4px 8px',
              background: 'rgba(34, 197, 94, 0.2)',
              borderRadius: '12px',
              color: '#22c55e',
              fontSize: '12px'
            }}>
              Welcome, {userName}!
            </div>
          )}
          {companyInfo?.name && (
            <div style={{ 
              marginTop: '4px',
              padding: '4px 8px',
              background: 'rgba(139, 92, 246, 0.2)',
              borderRadius: '12px',
              color: '#8b5cf6',
              fontSize: '12px'
            }}>
              ðŸ¢ {companyInfo.name}
            </div>
          )}
        </div>

        <div style={{ flex: 1, padding: '16px' }}>
          {platformModules.filter(m => m.status === 'active').map(module => {
            const IconComponent = getModuleIcon(module.id, module.type);
            return (
              <button
                key={module.id}
                onClick={() => setActiveModule(module.id)}
                style={{
                  width: '100%',
                  padding: '16px',
                  marginBottom: '8px',
                  background: activeModule === module.id 
                    ? 'rgba(59, 130, 246, 0.2)' 
                    : 'transparent',
                  border: activeModule === module.id
                    ? '1px solid rgba(59, 130, 246, 0.5)'
                    : '1px solid transparent',
                  borderRadius: '8px',
                  color: activeModule === module.id ? '#3b82f6' : '#94a3b8',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '12px',
                  textAlign: 'left',
                  fontSize: '14px',
                  fontWeight: '500',
                  transition: 'all 0.2s ease'
                }}
              >
                <IconComponent size={20} />
                <span>{module.name}</span>
                {module.type === 'ai-generated' && (
                  <span style={{ 
                    background: 'rgba(34, 197, 94, 0.3)',
                    padding: '2px 6px',
                    borderRadius: '4px',
                    fontSize: '10px',
                    marginLeft: 'auto'
                  }}>
                    AI
                  </span>
                )}
              </button>
            );
          })}
        </div>

        <div style={{ 
          padding: '16px', 
          borderTop: '1px solid rgba(59, 130, 246, 0.2)',
          textAlign: 'center'
        }}>
          <div style={{ color: '#6b7280', fontSize: '12px' }}>
            ðŸš€ Powered by Variable-Driven Aetheris
          </div>
          <div style={{ color: '#22c55e', fontSize: '12px', fontWeight: '500' }}>
            No Hard-Coded Data - Dynamic Everything
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div style={{ flex: 1, overflow: 'auto' }}>
        {renderActiveModule()}
      </div>

      {/* ðŸ”§ VARIABLE-DRIVEN: AI Chat Button */}
      <button
        onClick={() => setAiChatOpen(!aiChatOpen)}
        style={{
          position: 'fixed',
          bottom: '24px',
          right: '24px',
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
          border: 'none',
          color: 'white',
          fontSize: '24px',
          cursor: 'pointer',
          boxShadow: '0 8px 25px rgba(59, 130, 246, 0.4)',
          zIndex: 1000,
          transition: 'all 0.3s ease'
        }}
      >
        ðŸ§ 
      </button>

      {/* ðŸ”§ VARIABLE-DRIVEN: Chat panel with dynamic content */}
      {aiChatOpen && (
        <div style={{
          position: 'fixed',
          bottom: '100px',
          right: '24px',
          width: chatWidth,
          height: chatHeight,
          minWidth: '400px',
          maxWidth: '800px',
          minHeight: '400px',
          maxHeight: '80vh',
          background: 'rgba(15, 23, 42, 0.95)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '12px',
          zIndex: 1000,
          boxShadow: '0 20px 50px rgba(0, 0, 0, 0.5)',
          display: 'flex',
          flexDirection: 'column',
          resize: 'both',
          overflow: 'hidden'
        }}>
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between', 
            padding: '20px',
            borderBottom: '1px solid rgba(59, 130, 246, 0.3)'
          }}>
            <div>
              <h3 style={{ color: '#3b82f6', fontSize: '18px', fontWeight: '600', margin: 0 }}>
                ðŸ§  Chat with Aetheris
              </h3>
              {userName && (
                <p style={{ color: '#94a3b8', fontSize: '12px', marginTop: '2px', margin: 0 }}>
                  Building for {userName}
                </p>
              )}
              {companyInfo?.name && (
                <p style={{ color: '#8b5cf6', fontSize: '12px', marginTop: '2px', margin: 0 }}>
                  ðŸ¢ {companyInfo.name}
                </p>
              )}
            </div>
            <button
              onClick={() => setAiChatOpen(false)}
              style={{
                background: 'none',
                border: 'none',
                color: '#94a3b8',
                cursor: 'pointer',
                padding: '4px'
              }}
            >
              <X size={20} />
            </button>
          </div>
          
          <div 
            ref={chatMessagesRef}
            style={{
              flex: 1,
              padding: '16px',
              overflowY: 'auto',
              scrollBehavior: 'smooth'
            }}
          >
            {/* ðŸ”§ VARIABLE-DRIVEN: Empty state with dynamic suggestions */}
            {chatMessages.length === 0 && (
              <div style={{
                textAlign: 'center',
                color: '#94a3b8',
                fontSize: '14px',
                marginTop: '40px'
              }}>
                <div style={{ fontSize: '48px', marginBottom: '16px' }}>ðŸ§ </div>
                <p>Ask me anything about your {companyInfo?.name || 'business'}:</p>
                <p style={{ fontSize: '12px', marginTop: '8px' }}>
                  {generateDynamicHelpText()}
                </p>
              </div>
            )}
            
            {chatMessages.map(msg => (
              <div key={msg.id} style={{
                marginBottom: '16px',
                display: 'flex',
                justifyContent: msg.sender === 'user' ? 'flex-end' : 'flex-start'
              }}>
                <div style={{
                  maxWidth: '85%',
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'rgba(139, 92, 246, 0.3)',
                  border: `1px solid ${msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.5)' 
                    : 'rgba(139, 92, 246, 0.5)'}`,
                  color: '#e2e8f0',
                  fontSize: '14px',
                  lineHeight: '1.5',
                  whiteSpace: 'pre-wrap',
                  wordWrap: 'break-word'
                }}>
                  {msg.message}
                </div>
              </div>
            ))}
            
            {isProcessing && (
              <div style={{
                display: 'flex',
                justifyContent: 'flex-start',
                marginBottom: '16px'
              }}>
                <div style={{
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: 'rgba(139, 92, 246, 0.3)',
                  border: '1px solid rgba(139, 92, 246, 0.5)',
                  color: '#e2e8f0',
                  fontSize: '14px',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}>
                  <div style={{
                    width: '16px',
                    height: '16px',
                    border: '2px solid rgba(255,255,255,0.3)',
                    borderRadius: '50%',
                    borderTopColor: 'white',
                    animation: 'spin 1s linear infinite'
                  }}></div>
                  Aetheris is thinking...
                </div>
              </div>
            )}
          </div>

          {/* ðŸ”§ VARIABLE-DRIVEN: Chat input with dynamic placeholder */}
          <div style={{
            padding: '16px',
            borderTop: '1px solid rgba(59, 130, 246, 0.3)'
          }}>
            <div style={{ display: 'flex', gap: '8px', marginBottom: '8px' }}>
              <textarea
                ref={chatInputRef}
                value={currentMessage}
                onChange={(e) => setCurrentMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={generateDynamicHelpText()}
                style={{
                  flex: 1,
                  padding: '12px',
                  background: 'rgba(15, 23, 42, 0.6)',
                  border: '1px solid rgba(59, 130, 246, 0.3)',
                  borderRadius: '6px',
                  color: '#e2e8f0',
                  fontSize: '14px',
                  resize: 'vertical',
                  minHeight: '44px',
                  maxHeight: '120px',
                  fontFamily: 'inherit',
                  outline: 'none'
                }}
                disabled={isProcessing}
                rows="1"
              />
              <button
                onClick={processChatMessage}
                disabled={isProcessing || !currentMessage.trim()}
                style={{
                  padding: '12px 16px',
                  background: isProcessing 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                  border: 'none',
                  borderRadius: '6px',
                  color: 'white',
                  fontSize: '14px',
                  cursor: isProcessing ? 'not-allowed' : 'pointer',
                  fontWeight: '500',
                  minWidth: '60px'
                }}
              >
                {isProcessing ? 'WAIT' : 'SEND'}
              </button>
            </div>
            
            <div style={{ 
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              fontSize: '12px',
              color: '#6b7280'
            }}>
              <span>ðŸ’¡ Variable-Driven: Dynamic Data + No Hard-Coding</span>
              <span>Shift+Enter for new line</span>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation Dialog */}
      <DeleteConfirmationDialog />

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        
        textarea:focus {
          border-color: rgba(59, 130, 246, 0.6) !important;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1) !important;
        }
      `}</style>
    </div>
  );
};

export default SentariOS;
