import React, { useState, useEffect, useRef } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, LineChart, Line, PieChart, Pie, Cell, Area, AreaChart } from 'recharts';
import { Users, Calendar, Shield, TrendingUp, Bell, Settings, Menu, X, Brain, Zap, Activity, DollarSign, Clock, MapPin, AlertTriangle } from 'lucide-react';

const SentariOS = () => {
  // State Management
  const [currentView, setCurrentView] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [modules, setModules] = useState([]);
  const [userName, setUserName] = useState('');
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  
  // AI Chat State
  const [aiChatOpen, setAiChatOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [chatWidth, setChatWidth] = useState('500px');
  const [chatHeight, setChatHeight] = useState('600px');
  
  // Refs for auto-scroll
  const chatMessagesRef = useRef(null);
  
  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [chatMessages]);

  // Load initial data
  useEffect(() => {
    loadDashboardData();
    loadModules();
  }, []);

  const loadDashboardData = async () => {
    try {
      const response = await fetch('/api/dashboard');
      const data = await response.json();
      if (data.success) {
        setDashboardData(data.data);
        setUserName(data.data.user.name);
      }
    } catch (error) {
      console.error('Error loading dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadModules = async () => {
    try {
      const response = await fetch('/api/modules');
      const data = await response.json();
      if (data.success) {
        setModules(data.modules);
      }
    } catch (error) {
      console.error('Error loading modules:', error);
    }
  };

  // AI Chat Functions
  const sendMessage = async () => {
    if (!chatInput.trim() || isProcessing) return;

    const userMessage = {
      id: Date.now(),
      sender: 'user',
      content: chatInput.trim(),
      timestamp: new Date().toLocaleTimeString()
    };

    setChatMessages(prev => [...prev, userMessage]);
    setIsProcessing(true);
    
    const messageToSend = chatInput.trim();
    setChatInput('');

    try {
      const response = await fetch('/api/aetheris/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: messageToSend })
      });

      const data = await response.json();
      
      if (data.success) {
        // Add AI response
        const aiMessage = {
          id: Date.now() + 1,
          sender: 'ai',
          content: data.response,
          timestamp: new Date().toLocaleTimeString()
        };
        
        setChatMessages(prev => [...prev, aiMessage]);
        
        // Update user context if provided
        if (data.userContext?.name) {
          setUserName(data.userContext.name);
        }
        
        // Reload modules if new ones were created
        if (data.modulesCreated?.length > 0) {
          await loadModules();
          await loadDashboardData();
        }
        
        // Reload modules if one was removed
        if (data.moduleRemoved) {
          await loadModules();
          await loadDashboardData();
        }
      } else {
        throw new Error(data.error || 'Unknown error');
      }
      
    } catch (error) {
      console.error('Chat error:', error);
      const errorMessage = {
        id: Date.now() + 1,
        sender: 'ai',
        content: `üö® Connection Error: ${error.message}\n\nPlease make sure your backend server is running on port 3000!`,
        timestamp: new Date().toLocaleTimeString()
      };
      setChatMessages(prev => [...prev, errorMessage]);
    }
    
    setIsProcessing(false);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // Sample data for charts
  const revenueData = [
    { month: 'Jan', revenue: 45000, profit: 12000 },
    { month: 'Feb', revenue: 52000, profit: 15000 },
    { month: 'Mar', revenue: 48000, profit: 13500 },
    { month: 'Apr', revenue: 61000, profit: 18000 },
    { month: 'May', revenue: 67000, profit: 21000 },
    { month: 'Jun', revenue: 73000, profit: 24000 }
  ];

  const guardPerformanceData = [
    { name: 'John Smith', rating: 4.8, shifts: 28 },
    { name: 'Sarah Johnson', rating: 4.6, shifts: 32 },
    { name: 'Mike Wilson', rating: 4.9, shifts: 25 },
    { name: 'Lisa Brown', rating: 4.7, shifts: 30 },
    { name: 'David Lee', rating: 4.5, shifts: 27 }
  ];

  const clientSatisfactionData = [
    { name: 'Very Satisfied', value: 65, color: '#10b981' },
    { name: 'Satisfied', value: 25, color: '#3b82f6' },
    { name: 'Neutral', value: 8, color: '#f59e0b' },
    { name: 'Unsatisfied', value: 2, color: '#ef4444' }
  ];

  // Navigation function
  const navigateToModule = (moduleId) => {
    setCurrentView(moduleId);
    setSidebarOpen(false);
  };

  // Dashboard Component
  const DashboardComponent = () => (
    <div className="space-y-6">
      {/* Welcome Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-8 rounded-xl">
        <h1 className="text-3xl font-bold mb-2">
          Welcome back, {dashboardData?.user?.name || 'User'}! üëã
        </h1>
        <p className="text-blue-100">
          Your {dashboardData?.user?.industry || 'business'} intelligence platform is running smoothly
        </p>
      </div>

      {/* Key Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">Total Revenue</p>
              <p className="text-2xl font-bold text-gray-900">$73,000</p>
              <p className="text-green-500 text-sm">‚Üó +12% from last month</p>
            </div>
            <DollarSign className="text-green-500" size={24} />
          </div>
        </div>
        
        <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">Active Guards</p>
              <p className="text-2xl font-bold text-gray-900">47</p>
              <p className="text-blue-500 text-sm">‚Üó +3 new hires</p>
            </div>
            <Users className="text-blue-500" size={24} />
          </div>
        </div>
        
        <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">Client Sites</p>
              <p className="text-2xl font-bold text-gray-900">23</p>
              <p className="text-purple-500 text-sm">‚Üó +2 new contracts</p>
            </div>
            <Shield className="text-purple-500" size={24} />
          </div>
        </div>
        
        <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">Platform Modules</p>
              <p className="text-2xl font-bold text-gray-900">{dashboardData?.metrics?.totalModules || 0}</p>
              <p className="text-orange-500 text-sm">{dashboardData?.metrics?.activeModules || 0} active</p>
            </div>
            <Activity className="text-orange-500" size={24} />
          </div>
        </div>
      </div>

      {/* Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Revenue Chart */}
        <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
          <h3 className="text-lg font-semibold mb-4">Revenue & Profit Trends</h3>
          <ResponsiveContainer width="100%" height={300}>
            <AreaChart data={revenueData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip formatter={(value) => [`$${value.toLocaleString()}`, '']} />
              <Area type="monotone" dataKey="revenue" stroke="#3b82f6" fill="#3b82f6" fillOpacity={0.3} />
              <Area type="monotone" dataKey="profit" stroke="#10b981" fill="#10b981" fillOpacity={0.3} />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        {/* Client Satisfaction */}
        <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
          <h3 className="text-lg font-semibold mb-4">Client Satisfaction</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={clientSatisfactionData}
                cx="50%"
                cy="50%"
                outerRadius={100}
                fill="#8884d8"
                dataKey="value"
                label={({name, value}) => `${name}: ${value}%`}
              >
                {clientSatisfactionData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Guard Performance */}
      <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
        <h3 className="text-lg font-semibold mb-4">Top Guard Performance</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={guardPerformanceData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="rating" fill="#3b82f6" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* Recent Activity & Quick Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
          <h3 className="text-lg font-semibold mb-4">Recent Activity</h3>
          <div className="space-y-3">
            {dashboardData?.recentActivity?.map((activity, index) => (
              <div key={index} className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                <span className="text-gray-700">{activity}</span>
              </div>
            ))}
          </div>
        </div>

        <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
          <h3 className="text-lg font-semibold mb-4">Quick Actions</h3>
          <div className="grid grid-cols-2 gap-3">
            {dashboardData?.quickActions?.map((action, index) => (
              <button
                key={index}
                onClick={() => action.action === 'open-ai-chat' ? setAiChatOpen(true) : null}
                className="flex flex-col items-center p-4 bg-gradient-to-br from-blue-50 to-purple-50 hover:from-blue-100 hover:to-purple-100 rounded-lg border border-gray-200 transition-all duration-200"
              >
                <span className="text-2xl mb-2">{action.icon}</span>
                <span className="text-sm font-medium text-gray-700">{action.label}</span>
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  // Module View Component
  const ModuleView = ({ moduleId }) => {
    const module = modules.find(m => m.id === moduleId);
    
    if (!module) {
      return (
        <div className="text-center py-20">
          <div className="text-6xl mb-4">üîß</div>
          <h2 className="text-2xl font-bold text-gray-700 mb-2">Module Not Found</h2>
          <p className="text-gray-500">The requested module could not be loaded.</p>
        </div>
      );
    }

    return (
      <div className="space-y-6">
        {/* Module Header */}
        <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-8 rounded-xl">
          <div className="flex items-center space-x-4">
            <span className="text-4xl">{module.icon}</span>
            <div>
              <h1 className="text-3xl font-bold">{module.name}</h1>
              <p className="text-blue-100">{module.description}</p>
            </div>
          </div>
        </div>

        {/* Module Content */}
        <div className="bg-white p-8 rounded-xl shadow-md border border-gray-200">
          <h3 className="text-xl font-semibold mb-4">Module Features</h3>
          
          {module.features && (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              {module.features.map((feature, index) => (
                <div key={index} className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-gray-700 capitalize">{feature.replace('_', ' ')}</span>
                </div>
              ))}
            </div>
          )}

          <div className="border-t pt-6">
            <h4 className="font-semibold mb-3">Module Status</h4>
            <div className="flex items-center space-x-4">
              <span className={`px-3 py-1 rounded-full text-sm ${
                module.status === 'active' ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'
              }`}>
                {module.status}
              </span>
              <span className="text-gray-500 text-sm">
                Created: {module.created ? new Date(module.created).toLocaleDateString() : 'System default'}
              </span>
              {module.createdBy && (
                <span className="text-gray-500 text-sm">
                  by {module.createdBy}
                </span>
              )}
            </div>
          </div>
        </div>

        {/* Module Actions */}
        <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200">
          <h4 className="font-semibold mb-4">Module Actions</h4>
          <div className="flex space-x-3">
            <button 
              onClick={() => setAiChatOpen(true)}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              üß† Enhance with AI
            </button>
            <button className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors">
              ‚öôÔ∏è Configure
            </button>
            <button className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors">
              üìä View Reports
            </button>
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading Sentari OS...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 flex">
      {/* Sidebar */}
      <div className={`bg-white shadow-lg transition-all duration-300 ${
        sidebarOpen ? 'w-64' : 'w-16'
      }`}>
        <div className="p-4">
          <div className="flex items-center justify-between">
            {sidebarOpen && (
              <h2 className="text-xl font-bold text-gray-800">Sentari OS</h2>
            )}
            <button
              onClick={() => setSidebarOpen(!sidebarOpen)}
              className="p-2 hover:bg-gray-100 rounded-lg"
            >
              <Menu size={20} />
            </button>
          </div>
        </div>

        {/* Navigation */}
        <nav className="mt-8">
          {modules.map(module => (
            <button
              key={module.id}
              onClick={() => navigateToModule(module.id)}
              className={`w-full flex items-center space-x-3 px-4 py-3 text-left hover:bg-gray-100 transition-colors ${
                currentView === module.id ? 'bg-blue-50 border-r-2 border-blue-600' : ''
              }`}
            >
              <span className="text-xl">{module.icon}</span>
              {sidebarOpen && (
                <div>
                  <span className="text-gray-700 font-medium">{module.name}</span>
                  {module.type === 'ai-generated' && (
                    <div className="text-xs text-purple-600">AI Generated</div>
                  )}
                </div>
              )}
            </button>
          ))}
        </nav>

        {/* AI Chat Button */}
        <div className="absolute bottom-4 left-4 right-4">
          <button
            onClick={() => setAiChatOpen(true)}
            className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-3 rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-200 flex items-center justify-center space-x-2"
          >
            <Brain size={20} />
            {sidebarOpen && <span>Chat with AI</span>}
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-8">
        {currentView === 'dashboard' ? (
          <DashboardComponent />
        ) : (
          <ModuleView moduleId={currentView} />
        )}
      </div>

      {/* Enhanced AI Chat Panel (Resizable & Auto-scroll) */}
      {aiChatOpen && (
        <div style={{
          position: 'fixed',
          bottom: '100px',
          right: '24px',
          width: chatWidth,
          height: chatHeight,
          minWidth: '400px',
          maxWidth: '800px',
          minHeight: '400px',
          maxHeight: '80vh',
          background: 'rgba(15, 23, 42, 0.95)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(59, 130, 246, 0.3)',
          borderRadius: '12px',
          zIndex: 1000,
          boxShadow: '0 20px 50px rgba(0, 0, 0, 0.5)',
          display: 'flex',
          flexDirection: 'column',
          resize: 'both',
          overflow: 'hidden'
        }}>
          {/* Header with resize indicator */}
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between', 
            padding: '20px',
            borderBottom: '1px solid rgba(59, 130, 246, 0.3)',
            cursor: 'move',
            userSelect: 'none'
          }}>
            <div>
              <h3 style={{ color: '#3b82f6', fontSize: '18px', fontWeight: '600', margin: 0 }}>
                üß† Chat with Aetheris
              </h3>
              {userName && (
                <p style={{ color: '#94a3b8', fontSize: '12px', marginTop: '2px', margin: 0 }}>
                  Building for {userName}
                </p>
              )}
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <div style={{ 
                color: '#6b7280', 
                fontSize: '12px',
                padding: '4px 8px',
                background: 'rgba(59, 130, 246, 0.1)',
                borderRadius: '4px'
              }}>
                Resizable
              </div>
              <button
                onClick={() => setAiChatOpen(false)}
                style={{
                  background: 'none',
                  border: 'none',
                  color: '#94a3b8',
                  cursor: 'pointer',
                  padding: '4px'
                }}
              >
                <X size={20} />
              </button>
            </div>
          </div>
          
          {/* Messages with auto-scroll */}
          <div 
            ref={chatMessagesRef}
            style={{
              flex: 1,
              padding: '16px',
              overflowY: 'auto',
              scrollBehavior: 'smooth'
            }}
          >
            {chatMessages.length === 0 && (
              <div style={{
                textAlign: 'center',
                color: '#94a3b8',
                fontSize: '14px',
                marginTop: '40px'
              }}>
                <div style={{ fontSize: '48px', marginBottom: '16px' }}>üß†</div>
                <p>Ask me anything about your security business:</p>
                <p style={{ fontSize: '12px', marginTop: '8px' }}>
                  "Build all modules" ‚Ä¢ "Delete scheduling" ‚Ä¢ "Show me our guards"
                </p>
              </div>
            )}
            
            {chatMessages.map(msg => (
              <div key={msg.id} style={{
                marginBottom: '16px',
                display: 'flex',
                justifyContent: msg.sender === 'user' ? 'flex-end' : 'flex-start'
              }}>
                <div style={{
                  maxWidth: '85%',
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'rgba(139, 92, 246, 0.3)',
                  border: `1px solid ${msg.sender === 'user' 
                    ? 'rgba(59, 130, 246, 0.5)' 
                    : 'rgba(139, 92, 246, 0.5)'}`,
                  color: '#f1f5f9',
                  fontSize: '14px',
                  lineHeight: '1.5',
                  whiteSpace: 'pre-wrap'
                }}>
                  <div style={{ marginBottom: '4px', fontSize: '12px', opacity: 0.7 }}>
                    {msg.sender === 'user' ? 'You' : 'üß† Aetheris'} ‚Ä¢ {msg.timestamp}
                  </div>
                  {msg.content}
                </div>
              </div>
            ))}
            
            {isProcessing && (
              <div style={{
                display: 'flex',
                justifyContent: 'flex-start',
                marginBottom: '16px'
              }}>
                <div style={{
                  padding: '12px 16px',
                  borderRadius: '12px',
                  background: 'rgba(139, 92, 246, 0.3)',
                  border: '1px solid rgba(139, 92, 246, 0.5)',
                  color: '#f1f5f9',
                  fontSize: '14px'
                }}>
                  <div style={{ marginBottom: '4px', fontSize: '12px', opacity: 0.7 }}>
                    üß† Aetheris ‚Ä¢ thinking...
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                    <div style={{
                      width: '8px',
                      height: '8px',
                      borderRadius: '50%',
                      background: '#3b82f6',
                      animation: 'pulse 1.5s ease-in-out infinite'
                    }}></div>
                    <span>Processing your request...</span>
                  </div>
                </div>
              </div>
            )}
          </div>
          
          {/* Enhanced Input Area */}
          <div style={{
            padding: '16px',
            borderTop: '1px solid rgba(59, 130, 246, 0.3)',
            background: 'rgba(30, 41, 59, 0.8)'
          }}>
            <div style={{
              display: 'flex',
              gap: '12px',
              alignItems: 'flex-end'
            }}>
              <textarea
                value={chatInput}
                onChange={(e) => setChatInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask me to build modules, manage your platform, or analyze your business..."
                style={{
                  flex: 1,
                  minHeight: '44px',
                  maxHeight: '120px',
                  padding: '12px',
                  border: '1px solid rgba(59, 130, 246, 0.3)',
                  borderRadius: '8px',
                  background: 'rgba(15, 23, 42, 0.8)',
                  color: '#f1f5f9',
                  fontSize: '14px',
                  resize: 'vertical',
                  fontFamily: 'inherit'
                }}
                rows={1}
              />
              <button
                onClick={sendMessage}
                disabled={isProcessing || !chatInput.trim()}
                style={{
                  padding: '12px 20px',
                  background: isProcessing || !chatInput.trim() 
                    ? 'rgba(59, 130, 246, 0.3)' 
                    : 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
                  border: 'none',
                  borderRadius: '6px',
                  color: 'white',
                  fontSize: '14px',
                  cursor: isProcessing ? 'not-allowed' : 'pointer',
                  fontWeight: '500',
                  minWidth: '60px'
                }}
              >
                {isProcessing ? '‚è≥' : 'üöÄ'}
              </button>
            </div>
            
            <div style={{ 
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              fontSize: '12px',
              color: '#6b7280',
              marginTop: '8px'
            }}>
              <span>üí° Enhanced: Working AI + Module Building</span>
              <span>Shift+Enter for new line</span>
            </div>
          </div>
        </div>
      )}

      <style>{`
        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.5; }
        }
      `}</style>
    </div>
  );
};

export default SentariOS;
