  const processChatMessage = async () => {
    if (!currentMessage.trim()) return;
    
    const userMessage = currentMessage;
    setCurrentMessage(''); // Clear immediately to prevent jumping
    setIsProcessing(true);
    
    // Add user message to chat
    const newUserMessage = {
      id: Date.now(),
      sender: 'user',
      message: userMessage
    };
    setChatMessages(prev => [...prev, newUserMessage]);
    
    try {
      // Check if user wants to start interview
      if (userMessage.toLowerCase().includes('start') && 
          (userMessage.toLowerCase().includes('setup') || 
           userMessage.toLowerCase().includes('interview') || 
           userMessage.toLowerCase().includes('customize'))) {
        setInterviewActive(true);
        setInterviewStep(0);
        
        const aiMessage = {
          id: Date.now() + 1,
          sender: 'aetheris',
          message: `ðŸŽ¤ **Starting Business Setup Interview**

Let's customize your platform for your specific business needs.

**Question 1:** What industry are you in? (Security, Healthcare, Manufacturing, Retail, Construction, etc.)

*Please provide a real industry name so I can build appropriate modules for you.*`
        };
        setChatMessages(prev => [...prev, aiMessage]);
        setIsProcessing(false);
        return;
      }
      
      // NORMAL CHAT - Use chat endpoint
      let endpoint = '/api/aetheris/chat';
      let requestBody = { message: userMessage };
      
      // Only use interview endpoint if interview is actually active
      if (interviewActive && interviewStep < interviewQuestions.length) {
        // Validate interview answers
        if (userMessage.trim().length < 2) {
          const aiMessage = {
            id: Date.now() + 1,
